(window.webpackJsonp=window.webpackJsonp||[]).push([[6],{308:function(n,h,i){"use strict";i.r(h);var t=i(21),c=Object(t.a)({},function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"binh-an-voi-chua"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#binh-an-voi-chua","aria-hidden":"true"}},[n._v("#")]),n._v(" Bình an với Chúa")]),n._v(" "),i("Badge",{attrs:{text:n.$page.readingTime.text,type:"tip"}}),n._v(" "),i("h2",{attrs:{id:"loi-dan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#loi-dan","aria-hidden":"true"}},[n._v("#")]),n._v(" Lời dẫn")]),n._v(" "),i("p",[n._v("Chúng ta đang sống trong một thế giới bất an. Có thể nói mọi tin tức chúng ta nghe hằng ngày đều là những tin không lành. Tất cả đều là những tin tức khiến chúng ta bận tâm dù có thể không trực tiếp liên quan đến chúng ta. Chúng ta đang sống trong thời kỳ tin học, mọi thông tin đến với chúng ta thật nhanh và thật nhiều chẳng những qua truyền thanh và truyền hình nhưng cũng qua những phương tiện khác như Internet và các hình thức thông tin như Facebook, Twitter, báo chí, v.v... Biết nhiều thì cũng tốt nhưng lắm khi quá nhiều thông tin cũng đem lại cho chúng ta nhiều lo nghĩ.")]),n._v(" "),i("p",[n._v("Nói như vậy không có nghĩa là chúng ta bịt tai lại hay làm ngơ kể như không có gì đang xảy ra. Vấn đề là làm sao chúng ta có thể đối diện, đối đầu với những thông tin không lấy gì làm tốt lành với tấm lòng an bình, không xao xuyến. An bình trong tâm hồn hay an bình nội tâm vì vậy là điều cần thiết để sống trên đời. Khó khăn trên đời là điều không tránh được, chúng ta phải đối đầu với những khó khăn đó. Nhưng đối đầu như thế nào, đó là điều quan trọng. Chúa Cứu Thế Giê-xu có lời hứa sau đây cho những người tin nơi Ngài. Chúa phán:")]),n._v(" "),i("p",[i("strong",[n._v("“Ta để bình an lại cho các con, Ta ban bình an Ta cho các con. Ta cho các con bình an chẳng phải như thế gian cho. Lòng các con chớ bối rối và đừng sợ hãi” (Giăng 14:27)")])]),n._v(" "),i("p",[i("strong",[n._v("“Ta đã bảo các con những điều đó hầu cho các con có lòng bình an trong Ta. Các con sẽ có hoạn nạn trong thế gian nhưng hãy cứ vững lòng, Ta đã thắng thế gian rồi” (Giăng 16:32)")])]),n._v(" "),i("p",[n._v("Thật không có gì an ủi, khích lệ chúng ta hơn những lời nầy. Biết mình sẽ phải đối diện với hoạn nạn, thử thách khó khăn nhưng vẫn an tâm, vững lòng vì biết rằng Chúa là Đấng chiến thắng, Chúa cầm quyền trên mọi thử thách, khó khăn đó. Trước hết chúng ta thấy rằng trong lời hứa nầy, Chúa phán về “lòng bình an trong Ta.” Bình an Chúa hứa ban cho chúng ta là bình an trong Chúa, bình an với Chúa mà suốt hai mươi chương sách này giúp cho chúng ta có cái nhìn đầy sự an ủi từ Đấng Bình An.")]),n._v(" "),i("h2",{attrs:{id:"chuong-1-cuoc-tim-kiem-vi-dai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1-cuoc-tim-kiem-vi-dai","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 1: Cuộc tìm kiếm vĩ đại")]),n._v(" "),i("p",[i("strong",[n._v('"Chúng ta được xưng công chính bởi đức tin thì được hòa thuận với Đức Chúa Trời, bởi Chúa Cứu Thế Giê-xu, Chúa chúng ta" (Rô-ma 5:1)')])]),n._v(" "),i("h3",{attrs:{id:"loi-mo-dau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#loi-mo-dau","aria-hidden":"true"}},[n._v("#")]),n._v(" Lời mở đầu")]),n._v(" "),i("h4",{attrs:{id:"phan-i-tham-dinh-tinh-hinh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#phan-i-tham-dinh-tinh-hinh","aria-hidden":"true"}},[n._v("#")]),n._v(" Phần I: Thẩm Định Tình Hình")]),n._v(" "),i("p",[i("strong",[n._v('"Các ngươi sẽ tìm kiếm ta và gặp được khi các ngươi tìm kiếm ta hết lòng" (Giê-rê-mi 29:13)')])]),n._v(" "),i("p",[n._v("Ngay từ giây phút chào đời, bạn đã khởi sự tham gia vào Một Cuộc Tìm Kiếm Vĩ Đại. Có lẽ phải nhiều năm sau bạn mới nhận ra rằng không giây phút nào bạn ngừng tìm kiếm, tìm kiếm điều bạn chưa bao giờ có, tìm kiếm một điều quan trọng hơn mọi thứ trong đời. Đôi lúc bạn cố quên công cuộc tìm kiếm đó đi để vùi đầu vào bao nhiêu việc khác, để không còn thì giờ cho bất cứ cái gì ngoại trừ những công việc phải làm trước mắt. Vì vậy có lúc bạn tưởng mình đã được giải phóng khỏi cái thôi thúc tìm kiếm điều không tên kia; có những khoảnh khắc bạn tưởng hầu như mình đã hoàn toàn loại trừ được nó, nhưng rồi nó vẫn trở lại với bạn, vẫn tiếp tục thúc bách bạn đi tìm.")]),n._v(" "),i("p",[n._v("Trong những giây phút cô đơn nhất trong đời, bạn nhìn người khác tự hỏi không biết họ có đang đi tìm như bạn không - tìm điều họ không thể mô tả nhưng biết mình muốn, mình cần. Một số có vẻ như đã tìm được thỏa nguyện trong hôn nhân và đời sống gia đình. Có người ra đi đạt được danh vọng và giàu có trong những vùng đất khác; có người lại thành công thịnh vượng ngay tại quê nhà. Nhìn những người đó có thể bạn đã nghĩ: Đây là những người không mất công lao vào Cuộc Kiếm Tìm Vĩ Đại. Những người này đã tìm được đường đi, biết điều mình muốn và đã đạt được. Chỉ có mình ta lầm lũi trên con đường chẳng dẫn đến đâu. Chỉ mình ta cứ tiếp tục hỏi, tìm, bước đi chệnh choạng trên lối mòn tăm tối và vô vọng không bảng chỉ đường.")]),n._v(" "),i("h5",{attrs:{id:"tieng-keu-cua-nhan-loai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#tieng-keu-cua-nhan-loai","aria-hidden":"true"}},[n._v("#")]),n._v(" Tiếng kêu của nhân loại")]),n._v(" "),i("p",[n._v("Nhưng bạn không cô đơn, vì toàn thể nhân loại đang cùng đi với bạn. Mọi người đều đang theo đuổi công cuộc tìm kiếm đó. Mọi người đang tìm giải đáp cho tình trạng rối loạn, đạo đức suy đồi và trống rỗng thuộc linh đang khống chế thế giới. Toàn thể nhân loại đang kêu gào xin được bình an, được chỉ dẫn và khích lệ.")]),n._v(" "),i("p",[n._v("Người ta bảo rằng chúng ta đang sống trong "),i("em",[n._v('"thời đại âu lo"')]),n._v('. Các sử gia cho thấy rằng ít có khi nào con người đã phải đương đầu với nhiều sợ hãi và bấp bênh như vậy. Tất cả những cây cột chống đỡ quen thuộc dường như đã bị cuốn trôi. Chúng ta nói về hòa bình nhưng quay đâu cũng đều chỉ thấy chiến tranh. Chúng ta hoạch định bao nhiêu kế hoạch an ninh mà không có an ninh. Chúng ta bám lấy mọi cái phao bập bềnh trôi qua, nhưng vừa khi nắm được, nó lại biến mất. Từ bao nhiêu thế hệ, chúng ta đã bỏ chạy như đám trẻ kinh hoảng, từ ngõ cụt này đến ngõ cụt khác. Lần nào chúng ta cũng đều tự nhủ: "Đúng rồi đây, đường này sẽ đưa ta đến nơi." Nhưng lần nào chúng ta cũng đều sai.')]),n._v(" "),i("h5",{attrs:{id:"con-duong-tu-do-chinh-tri"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#con-duong-tu-do-chinh-tri","aria-hidden":"true"}},[n._v("#")]),n._v(" Con đường tự do chính trị")]),n._v(" "),i("p",[n._v("Một trong những con đường đầu tiên chúng ta chọn có tên là "),i("em",[n._v('"tự do chính trị"')]),n._v(", bảo rằng nếu mọi người có tự do thì thế giới sẽ hạnh phúc. Hãy để chúng ta được quyền chọn lựa thành phần lãnh đạo thì chúng ta sẽ có một chính phủ đem lại cuộc đời đáng sống. Ngày nay dù đã đạt được tự do chính trị nhưng chúng ta vẫn chưa có một thế giới tốt đẹp hơn. Hàng ngày báo chí đăng tải đầy dẫy những chuyện tham ô ngay tại những cấp cao trong chính quyền, những chuyện bất công, bè phái, hối mại quyền thế đầy dẫy, không kém mà có khi lại tệ hại hơn tình trạng nhũng lạm trong các triều đình vua chúa ngày xưa. Tự do chính trị quan trọng và rất quí, nhưng không đủ đem lại cho chúng ta cái thế giới chúng ta mong ước.")]),n._v(" "),i("p",[n._v("Nhiều người đặt trọn niềm tin vào một con đường khác đầy triển vọng có tên là "),i("em",[n._v('"giáo dục"')]),n._v(". Họ bảo rằng tự do chính trị kết hợp với giáo dục sẽ giải quyết được hết, và tất cả chúng ta đã đổ xô về con đường đó. Đây dường như là một con đường tươi đẹp, lý thú, đầy ánh sáng. Chúng ta đã vui chân bước đi đầy hy vọng trên con đường này trong một thời gian khá lâu, nhưng nó đã đưa chúng ta đến đâu? Chính bạn đã biết câu trả lời rồi. Chúng ta là những người hiểu biết nhất trong cả lịch sử văn minh nhân loại - nhưng cũng là những người nhiều băn khoăn nhất. Các học sinh trung học của chúng ta ngày nay biết các định luật vật lý của vũ trụ nhiều hơn nhà khoa học lỗi lạc nhất của thời Aristotle. Tuy nhiên, dù đầu óc đầy ắp kiến thức, nhưng tấm lòng chúng ta hoàn toàn trống rỗng.")]),n._v(" "),i("p",[n._v("Trong tất cả các nẻo đường, con đường sáng sủa, mời gọi nhất có tên là "),i("em",[n._v('"nếp sống cao hơn"')]),n._v('. Hầu hết mọi người đều cảm thấy có thể tin cậy con đường này sẽ tự động đưa họ vào một thế giới tốt đẹp, vui tươi hơn. Người ta cảm thấy đây là con đường chắc ăn nhất, chỉ cần "bấm nút" là đạt được ngay. Đây là con đường đưa chúng ta qua bao nhiêu tạp chí quảng cáo đầy mầu sắc, qua bao nhiêu xe hơi lộng lẫy, hàng dãy tủ lạnh, máy giặt, máy sấy, với hằng hà sa số các mặt hàng tiêu dùng. Những con đường khác có vẻ đã sai lầm, nhưng lần này chúng ta biết là mình đã trúng số, đã chọn đúng đường!')]),n._v(" "),i("p",[n._v("Vâng, nhưng bây giờ chúng ta thử nhìn ra xung quanh. Vào chính giây phút này trong lịch sử, bạn thấy tự do chính trị tại Hoa Kỳ tốt đẹp đến độ ngay cả con người ở nhiều vùng đất văn minh trên thế giới cũng không dám mơ ước đến. Bạn cũng thấy tại đây con người đã thiết kế được một hệ thống giáo dục công lập rộng lớn chưa từng có, và chúng ta đã từng khoe khắp mọi nơi về tiêu chuẩn sống cao vượt bậc tại Hoa Kỳ. Khi đề cập đến "),i("em",[n._v('"nếp sống Mỹ"')]),n._v(" chúng ta muốn nói đến nếp sống điện khí hóa, hoàn toàn tự động, nói đến nền kinh tế tiêu thụ cao độ, nhưng vấn đề là nó có làm chúng ta hạnh phúc không? Nếp sống đó có đem lại cho chúng ta thoả nguyện, niềm vui, và lý do chúng ta sống cuộc đời đang tìm kiếm không?")]),n._v(" "),i("p",[n._v("Không. Ngay cả khi chúng ta ngồi đây cảm thấy thỏa nguyện và hãnh diện đã thành đạt được quá nhiều điều mà những thế hệ tiền bối chỉ dám ước mơ. Chúng ta đã vượt đại dương tính bằng giờ thay vì phải đi hàng tháng, chúng ta phát minh ra những loại thần dược xóa sạch một số căn bệnh nan y đáng sợ nhất của loài người. Chúng ta đã xây những tòa nhà khiến cho tháp Ba-bên ngày xưa đem so sánh thì chỉ như tổ kiến và khi chúng ta học biết nhiều hơn về những bí mật trong lòng biển sâu, thám hiểm xa hơn vào ngoại tầng không gian, chúng ta có làm giảm bớt đi một mảy may nào nỗi trống vắng trong lòng không? Tất cả những công trình hiện đại này có đem đến cho chúng ta một ý thức mãn nguyện nào và có giải thích cho chúng ta biết tại sao chúng ta có mặt ở đây, và có cho biết chúng ta phải học hỏi những điều gì không?")]),n._v(" "),i("p",[n._v("Hay cái cảm xúc trống vắng ghê gớm đó vẫn gan lì ám ảnh chúng ta? Phải chăng mỗi khám phá mới về tính cách bao la của vũ trụ an ủi chúng ta hay chỉ làm chúng ta cô đơn hơn, thất vọng hơn? Phải chăng liều thuốc giải độc chữa trị nỗi sợ hãi, lòng thù hận và tình trạng suy đồi của con người vẫn còn đang nằm trong ống thử tại phòng thí nghiệm hay trong viễn vọng kính của nhà thiên văn?")]),n._v(" "),i("h5",{attrs:{id:"kha-nang-thuyet-phuc-cua-khoa-hoc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#kha-nang-thuyet-phuc-cua-khoa-hoc","aria-hidden":"true"}},[n._v("#")]),n._v(" Khả năng thuyết phục của khoa học")]),n._v(" "),i("p",[n._v("Chúng ta không thể phủ nhận khoa học đã đem đến cho con người nhiều điều con người tưởng mình cần. Nhưng cũng chính nền khoa học này đã tặng nhân loại món quà kinh khủng hơn hết ảnh hưởng đến sự sống cũng như mọi sinh vật trên hành tinh này. Nó đứng sừng sững như một bóng đen đằng sau mọi suy tưởng của chúng ta. Nó lẻn vào như một bóng ác quỉ khống chế cả giấc mơ của trẻ em. Chúng ta tảng lờ như không có nó, như chúng ta chưa từng nhận món quà kinh khủng này và tất cả chỉ là trò đùa. Rồi một buổi sáng kia sau khi thức giấc chúng thấy con người chưa hề chinh phục được ngoại tầng không gian và khả năng hoàn thiện bom nguyên tử chưa hề có, trong khi đó thì các tờ nhật báo lại nói ngược hẳn lại!")]),n._v(" "),i("p",[n._v("Tất nhiên còn nhiều con đường khác nhiều người đang theo đuổi trong chính giờ phút này. Có những con đường danh vọng và giàu sang, con đường lạc thú và quyền lực. Tất cả những con đường đó không đưa đến đâu hết, chỉ kéo con người chìm sâu hơn xuống vũng lầy. Chúng ta bị sa vào bẫy là chính mạng lưới tư tưởng của mình, bị sập bẫy một cách tinh vi và toàn diện đến nỗi không thể thấy được cả nguyên nhân hoặc phương thuốc trị liệu căn bệnh đã làm chúng ta thống khổ.")]),n._v(" "),i("p",[n._v("Nếu đúng như điều người ta nói "),i("em",[n._v('"bệnh nào cũng có thuốc chữa"')]),n._v(" thì chắc chúng ta đã đi tìm ngay phương thuốc đó. Thời gian đang trôi nhanh và nếu có con đường nào đưa chúng ta đến với ánh sáng, nếu có cách nào phục hồi lại sức khỏe tâm linh thì chúng ta không được phép để mất dù chỉ một giờ!")]),n._v(" "),i("h5",{attrs:{id:"cong-cuoc-di-tim-giai-phap"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#cong-cuoc-di-tim-giai-phap","aria-hidden":"true"}},[n._v("#")]),n._v(" Công cuộc đi tìm giải pháp")]),n._v(" "),i("p",[n._v("Trong thời gian khủng hoảng này nhiều người đang cố sức vùng vẫy để thấy rằng những nỗ lực đó không nâng họ lên mà lại kéo họ chìm xuống sâu hơn trong hố thẳm. Trong thập niên 80, tỉ lệ tự tử đã tăng vọt. Trong vòng 10 năm, số thiếu niên quyên sinh trong lứa tuổi từ 10 - 14 tăng lên gấp ba. Tạp chí Leadership ước tính hàng năm có đến nửa triệu người toan tự vận, trong đó 50,000 trường hợp đã chết. Trong năm 1981 số người chết vì tự sát nhiều hơn số người bị mưu sát. Trong năm 1983, hàng ngàn người Mỹ, trong đó có nhiều thiếu niên vì không tìm ra giải đáp, dù là một giải đáp sai, nên đã thà quyên sinh còn hơn là tiếp tục lang thang vô định trong khu rừng nhân tạo mà người ta gọi nền văn minh.")]),n._v(" "),i("p",[n._v("Trong nhiều thập niên qua tỉ lệ ly hôn trong xã hội Mỹ và ngay cả trong Hội Thánh cũng leo thang. Cứ hai cuộc hôn nhân thì một kết thúc trong tan rã. Kể từ năm 1900 đến nay, tỉ lệ ly dị đã tăng 100%.")]),n._v(" "),i("p",[n._v("Người Mỹ đã tiêu rất nhiều tiền mua những con búp bê đặc biệt với hình thức như là "),i("em",[n._v('"nhận con nuôi"')]),n._v(" từ các cửa hàng đồ chơi, trong khi chính trẻ con Mỹ trở thành nạn nhân của tình trạng lạm dụng hay ngược đãi trẻ thơ hoặc trở thành nạn nhân của tệ nạn kinh khủng là in ấn các tranh ảnh gợi dục dùng trẻ nhỏ. Chúng ta cũng nghe nói về những nhu cầu đòi phá thai, về nhu cầu "),i("em",[n._v('"làm mẹ thuê" (surrogate motherhood)')]),n._v(", tức là nuôi bào thai thay cho những người không muốn mang thai và sinh nở sợ sẽ làm ảnh hưởng đến vẻ đẹp của thân hình hay sức khỏe, về sự hiện hữu của các "),i("em",[n._v('"ngân hàng tinh trùng"')]),n._v(", v.v... Định chế gia đình của chúng ta đang rơi vào khủng hoảng với đủ các loại lạm dụng, sai lầm, thác loạn.")]),n._v(" "),i("p",[n._v("Như vậy bạn sẽ hỏi, "),i("em",[n._v('"Chúng ta hiện đang ở đâu và sẽ đi về đâu?"')]),n._v(" Tôi sẽ cho bạn biết chúng ta đang ở đâu và chúng ta sẽ ra sao. Chúng ta là một dân tộc với những con người trống rỗng. Trí óc chúng ta đầy ắp kiến thức, nhưng trong linh hồn chúng ta là một khoảng trống tâm linh mênh mông. Trong quá khứ chúng ta than phiền rằng tuổi trẻ của đất nước này đã mất hết động lực và ý muốn hoạt động để vượt lên hàng đầu. Hàng ngày chúng ta nghe cha mẹ than trách không hiểu tại sao con cái họ không muốn làm việc mà chỉ muốn có sẵn mọi thứ. Các bậc cha mẹ dường như không nhận ra rằng những đứa con của họ tuy được nuôi dưỡng chu đáo, được học hành tới nơi tới chốn, nhưng bên trong hoàn toàn trống rỗng. Bên trong chúng nó không có cái tinh thần làm chúng nó vui thích trong công việc. Bên trong chúng nó không có một quyết tâm khiến cho nỗ lực vươn lên trở thành niềm vui. Tại sao tâm hồn con cái chúng ta trống vắng như vậy? Bởi vì chúng không biết mình đến từ đâu, tại sao ở đây và rồi sẽ đi đâu.")]),n._v(" "),i("p",[n._v("Những người trẻ của chúng ta hôm nay đang thắc mắc về hướng đi và về viễn ảnh tương lai. Họ đang đi tìm mục tiêu và những gương mẫu để noi theo. Họ giống như những dãy xe hơi bóng lộn, tốt đẹp hoàn toàn nhưng bình xăng trống rỗng. Ngoại hình thì đẹp, nhưng không có năng lực gì bên trong cho nên họ đành phải đứng yên, phải ngồi đó, ủ rũ, rỉ sét vì buồn chán.")]),n._v(" "),i("h5",{attrs:{id:"chieu-rong-cua-noi-buon"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chieu-rong-cua-noi-buon","aria-hidden":"true"}},[n._v("#")]),n._v(" Chiều rộng của nỗi buồn")]),n._v(" "),i("p",[n._v("Người ta bảo rằng Hoa Kỳ là nơi có tỉ lệ buồn chán tính trên đầu người cao nhất thế giới! Chúng ta biết như vậy là vì tại đây có con số những thú tiêu khiển và trò giải trí nhiều nhất và đa dạng nhất. Con người đã quá trống rỗng đến nỗi họ không thể tự giải khuây mà phải trả tiền cho người khác giúp vui và đem lại cho họ vài giây phút vui vẻ, thoải mái, ấm áp, để cố xua đi cái cảm xúc trống vắng ghê gớm, đáng sợ, cái cảm giác kinh khủng, hãi hùng trong lúc cô đơn lạc lõng trên đường vắng.")]),n._v(" "),i("p",[n._v("Có thể bạn cho buồn chán là chuyện nhỏ nhặt, vì ai mà chẳng có lúc buồn và đó là điều tự nhiên. Nhưng để tôi nói thêm cho bạn điều này về nỗi buồn, về cái tình trạng lãnh đạm nguy hiểm đang lan rộng cả nước, chụp phủ trí óc và tâm hồn mọi người. Con người là tạo vật duy nhất của Đức Chúa Trời có khả năng biết chán mặc dù tôi cũng đã thấy có nhiều con vật trong sở thú trông dáng vẻ rất buồn chán. Không một loài sống nào trừ con người biết buồn chán với chính mình hoặc với môi trường xung quanh. Điều này rất quan trọng vì Đấng Tạo Hóa không bao giờ làm gì vô mục đích cho nên nếu Ngài phú ban cho con người khả năng buồn chán thì chắc hẳn Chúa có mục đích cho việc đó.")]),n._v(" "),i("p",[n._v("Buồn chán là một trong những cách chắc chắn nhất đo lường mức trống rỗng nội tâm. Đó là cái nhiệt kế rất chính xác về thực tại trống rỗng tâm linh của bạn. Một người chán nản toàn diện là người sống và làm việc trong khoảng chân không. Con người sâu kín bên trong cũng là khoảng chân không, trong khi đó không có gì thiên nhiên ghét hơn là khoảng chân không, vì vậy một trong những qui luật bất di của vũ trụ này là tất cả mọi khoảng chân không phải được lấp đầy và lấp đầy tức khắc.")]),n._v(" "),i("h5",{attrs:{id:"mot-dan-toc-trong-rong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-dan-toc-trong-rong","aria-hidden":"true"}},[n._v("#")]),n._v(" Một dân tộc trống rỗng")]),n._v(" "),i("p",[n._v("Chúng ta không cần phải trở lại thời cổ đại mới có thể thấy được những gì đã xảy ra cho một dân tộc có tâm hồn trống rỗng, vì ngay trong lịch sử cận đại của nước Đức và nước Ý chúng ta có thể thấy điều kinh khủng đã khiến cho thiên nhiên nhanh chóng lấp đầy khoảng trống mênh mông trong chúng ta. Chủ nghĩa Quốc xã Đức và Phát xít Ý không thể nào tìm được chỗ đứng trong lòng và trong linh hồn một người được đổ đầy Thánh Linh của Đức Chúa Trời, nhưng những lý tưởng sai lầm sẽ dễ dàng tràn ngay vào những tâm trí và tấm lòng trống rỗng đang khắc khoải đợi chờ. Thiên nhiên rất ghét khoảng chân không nhưng mỗi cá nhân sẽ là người quyết định sẽ đổ đầy những khoảng chân không nội tâm đó bằng thứ gì.")]),n._v(" "),i("p",[n._v("Đó chính là chỗ đứng của chúng ta hôm nay - là quốc gia với những con người trống rỗng. Chúng ta đang cố gắng đổ đầy tâm trí bằng khoa học và giáo dục, bằng lạc thú và cuộc sống khá hơn với bao nhiêu thứ chúng ta tưởng rằng mình mong muốn. Trong một thái cực, chúng ta ngày càng làm suy đồi chủ nghĩa tư bản, và ở thái cực kia chúng ta tấn công chủ nghĩa cộng sản vô thần, dầu vậy, chúng ta vẫn hoàn toàn trống rỗng. Tại sao chúng ta trống rỗng? Vì Đức Chúa Trời đã tạo dựng chúng ta cho Ngài cho nên chúng ta sẽ không bao giờ tìm được sự viên mãn và đầy trọn ở ngoài mối tương giao với Ngài.")]),n._v(" "),i("p",[n._v("Trong cuộc phỏng vấn ngày 2 tháng 11, 1983 nhà báo Công Giáo danh tiếng Michael Novak nói về tình huống chúng ta như sau: "),i("em",[n._v('"Chủ nghĩa xã hội là một hệ thống dành cho các thánh... chủ nghĩa tư bản dân chủ dành cho tội nhân"')]),n._v(". Đó chính là lý do ông thấy tại sao chủ nghĩa xã hội không thành công trong thế giới này.")]),n._v(" "),i("p",[n._v("Từ thế kỷ đầu, Chúa Giê-xu đã dạy chúng ta rằng, "),i("strong",[n._v('"Loài người sống chẳng phải chỉ nhờ bánh mà thôi" (Lu-ca 4:4)')]),n._v(', nhưng chúng ta không nghe. Chúng ta đã cố ý không nghe. Chúng ta không thể chịu nổi cái trống vắng kinh khủng bên trong, chúng ta không thể nào nhìn tới con đường cô đơn hoang vắng phía trước. Chúng ta mệt mỏi, tuyệt vọng đến rã rời về những hận thù và tham dục đầy ứ bên trong nhưng không làm sao trút bỏ đi để thay vào bằng những điều tốt lành hơn. Sir Walter Scott từng bảo rằng "Thời gian và thủy triều không chờ bất cứ ai." Các phương tiện để hủy hoại toàn diện đã được đặt trong tầm tay chúng ta. Chúng ta không thể đi lạc thêm một lần nữa, chúng ta không còn thì giờ khám phá những nẻo đường chưa biết, chúng ta không được phép để bị lạc vào bất cứ một ngõ cụt nào nữa. Chúng ta không còn nhiều thời gian! Vì thế hệ chúng ta đã thành đạt được những điều các thế hệ khác mới chỉ thử làm, hay mới chỉ mơ tưởng trong những phút điên cuồng liều lĩnh hơn hơn hết. Chúng ta đã hoàn thành được những vũ khí đem lại sự hủy diệt toàn diện. Chúng ta đang chứng kiến cao điểm cơn điên loạn của nhân loại, đó là một cuộc tàn sát trong tương lai bằng vũ khí hạt nhân.Ma quỉ chắc đã cười lớn khi thấy những con người thông minh nhất trên trần gian làm việc cật lực ròng rã bao nhiêu năm để thành đạt được nỗi kinh hoàng này!')]),n._v(" "),i("p",[n._v("Người ta đã phân ly được nguyên tử để tạo ra sức nổ hạch tâm! Chia cắt và chinh phục! Cắt rời, hủy diệt, đập vỡ, chà nát, bóp vụn! Tên đại bịp đã thành công và con người đang nỗ lực hỗ trợ nó. Chúng ta thấy trước mắt kiệt tác của Sa-tan, cái lưỡi lửa giả mạo thật khéo léo. Cái khối lửa này của Sa-tan và lửa Ngũ tuần đều từ trời xuống, đều chiếu sáng, đều biến đổi mọi thứ gì nó chạm vào nhưng với hiệu quả thật khác biệt, khác biệt như địa ngục khác với thiên đàng!")]),n._v(" "),i("h5",{attrs:{id:"mot-the-gioi-dao-nguoc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-the-gioi-dao-nguoc","aria-hidden":"true"}},[n._v("#")]),n._v(" Một thế giới đảo ngược")]),n._v(" "),i("p",[n._v("Chúng ta đang sống trong một thế giới đảo ngược với tất cả mọi sự đều hỗn loạn, nhưng đây là sự hỗn loạn có hoạch định, theo kế hoạch của Sa-tan! Kinh Thánh cho biết Sa-tan là tên đại bịp đã dồn hết nỗ lực cho việc khiến con người tự dối mình trong phạm vi cá nhân cũng như phỉnh gạt giữa các quốc gia trong bình diện thế giới. Sa-tan đã khiến cho chúng ta tin rằng mọi sự ngày càng khá hơn khi trong thực tế tình trạng ngày càng tệ hại hơn.")]),n._v(" "),i("p",[n._v("Tất cả chúng ta đều nhận rằng thế giới đã đổi thay tận gốc rễ kể từ khởi đầu thế kỷ 20. Chúng ta biết được nhịp độ thay đổi ngày càng gia tăng, biết tinh thần của cuộc cách mạng đang quét đi những chứng tích và truyền thống tốt đẹp, biết tốc độ thay đổi thể hiện trong sự thay đổi ngôn ngữ, thời trang, phong tục, nhà cửa, lối sống và nếp suy nghĩ.")]),n._v(" "),i("p",[n._v("Chỉ mới cách đây ít năm, trẻ con còn cảm thấy hồi hộp mong được ra nhà ga xem xe lửa nhưng bây giờ chúng hầu như lãnh đạm ngay cả với các phi thuyền không gian. Có bao nhiêu người biết khi nào là lần phóng tới và những phi hành gia nào sẽ bay trên chuyến đó? Chúng ta từng ngạc nhiên thấy máy vô tuyến viễn ký hoạt động nhưng bây giờ lại hoàn toàn thản nhiên trước máy vô tuyến truyền hình, là một phát minh kỳ diệu hơn máy vô tuyến viễn ký rất nhiều. Cách đây không lâu, có những bệnh tật con người cho là nan y, vô vọng nhưng ngày nay chúng ta có những loại thuốc hiệu nghiệm đến độ nhiều chứng bệnh lâu đời đã bị quét sạch. Hiển nhiên là chúng ta đã thành đạt được rất nhiều.")]),n._v(" "),i("p",[n._v("Nhưng với tất cả những tiến bộ này, nan đề căn bản của nhân loại vẫn chưa giải quyết được. Chúng ta có thể xây dựng những tòa nhà cao nhất, chế tạo những máy bay nhanh nhất, xây những cây cầu dài nhất. Chúng ta cũng đã thành công trong việc thám hiểm những khoảng không gian xa xôi và chinh phục những vùng chưa từng biết, nhưng chúng ta vẫn không thể làm chủ chính mình hay sống với nhau trong hòa bình và bình đẳng!")]),n._v(" "),i("p",[n._v("Chúng ta có thể sáng tạo những trường phái âm nhạc và nghệ thuật mới, chúng ta có thể khám phá những loại sinh tố mới hơn, tốt hơn, nhưng tất cả những nan đề của chúng ta không có biến chuyển gì mới. Chúng là những nan đề con người vẫn từng có từ bao lâu nay, nhưng ngày nay lại dường như lớn hơn và nhiều hơn. Chúng có vẻ tấn công chúng ta bằng những phương thức mới, và dường như làm cho nỗi đau đớn của chúng ta sâu hơn, nhức nhối hơn. Nhưng tựu chung, chúng ta vẫn phải đối diện với cùng những cám dỗ, thách thức, trắc nghiệm mà con người trong muôn thuở luôn luôn phải đối đầu.")]),n._v(" "),i("p",[n._v("Cho đến muôn đời kể từ cái khoảnh khắc bi thảm ở vườn Ê-đen, lúc con người loại bỏ ý Chúa để làm theo ý mình, thì con người đã vướng mắc vào đủ mọi nan đề mà nguyên nhân được ghi trong sách Sáng Thế Ký chương 3 với những điều kiện kinh khủng gây ra những nan đề đó có liên hệ với chương đầu của thư Rô-ma, trong khi Tin Lành của Chúa Cứu Thế Giê-xu đưa ra giải pháp cho chúng ta.")]),n._v(" "),i("p",[n._v("Chính tội lỗi và bản chất suy đồi đã khiến con người ngập tràn thù hận, ganh gổ, tham lam và ghen tuông. Sự rủa sả của tội lỗi đổ trên thân xác khiến con người muôn đời bị ám ảnh bởi nỗi sợ chết không nguôi. Tài năng đầy sáng tạo giúp con người thay đổi mọi thứ trừ chính mình, vì vậy dù người ta có khoe khoang ồn ào về những "),i("em",[n._v('"tiến bộ"')]),n._v(" của thời đại bao nhiêu đi nữa, con người vẫn không có gì thay đổi, vẫn y nguyên như những ngày đầu.")]),n._v(" "),i("h5",{attrs:{id:"toi-loi-van-y-nguyen"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#toi-loi-van-y-nguyen","aria-hidden":"true"}},[n._v("#")]),n._v(" Tội lỗi vẫn y nguyên")]),n._v(" "),i("p",[n._v("Tội lỗi vẫn y nguyên mặc dù con người đã nỗ lực hết sức để thay đổi. Chúng ta đã cố gắng đặt cho tội lỗi những cái tên mới. Chúng ta đã dán nhãn mới lên những chai thuốc độc cũ. Chúng ta đã cố gắng sơn phết căn nhà hư mục làm bộ như nó còn tốt hay còn mới!")]),n._v(" "),i("p",[n._v("Chúng ta đã cố gọi tội lỗi là "),i("em",[n._v('"sai sót"')]),n._v(", là "),i("em",[n._v('"nhầm lẫn"')]),n._v(" hay "),i("em",[n._v('"kém phán đoán"')]),n._v(", nhưng dầu vậy tội lỗi vẫn y nguyên. Dù có cố gắng xoa dịu lương tâm đến đâu đi nữa, chúng ta thừa biết rằng con người vẫn là tội nhân và hậu quả của tội lỗi vẫn là bệnh tật, thất vọng, vỡ mộng, chán chường và chết chóc.")]),n._v(" "),i("p",[n._v("Sự đau khổ cũng không thay đổi. Từ khi A-đam và Ê-va đau đớn nhìn cái xác không hồn của A-bên là đứa con bị giết thì họ đã hiểu sức nặng nghiền nát của sự đau khổ là gì. Cứ thế cho đến hôm nay, sự đau khổ vẫn là ngôn ngữ phổ thông của con người. Không ai thoát mà mọi người đều kinh nghiệm khổ đau. Đối với một người bạn của Gióp, sự đau khổ còn dường như là mục đích cuộc đời khi bảo rằng, "),i("strong",[n._v('"loài người sinh ra để bị khốn khó, như tia lửa bay chớp lên không" (Gióp 5:7)')]),n._v(".")]),n._v(" "),i("p",[n._v("Sự chết cũng vậy, vẫn y nguyên dù con người đã cố gắng thay đổi bộ mặt của nó. Chúng ta đã đổi tên "),i("em",[n._v('"nhà đòn"')]),n._v(" thành "),i("em",[n._v('"người lo hậu sự"')]),n._v(". Chúng ta đặt xác chết trong "),i("em",[n._v('"quan tài"')]),n._v(" thay vì bỏ vào "),i("em",[n._v('"hòm"')]),n._v(". Chúng ta có "),i("em",[n._v('"nhà quàn"')]),n._v(" thay vì "),i("em",[n._v('"nhà xác"')]),n._v(" với "),i("em",[n._v('"nghĩa trang"')]),n._v(" thay cho "),i("em",[n._v('"nghĩa địa"')]),n._v(". Chúng ta cố làm nhẹ tính chất thô thiển của những nghi thức tang lễ, nhưng dù chúng ta gọi là gì đi nữa hay cố gắng tô điểm cho người chết đến đâu thì cái thực tại cứng, lạnh, tàn khốc của sự chết vẫn không thay đổi suốt cả lịch sử nhân loại. Cả ba sự kiện này đã tạo thành lịch sử đích thực của con người: quá khứ đầy ắp tội lỗi; hiện tại tràn ngập đau buồn và điều chắc chắn nhất trong tương lai là cái chết.")]),n._v(" "),i("p",[n._v("Kinh Thánh tuyên bố, "),i("strong",[n._v('"Theo như đã định cho loài người phải chết một lần..." (Hê-bơ-rơ 9:27)')]),n._v(". Đối với một người bình thường, đây quả thật là một tình huống nghiệt ngã và vô vọng. Hàng trăm triết thuyết và hàng chục tôn giáo đã được con người sáng lập với nỗ lực lẩn tránh lời trên của Đức Chúa Trời. Các triết gia hiện đại và các nhà tâm lý đang cố gắng khiến cho con người tưởng rằng ngoài con đường là Chúa Giê-xu vẫn còn có những lối thoát khác. Nhưng người ta đã thử đi vào tất cả những con đường đó và nhận ra rằng không con đường nào dẫn họ tới đâu trừ ra là đi xuống!")]),n._v(" "),i("p",[n._v("Chúa Cứu Thế Giê-xu đã đến đem giải đáp cho tất cả ba nan đề dai dẳng trên: tội lỗi, đau khổ và sự chết. Duy chỉ có một mình Chúa Giê-xu là Đấng bất biến, "),i("strong",[n._v('"hôm qua, ngày này và cho đến đời đời không hề thay đổi" (Hê-bơ-rơ 13:8)')]),n._v(", như lời bài hát của "),i("em",[n._v("Henry F. Lyte")]),n._v(": "),i("em",[n._v('"Con thấy khắp xung quanh đầy đổi thay, tan rữa, nhưng xin Đấng không hề biến dịch ở mãi bên con."')])]),n._v(" "),i("div",{staticClass:"tip custom-block"},[i("p",{staticClass:"custom-block-title"},[n._v("Note")]),n._v(" "),i("p",[i("a",{attrs:{href:"https://en.wikipedia.org/wiki/Abide_with_Me",target:"_blank",rel:"noopener noreferrer"}},[n._v("Abide with me"),i("OutboundLink")],1)])]),n._v(" "),i("p",[n._v("Dù tất cả đổi thay, nhưng Chúa Cứu Thế Giê-xu không hề thay đổi. Giữa tất cả những xao động không ngừng của biển tham dục con người, Chúa Cứu Thế Giê-xu vẫn đứng bình lặng, vững vàng như thạch trụ, sẵn sàng cứu vớt những ai quay lại với Ngài để tiếp nhận phước lành bình an. Vì chúng ta đang sống trong thời đại ân sủng có lời hứa của Đức Chúa Trời rằng bất cứ người nào muốn đều có thể đến tiếp nhận Con Ngài. Nhưng xin nhớ rằng thời kỳ ân điển này sẽ không kéo dài vô tận và thời gian chúng ta đang sống cũng chỉ là số thời gian vay mượn.")]),n._v(" "),i("h2",{attrs:{id:"chuong-2-kinh-thanh-khong-the-bi-tieu-huy"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2-kinh-thanh-khong-the-bi-tieu-huy","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 2: Kinh Thánh không thể bị tiêu hủy")]),n._v(" "),i("p",[i("strong",[n._v('"Trời đất sẽ qua nhưng lời Ta nói chẳng bao giờ qua đi" (Ma-thi-ơ 24:35)')])]),n._v(" "),i("p",[n._v("Thời gian đang cạn dần, chỉ còn lại vài giây cuối trong ngày trước khi đồng hồ báo hiệu nửa đêm. Nhân loại sắp lao vào cõi chết.")]),n._v(" "),i("p",[i("em",[n._v("Phải chăng có một quyền lực nào hay một tạo hóa vô danh nào đã đặt chúng ta ở đây mà không cho biết chúng ta đến từ đâu, tại sao ở đây và rồi sẽ đi đâu? Chúng ta sẽ phải đi lối nào?")])]),n._v(" "),i("p",[i("em",[n._v("Có còn một thẩm quyền nào cho chúng ta nhờ cậy? Có con đường nào cho chúng ta theo? Có chút ánh sáng nào xuyên thấu bóng tối dầy đặc bao quanh?")])]),n._v(" "),i("p",[i("em",[n._v("Liệu chúng ta có tìm được quyển sách giải mã giúp chúng ta tìm ra lối thoát cho tình trạng bế tắc hiện nay không? Liệu có một nguồn thẩm quyền nào cho chúng ta trông đợi không?")])]),n._v(" "),i("p",[n._v("Câu trả lời là "),i("strong",[n._v("CÓ")]),n._v(".")]),n._v(" "),i("p",[n._v("Vâng, chúng ta có một cuốn sách giải mã, chúng ta có chìa khóa, chúng ta có một nguồn tài liệu đầy thẩm quyền, và đó là "),i("strong",[n._v("Kinh Thánh")]),n._v(". Đây là quyển sách đã truyền lại cho chúng ta qua nhiều thời đại, đã được chuyển qua nhiều bàn tay, xuất hiện trong nhiều hình thức và đã từng tồn tại qua đủ mọi loại tấn công. Từ những lối phá hoại man rợ cho đến những hình thức tấn công bằng kiến thức của thế giới văn minh cũng không hề làm Kinh Thánh suy suyển. Không một ngọn lửa thiêu hủy nào hay tiếng cười khinh mạn nào của chủ nghĩa hoài nghi có thể tiêu diệt Kinh Thánh. Qua bao nhiêu thời đại đen tối của nhân loại, những lời hứa vinh diệu trong Kinh Thánh vẫn tồn tại không hề thay đổi. Một sự kiện đáng nói đó là trong khi tại Hoa Kỳ, việc đọc kinh Thánh trong trường học bị cấm chỉ thì Kinh Thánh lại buộc phải được đọc trong các trường Công Giáo ở nước cộng sản Ba-lan. Trong thế chiến thứ hai, trụ sở Thánh Kinh Hội tại Ba-lan nằm trên đường có tên là đường Giê-ru-sa-lem, là một trong những con đường chính của thủ đô Warsaw. Khi quân Đức khởi sự thả bom thành phố này thì bà vợ của viên giám đốc Thánh Kinh Hội đã vào kho di chuyển được khoảng 2000 cuốn Kinh Thánh đem cất giấu dưới hầm. Bom đạn làm sập nhà và sau đó bà bị quân Đức bắt đưa vào trại tù. Bà đã tìm cách vượt thoát và sau khi chiến tranh chấm dứt, bà đã đem phân phối được 2000 cuốn Kinh Thánh đó cho những người cần.")]),n._v(" "),i("p",[n._v("Thủ đô Warsaw bị san bằng, nhưng trên Phố Giê-ru-sa-lem, tại tòa nhà trụ sở Thánh Kinh Hội, có một bức tường vẫn còn đứng trên đó có sơn hàng chữ này: "),i("strong",[n._v('"Trời đất sẽ qua, nhưng lời Ta nói không bao giờ qua đi."')])]),n._v(" "),i("p",[n._v("Trong khi nhân loại đang tiến gần đến một giờ quyết định khác trong lịch sử thế giới, chúng ta hãy tái xem xét quyển sách bất khả hủy diệt về sự khôn ngoan và các lời tiên tri để tìm hiểu tại sao quyển sách này đã tồn tại và trở thành một nguồn mạch sung mãn cho đức tin và sức mạnh thuộc linh.")]),n._v(" "),i("h3",{attrs:{id:"kinh-thanh-khong-chi-la-mot-dai-tac-pham-van-chuong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#kinh-thanh-khong-chi-la-mot-dai-tac-pham-van-chuong","aria-hidden":"true"}},[n._v("#")]),n._v(" Kinh Thánh không chỉ là một đại tác phẩm văn chương")]),n._v(" "),i("p",[n._v("Có những người coi Kinh Thánh chính yếu chỉ là tác phẩm về lịch sử Israel trong khi những người khác nhận rằng Kinh Thánh đưa ra những nguyên tắc đạo đức sâu sắc nhất từ trước đến nay. Tuy tất cả những điều này, dù quan trọng, cũng chỉ là những khía cạnh bổ xung đối với chủ đề chính của Kinh Thánh, đó là chương trình cứu chuộc của Đức Chúa Trời qua Chúa Cứu Thế Giê-xu. Bài xã luận của nhật báo "),i("em",[n._v("International Herald Tribune")]),n._v(" số ra ngày 30 tháng 6, 1983 có gợi ý rằng Kinh Thánh là một tác phẩm văn chương nên đọc vì "),i("em",[n._v('"tiếng Anh trong Kinh Thánh trau chuốt nhất"')]),n._v(". Tuy nhiên, những ai đọc Kinh Thánh chỉ để thưởng ngoạn văn chương, thi ca hay lịch sử là đã đánh mất ý nghĩa và sứ điệp đích thực của Kinh Thánh.")]),n._v(" "),i("p",[n._v("Đức Chúa Trời khiến Kinh Thánh viết nên với mục tiêu cấp bách là bày tỏ cho con người chương trình cứu chuộc. Đức Chúa Trời khiến viết Kinh Thánh để mặc khải luật vĩnh hằng cho con dân Ngài nhờ đó họ được hướng dẫn bằng sự khôn ngoan sâu sắc, được an ủi bằng tình yêu vĩ đại của Ngài suốt hành trình trên đất. Vì không có Kinh Thánh thế giới này sẽ là một chốn tối tăm kinh khiếp, không một tia sáng, không một tấm bảng chỉ đường.")]),n._v(" "),i("p",[n._v("Kinh Thánh có thừa điều kiện là quyển sách duy nhất chứa đựng mặc khải của Đức Chúa Trời. Nhiều tôn giáo khác cũng có kinh sách như kinh Koran của Hồi giáo, các kinh Tạng Phật Giáo, kinh Vệ-đà của Ấn giáo và tất cả những kinh sách này đều có những bản dịch đáng tin cậy. Ai muốn cũng đều có thể đọc, so sánh với Kinh Thánh và tự thẩm định. Bằng cách đó, không bao lâu người nghiên cứu sẽ thấy ngay tất cả những kinh sách ngoài Cơ Đốc giáo đều chỉ có một phần chân lý, nhưng đến cuối cùng tất cả đều phát triển về một hướng hoàn toàn sai lầm. Những kinh sách đó khởi sự với vài tia sáng thật nhưng kết thúc trong tăm tối mịt mù. Ngay cả một quan sát viên hờ hững nhất cũng phải thấy Kinh Thánh khác biệt hẳn với tất cả những kinh sách kia. Kinh Thánh là cuốn sách duy nhất cung ứng cho con người sự cứu chuộc và chỉ cho con người lối thoát khỏi tình trạng tiến thoái lưỡng nan. Kinh Thánh là cuốn sách hướng dẫn chắc chắn nhất trong một thế giới đầy bấp bênh.")]),n._v(" "),i("p",[n._v("Việc trứ tác Kinh Thánh đã phải mất 1600 năm mới hoàn tất. Đây là công việc của hơn ba mươi trước giả, mỗi người viết như các viên thư ký của Đức Chúa Trời. Trong số những trước giả này, nhiều người sống cách xa nhau nhiều thế hệ và cũng không ghi xuống những suy nghĩ hay hy vọng cá nhân. Họ hành xử như là những phương tiện thể hiện sự mặc khải của Đức Chúa Trời; họ viết theo như Chúa chỉ dẫn và trong sự thần cảm của Đức Chúa Trời họ có thể thấy những chân lý lớn lao bền vững, họ ghi lại để những người khác cũng có thể thấy và biết những chân lý đó.")]),n._v(" "),i("p",[n._v("Trong suốt 1600 năm, 66 sách của Kinh Thánh đã được viết ra do những trước giả sử dụng những ngôn ngữ khác nhau, sống trong những thời đại khác nhau, tại những xứ sở khác nhau, nhưng sứ điệp họ viết chỉ là một. Đức Chúa Trời phán bảo mỗi trước giả trong ngôn ngữ và thời đại riêng của từng người nhưng sứ điệp của Ngài lúc nào cũng giống nhau. Khi các đại học giả gom góp nhiều cổ bản Kinh Thánh viết bằng tiếng Hê-bơ-rơ, A-ram và Hy-lạp rồi dịch sang cùng một ngôn ngữ hiện đại, họ thấy rằng những lời hứa của Đức Chúa Trời và đại sứ điệp của Ngài cho con người vẫn y nguyên không có gì thay đổi. Ngày nay khi đọc những lời muôn thuở đó, chúng ta thấy những qui tắc cư xử do các trước giả cổ đại thiết định vẫn mới mẻ và đầy ý nghĩa đối với thế hệ hôm nay cũng như với những con người của thời đại Chúa Giê-xu. "),i("em",[n._v("John Ruskin")]),n._v(" nói: "),i("em",[n._v('" Kinh Thánh là quyển sách duy nhất mà bất cứ con người biết suy nghĩ nào cũng có thể đến với bất cứ câu hỏi thành thật nào về cuộc đời hay về số phận tương lai, và tìm được giải đáp của Đức Chúa Trời qua sự tìm kiếm chân thành"')]),n._v(".")]),n._v(" "),i("h3",{attrs:{id:"quyen-sach-ban-chay-nhat-the-gioi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#quyen-sach-ban-chay-nhat-the-gioi","aria-hidden":"true"}},[n._v("#")]),n._v(" Quyển sách bán chạy nhất thế giới")]),n._v(" "),i("p",[n._v("Như vậy, không có gì đáng ngạc nhiên khi chúng ta thấy Kinh Thánh luôn luôn là quyển sách bán chạy nhất thế giới. Không cuốn sách nào có thể so với sự khôn ngoan sâu sắc, nét đẹp nên thơ trong đó cũng như tính chính xác trong các phần lịch sử và tiên tri của Kinh Thánh. Một số người chỉ trích bảo rằng Kinh Thánh đầy dẫy những lời ngụy tạo, chuyện giả tưởng và những lời hứa không tròn. Những người đó thường thấy khó khăn nằm trong chính họ hơn là trong Kinh Thánh. Những công cuộc sưu khảo sâu rộng và thận trọng cho thấy rằng những mâu thuẫn hiển nhiên nhất trong Kinh Thánh đều do các bản dịch không chính xác hơn là do những bất nhất thần cảm. Chính con người cần sửa chữa chứ không phải Kinh Thánh và có người đã bảo rằng "),i("em",[n._v('"Kinh Thánh cần được đọc lại chứ không cần viết lại"')]),n._v(".")]),n._v(" "),i("p",[n._v("Thế mà trong nhiều gia đình những người mệnh danh là có học, lấy Kinh Thánh ra giễu cợt là chuyện hợp thời và coi Kinh Thánh là sách đóng bụi hơn là Lời Hằng Sống của Đức Chúa Trời. Một ông mục sư thử hỏi một em bé xem em biết trong Kinh Thánh có gì. Em hăng hái trả lời là em biết tất cả mọi thứ có trong Kinh Thánh, rồi bắt đầu liệt kê, "),i("em",[n._v('"có hình bạn trai của chị, có phiếu giảm giá mua kem dưỡng da của mẹ, có cái kẹp tóc của em bé và một cái vé xem hát!"')]),n._v(" Đó là tất cả những thứ em biết trong cuốn Kinh Thánh của gia đình. Nhiều nhà đã dùng Kinh Thánh làm nơi cất các bức thư cũ và để ép hoa, hoàn toàn không thấy sự trợ giúp và bảo đảm Đức Chúa Trời dành cho họ qua Kinh Thánh.")]),n._v(" "),i("p",[n._v("Ngày nay thái độ đó đang thay đổi thật nhanh chóng! Cuộc sống đang bị lột sạch tất cả những cái trang hoàng nhân tạo vô nghĩa. Những hứa hẹn giả dối con người nói với nhau đang hiển lộ là những sai lầm trơ trẽn. Trong khi sợ hãi nhìn xung quanh tìm một điều chân thật và bền vững, một lần nữa chúng ta lại quay về với quyển sách cổ này, quyển sách đem lại nguồn an ủi, nâng đỡ, cứu chuộc cho hàng triệu người suốt bao nhiêu thế kỷ qua. Nhà tôi có lần đã nói: "),i("em",[n._v('"Nếu con cái chúng ta có nền tảng của một gia đình hạnh phúc, kính sợ Chúa với một đức tin không lay chuyển rằng Kinh Thánh thật là Lời Đức Chúa Trời, chúng sẽ có chỗ dựa vững chắc đến nỗi các quyền lực của địa ngục cũng không thể nào lay chuyển."')]),n._v(" Tôi tạ ơn Chúa về ảnh hưởng tin kính Chúa của nhà tôi đối với các con.")]),n._v(" "),i("p",[n._v('Vâng, ngày nay người ta đang tái "khám phá" Kinh Thánh! Họ đang đem Kinh Thánh ra quét sạch bụi bặm hay mua Kinh Thánh mới. Họ thấy rằng những câu Kinh Thánh quen thuộc nhưng đã bị quên lãng đang vang lên những ý nghĩa mới khiến họ tưởng như Kinh Thánh vừa được viết hôm qua. Sở dĩ như vậy là vì Kinh Thánh hàm chứa tất cả những kiến thức con người cần cho linh hồn và giải quyết mọi nan đề của họ. Kinh Thánh là tấm bản đồ thiết kế của vị Đại Kiến Trúc Sư cho nên chỉ do cẩn thận làm theo tất cả những chỉ dẫn trong đó chúng ta mới có thể xây dựng nổi cuộc đời chúng ta tìm kiếm.')]),n._v(" "),i("p",[n._v("Tại Hoa Kỳ có một văn kiện quan trọng khác được quí trọng. Văn kiện này được viết hơn hai trăm năm trước do một số người dốc đổ nhiều công sức soạn thảo, lại dành nhiều thì giờ hơn nữa để bàn thảo kỹ lưỡng từng điều khoản. Cuối cùng bản dự thảo được gửi cho tất cả 13 thành viên của liên bang phê chuẩn. Những người soạn thảo Hiến pháp biết họ đang viết một văn kiện căn bản cho chính quyền của những người tự do; họ nhận định rằng con người chỉ có thể sống như những hữu thể tự do và độc lập nếu mỗi người biết và hiểu rõ luật pháp. Họ cũng cần phải biết rõ những quyền lợi, các đặc ân và cả những những giới hạn nữa. Họ phải được đối xử bình đẳng trước tòa án và ngay cả các thẩm phán cũng không thể xét xử bất công, vì chính họ cũng bị ràng buộc bởi cùng một luật lệ trong khi điều khiển từng vụ án.")]),n._v(" "),i("h3",{attrs:{id:"chan-ly-se-giai-phong-chung-ta"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chan-ly-se-giai-phong-chung-ta","aria-hidden":"true"}},[n._v("#")]),n._v(" Chân lý sẽ giải phóng chúng ta")]),n._v(" "),i("p",[n._v("Trong khi phần còn lại của thế giới theo dõi cuộc thử nghiệm lớn lao này của nhân loại, con người khám phá ra rằng nếu họ biết và sống theo luật pháp họ có thể thực sự được giải phóng! Bây giờ một người có thể biết mình đang ở đâu, biết những quyền hiến định và cũng có những trách nhiệm hiến định. Nếu xao lãng trách nhiệm, quyền lợi sẽ bị ảnh hưởng - như trường hợp các cử tri khinh suất về sau đã khám phá ra rằng mình đã bị chính quyền chất lên nhiều giới hạn họ không thích.")]),n._v(" "),i("p",[n._v("Như nước Mỹ đã tăng trưởng và phát triển trong khuôn khổ của hiến pháp, thì Cơ Đốc giáo cũng phát triển và lan rộng dựa vào luật pháp ghi trong Kinh Thánh. Như hiến pháp được áp dụng đồng đều cho mọi người sống dưới thẩm quyền của nó, không phân biệt hay ưu đãi bất cứ ai thì cũng vậy, Kinh Thánh là bản hiến pháp tối cao dành cho toàn thể nhân loại. Luật pháp của Kinh Thánh áp dụng đồng đều cho mọi người sống trong khuôn khổ Kinh Thánh không có ngoại lệ hay cách giải thích đặc biệt nào.")]),n._v(" "),i("p",[n._v("Như hiến pháp là luật cao nhất của quốc gia thì Kinh Thánh là bộ luật cao nhất của Đức Chúa Trời, trong đó Ngài công bố các qui luật thuộc linh. Chính trong Kinh Thánh Đức Chúa Trời đưa ra những lời hứa bền vững của Ngài và cũng chính trong Kinh Thánh, Đức Chúa Trời bày tỏ chương trình cứu chuộc cho nhân loại.")]),n._v(" "),i("p",[n._v("Trong những kỳ quan thiên nhiên chúng ta thấy các qui luật của Đức Chúa Trời vận hành. Ai là người chưa từng nhìn lên các vì sao trong bầu trời đêm để kinh sợ đến lặng người trước vinh quang của công việc Đức Chúa Trời? Ngay cả các phi hành gia của chúng ta cũng phải ca ngợi Đức Chúa Trời là Tạo Hoá của cõi không gian vô tận và tất cả những cái phức tạp của một vũ trụ chúng ta mới khởi sự khám phá. Nếu không thể dựa vào những qui luật của Đức Chúa Trời, chúng ta không thể thực hiện những cuộc viễn du vào không gian. Ai không cảm thấy lòng lâng lâng khi trời đất bước vào mùa xuân, khi vạn vật bừng lên sức sống mới? Trong vẻ đẹp rực rỡ xung quanh, chúng ta thấy cái vĩ đại của quyền năng Chúa và những chi tiết vô tận trong kế hoạch của Ngài; nhưng thiên nhiên đã không cho chúng ta biết gì về tình yêu hay ân sủng của Đức Chúa Trời. Chúng ta cũng không tìm được lời hứa cứu chuộc của Ngài trong cõi thiên nhiên.")]),n._v(" "),i("p",[n._v("Tận nơi sâu thẳm của linh hồn, lương tri nói cho chúng ta biết về sự hiện diện của Đức Chúa Trời và về cái khác biệt luân lý giữa tốt và xấu; nhưng đó chỉ là những mẩu sứ điệp vụn vặt, không rõ và không dễ hiểu như những bài học trong Kinh Thánh. Chỉ trong những trang Kinh Thánh chúng ta mới tìm được sứ điệp rõ ràng không thể nhầm lẫn làm nền tảng cho Cơ Đốc Giáo chân chính.")]),n._v(" "),i("p",[n._v("Luật pháp của đất nước chúng ta bắt nguồn từ Mười Giới Răn, và Sir William Blackstone, một thẩm phán lỗi lạc người Anh đã viết: "),i("em",[n._v('"Kinh Thánh luôn luôn được coi là một phần Luật Phổ Thông của nước Anh."')])]),n._v(" "),i("p",[n._v("Tất cả các giáo lý trong Cơ Đốc giáo đều dựa trên Lời Kinh Thánh và Cơ Đốc giáo cũng khẳng định không hề thêm bớt gì vào Lời Đức Chúa Trời. Trong khi hiến pháp Hoa Kỳ theo thời gian có thể phải được tu chính nhưng Kinh Thánh thì không bao giờ cần sửa đổi. Chúng ta chân thành tin tưởng rằng những người trứ tác Kinh Thánh thực sự được Đức Thánh Linh hướng dẫn cả trong tư duy cũng như trong cách chọn lời diễn đạt bằng văn tự, như Thánh Phi-e-rơ đã nói, "),i("strong",[n._v('"...chẳng hề có lời tiên tri nào là bởi ý một người nào mà ra, nhưng ấy là bởi Đức Thánh Linh cảm động mà người ta đã nói bởi Đức Chúa Trời" (2 Phi-e-rơ 1:21)')]),n._v(". Thánh Phao-lô thì khẳng định, "),i("strong",[n._v('"Cả Kinh Thánh đều là bởi Đức Chúa Trời soi dẫn, có ích cho sự dạy dỗ, bẻ trách, sửa trị, dạy người trong sự công bình, hầu cho người của Đức Chúa Trời được trọn vẹn và trang bị để làm mọi việc lành" (2 Ti-mô-thê 3:16)')]),n._v(".")]),n._v(" "),i("p",[n._v("Khi ghi lại những sứ điệp trực tiếp từ Chúa, các trước giả Kinh Thánh không hề có ý tránh né những thực tại đời sống. Tội lỗi của cả vĩ nhân cũng như hạng tầm thường đều được nêu lên không giấu giếm, những yếu đuối của con người thiên nhiên được thừa nhận và cuộc sống trong các thời đại của Kinh Thánh được ghi lại một cách trung thực. Điều làm chúng ta kinh ngạc đó là cuộc sống và động cơ của những con người sống trong những thời xa xưa như thế nhưng vẫn có những màu sắc của con người hiện đại! Những trang Kinh Thánh giống như những tấm gương đặt trước tâm trí và tấm lòng, phản ánh những thành kiến và kiêu ngạo, những ô nhục và thất bại, những đau buồn và tội lỗi của chính chúng ta.")]),n._v(" "),i("p",[n._v("Chân lý vô thời gian. Chân lý không đổi khác khi thời đại, con người hay địa lý đổi thay. Tư tưởng loài người có thể khác nhau, tập quán thay đổi, những qui luật đạo đức cũng có thể biến thiên, nhưng Chân Lý vĩ đại, siêu việt vẫn bền vững đến muôn đời.")]),n._v(" "),i("p",[n._v("Sứ điệp của Chúa Cứu Thế Giê-xu, Đấng Cứu Tinh chúng ta là câu chuyện trung tâm của Kinh Thánh - câu chuyện về sự cứu rỗi. Những nhà nghiên cứu Kinh Thánh sâu sắc đã truy nguyên câu chuyện về Chúa Cứu Thế Giê-xu từ khởi đầu Cựu ước, vì Ngài là chủ đề chính của cả Cựu Ước lẫn Tân Ước.")]),n._v(" "),i("p",[n._v("Sự kiện về Chúa Giê-xu là sứ điệp đời đời của Kinh Thánh. Đây là câu chuyện về sự sống, bình an, cõi vĩnh hằng và thiên đàng. Trong Kinh Thánh không có một mục đích ẩn giấu nào và để hiểu Kinh Thánh cũng không cần phải có một phương thức giải nghĩa đặc biệt nào. Kinh Thánh có một sứ điệp rõ ràng, đơn sơ nhưng mạnh mẽ cho tất cả mọi người - sứ điệp của Chúa Cứu Thế với lời hứa ban sự bình an với Thượng Đế.")]),n._v(" "),i("p",[n._v("Một ngày kia Chúa Giê-xu với các môn đồ ngồi trên một triền núi gần thành phố Ca-bê-na-um. Tất cả qui tụ lại ngồi đối diện với Chúa. Bên cạnh Chúa có lẽ là Phi-e-rơ, bên kia là Giăng. Chúa yên lặng, trìu mến nhìn đám đông môn đệ nhiệt thành, với ánh mắt yêu thương dừng lại ở từng khuôn mặt như cha mẹ nhìn từng đứa con, yêu thương từng con riêng biệt bằng từng lý do đặc biệt, khiến cho mỗi người cảm nhận rằng chính mình được tách ra khỏi đám đông và được một mình ở trong vòng tay yêu thương của Chúa. Chắc hẳn Chúa Giê-xu đã yêu thương và biểu lộ tình thương với các môn đồ như thế và chắc hẳn trong ánh mắt trìu mến đó nhóm môn đồ nhỏ bé này cũng đã đáp ứng bằng thái độ cung kính âm thầm. Tất cả yên lặng đợi chờ, biết rằng Chúa sắp nói với họ những điều quan trọng, những điều họ phải truyền lại cho thế giới, cho những người không có đặc ân được nghe từ chính môi miệng của Thầy.")]),n._v(" "),i("p",[n._v("Tại trên triền núi đó, có lẽ Chúa Giê-xu đang đứng dưới những tàn lá xanh màu bạc của một cây ô-liu, giảng một bài giảng thâm sâu quan trọng nhất cho con người về cốt lõi của cuộc sống Cơ Đốc. Khi Chúa giảng xong, toàn thể các môn đồ im phăng phắc, kinh ngạc trong cái im lặng thiêng liêng mà Ma-thi-ơ ký thuật rằng, "),i("strong",[n._v('"đoàn dân vô cùng ngạc nhiên về sự dạy dỗ của Ngài, vì Ngài dạy dỗ đầy uy quyền chứ không như các thầy thông giáo" (Ma-thi-ơ 7: 28,29)')]),n._v(".")]),n._v(" "),i("p",[n._v("Chúa thật đã giảng dạy với thẩm quyền của chính Đức Chúa Trời, và những qui tắc Chúa thiết định chính là luật pháp của Đức Chúa Trời mà mỗi Cơ Đốc nhân mong muốn được cứu rỗi phải tuân giữ.")]),n._v(" "),i("h3",{attrs:{id:"kinh-thanh-va-ban"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#kinh-thanh-va-ban","aria-hidden":"true"}},[n._v("#")]),n._v(" Kinh Thánh và bạn")]),n._v(" "),i("p",[n._v("Nếu bạn chưa có Kinh Thánh, xin tìm mua ngay một cuốn. Tìm cuốn nào vừa ý nhất, về kích thước cũng như về khổ chữ rồi khởi sự đọc để cố gắng tự tìm hiểu xem tại sao quyển sách này chịu đựng nổi với thuốc thử thời gian. Đừng tiếc khi cần phải mua một cuốn Kinh Thánh đắt tiền, vì thật ra đây là một cuộc đầu tư. Chúng ta đã tốn kém rất nhiều cho áo quần là những thứ không tồn tại mãi nhưng lại do dự khi phải mua một cuốn Kinh Thánh thật tốt để đầu tư cho cõi vĩnh hằng! Hãy tự khám phá xem tại sao Kinh Thánh đáp ứng được cho các nhu cầu của nhân loại, tại sao Kinh Thánh đã ban đức tin và sức mạnh, giúp con người tiếp tục đi tới.")]),n._v(" "),i("p",[n._v("Nếu bạn đã rời xa Kinh Thánh từ lâu thì điều tốt nhất bây giờ là bạn trở lại làm quen với Kinh Thánh bằng cách khởi sự đọc Phúc Âm Giăng, được coi là một trong những quyển thâm sâu nhất trong bộ Kinh Thánh, nhưng lại là sách sáng sủa nhất, dễ hiểu nhất. Phúc Âm Giăng đã được viết với mục đích trình bày cặn kẽ về sự cứu rỗi loài người đến nỗi mọi thắc mắc trong tâm trí cũng như những khát khao trong lòng đều có thể được thỏa mãn.")]),n._v(" "),i("p",[n._v("Đọc xong sách Giăng, bạn có thể tiếp tục làm quen với các sách Phúc Âm Mác, Lu-ca và Ma-thi-ơ để thấy những trước giả rất khác nhau về cá tính và văn phong trình bày câu chuyện muôn đời về sự cứu rỗi qua Chúa Cứu Thế Giê-xu. Rồi bạn sẽ nhận ra đó chính là chân lý phổ quát và mạnh mẽ, là trọng tâm của Tin Lành khiến cho bạn càng cảm thấy thấm thía hơn với điều một trước giả Kinh Thánh viết rằng "),i("strong",[n._v('"Chúa Cứu Thế Giê-xu hôm qua, ngày nay và cho đến đời đời không hề thay đổi" (Hê-bơ-rơ 13:8)')])]),n._v(" "),i("p",[n._v("Sau khi đã đọc riêng từng sách Phúc Âm, bạn hãy khởi sự đọc từ đầu Tân Ước cho đến hết theo thứ tự. Đọc như vậy xong, bạn đã tạo được một ý thích muốn đọc Kinh Thánh, bạn đã thấy Kinh Thánh là một nguồn suối linh diệu, là một người hướng đạo, một người cố vấn rất thực tiễn, là một kho tàng quí giá đầy những lời khuyên khôn ngoan, khiến cho đối với bạn, việc đọc Kinh Thánh trở thành một phần cuộc sống mỗi ngày.")]),n._v(" "),i("p",[n._v("Để có một cuộc sống phong phú và đầy ý nghĩa, kiến thức về Kinh Thánh là điều thiết yếu, vì những lời trong quyển sách này có khả năng trám vào những chỗ khiếm khuyết trong cuộc sống, nối kết những khoảng trống, biến những mảng màu hoen ố của cuộc đời chúng ta trở nên bóng lộn như vàng ngọc. Hãy tập đem mọi nan đề của bạn đến với Kinh Thánh rồi trong những trang sách đó, bạn sẽ tìm ra lời giải đáp.")]),n._v(" "),i("p",[n._v("Nhưng trên hết, Kinh Thánh là mặc khải về bản chất của Đức Chúa Trời. Các triết gia của hàng bao nhiêu thế kỷ đã vật lộn về vấn đề có một Đấng Tối Cao. Ngài là ai? Ngài như thế nào? Ngài ở đâu? Nếu quả có một Đấng như vậy thì Ngài có quan tâm đến tôi không? Nếu có, làm sao tôi biết Ngài? Những câu hỏi này và hàng nghìn những câu hỏi khác về Đức Chúa Trời đã được trình bày trong một quyển sách chúng ta gọi là Kinh Thánh. Một Cơ đốc nhân có lần nói, "),i("em",[n._v('"Bạn có biết quyển sách bạn muốn được kê dưới đầu làm gối lúc lâm chung không?"')]),n._v(" Joseph Cook nói tiếp, "),i("em",[n._v('"Đó cũng là cuốn sách bạn phải học hỏi lúc sinh tiền. Trên thế giới này chỉ có một cuốn sách như thế mà thôi"')]),n._v(".")]),n._v(" "),i("h2",{attrs:{id:"chuong-3-duc-chua-troi-nhu-the-nao"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-3-duc-chua-troi-nhu-the-nao","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 3: Đức Chúa Trời như thế nào?")]),n._v(" "),i("p",[i("strong",[n._v('"Nếu ông dò xét có thể biết được sự sâu nhiệm của Đức Chúa Trời, và thấu rõ Đấng Toàn năng sao?" (Gióp 11:7)')])]),n._v(" "),i("p",[n._v("Đức Chúa Trời là ai? Ngài như thế nào? Làm thế nào chúng ta biết Ngài hiện hữu? Ngài có từ bao giờ? Chúng ta có thể biết Ngài không?")]),n._v(" "),i("p",[n._v('Mọi người đều hỏi hay tự hỏi những câu này vì chúng ta không thể nào nhìn thế giới xung quanh mà không kinh ngạc về công trình sáng tạo. Hàng ngày chúng ta đối diện với phép lạ trong sự sống và cái bí hiểm trong sự chết, vẻ rực rỡ huy hoàng của thảo mộc, cỏ hoa, vẻ vinh quang của bầu trời đêm đầy sao, cái vĩ đại của núi non, biển cả. Ai đã tạo dựng nên tất cả những thứ này? Ai đã nghĩ ra luật về dẫn lực để nối kết ràng buộc mọi vật đúng vị trí riêng? Ai đã qui định ngày đêm và điều khiển sự chuyển đổi các mùa? Còn về tính vô hạn của vũ trụ thì sao? Chúng ta có thành thật tin như điều có người đã viết, "Phải chăng tất cả vũ trụ trong quá khứ, hiện tại hay tương lai chỉ có thế này?"')]),n._v(" "),i("p",[n._v('Giải đáp khả dĩ duy nhất là tất cả những điều này và nhiều điều khác nữa là công việc của một Thượng Đế Tối Cao. Như chiếc đồng hồ phải có người vẽ kiểu thì vũ trụ với cơ chế chính xác như thế cũng phải có một Đấng Thiết Kế Vĩ Đại. Chúng ta gọi Ngài là Đức Chúa Trời hay là Ông Trời. Toàn thể nhân loại quen thuộc danh Ngài. Ngay từ thơ ấu, chúng ta đã nói đến danh Ngài. Kinh Thánh công bố rằng Đức Chúa Trời chúng ta nói, chúng ta hát về Ngài là Đức Chúa Trời "đầy ân phúc" chính là Đấng đã sáng tạo thế giới và đặt để chúng ta trong đó. Công cuộc thám hiểm không gian của chúng ta sẽ không thể thực hiện được trong một vũ trụ không vận hành theo qui luật của Đức Chúa Trời.')]),n._v(" "),i("p",[n._v('Một người khôn ngoan không kém ai là Benjamin Franklin đã nói, "Tôi là người sống lâu và càng sống lâu tôi càng thấy có nhiều bằng chứng đầy thuyết phục rằng Đức Chúa Trời là Đấng điều khiển công việc của loài người". Một người khôn ngoan khác là Blaise Pascal đã viết: "Nếu một người không được tạo dựng cho Đức Chúa Trời thì tại sao người chỉ có hạnh phúc trong Ngài? Nếu một người được tạo dựng cho Đức Chúa Trời thì tại sao người chống lại Đức Chúa Trời?" Đây là tình huống bế tắc của chúng ta.')]),n._v(" "),i("p",[n._v('Nhưng bạn hỏi, "Ngài là Ai? Ngài ở đâu?" Chúng ta biết Ngài hiện hữu, chúng ta kêu van Ngài - kêu Trời - trong những giây phút khó khăn thử thách cam go nhất. Có người luôn luôn suy tưởng về Ngài mọi lúc, mọi nơi. Nhưng có người lại bảo rằng họ không tin Ngài hiện hữu, trong khi những người khác bảo, "Hãy giải thích Thượng Đế cho tôi may ra có thể tôi sẽ tiếp nhận Ngài."')]),n._v(" "),i("p",[n._v('Vào thời điểm quyết định này trong lịch sử thế giới, đối với những người tự hỏi, "Đức Chúa Trời như thế nào?" chỉ có một câu trả lời đơn giản: Đức Chúa Trời giống như Chúa Cứu Thế Giê-xu. Như Chúa Giê-xu đã đến để khiến hình ảnh Đức Chúa Trời trở nên rõ ràng cho nhân loại và trở thành Đấng Cứu Chuộc chúng ta, thì khi trở về thiên đàng, Ngài đã ban Đức Thánh Linh xuống cư trú trong tín hữu giúp họ có thể sống cuộc đời làm nổi bật hình ảnh Chúa Cứu Thế cho một trần gian vô tín.')]),n._v(" "),i("p",[n._v("Nếu bạn muốn biết Đức Chúa Trời như thế nào, nếu bao nhiêu năm qua trong cuộc đời bạn đã nghe về Đức Chúa Trời và nói về Ngài, nhưng vẫn còn chờ phải có người giải thích rồi mới đặt đức tin nơi Ngài thì bây giờ chúng ta xem thử Kinh Thánh có những mô tả cụ thể như thế nào về Đức Chúa Trời.")]),n._v(" "),i("h3",{attrs:{id:"duc-chua-troi-nhu-the-nao"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#duc-chua-troi-nhu-the-nao","aria-hidden":"true"}},[n._v("#")]),n._v(" Đức Chúa Trời như thế nào?")]),n._v(" "),i("p",[n._v('Vào thời điểm quyết định này trong lịch sử thế giới mọi người cần phải tìm giải đáp cho câu hỏi, "Đức Chúa Trời như thế nào?" Ai cũng cần phải hỏi và cùng đều cần biết rõ câu trả lời. Mỗi người cần biết thật chính xác không chút hoài nghi Đức Chúa Trời là ai và Ngài có thể làm được những gì vì Thánh kinh khẳng định rằng '),i("strong",[n._v('"điều chi có thể biết về Đức Chúa Trời thì đã trình bày ra cho họ, Đức Chúa Trời đã tỏ điều đó cho họ rồi" (Rô-ma 1:19)')]),n._v(".")]),n._v(" "),i("p",[n._v("Chính vì thiếu kiến thức này về Đức Chúa Trời và sự kiện con người từ chối vâng lời Ngài mà con người không sao giải tỏa được bao nhiêu nan đề đeo đẳng. Chính vì con người không hiểu chương trình của Đức Chúa Trời mà thế giới rơi vào hỗn loạn. Chính vì con người không muốn biết và không muốn tuân giữ các qui luật của Đức Chúa Trời mà linh hồn chồng chất bao nhiêu gánh nặng. Vì vậy chúng ta hãy nỗ lực hết sức học biết về Ngài.")]),n._v(" "),i("p",[n._v("Chúng ta sẽ tìm những tri thức này ở đâu? Ai trong chúng ta sẽ chỉ dạy chúng ta chân lý? Không phải tất cả chúng ta ở đây đều là những vật thọ tạo hữu hạn sao? Đức Chúa Trời có dự liệu một người nào trên đất có đầy đủ thẩm quyền tối hậu để nói về Ngài? Câu trả lời là không, và người duy nhất Đức Chúa Trời giao phó trách nhiệm đó sống cách đây hai nghìn năm thì chúng ta đã đóng đinh trên thập tự giá! Như vậy bây giờ làm sao chúng ta có thể biết Đức Chúa Trời?")]),n._v(" "),i("p",[n._v("Chúng ta có thể hỏi các học giả uyên bác, họ sẽ cho biết Đức Chúa Trời là diễn đạt của mọi điều trong thiên nhiên và cuộc sống, rằng mọi hữu thể sống đều là một với Ngài, rằng chính sự sống cũng là một thể hiện của Hữu Thể Thiên Thượng. Họ sẽ cho biết bạn có thể thấy Đức Chúa Trời trong một giọt nước nhỏ nhất cũng như trong vòm trời bao la mênh mông trên cao.")]),n._v(" "),i("p",[n._v("Hỏi một triết gia, ông ta sẽ cho bạn biết rằng Đức Chúa Trời là một quyền lực nguyên thủy bất biến đàng sau toàn thể công trình sáng tạo, rằng Ngài là Động Lực Chính, là Đấng duy trì chuyển động của tất cả vũ trụ, rằng Ngài là Quyền Lực vô thủy vô chung. Triết gia sẽ bảo rằng mỗi mẩu sự sống và vẻ đẹp chúng ta thấy là thể hiện của quyền lực này lưu chảy không ngừng từ Động Lực Chính rồi quay lại đó.")]),n._v(" "),i("p",[n._v("Tiếp tục hỏi nữa, người ta sẽ cho bạn biết rằng Đức Chúa Trời là tuyệt đối và Ngài là Tất Cả trong Tất Cả, và không ai có thể biết gì thêm về Ngài. Có nhiều định nghĩa về Đức Chúa Trời. Tiến sĩ Akbar Haqq bảo rằng nguyên thủy mọi người đều theo quan điểm độc thần về Thượng Đế. Mỗi quốc gia, mỗi chủng tộc, mỗi gia đình, mỗi cá nhân đều cố gắng giải thích Hữu Thể Vĩ Đại đàng sau vũ trụ bao la này. Con người ở mọi thời đại đã cố gắng khám phá Đấng Tạo Hóa, tác giả của bao công trình họ đã thấy, nhưng lại không biết Ngài. Trong những lời giải thích trên về Đức Chúa Trời, điều nào đúng nhất? Những lý thuyết nào có thể chấp nhận? Trong số những thẩm quyền tự tạo trên, cái nào có thể hướng dẫn chúng ta?")]),n._v(" "),i("p",[n._v("Như đã thấy trong chương trước, Đức Chúa Trời đã tự bày tỏ chính Ngài trong một cuốn sách gọi là Kinh Thánh. Trong Kinh Thánh chúng ta có mặc khải về Chúa và dựa trên mặc khải đó, tâm trí chúng ta thỏa mãn và lòng chúng ta ấm áp. Chúng ta an tâm biết chắc có câu trả lời đúng, và rằng chúng ta đang trên hành trình hiểu biết bản chất thật của Đức Chúa Trời.")]),n._v(" "),i("p",[n._v("Đức Chúa Trời bày tỏ chính Ngài trong hàng trăm cách khác nhau trong Kinh Thánh và nếu chúng ta đọc Kinh Thánh cẩn trọng và đều đặn như đọc nhật báo, chúng ta sẽ quen và biết Đức Chúa Trời như chúng ta biết rõ các cầu thủ chúng ta có cảm tình!")]),n._v(" "),i("p",[n._v("Như viên kim cương có nhiều mặt thì mặc khải về Đức Chúa Trời cũng có vô số khía cạnh không thể nào trình bày hết được, vì vậy trong phạm vi giới hạn của chương này, chúng ta sẽ nói về bốn phương diện mặc khải có phần quan trọng nhất về Đức Chúa Trời, đó cũng là những khía cạnh chúng ta cần ghi nhớ luôn luôn.")]),n._v(" "),i("h3",{attrs:{id:"duc-chua-troi-la-than-linh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#duc-chua-troi-la-than-linh","aria-hidden":"true"}},[n._v("#")]),n._v(' "Đức Chúa Trời là Thần Linh"')]),n._v(" "),i("p",[n._v("Trước hết, Kinh Thánh công bố Đức Chúa Trời là Thần Linh. Khi nói chuyện với người phụ nữ Sa-ma-ri bên giếng Si kha, Chúa Giê-xu đã xác định điều này: "),i("strong",[n._v('"Đức Chúa Trời là thần linh" (Giăng 4:24)')]),n._v(".")]),n._v(" "),i("p",[n._v('Bạn nghĩ gì khi nghe từ ngữ thần linh? Tâm trí bạn gợi ra hình ảnh nào? Bạn có nghĩ về thần linh giống như một làn sương khói bay nhẹ lên trời? Thần linh có phải là cái làm trẻ con sợ trong dịp hội ma quái Halloween mỗi cuối tháng mười? Hay đối với bạn, thần linh chỉ là cái trống rỗng không hình không dáng? Bạn có nghĩ rằng đó là điều Chúa Giê-xu hàm ý khi Ngài bảo "Đức Chúa Trời là Thần Linh"?')]),n._v(" "),i("p",[n._v('Để khám phá "thần linh" thực sự là gì và Chúa Giê-xu muốn nói đến điều gì khi Ngài dùng từ ngữ đặc biệt này, chúng ta lại phải trở về với Kinh Thánh tới chỗ mô tả sau khi phục sinh, Chúa đã nói, "Hãy rờ đến ta và hãy xem, thần thì không có thịt xương, mà các ngươi thấy ta có" (Lu-ca 24:39). Vì vậy, chúng ta biết chắc rằng thần linh không có thân xác. Thần linh đối ngược với thân xác, nhưng vẫn là một hữu thể có quyền năng. Đây là điều rất khó hiểu vì chúng ta đang cố hiểu bằng tâm trí hữu hạn của loài người.')]),n._v(" "),i("p",[n._v('Vì loài người đã bị truất bỏ nhãn quan vô hạn Đức Chúa Trời từ nguyên thủy định ban cho nên chúng ta không thể hiểu sự vinh quang và vĩ đại của Thần Linh hiện hữu quá xa bên ngoài chúng ta. Khi nghe nói đến từ ngữ "thần linh", chúng ta lập tức cố giảm bớt, hạ thấp từ ngữ đó xuống cho ngang bằng với kích thước nhỏ bé của chúng ta, cho vừa với phạm vi hạn hẹp của tâm trí chúng ta. Giống như nỗ lực giải thích cái hùng vĩ, bao la, mênh mông của đại dương cho một người chưa bao giờ thấy một hồ nước lớn hơn cái vũng trâu đầm. Làm sao người đó có thể hình dung được đại dương vô tận? Làm sao người đó khi nhìn vào cái ao tù nước đọng kia, có thể hiểu được những chiều sâu vô biên, sự sống bí mật, sức mạnh sôi trào, những chuyển động không ngừng với cái tàn bạo ghê gớm của cơn bão biển hay cái vẻ đẹp tráng lệ tuyệt vời khi biển lặng? Làm thế nào một người suốt đời chỉ thấy vũng bùn có thể hiểu được bạn đang nói gì? Bạn sẽ dùng những ngôn từ nào để mô tả đại dương hùng vĩ cho người đó hiểu? Làm sao bạn có thể thuyết phục người đó tin rằng những đại dương kỳ diệu đó thực sự hiện hữu?')]),n._v(" "),i("p",[n._v('Đối với chúng ta, để hiểu đúng điều Chúa nói "Đức Chúa Trời là Thần Linh" còn khó khăn hơn vô cùng. Chúa Giê-xu thì khác. Ngài biết hết. Tâm trí Ngài không bị giới hạn như tâm trí chúng ta. Mắt Ngài không nhìn vào vũng bùn của đời sống, nhưng biết hết mọi biên cương vô hạn của Thần Linh, và Ngài đến để nỗ lực giúp chúng ta hiểu phần nào cái kỳ diệu, êm dịu và bình an Thần Linh đem lại.')]),n._v(" "),i("p",[n._v("Chúng ta biết chắc rằng thần linh không bị giới hạn trong thân xác, cũng không hao mòn, già cỗi hoặc đổi thay như thân xác. Kinh Thánh công bố rằng Đức Chúa Trời là Thần Linh, nghĩa là Ngài không bị giới hạn trong thân xác, không bị giới hạn vào vóc dáng, không bị giới hạn vào một biên cương nào; không ai có thể đo lường được Ngài, cũng không con mắt trần nào có thể thấy Ngài. Kinh Thánh cho biết vì Ngài vô hạn như thế cho nên Ngài cùng một lúc có thể ở khắp mọi nơi, Ngài nghe tất cả, thấy tất cả và biết tất cả.")]),n._v(" "),i("p",[n._v('Vì không có những khả năng đó cho nên chúng ta có khuynh hướng giới hạn Ngài trong cái hạn hẹp của chúng ta. Chúng ta cố phủ nhận quyền năng của Đức Chúa Trời đối với những việc chúng ta không thể làm. Vì không thể cùng một lúc có mặt khắp nơi cho nên chúng ta cho rằng Đức Chúa Trời cũng như vậy. Chúng ta giống như một người nghe nói về đại dương rồi một ngày kia tìm đến được bên bờ biển, đi xuống vốc được ít nước trong tay, kêu lên rằng, "cuối cùng đại dương đã nằm trong lòng bàn tay ta, ta đã sở hữu đại dương mênh mông!" Vâng, anh ta có được một phần biển cả, nhưng cùng lúc đó, trên hàng ngàn các bãi biển khác, nhiều người khác cũng đang cúi xuống vốc nước trong tay bảo rằng mình đang sở hữu vài ba giọt nước biển. Hàng triệu người trên thế giới có thể đi xuống bãi biển làm như thế, hoặc mỗi người có thể lấy bao nhiêu nước biển cũng được, nhưng đại dương vẫn không hề thay đổi. Năng lực và sức mạnh của biển cả vẫn y nguyên, sự sống và những chỗ sâu không dò được vẫn như cũ, dù nó đã cung cấp nhu cầu cho tất cả mọi người đang đứng đưa tay trên khắp các bờ đại dương.')]),n._v(" "),i("p",[n._v("Đức Chúa Trời cũng như vậy. Ngài có mặt khắp nơi trong cùng một lúc, nghe những lời cầu nguyện của tất cả mọi người dâng lên trong danh Chúa Cứu Thế Giê-xu; Ngài cũng đồng thời làm bao nhiêu phép lạ quyền năng để giữ các vì tinh tú trong vị trí, làm cho cây cối đâm chồi nẩy lộc trên mặt địa cầu, trong khi tôm cá bơi lội trong biển cả. Đức Chúa Trời không bị một giới hạn nào và sự khôn ngoan của Ngài cũng thật hoàn hảo. Quyền năng Ngài không giới hạn, tình yêu và lòng thương xót của Ngài không có biên cương.")]),n._v(" "),i("p",[n._v("Nếu bạn đang từng cố gắng giới hạn Đức Chúa Trời, xin hãy dừng lại. Đừng cố giam hãm Ngài hay công trình của Ngài vào bất cứ nơi nào hay lãnh vực nào. Tất nhiên bạn không cố giới hạn biển cả. Bạn không thể giới hạn vũ trụ. Bạn không đủ liều lĩnh để thử thay đổi đường đi của mặt trăng hay giữ trái đất đừng quay quanh trục! Như vậy cố giới hạn Đức Chúa Trời là Đấng sáng tạo tất cả những kỳ quan đó còn muôn đời ngu dại đến đâu!")]),n._v(" "),i("p",[n._v('Tôi vô cùng biết ơn mẹ tôi về nhiều điều nhưng một trong những ơn phước lâu bền nhất cho cuộc sống là bà đã dạy tôi trong Phúc Âm Yếu Chỉ (Giáo Lý Căn Bản) năm tôi lên mười rằng "Đức Chúa Trời là Thần Linh, vô hạn, vĩnh hằng và bất biến trong sự hiện hữu, khôn ngoan, năng quyền, thánh khiết, công chính, thiện hảo và chân lý." Định nghĩa đó về Đức Chúa Trời đã theo tôi suốt đời, và khi sâu kín trong lòng, một người biết rõ Đức Chúa Trời là Thần Linh vô hạn, vĩnh hằng và bất biến, người sẽ thắng được cám dỗ muốn giới hạn Ngài. Hiểu biết đó cũng giúp người vượt qua mọi ý tưởng nghi ngờ khả năng của Đức Chúa Trời đối với những việc chúng ta không làm nổi!')]),n._v(" "),i("p",[n._v("Một số nghi ngờ Kinh Thánh không phải là Lời chân thật của Đức Chúa Trời vì không muốn gán cho Ngài bất cứ điều gì chính họ không thể thành đạt. Nếu bạn có điều gì không chắc về sự linh cảm của Kinh Thánh, xin hãy lấy ra đọc lại, đọc trong tâm trạng của một người suốt đời chỉ biết chú mục vào cái ao tù mà lần đầu tiên trong đời được đứng trước đại dương mênh mông! Có lẽ bây gì bạn mới thoáng có cái nhìn đầu tiên về quyền năng vô hạn của Đức Chúa Trời. Có lẽ bây giờ bạn mới bắt đầu hiểu Đức Chúa Trời thực sự như thế nào, vì nếu Đức Chúa Trời là Thần Linh như Chúa Giê-xu công bố thì sẽ không có gì khó hiểu đối với sự quan phòng của Ngài, đối với quyền tối thượng của Ngài trong việc can thiệp vào cuộc sống con người, cũng không khó khăn gì đối với sự linh cảm con người viết nên Kinh Thánh. Một khi bạn hiểu Đức Chúa Trời là ai và Ngài như thế nào thì mọi suy tưởng ngổn ngang sẽ ổn định.")]),n._v(" "),i("h3",{attrs:{id:"duc-chua-troi-la-mot-ngoi-vi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#duc-chua-troi-la-mot-ngoi-vi","aria-hidden":"true"}},[n._v("#")]),n._v(" Đức Chúa Trời là một ngôi vị")]),n._v(" "),i("p",[n._v('Thứ hai: Kinh Thánh tiết lộ Đức Chúa Trời là một Ngôi Vị (nghĩa là có nhân cách, vị cách). Suốt cả Kinh Thánh chúng ta thấy "Đức Chúa Trời yêu thương," "Đức Chúa Trời phán," "Đức Chúa Trời làm." Mọi điều chúng ta qui cho một người cũng được qui cho Đức Chúa Trời. Người là một cá thể biết cảm xúc, suy nghĩ, mong mỏi, ước muốn và có mọi cách thể hiện nhân cách. Sống trên đất chúng ta có khuynh hướng qui chiếu nhân cách cho thân xác. Trí óc hữu hạn của chúng ta không thấy được nhân cách không thể hiện ra trong xương thịt. Chúng ta biết rằng những nhân cách của chúng ta không luôn luôn bị gói ghém trong các xác thân chúng đang cư trú. Chúng ta biết rằng trong lúc chết, nhân cách chúng ta sẽ lìa thân xác để tiếp tục bước vào số phận dành cho chúng. Chúng ta đều biết rõ điều này, nhưng lại thấy khó chấp nhận.')]),n._v(" "),i("p",[n._v("Mạc khải quan trọng chúng ta cần có đó là nhận ra rằng nhân cách không nhất thiết phải được xác định với một hữu thể có thân xác. Đức Chúa Trời không ở trong thân xác nhưng Ngài vẫn là một Ngôi Vị. Ngài cảm xúc, suy tưởng, yêu thương, tha thứ. Ngài cảm thông với những nan đề và những phiền ưu chúng ta phải đương đầu.")]),n._v(" "),i("h3",{attrs:{id:"duc-chua-troi-thanh-khiet-va-cong-chinh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#duc-chua-troi-thanh-khiet-va-cong-chinh","aria-hidden":"true"}},[n._v("#")]),n._v(" Đức Chúa Trời Thánh Khiết và Công Chính")]),n._v(" "),i("p",[n._v("Thứ ba: Kinh Thánh cho biết Đức Chúa Trời không chỉ là Thần Linh và là một Ngôi Vị, nhưng Ngài còn là Đấng Thánh khiết và Công chính. Từ Sáng thế ký đến Khải huyền, Đức Chúa Trời bày tỏ chính Ngài là Đức Chúa Trời Thánh khiết. Ngài là Đấng toàn hảo và tuyệt đối trong tất cả mọi khía cạnh. Ngài cũng là Đấng quá thánh khiết không thể dung chịu được con người tội lỗi, cũng như cuộc sống tội lỗi.")]),n._v(" "),i("p",[n._v("Chỉ cần thấy được bức tranh thật về sự công chính vô cùng uy nghi của Ngài, cuộc sống chúng ta trên bình diện cá nhân cũng như quốc gia sẽ khác xa biết bao nhiêu! Ước gì chúng ta nhận ra cái khoảng mênh mông chia cách con gười bất nghĩa với Đức Chúa Trời hoàn toàn công chính! Kinh Thánh công bố Ngài là Nguồn Sáng, trong Ngài không có một chút bóng tối nào, Ngài là Hữu Thể Tối Thượng không tì vết.")]),n._v(" "),i("p",[n._v("Đây cũng lại là một khái niệm rất khó đối với con người bất toàn. Chúng ta với bao nhiêu lầm lỗi và yếu đuối rất khó có thể tưởng tượng được sự thánh khiết vô cùng của Đức Chúa Trời như thế nào, tuy nhiên để có thể hiểu Kinh Thánh chúng ta cần phải nhận thức sự thánh khiết đó. Vực sâu ngăn cách giữa con người bất toàn với Đức Chúa Trời toàn hảo được nhấn mạnh qua suốt Kinh Thánh. Sự kiện này thể hiện qua việc phân cách giữa nơi thánh và nơi chí thánh trong Đền tạm Cựu Ước cũng như trong Đền thờ Tân Ước. Sự ngăn cách này được thể hiện qua qui định rằng khi một tội nhân muốn đến gần Đức Chúa Trời thì phải đem theo một của dâng. Chính hệ thống tư tế là trung gian giữa Đức Chúa Trời và con người cũng nhấn mạnh sự phân cách giữa Đức Chúa Trời thánh khiết với con người tội lỗi. Điều này cũng được nhấn mạnh trong các giới luật về sự tinh sạch trong sách Lê vi ký. Chúng ta cũng thấy sự phân cách đó trong các kỳ lễ Do Thái và sự cô lập của người Do Thái tại Palestine. Sự thánh khiết của Đức Chúa Trời chi phối tất cả những nguyên tắc khác của Ngài.")]),n._v(" "),i("p",[n._v('Kinh Thánh công bố rằng ngôi Đức Chúa Trời được thiết lập trên nền tảng thánh khiết. Chính vì Ngài thánh khiết trong khi con người bất khiết mà có khoảng cách chia giữa Đức Chúa Trời với tội nhân không chịu ăn năn. Kinh Thánh cho biết chính tội lỗi ngăn cách chúng ta với Đức Chúa Trời đến nỗi Ngài che mặt khỏi chúng ta và khi chúng ta kêu cầu Ngài không thèm nghe. Tác giả Thi thiên 66:18 viết rằng "Nếu lòng tôi có chú về tội ác, ắt Chúa chẳng nghe tôi". Mặt khác, tác giả Thi-thiên cũng lại nói rằng, "Mắt Chúa Hằng hữu đoái xem người công bình, tai Ngài lắng nghe tiếng kêu cầu của họ...Chúa Hằng Hữu ở gần người mọi người cầu khẩn Ngài. Tức ở gần mọi người có lòng thành thực cầu khẩn Ngài. Ngài làm thỏa nguyện mọi người kính sợ Ngài; Cũng nghe tiếng kêu cầu của họ, và giải cứu cho" (Thi-thiên 34:15; 145:18,19).')]),n._v(" "),i("p",[n._v("Đức Chúa Trời quá trong sạch không thể chấp nhận điều ác, nghĩa là Ngài quá thánh khiết không thể dính dáng đến tội lỗi. Trước khi tội lỗi xâm nhập dòng giống loài người, Đức Chúa Trời và con người có mối tương giao thân thiết. Bây giờ thì mối tương giao đó đã gãy đổ từ lâu và ở ngoài Chúa Cứu Thế Giê-xu thì mọi giao tiếp giữa Đức Chúa Trời và con người hoàn toàn bị mất. Chỉ qua Chúa Giê-xu con người mới có thể tái thiết lập lại tương giao với Đức Chúa Trời. Có những người bảo rằng tất cả mọi con đường đều dẫn đến Đức Chúa Trời, nhưng Chúa Giê-xu tuyên bố, "),i("strong",[n._v('"Ta là con đường, chân lý và sự sống, không bởi ta thì không ai đến được cùng Cha" (Giăng 14:6)')]),n._v(". Ngài cũng phán rằng, "),i("strong",[n._v('"Ta là cái cửa: ai bởi ta mà vào sẽ được cứu rỗi; họ sẽ vào, ra và gặp đồng cỏ" (Giăng 10:9)')]),n._v(".")]),n._v(" "),i("p",[n._v("Người là tội nhân, hoàn toàn bất lực không thể thay đổi tình trạng của mình, không thể làm gì để tiếng kêu thấu đến tai thánh khiết của Đức Chúa Trời, trừ phi kêu cầu với lòng chân thành xin ơn thương xót. Loài người sẽ tiếp tục đời đời hư vong nếu Đức Chúa Trời không do lòng thương xót vô biên sai Con Ngài xuống trần gian lấp đầy hố ngăn cách đó.")]),n._v(" "),i("p",[n._v("Chính trong sự thánh khiết của Đức Chúa Trời mà chúng ta tìm ra lý do cái chết của Chúa Cứu Thế Giê-xu. Chúa Giê-xu là con người duy nhất có đủ sự trọn lành, trong sạch, sức mạnh để mang lấy tội lỗi của toàn nhân loại. Sự thánh khiết của Đức Chúa Trời đòi hỏi án phạt chính xác dành cho tội lỗi còn tình thương của Ngài đã ban Chúa Giê-xu để trả cho án phạt này, đem sự cứu chuộc cho nhân loại. Vì Đức Chúa Trời chúng ta tôn thờ là Đức Chúa Trời trong sạch, thánh khiết, công bình và công chính nên Ngài đã ban cho chúng ta Con độc sanh của Ngài để mở ra cho loài người con đường đến với Ngài. Nhưng nếu chúng ta làm ngơ đối với sự trợ giúp này, không vâng theo những luật lệ Ngài thiết định, chúng ta không thể kêu xin Ngài thương xót khi sự trừng phạt của Ngài giáng xuống.")]),n._v(" "),i("h3",{attrs:{id:"duc-chua-troi-la-tinh-thuong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#duc-chua-troi-la-tinh-thuong","aria-hidden":"true"}},[n._v("#")]),n._v(' "Đức Chúa Trời Là Tình Thương"')]),n._v(" "),i("p",[n._v("Thứ tư: Đức Chúa Trời là Tình Yêu. Cũng như với tất cả những thuộc tính khác của Đức Chúa Trời, nhiều người không đọc Kinh Thánh sẽ không hiểu được điều Kinh Thánh nói: "),i("strong",[n._v('"Đức Chúa Trời là tình yêu" (1 Giăng 4:8)')]),n._v(".")]),n._v(" "),i("p",[n._v('Chúng ta không luôn luôn hiểu rõ mình muốn nói gì khi dùng từ ngữ yêu. Đây là một trong những từ ngữ bị dùng sai nhiều hơn hết trong tiếng Anh. Chúng ta dùng chữ yêu để mô tả những cái thấp thỏi nhất cũng như những điều tôn quí nhất trong các mối tương giao (Người Mỹ thường thay động từ "thích" bằng "yêu" bảo rằng họ "yêu" du lịch, "yêu bánh xô cô la", "yêu chiếc xe mới", "yêu màu giấy dán tường"...Tất nhiên họ cũng nói họ "yêu nhà hàng xóm", nhưng chỉ nói thế thôi chứ hầu như không làm gì để minh chứng "tình yêu" đó). Chính vì thế không ai ngạc nhiên khi thấy chúng ta không hiểu rõ khái niệm của Kinh Thánh bảo rằng: "Đức Chúa Trời là Tình Yêu."')]),n._v(" "),i("p",[n._v("Bạn không nên phạm sai lầm nghĩ rằng vì Đức Chúa Trời là tình yêu nên mọi sự sẽ dễ thương, đẹp đẽ, vui vẻ và không ai sẽ bị trừng phạt. Sự thánh khiết của Đức Chúa Trời đòi mọi tội lỗi phải bị trừng trị, nhưng tình thương của Đức Chúa Trời cung ứng kế hoạch cứu chuộc tội nhân. Tình yêu của Đức Chúa Trời đã dự liệu thập tự giá cho Chúa Giê-xu, qua đó con người có thể được tha tội, được thanh tẩy. Chính tình yêu của Đức Chúa Trời đã để cho Chúa Giê-xu lên thập tự giá!")]),n._v(" "),i("p",[n._v('Bạn đừng bao giờ đặt vấn đề đối với tình yêu vĩ đại của Đức Chúa Trời, vì tình yêu đó không hề thay đổi, là thành phần trong Đức Chúa Trời y như đức thánh khiết của Ngài. Dù tội lỗi bạn có kinh khủng đến đâu, Đức Chúa Trời vẫn yêu thương bạn. Nếu Đức Chúa Trời không có tình thương như thế thì không một người nào trong chúng ta có hy vọng gì trong cuộc sống tương lai. Nhưng Đức Chúa Trời là tình yêu và tình yêu của Ngài là tình yêu muôn đời. Thánh Phao-lô viết trong Rô-ma 5:8 như sau: "Đức Chúa Trời tỏ lòng yêu thương Ngài đối với chúng ta, khi chúng ta còn là người có tội, thì Chúa Cứu Thế vì chúng ta chịu chết."')]),n._v(" "),i("p",[n._v("Những lời hứa về tình yêu và sự tha thứ của Đức Chúa Trời là thật, là chắc chắn, là khẳng định hết mức diễn đạt của ngôn từ con người. Nhưng cũng giống như cách diễn tả đại dương, chúng ta sẽ không thể biết hết được vẻ đẹp của biển cả cho đến khi thấy tận mắt. Tình thương của Đức Chúa Trời cũng vậy. Cho đến khi bạn thực sự tiếp nhận, thực sự kinh nghiệm tình yêu của Ngài, cho đến khi bạn thực sự sở hữu sự bình an thật với Chúa thì không ai có thể mô tả cho bạn hiểu được những điều kỳ diệu trong tình yêu của Đức Chúa Trời.")]),n._v(" "),i("p",[n._v("Tình yêu không phải là cái bạn có thể hiểu bằng tâm trí. Tâm trí hữu hạn của bạn không thể hiểu nổi điều vĩ đại như tình yêu của Đức Chúa Trời. Trí óc bạn sẽ thấy thật khó giải thích sự kiện làm thế nào một con bò đen, ăn cỏ xanh mà lại cho sữa trằng- nhưng bạn vẫn thường uống sữa và nhận được sức lực từ sữa. Trí óc bạn không thể lý giải tất cả tiến trình phức tạp xảy ra khi hạt dưa hấu nhỏ bé bạn gieo mọc lên, trở thành một giây leo, sinh ra những quả dưa thật to, thật đỏ. Bạn không hiểu nhưng bạn vẫn ăn, vẫn thưởng thức quả dưa ngon ngọt đó! Bạn không hiểu hết về máy radio nhưng bạn vẫn nghe hàng ngày. Trí óc bạn không thể giải thích tại sao điện sinh ra ánh sáng dù bạn đang ngồi đọc sách dưới ánh đèn, nhưng bạn biết điện đang có đó và điện đang cho bạn phương tiện để đọc!")]),n._v(" "),i("p",[n._v("Bạn phải tiếp nhận Đức Chúa Trời bằng đức tin - đức tin nơi Con Đức Chúa Trời là Chúa Cứu Thế Giê-xu. Khi có đức tin như thế bạn sẽ thấy nghi ngờ không còn chỗ đứng. Lúc đó bạn không cần hỏi Đức Chúa Trời có trong lòng bạn hay không vì bạn tự biết.")]),n._v(" "),i("p",[n._v("Mỗi khi có ai hỏi làm sao tôi biết chắc Đức Chúa Trời là ai và Ngài như thế nào, tôi lại nhớ đến câu chuyện về một cậu bé thả diều. Hôm đó là một ngày đẹp trời, thật lý tưởng để chơi diều. Gió đều và khá mạnh, bên trên nhiều lớp mây chồng chất đang đuổi nhau bay ngang bầu trời. Con diều bọc gió bay lên nhanh và mạnh, không bao lâu đã lên tít trên cao, khuất sau những cụm mây thấp. Một người đi qua hỏi cậu bé:\n"),i("em",[n._v('"Cháu đang làm gì đó?"')]),n._v(" "),i("em",[n._v('"Dạ, cháu thả diều."')]),n._v(" "),i("em",[n._v('"Thế diều đâu sao không thấy? Cháu có chắc con diều trên đó không?"')]),n._v(" "),i("em",[n._v('"Dạ chắc chứ! Nó đang kéo căng sợi chỉ cháu cầm đây!"')])]),n._v(" "),i("p",[n._v("Bạn không cần phải dựa vào lời ai khác nói về Đức Chúa Trời vì chính bạn có thể tìm gặp Ngài, lúc đó chính bạn cũng sẽ biết Ngài hiện hữu qua sức kéo êm dịu, lạ lùng tác động trên những sợi tơ trong tâm hồn. Bạn biết chắc Ngài đang ở đó!")]),n._v(" "),i("h2",{attrs:{id:"chuong-4-su-kien-kinh-khung-ve-toi-loi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-4-su-kien-kinh-khung-ve-toi-loi","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 4: Sự kiện kinh khủng về tội lỗi")]),n._v(" "),i("p",[i("strong",[n._v('"Vì mọi người đều đã phạm tội thiếu mất sự vinh hiển của Đức Chúa Trời" (Rô-ma 3:23)')])]),n._v(" "),i("p",[n._v("Nếu Đức Chúa Trời là Đấng yêu thương công chính như thế thì tại sao lại có quá nhiều gian ác, đau thương, sầu khổ? Tất cả những hận thù ghen ghét này đến từ đâu? Tại sao chúng ta đã tạo ra những thần tượng giả dối này? Tại sao chúng ta đi đến chỗ thờ lạy những đền miếu của lòng tham, tư lợi và tranh chấp? Tại sao nhân loại đã được tạo dựng theo hình ảnh Đức Chúa Trời lại rơi vào tình trạng suy đồi quá sâu đậm đến nỗi Đức Chúa Trời đã phải ban Mười Giới Răn và truyền bảo con người tuân giữ? Tại sao Đức Chúa Trời đã phải sai Con Ngài đến cứu chúng ta? Tại sao tạo vật của Đức Chúa Trời lại đầy dẫy tham dục, gian ác?")]),n._v(" "),i("p",[n._v("Để hiểu tình trạng này, để thấy thật rõ tại sao các nước kình chống lẫn nhau, tại sao gia đình phân rẽ, tại sao trên báo chí đầy dẫy tin tức về sự bạo hành, những hành vi điên cuồng, tàn ác, hận thù... chúng ta phải trở lại với câu chuyện A-đam tại vườn Ê-đen trong chương đầu sách Sáng Thế Ký.")]),n._v(" "),i("p",[n._v("Có người bảo rằng câu chuyện sáng tạo quen thuộc này chỉ là huyền thoại. Họ bảo đó là một cách giải thích đơn giản cho một câu hỏi không thể trả lời của trẻ con. Nhưng thật ra không phải như vậy. Kinh Thánh cho chúng ta biết thật đích xác điều gì đã xảy ra từ đầu và tại sao con người từ đó đã trôi thật nhanh vào con đường tự hủy.")]),n._v(" "),i("p",[n._v("Đức Chúa Trời đã sáng tạo thế giới là một toàn thể hoàn hảo. Ngài tạo dựng một thế giới hài hòa, tốt đẹp, nhưng đã bị con người ném bỏ. Một thế giới toàn hảo ngày nay chúng ta mong có lại, một thế giới tất cả chúng ta đang tìm kiếm.")]),n._v(" "),i("p",[n._v("Trong cái thế giới toàn bích đó, Đức Chúa Trời đặt để một con người toàn bích. Sở dĩ A-đam toàn hảo vì không có gì Đức Chúa Trời sáng tạo lại ở dưới mức toàn hảo và trong con người hoàn toàn này, Đức Chúa Trời đã phú ban món quà quí giá hơn hết là sự sống vĩnh hằng. Ngài cũng ban cho con người món quà tự do là quyền tự do chọn lựa.")]),n._v(" "),i("p",[n._v("Tôi có một người bạn, Tiến sĩ M.L. Scott - một nhà truyền đạo da đen danh tiếng. Ông kể rằng một người bạn của ông có cậu con đi học xa trong một trường đại học, ngày kia trở về thăm nhà với nhiều kiến thức mới. Một buổi tối, cậu nói với cha bằng một giọng quan trọng:")]),n._v(" "),i("p",[i("em",[n._v('"Thưa cha, từ ngày lên đại học, con thấy con không thể nào chịu nổi cái đức tin đơn sơ, trẻ con của cha đối với Kinh Thánh nữa."')])]),n._v(" "),i("p",[n._v("Người cha ngồi đó nhìn chăm chú gương mặt con không chớp mắt. Cuối cùng ông bảo, "),i("em",[n._v('"Được rồi, đó là quyền tự do của con - cái quyền tự do ghê gớm của con."')])]),n._v(" "),i("p",[n._v("Đó chính là điều Đức Chúa Trời ban cho A-đam - quyền tự do chọn lựa. Quyền tự do ghê gớm của A-đam.")]),n._v(" "),i("p",[n._v("Con người đầu tiên không phải là người sống trong hang, không phải là một loài thú gầm gừ trong rừng sâu nỗ lực chế ngự những hiểm nguy của rừng rậm và thú hoang. A-đam đã được tạo dựng là một người trưởng thành hoàn toàn với mọi khả năng của thể xác và trí tuệ được phát triển đầy đủ và hàng ngày được tương giao với Đức Chúa Trời. Ý định của Đức Chúa Trời là để A-đam làm vua, cai quản địa cầu theo ý chỉ Ngài.")]),n._v(" "),i("p",[n._v("Đây là địa vị A-đam trong vườn, con người đầu tiên, con người toàn hảo với món quà vô giá, món quà kinh khủng là quyền tự do. A-đam có tự do hoàn toàn - tự do chọn lựa hay loại bỏ, tự do vâng theo hay chống nghịch mạng lệnh Đức Chúa Trời, tự do làm cho mình hạnh phúc hay khốn khổ. Vì không phải chỉ do sở hữu quyền tự do mà con người thỏa nguyện, vấn đề là cách con người sử dụng quyền tự do như thế nào sẽ qui định con người sẽ tìm được sự bình an với Chúa và với chính mình hay không.")]),n._v(" "),i("h3",{attrs:{id:"trung-tam-cua-van-de"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#trung-tam-cua-van-de","aria-hidden":"true"}},[n._v("#")]),n._v(" Trung Tâm Của Vấn Đề")]),n._v(" "),i("p",[n._v('Đây là trung tâm của vấn đề, vì đúng vào lúc được ban quyền tự do, con người phải đối diện với hai con đường. Tự do trở thành vô nghĩa nếu chỉ có một con đường. Tự do hàm ý chọn lựa, quyết định phương thức hành động. Tất cả chúng ta đều biết những con người chân thật, không phải do quyền tự do chọn lựa, nhưng vì họ không có những cơ hội gian dối. Tiến sĩ Malfred Gutzke từng nói, "Xin quí vị lớn tuổi đừng nghĩ rằng quí vị ngày càng tốt hơn chỉ vì quí vị cứng hơn." Chúng ta đều biết có những người kiêu hãnh vì họ tốt trong khi thật ra chỉ vì họ ở trong môi trường và lối sống giữ họ không thể sống bệ rạc được. Nếu chúng ta bình yên vô sự chỉ vì không có cám dỗ thì không thể khoe đã chống được cám dỗ!')]),n._v(" "),i("p",[n._v("Đức Chúa Trời không để A-đam trong tình trạng bất lợi, nhưng đã cho A-đam khả năng lựa chọn đồng thời cho cả cơ hội vận dụng tự do đó. Vì Đức Chúa Trời không thể làm điều gì dưới mức hoàn hảo cho nên Ngài đã đặt để A-đam trong một bối cảnh tối ưu để minh chứng ông có tùng phục Ngài hay không.")]),n._v(" "),i("p",[n._v("Ở trong vườn, A-đam hoàn toàn trong trắng, vô tội, không tì vết. Cả vũ trụ đặt trước mặt ông. Toàn thể lịch sử chưa viết của nhân loại trải dài như tấm giấy da mới nguyên trong tay A-đam chờ ông viết chương đầu tiên, chờ ông quyết định chọn con đường cho tất cả các thế hệ tương lai.")]),n._v(" "),i("p",[n._v("Đức Chúa Trời đã hoàn tất công trình của Ngài, đã tạo dựng khu vườn trên đất với đầy đủ mọi điều cho nhu cầu con người. Ngài đã tạo dựng con người toàn hảo theo hình ảnh Đức Chúa Trời. Ngài đã phú ban cho con người linh hồn và tâm trí, cho con người đầy đủ tự do để sử dụng tâm trí, linh hồn tùy ý. Cuối cùng Ngài đã hành sử như một người Cha khôn ngoan chờ xem đứa con sẽ chọn lựa điều gì.")]),n._v(" "),i("h3",{attrs:{id:"chon-lua-cua-con-nguoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chon-lua-cua-con-nguoi","aria-hidden":"true"}},[n._v("#")]),n._v(" Chọn Lựa Của Con Người")]),n._v(" "),i("p",[n._v("Đây là bài trắc nghiệm! Đây là thời điểm A-đam sẽ dùng ý chí tự do để chọn con đường đúng hoặc con đường sai lầm. A-đam chọn vì ông muốn chọn chứ không phải vì chỉ có một con đường duy nhất!")]),n._v(" "),i("p",[n._v('A-đam đã chọn. Tổ phụ loài người đã lãnh mọi hậu quả và cũng truyền lại cho toàn nhân loại nối tiếp theo sau. "Như bởi chỉ một tội mà sự đoán phạt rải khắp hết thảy mọi người..." (Rô-ma 5:18). Thánh phao-lô cũng bảo rằng, "Cho nên, như bởi một người mà tội lỗi vào trong thế gian, lại bởi tội lỗi mà có sự chết, thì sự chết đã trải qua hết thảy mọi người như vậy, vì mọi người đều phạm tội" (Rô-ma 5:12). Vì A-đam là đầu nguồn nhân loại, ông đã phun lên như một dòng suối trong vắt, được phép chọn lựa trở thành hoặc là một dòng sông tươi mát chảy qua những cánh đồng xanh màu mỡ hay thành một dòng thác đục ngầu xô vào bờ đá và xoáy qua những dốc núi thăm thẳm, tối tăm, đau thương, lạnh lẽo, không thể nào đem vui tươi, kết quả cho vùng đất xung quanh.')]),n._v(" "),i("p",[n._v("Người ta không được đổ lỗi cho Đức Chúa Trời về tình trạng rối loạn bi thương quá lâu của thế giới. Lỗi phạm này là của A-đam. A-đam đã được ban cho quyền chọn lựa, đã quyết định nghe theo lời dối trá của Kẻ Cám Dỗ hơn là chân lý của Đức Chúa Trời! Lịch sử nhân loại từ ngày đó cho đến nay là lịch sử nỗ lực vô ích của con người cố gắng đoạt lại địa vị đã mất do A-đam sa ngã, nhưng thất bại cho nên cũng đã không thể đảo ngược số phận bị rủa sả.")]),n._v(" "),i("p",[n._v('Nhưng có thể bạn sẽ bảo rằng, "Như vậy là không công bình! Tại sao ngày nay chúng ta phải chịu đau khổ vì con người đầu tiên phạm tội trong thời xa xưa? Tại sao trong suốt thời gian dài sau đó con người không thể phục hồi? Tại sao chúng ta vẫn tiếp tục bị trừng phạt mỗi ngày?" Suy nghĩ thông thường hôm nay là có thể cải thiện con người nếu cải tiến môi trường. Nhưng điều kỳ lạ chúng ta cần nhận thức đó là tội lỗi đầu tiên con người đã phạm là phạm trong một môi trường toàn hảo!')]),n._v(" "),i("p",[n._v("Bây giờ chúng ta hãy trở lại với câu chuyện của dòng sông - dòng sông lạnh, tăm tối đang lưu chảy dưới đáy vực. Tại sao dòng sông không tìm đường vươn lên chảy bên những cánh đồng ấm áp phía trên? Tại sao nó không từ bỏ chặng đường buồn thảm đó, trở thành dòng sông tràn trề hạnh phúc, vui tươi như khi nó vừa khơi nguồn trên đất? Lý do đơn giản là nó bất lực và trong nó không có sức làm điều gì khác. Một khi nó đã lao mình xuống bờ dốc thẳm vào vùng tăm tối, nó không thể tự nâng lên vùng đất bên trên rực rỡ ánh mặt trời. Phương tiện để nâng dòng sông lên vẫn có đó, vẫn hiện hữu nhưng dòng sông không hiểu phải sử dụng phương tiện đó như thế nào. Điều này nhắc tôi nhớ đến sông Hoàng Hà (nay là Trường Giang) bên Trung Hoa. Con sông này cuốn lôi bùn đất ra biển có đến hàng dậm, biến vùng nước trong xanh của đại dương thành mầu vàng đục. Nó hoàn toàn không thể làm gì khác!")]),n._v(" "),i("p",[n._v("Có một phép lạ luôn luôn sẵn sàng đưa dòng sông nhân loại ra khỏi cảnh khốn cùng trở lại với thung lũng ấm áp, bình yên, nhưng dòng sông không muốn. Nó cảm thấy mình không thể làm gì khác hơn là tiếp tục hành trình khốn khổ đau thương cho đến khi lạc mất vào đại dương hủy diệt.")]),n._v(" "),i("p",[n._v("Câu chuyện của dòng sông là câu chuyện của con người từ thời A-đam, quanh co, uốn lượn, lao xuống ngày càng sâu hơn vào bóng tối kinh hoàng. Dù đã cao giọng kêu la cầu cứu, nhưng chúng ta vẫn cố tình chọn lựa con đường lầm lạc như A-đam ngày xưa. Trong nỗi thất vọng sâu xa, chúng ta quay lại chống nghịch Đức Chúa Trời, đổ lỗi cho Ngài đã đưa chúng ta đến đường cùng. Chúng ta nghi ngờ sự khôn ngoan và sự phán xét của Ngài. Chúng ta chỉ trích lòng thương xót và tình yêu của Ngài. Chúng ta quên rằng A-đam là đầu của nhân loại, như tại Hoa kỳ Tổng thống đứng đầu chính phủ. Khi Tổng thống hành động, đó thực sự là toàn thể dân chúng Mỹ hành động qua ông. Khi Tổng thống quyết định, quyết định đó là quyết định của toàn dân.")]),n._v(" "),i("p",[n._v('A-đam là cội nguồn chung cho toàn nhân loại, cũng là thủy tổ chúng ta. Như chúng ta thừa hưởng các đặc tính thông minh, màu da, vóc dáng, tính tình từ ông bà, cha mẹ, thì nhân loại cũng thừa hưởng bản chất sa ngã, hư hoại từ A-đam. Khi A-đam khuất phục cám dỗ và sa ngã, những thế hệ tương lai cùng sa ngã theo, vì Kinh Thánh tuyên bố thật rõ ràng rằng hậu quả tội lỗi A-đam sẽ để lại trên từng hậu tự. Chúng ta đều biết quá rõ chân lý đắng cay trong những phân đoạn Kinh Thánh như Sáng Thế Ký 3:17-19 mô tả thảm kịch mà hành động của A-đam đã để lại cho tất cả chúng ta: "Đất sẽ bị rủa sả vì ngươi; trọn đời ngươi phải chịu khó nhọc mới có vật đất sinh ra mà ăn. Đất sẽ sinh chông gai và cây tật lê, và ngươi sẽ ăn rau của đồng ruộng; ngươi sẽ làm đổ mồ hôi trán mới có mà ăn, cho đến ngày nào ngươi trở về đất, là nơi có ngươi ra; vì ngươi là bụi đất, ngươi sẽ trở về bụi đất." Còn đối với Ê-va, Chúa phán: '),i("strong",[n._v('"Ta sẽ thêm cực khổ bội phần trong cơn thai nghén; ngươi sẽ chịu đau đớn mỗi khi sinh con, và sự dục vọng ngươi phải xu hướng về chồng, và chồng sẽ cai trị ngươi" (Sáng Thế Ký 3:16)')]),n._v(".")]),n._v(" "),i("p",[n._v("Nói cách khác, vì nguyên tội của A-đam mà đất có thời chỉ sinh ra những giống cây ích dụng, xinh tốt thì nay sinh ra cả cây xấu lẫn cây tốt. Trước đó con người sống trong vườn chỉ cần đưa tay ra là có thức ăn, không cần áo quần hay nơi ở, nay phải lao động cực nhọc suốt đời để lo cho mình và gia đình. Người nữ từng là một sinh vật vô lo hơn hết, nay đầy sầu thảm, đau buồn; Cả người nam và người nữ đều ở dưới án chết cả thuộc thể lẫn thuộc linh. Cái chết có ba phương diện:")]),n._v(" "),i("ol",[i("li",[n._v("chết tức khắc về phương diện thuộc linh.")]),n._v(" "),i("li",[n._v("khởi đầu của cái chết thuộc thể (từ khi chào đời chúng ta đã khởi sự bước vào tiến trình chết thuộc thể);")]),n._v(" "),i("li",[n._v("cuối cùng là cái chết đời đời.")])]),n._v(" "),i("h3",{attrs:{id:"toi-loi-xam-nhap"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#toi-loi-xam-nhap","aria-hidden":"true"}},[n._v("#")]),n._v(" Tội Lỗi Xâm Nhập")]),n._v(" "),i("p",[n._v('Qua A-đam tội lỗi đã xâm nhập nhân loại và từ đó đến nay con người đã nỗ lực loại trừ tội lỗi nhưng không thành công. Cũng vì vậy nhân loại đã hoài công tìm kiếm phương cách có thể đảo ngược số phận bị rủa sả này. Kinh Thánh cho biết Đức Chúa Trời đã cảnh cáo A-đam trước khi phạm tội rằng nếu ăn trái cây tri thức thì chắc sẽ chết. Kinh Thánh cũng cho biết Đức Chúa Trời truyền dặn A-đam và Ê-va "sinh sản thêm nhiều và làm đầy dẫy đất." Nhưng dù đã được tạo dựng theo hình ảnh Đức Chúa Trời, sau khi sa ngã, A-đam và Ê-va đã sinh con cái giống như họ, theo hình ảnh họ. Hậu quả là Ca-in và A-bên đã nhiễm căn bệnh tội lỗi đem đến cái chết thừa hưởng từ cha mẹ và lưu truyền cho từng thế hệ kể từ ngày đó. Tất cả chúng ta đều là tội nhân do di truyền và cho dù cố gắng đến đâu, chúng ta không thể nào thoát khỏi cái quyền thừa kế đó.')]),n._v(" "),i("p",[n._v("Chúng ta đã tìm đến đủ mọi phương cách để dành lại địa vị A-đam làm mất. Chúng ta đã thử dùng giáo dục, triết học, tôn giáo, chính quyền để tháo gỡ cái ách tội lỗi và sa đọa. Chúng ta đã tìm cách hoàn tất những điều Đức Chúa Trời muốn bằng cách dùng tâm trí đã bị tội lỗi giới hạn, trong khi biết rõ rằng nỗ lực này chỉ có thể đến từ trên cao. Động cơ của chúng ta có thể tốt và những nỗ lực của chúng ta đáng khen, nhưng tất cả còn quá thấp, quá xa mục tiêu cần đạt tới. Tất cả các kiến thức, các phát minh, tất cả những tiến bộ và những kế hoạch đầy tham vọng chỉ giúp chúng ta vươn tới rất ít, để rồi ném chúng ta trở lại khởi điểm, vì chúng ta vẫn phạm cùng những lỗi A-đam đã phạm - chúng ta vẫn đang cố gắng tự làm vua bằng sức riêng, bằng quyền riêng thay vì tuân giữ luật Chúa.")]),n._v(" "),i("p",[n._v("Trước khi dán nhãn bất công, vô lý cho Đức Chúa Trời và đổ thừa Ngài đã cho phép tội lỗi tràn vào thế gian, chúng ta cần nhìn vào tình hình kỹ lưỡng hơn. Với lòng thương xót vô biên Đức Chúa Trời đã sai Con Ngài đến chỉ cho chúng ta con đường ra khỏi khó khăn. Đức Chúa Trời đã đưa con Ngài đến với cùng những cám dỗ A-đam đối diện, nhưng Con Đức Chúa Trời đã đắc thắng. Sa-tan đã cám dỗ Chúa Giê-xu như nó đã cám dỗ A-đam. Sa-tan hứa dâng cho Chúa Giê-xu uy quyền và vinh quang nếu Ngài từ bỏ Đức Chúa Trời, y như những gì nó đã hứa cho A-đam qua Ê-va.")]),n._v(" "),i("h3",{attrs:{id:"lua-chon-cua-chua-cuu-the"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#lua-chon-cua-chua-cuu-the","aria-hidden":"true"}},[n._v("#")]),n._v(" Lựa Chọn Của Chúa Cứu Thế")]),n._v(" "),i("p",[n._v("Điểm khác biệt lớn nhất là Chúa Cứu Thế Giê-xu đã chống lại cám dỗ! Khi ma quỉ chỉ cho Ngài các nước thế gian và hứa dâng cho Ngài mọi vinh quang các nước đó nếu Ngài theo nó thay vì theo Đức Chúa Trời thì Chúa chúng ta đã phán, "),i("strong",[n._v('"Hỡi quỉ Sa-tan, ngươi hãy lui ra! Vì có lời chép rằng: Ngươi phải thờ phượng Chúa là Đức Chúa Trời ngươi, và chỉ hầu việc một mình Ngài mà thôi" (Ma-thi-ơ 4:10)')]),n._v(". Chúa Giê-xu đã hoàn toàn đắc thắng Kẻ cám dỗ để bày tỏ cho mọi người thuộc các thế hệ hậu lai bản chất vô tội của Ngài. Chúa Giê-xu chính là chiến thắng của chúng ta!")]),n._v(" "),i("p",[n._v("Với bản chất sa ngã và bao nhiêu yếu đuối trong cuộc sống, chúng ta đã chứng tỏ mình là con cái thật của A-đam, đã bước theo đúng từng bước chân A-đam. Có thể chúng ta không đồng ý với chọn lựa của A-đam nhưng chúng ta vẫn bắt chước A-đam! Không có ngày nào chúng ta không phải đối diện với cùng thử thách đặt trước A-đam. Không ngày nào chúng ta không có cơ hội chọn lựa giữa những lời hứa quỉ quyệt của ma quỉ và những lời chắc chắn của Đức Chúa Trời. Chúng ta mong sẽ có ngày các nỗi thất vọng, bệnh tật và sự chết biến mất, nhưng giấc mơ này không có cơ hội nào thành sự thật được chừng nào chúng ta còn là con cháu chưa được tân sinh của A-đam. Phải có cách giải quyết tội lỗi và trong những chương trước chúng ta đã thấy Đức Chúa Trời đã có hành động đối với vấn đề căn bản này của nhân loại.")]),n._v(" "),i("p",[n._v("Từ khởi đầu thời gian cho đến ngày nay, công cuộc tìm kiếm quyền lực đầy tinh thần vô đạo của con người, quyết tâm sử dụng quyền tự do cho những mục tiêu vị kỷ đã đưa con người đến bờ hủy hoại. Tất cả những đống đổ nát hoang tàn của bao nhiêu nền văn minh nằm rải rác khắp mặt địa cầu, là chứng nhân thầm lặng cho sự kiện con người không có khả năng tạo lập một thế giới trường tồn không có Đức Chúa Trời. Những đổ nát mới, những đau thương mới xuất hiện thêm mỗi ngày, nhưng con người vẫn tiếp tục lao vào con đường hủy hoại.")]),n._v(" "),i("p",[n._v("Trong lúc đó, Đức Chúa Trời với sự khôn ngoan và lòng thương xót vô tận vẫn tiếp tục dõi theo loài người, tiếp tục chờ đợi với lòng kiên trì và cảm thương không ai có thể hiểu. Ngài chờ để ban sự cứu rỗi và bình an cho những người đến kêu xin ơn thương xót. Hai con đường Đức Chúa Trời đặt trước A-đam, cũng được đặt trước mỗi chúng ta, và ngày nay chúng ta vẫn được tự do lựa chọn. Chúng ta đang sống trong thời đại ân sủng khi Đức Chúa Trời vẫn giữ lại sự trừng phạt đời đời mỗi chúng ta đáng phải chịu.")]),n._v(" "),i("p",[n._v("Chính sự hiện diện của tội lỗi làm con người không có hạnh phúc thật. Chính tội lỗi khiến con người không thể đạt được thế giới đại đồng từng mơ ước. Mỗi kế hoạch, mỗi nền văn minh con người xây dựng đến cuối cùng đều thất bại và rơi vào quên lãng vì mọi công trình con người làm đều không thực hiện trong sự công chính. Những đổ nát xung quanh chúng ta vào chính giây phút này là chứng nhân cho tội lỗi đang ngập tràn thế giới.")]),n._v(" "),i("h3",{attrs:{id:"nguyen-nhan-va-hau-qua"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#nguyen-nhan-va-hau-qua","aria-hidden":"true"}},[n._v("#")]),n._v(" Nguyên Nhân Và Hậu Quả")]),n._v(" "),i("p",[n._v('Con người dường như đã không còn thấy luật nhân quả thường hữu vẫn đang vận hành ở mọi cấp độ trong vũ trụ. Hậu quả thì khá hiển nhiên, nhưng nguyên nhân sâu xa, bao trùm thì dường như kém rõ ràng hơn, có lẽ vì những tác hại của triết lý hiện đại về "tiến bộ" đã làm nhãn quan nhân loại không còn tinh tường nữa. Có lẽ vì con người quá say mê cái lý thuyết nhân tạo, ngu dại này nên đã bám chặt lấy niềm tin tưởng rằng nhân loại đang tiến chậm nhưng chắc đến đỉnh cao hoàn thiện.')]),n._v(" "),i("p",[n._v("Nhiều triết gia lại còn lập luận rằng thảm kịch hiện đại của thế giới bất quá chỉ là một biến cố trong bước tiến đi lên khi nêu ra rằng trong lịch sử nhân loại cũng đã từng có những thời kỳ u tối vô vọng như vậy. Họ cũng bảo rằng những điều kiện đáng buồn của thời đại chúng ta đang sống chẳng qua chỉ là cơn đau lúc lâm bồn báo hiệu một ngày tươi sáng! Rằng nhân loại đang chập chững đi trong vườn trẻ mà chặng đường nên người hiểu biết, trưởng thành vẫn còn hàng bao nhiêu thế kỷ nữa!")]),n._v(" "),i("p",[n._v("Nhưng Kinh Thánh đã làm sáng tỏ điều khoa học tự nhiên dường như không muốn nhìn nhận đó là thiên nhiên bày tỏ cả về Đấng Tạo Hóa lẫn kẻ phá hoại. Con người đổ lỗi cho Đấng Tạo Hóa về việc làm của kẻ phá hoại. Con người quên rằng thế giới chúng ta hiện đang sống không còn giống như thế giới Đức Chúa Trời tạo dựng. Đức Chúa Trời sáng tạo thế giới toàn hảo nhưng tội lỗi đã làm hư hỏng. Đức Chúa Trời tạo dựng con người trong trắng, nhưng tội lỗi xâm nhập khiến con người trở thành ích kỷ. Mọi biểu hiện của điều ác đều là hậu quả của nguyên tội căn bản không thay đổi từ khi nó xâm nhập nhân loại. Nó có thể biểu lộ khác nhau nhưng về căn bản nó vẫn là cái khiến cho một người Phi châu man rợ với cây giáo trong tay rình rập nạn nhân bên lối mòn, cũng như khiến cho viên phi công học thức lái phản lực cơ bay ngang cùng khu rừng đó bất ngờ thả bom xuống ngôi làng.")]),n._v(" "),i("p",[n._v('Hai con người trên có lẽ cách xa nhau hàng thế kỷ văn hóa. Một đàng "tiến bộ" hơn, có mọi ưu thế của một nền văn minh nhân tạo, trong khi người kia vẫn còn ở tình trạng bán khai. Nhưng có thật sự họ khác nhau không? Không phải cả hai đều bị thôi thúc bởi nỗi sợ hãi và nghi ngờ người khác sao? Không phải cả hai đều hành động theo tư kỷ, nhất định đạt mục tiêu riêng bằng mọi giá? Quả bom có ít man rợ hơn, ít độc ác hơn mũi giáo trần không? Hay quả bom có vẻ "văn minh" hơn? Chúng ta có hy vọng tìm ra giải pháp cho những nan đề của nhân loại trong khi cả những người "sơ khai" nhất lẫn những người "tiến bộ" nhất đều chỉ muốn chém giết hơn là yêu thương hay không? Tất cả những chuyện đau buồn, cay đắng, bạo động, thảm kịch, đau thương, hổ nhục trong lịch sử con người đều được tóm tắt trong một chữ - tội lỗi. Ngày nay, phản ứng thông thường nhất khi tội ác được chỉ đích danh là người ta sẽ vênh mặt lên bảo, "Thì đã sao?" Thật ra con người đã cố ý làm cho tội lỗi trở thành phổ thông và đáng yêu! Những chương trình truyền hình nổi tiếng trên đất nước này rất quan tâm đến những thành phần giàu có, suy đồi. Tạp chí thì cổ võ cho những chuyện vô luân, đồi bại, bệnh hoạn. Tội lỗi ở trong con người. Tội ở trong "tôi."')]),n._v(" "),i("p",[n._v('Con người không muốn bị gọi là tội nhân dù chính cha mẹ, ông bà của họ cũng đã là tội nhân từ trước! Dầu vậy Kinh Thánh tuyên bố, "Không phân biệt gì hết, mọi người đều đã phạm tội, không còn phản chiếu vinh quang Thượng Đế" (Rô-ma 3:22-23). Kinh Thánh tuyên bố rằng mỗi con người trên trần gian đều là tội nhân trước mặt Đức Chúa Trời; mỗi khi tôi nghe có ai muốn luồn lọt, tránh né lời tuyên bố mạnh mẽ trên tôi nhớ lại câu chuyện một chấp sự đến nói với mục sư về vấn đề tội lỗi.')]),n._v(" "),i("p",[n._v('Ông bảo, "Thưa mục sư, hội thánh chúng tôi xin mục sư đừng nói quá nhiều và quá thẳng về tội lỗi. Chúng tôi ngại rằng con cái chúng tôi nếu phải nghe ông giảng quá nhiều về đề tài đó, chúng sẽ rất dễ trở thành tội nhân. Chúng tôi đề nghị ông chỉ nên gọi đó là những "sai sót" hay bảo rằng thanh niên của chúng ta có lỗi "phán đoán dở" chứ xin đừng nói quá mạnh đến chuyện phạm tội."')]),n._v(" "),i("p",[n._v('Ông mục sư bước tới, lấy xuống một lọ thuốc độc để ở ngăn cao nhất trên kệ. Lọ thuốc dán nhãn đỏ thật to: "Thuốc Độc". Đưa cho viên chấp sự xem, ông hỏi, "Ông muốn tôi làm gì? Ông có muốn tôi lột bỏ cái nhãn này rồi thay vào bằng một nhãn khác đề "Thuốc Bạc Hà" không? Ông không thấy là dán nhãn nhẹ hơn sẽ làm lọ thuốc độc này càng nguy hiểm hơn sao?"')]),n._v(" "),i("p",[n._v("Tội lỗi - cái tội muôn đời, cố cựu ngày xưa đã khiến A-đam thất bại chính là cái làm chúng ta ngày nay đau khổ. Nó sẽ gây tổn hại nặng nề hơn nếu chúng ta dán cho nó cái nhãn đẹp đẽ, hấp dẫn hơn. Chúng ta không cần từ mới để chỉ tội lỗi. Điều cần là tìm hiểu xem từ ngữ đã dùng mang ý nghĩa nào bởi vì dù ngày nay tội lỗi lan tràn khắp thế giới, trở thành phổ thông và đầy quyến rũ, nhưng vô số người vẫn không thực sự hiểu được ý nghĩa đích thật của tội lỗi là gì. Chính quan điểm thiển cận, sai lạc về tội lỗi đã cản trở nhiều người đến với Chúa. Cũng chỉ vì thiếu sự hiểu biết tận tường về tội lỗi mà nhiều Cơ đốc nhân đã không thể sống cuộc đời cơ đốc chân thật.")]),n._v(" "),i("p",[n._v('Một bài tâm linh ca cổ điển của người da đen có câu, "Mọi người nói về thiên đàng mà không đến thiên đàng," điều này cũng rất đúng đối với tội lỗi. Mọi người nói về tội lỗi nhưng không có một nhận thức rõ ràng tội lỗi là gì vì vậy điều tối quan trọng là làm sao chúng ta thấy thật rõ, thật rành rọt về quan điểm của Đức Chúa Trời về tội lỗi.')]),n._v(" "),i("p",[n._v('Có thể chúng ta cố gắng coi nhẹ tội lỗi, cho là "những yếu đuối của con người." Chúng ta cũng có thể coi đó là chuyện vặt, nhưng Đức Chúa Trời gọi đó là một thảm kịch. Chúng ta làm cho tội lỗi có vẻ là những tai nạn, nhưng Đức Chúa Trời coi đó là cả một sự ghê tởm. Con người tìm cách chữa tội nhưng Đức Chúa Trời công bố tội lỗi để có thể cứu con người khỏi tội. Tội lỗi không phải là đồ chơi để mua vui nhưng là một nỗi kinh hoàng phải tránh né! Vì vậy chúng ta cần học hỏi xem tội lỗi là gì trong cái nhìn của Đức Chúa Trời!')]),n._v(" "),i("p",[i("strong",[n._v("Tiến sĩ Richard Beal cho chúng ta năm từ chỉ tội lỗi.")])]),n._v(" "),i("p",[i("strong",[n._v("Thứ nhất:")]),n._v(" tội lỗi là vô luật pháp, vi phạm luật pháp của Đức Chúa Trời (I Giăng 3:4). Đức Chúa Trời thiết định biên giới giữa thiện lành và gian ác, và mỗi khi chúng ta vượt qua giới hạn, mỗi khi xâm phạm vùng cấm địa của tội lỗi, chúng ta can phạm luật Chúa. Mỗi khi không sống theo muời giới răn, mỗi khi đi trái với những nguyên tắc trong bài giảng trên núi, chúng ta phạm luật Chúa và phạm tội.")]),n._v(" "),i("p",[n._v("Nếu nhìn vào từng điều trong Mười giới răn, bạn sẽ thấy ngày nay con người không chỉ cố ý phạm mà còn thích thú ca ngợi việc sai phạm! Từ tội thờ thần tượng, tức là đặt bất cứ điều gì trên Đức Chúa Trời, cho đến việc nhớ và biệt thánh ngày nghỉ (nếu tín hữu đồng loạt không xem thì các trận đấu bóng bầu dục và dã cầu vào những ngày Chúa Nhật sẽ ra sao?) đến việc hiếu kinh cha mẹ (có những quyển sách như Mommie Dearest phơi bầy tội lỗi cha mẹ), đến tội tham lam, tội tà dâm, chúng ta thấy dường như người ta bảo nhau đồng loạt cố ý phạm tất cả các giới răn của Đức Chúa Trời. Không những thế, dường như người ta còn cố ý làm cho việc phạm giới răn trở thành thích thú, hấp dẫn!")]),n._v(" "),i("p",[n._v('Thánh Gia-cơ vạch rõ rằng tất cả chúng ta đều mắc tội, ông viết, "Nhưng mỗi người bị cám dỗ khi mắc tư dục xui giục mình. Đoạn lòng tư dục cưu mang, sinh ra tội ác; tội ác đã trọn sinh ra sự chết" (Gia-cơ 1:14,15). Đó là vì tất cả chúng ta đều phạm luật Chúa, tất cả đều phạm giới răn khiến chúng ta đều trở thành tội nhân.')]),n._v(" "),i("p",[i("strong",[n._v("Thứ hai:")]),n._v(" Kinh Thánh mô tả tội lỗi là sự độc ác, bất công ("),i("em",[n._v("iniquity")]),n._v("). Bất công là đi lệch khỏi điều phải dù hành vi đó có bị cấm đoán minh thị hay không. Sự độc ác, bất công liên quan đến những động cơ sâu kín bên trong là những điều chúng ta thường cố giấu khỏi con mắt loài người cũng như Đức Chúa Trời. Chúng là những sai trật xuất phát từ bản chất sa ngã hư hoại, hơn là từ những hành vi gian ác dưới áp lực của hoàn cảnh khiến chúng ta phạm tội.")]),n._v(" "),i("p",[n._v("Chúa Giê xu mô tả bản chất hư hoại bên trong này như sau, "),i("strong",[n._v('"Vì thật là tự trong, tự lòng người mà ra những ác tưởng, sự dâm dục, trộm cướp, giết người, tà dâm, tham lam, hung ác, gian dối, hoang đàng, con mắt ganh đố, lộng ngôn, kiêu ngạo, điên cuồng. Hết thảy những điều xấu ấy ra từ trong lòng thì làm cho dơ dáy người" (Mác 7: 21-23)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v("Thứ ba:")]),n._v(" Kinh Thánh định nghĩa tội lỗi là không trúng đích, không đạt được mục tiêu đề ra. Mục tiêu của Đức Chúa Trời là Chúa Cứu Thế Giê-xu. Đối tượng và mục đích cuối cùng của mọi cuộc đời là sống theo tiêu chuẩn đời sống Chúa Cứu Thế. Ngài đến để chỉ cho chúng ta điều con người có thể thành đạt trên đất, và khi không theo nổi gương Chúa, chúng ta sai mục tiêu, hụt mất tiêu chuẩn của Đức Chúa Trời.")]),n._v(" "),i("p",[i("strong",[n._v("Thứ tư:")]),n._v(" Tội lỗi là một hình thức vi phạm. Đó là theo ý riêng xâm phạm vào lãnh vực thẩm quyền của Đức Chúa Trời. Tội lỗi không chỉ có khía cạnh tiêu cực, không chỉ là thiếu lòng yêu mến Đức Chúa Trời. Tội lỗi là một sự lựa chọn tích cực, ưa thích bản ngã hơn Đức Chúa Trời. Đó là tập trung tình yêu vào chính mình thay vì đem cả tấm lòng dâng lên yêu mến Đức Chúa Trời. Chủ trương duy ngã và ích kỷ là những dấu hiệu rõ ràng của tội lỗi y như trộm đạo và giết người. Có lẽ đây lại chính là hình thức tội lỗi quỉ quyệt và tàn hại nhất của tội lỗi, vì chính trong hình thức tội lỗi này người ta dễ bỏ qua không thấy cái nhãn trên chai thuốc độc. Những người bám chặt lấy chính mình, tập trung mọi quan tâm, chú ý vào chính mình, những người chỉ lưu tâm đến những điều mình thích và chỉ tranh đấu, bảo quyền lợi riêng, thì đây là những tội nhân không thua gì những kẻ say sưa và phường đĩ điếm.")]),n._v(" "),i("p",[n._v("Chúa Giê-xu phán: "),i("strong",[n._v('"Nếu một người được cả thiên hạ mà mất linh hồn thì có ích gì?" (Mác 8:36)')]),n._v('. Chúng ta có thể dùng ngôn ngữ hiện đại để diễn đạt như sau: "Nếu một người xây dựng được cả một nền kỹ nghệ khổng lồ mà bị bệnh ung loét bao tử, không được vui hưởng lạc thú cuộc đời thì có ích gì? Nếu một nhà độc tài chinh phục được nửa địa cầu mà phải trường kỳ sợ hãi viên đạn báo thù hay lưỡi dao thích khách thì có ích gì? Nếu một bậc cha mẹ nuôi con bằng sự khắc nghiệt, áp chế để khi lớn nó bỏ mặc cha mẹ trong cô đơn của tuổi già thì có ích gì?" Hiển nhiên tội yêu bản ngã là một tội chết người.')]),n._v(" "),i("p",[i("strong",[n._v("Thứ năm:")]),n._v(" Tội lỗi là vô tín. Vô tín là tội vì nó xúc phạm tính chân thật của Đức Chúa Trời. "),i("strong",[n._v('"Ai tin Con Đức Chúa Trời thì có chứng ấy trong mình; còn ai không tin Đức Chúa Trời, thì cho Ngài là nói dối, vì chẳng tin đến chứng Đức Chúa Trời đã làm về Con Ngài" (I Giăng 5:10)')]),n._v(".")]),n._v(" "),i("p",[n._v("Chính lòng vô tín đóng cửa thiên đàng và mở toang cửa địa ngục. Chính lòng vô tín loại bỏ Lời Đức Chúa Trời và phủ nhận Chúa Giê-xu là Chúa Cứu Thế. Chính lòng vô tín đã khiến con người bịt tai không nghe Tin Lành và phủ nhận phép lạ của Chúa.")]),n._v(" "),i("p",[n._v("Tội lỗi đưa đến án chết và không ai có khả năng tự cứu khỏi án phạt tội lỗi hoặc tẩy sạch chính tấm lòng hư hoại của mình. Cả thiên sứ lẫn con người không thể chuộc tội. Chỉ trong Chúa Cứu Thế mới có phương thuốc chữa bịnh tội lỗi. Chỉ duy Chúa Cứu Thế mới cứu được tội nhân khỏi số phận đời đời đang chờ đón như Kinh Thánh khẳng định:")]),n._v(" "),i("p",[i("strong",[n._v('"Tiền công của tội lỗi là sự chết" (Rô-ma 6:23)')])]),n._v(" "),i("p",[i("strong",[n._v('"Linh hồn nào phạm tội sẽ chết" (Ê-xê-chi-ên 18:4)')])]),n._v(" "),i("p",[i("strong",[n._v('"Chẳng có người nào chuộc được anh em mình, hoặc đóng giá chuộc người nơi Đức Chúa Trời" (Thi-thiên 49:7)')])]),n._v(" "),i("p",[i("strong",[n._v('"Hoặc bạc hoặc vàng của chúng nó, đều không có thể giải cứu chúng nó trong ngày thạnh nộ của Chúa Hằng Hữu..." (Sô-phô-ni 1:18)')]),n._v(".")]),n._v(" "),i("h3",{attrs:{id:"phuong-thuoc-duy-nhat"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#phuong-thuoc-duy-nhat","aria-hidden":"true"}},[n._v("#")]),n._v(" Phương Thuốc Duy Nhất")]),n._v(" "),i("p",[n._v("Phương thuốc duy nhất cứu rỗi con người ra khỏi tội lỗi tìm thấy trên một ngọn đồi trọc hoang vu hình sọ, một tên trộm treo thân trên cây thập tự, cây bên kia treo một kẻ sát nhân, chính giữa là Con Người đội mão gai. Máu từ tay, chân Ngài chảy ra. Máu tuôn ra từ vết giáo bên sườn, máu từ những lỗ gai đâm trên đầu trên trán Ngài tuơm đầy mặt, trong khi đám đông bên dưới dửng dưng đứng nhìn chế diễu, nhạo cười.")]),n._v(" "),i("p",[n._v("Con người chịu cực hình này là ai, con người bị săn đuổi, hạ nhục và giết hại này là ai? Ngài là Con Thượng Đế, là Chúa Bình An, là Sứ Giả của thiên đàng cho trần gian ô tội. Ngài là Đấng các thiên thần phủ phục tôn thờ, che mặt không dám nhìn lên, thế mà Ngài đã bị chối bỏ, bị treo thân, đổ máu trên thập tự kia! Điều gì đã đưa Ngài đến chỗ kinh khủng này? Ai đã khiến cho Đấng đến dạy chúng ta bài học yêu thương phải chịu những cực hình kia? Cả tôi và bạn đều là thủ phạm, vì đó là do tội lỗi của bạn cũng như tội lỗi của tôi mà Chúa Giê-xu bị đóng đinh vào thập giá. Trong chính giấy phút bất tử này nhân loại vươn tới những kinh nghiệm đen tối nhất của tội lỗi, chìm xuống những vực thẳm sâu hơn hết, đụng đến những giới hạn gian ác nhất, chẳng trách được chính vì thế mà cả đến mặt trời cũng phải che mặt lại! Như Charles Wesley đã viết:")]),n._v(" "),i("p",[n._v('"Tôi được hưởng gì trong huyết Chúa Cứu Thế? Ngài đã chết vì tôi, nhưng ai đã làm Ngài đau đớn... Ôi tình yêu kỳ diệu! Làm sao Đức Chúa Trời lại có thể chịu chết vì tôi?\nNhưng tội lỗi đã bị đánh bại trên thập tự giá. Sự bất công ghê tởm của con người khiến Chúa Cứu Thế bị đóng đinh đã trở thành phương tiện mở đường cho con người được giải phóng. Kiệt tác của tội lỗi về ô nhục và hận thù đã trở thành kiệt tác của Đức Chúa Trời về lòng thương xót và tha thứ. Qua sự chết của Chúa Cứu Thế trên thập tự giá, chính tội lỗi của những người tin Ngài đã bị đóng đinh vào đó. Trên thập tự giá, tội lỗi đã bị chinh phục, sự chết của Chúa Cứu Thế trở thành nền tảng của hy vọng, thành lời hứa chiến thắng của chúng ta! Trên cây gỗ Chúa Cứu Thế đã mang trên chính thân thể Ngài mọi tội ác vốn xiềng xích chúng ta. Ngài đã chết cho chúng ta và sống lại. Ngài đã minh chứng rằng mọi lời hứa của Đức Chúa Trời cho con người là chân thật cho nên nếu bạn tiếp nhận Chúa Cứu ThếGiê-xu hôm nay bằng đức tin, mọi tội lỗi bạn cũng sẽ được tha thứ. Bạn có thể biết chắc chắn rằng qua tình thương của Chúa Cứu Thế Giê-xu, linh hồn bạn được tẩy sạch và được cứu khỏi án phạt tội lỗi.')]),n._v(" "),i("h2",{attrs:{id:"chuong-5-doi-pho-voi-ma-qui"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-5-doi-pho-voi-ma-qui","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 5: Đối phó với ma quỉ")]),n._v(" "),i("p",[i("strong",[n._v('"Vì chúng ta đánh trận, chẳng phải cùng thịt và huyết, bèn là cùng chủ quyền, cùng thế lực, cùng vua chúa của thế gian mờ tối này, cùng các thần dữ ở các miền trên trời vậy." (Ê-phê-sô 6:12)')])]),n._v(" "),i("p",[n._v("Có một nguyên tắc của Sa-tan dính dấp vào tất cả mọi điều đang xảy ra hôm nay. Kinh Thánh mô tả đó là "),i("strong",[n._v('"con rắn xưa tức là ma quỉ và Sa-tan, là kẻ lừa dối cả thế gian" (Khải Huyền 12:9)')]),n._v(", và chúng ta biết nó đang hành động, làm rối trí mọi dân tộc và mọi quốc gia. Chúng ta có thể thấy công việc của nó ở rất nhiều nơi.")]),n._v(" "),i("p",[n._v('Trong khi hy vọng nền hòa bình trên toàn thế giới đang ngày càng tiến lại gần thì dường như chúng ta lại thấy như mình đang đứng bên bờ vực của trận Hạt-ma-ghê-đôn. Chúng ta được biết đã có đến hàng trăm cuộc chiến tranh "nhỏ" trong khoảng từ 1945 đến 1979 cướp đi từ mười hai đến mười ba triệu sinh mạng, vì Sa-tan đã quyết chí đưa dòng sông nhân loại tăm tối, buồn thảm tiếp tục trôi theo con đường đau thương cho đến tận cùng thời gian. Nó đã thắng A-đam trong vườn Ê-đen cho nên nó tin rằng có thể đoạt luôn cả linh hồn hậu tự A-đam.')]),n._v(" "),i("p",[n._v("Ngày nay không có con người biết suy nghĩ nào lại không nhiều lần thắc mắc về sự hiện hữu của ma quỉ. Việc ma quỉ hiện hữu gần như là điều hiển nhiên và chúng ta thấy ảnh hưởng của nó khắp mọi nơi. Vấn đề không phải là có ma quỉ hay không, nhưng là tại sao ma quỉ hiện hữu và nó đã khởi sự xuất hiện như thế nào. Qua câu chuyện A-đam và Ê-va, chúng ta biết ma quỉ có mặt trên địa cầu trước khi Đức Chúa Trời tạo dựng con người đầu tiên. Sự ác đã hiện hữu từ trước, nếu không Đức Chúa Trời đã không làm nên cây biết điều thiện và điều ác, vì không cần thiết. Nếu sự ác không hiện hữu từ trước thì con người cũng không cần được bảo vệ khỏi ảnh hưởng của nó.")]),n._v(" "),i("h3",{attrs:{id:"duc-chua-troi-co-tao-nen-su-ac-khong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#duc-chua-troi-co-tao-nen-su-ac-khong","aria-hidden":"true"}},[n._v("#")]),n._v(" Đức Chúa Trời có tạo nên sự ác không")]),n._v(" "),i("p",[n._v("Tại đây chúng ta đối diện với một bí nhiệm lớn nhất, một bí mật quan trọng nhất, một vấn nạn khó trả lời nhất trong tất cả các vấn nạn. Làm thế nào Đức Chúa Trời là Đấng toàn năng, toàn thánh, toàn ái lại có thể tạo ra điều ác hoặc cho phép ma quỉ tạo ra điều ác? Tại sao A-đam cần phải bị cám dỗ? Tại sao Đức Chúa Trời không tiêu diệt Sa-tan ngay khi nó nhập vào con rắn để thì thầm lời dụ dỗ Ê-va phạm tội?")]),n._v(" "),i("p",[n._v("Kinh Thánh tiết lộ cho chúng ta một vài chỉ dẫn về lời giải đáp cho những vấn nạn trên, nhưng cũng vạch rõ rằng con người không được phép biết toàn thể câu trả lời cho đến khi Đức Chúa Trời cho phép ma quỉ và toàn bộ kế hoạch của nó hoàn thành chương trình lớn của Ngài.")]),n._v(" "),i("p",[n._v("Trước khi A-đam sa ngã, có thể là từ rất lâu trước khi A-đam hiện hữu, vũ trụ của Đức Chúa Trời dường như được chia thành nhiều lãnh vực ảnh hưởng. Mỗi lãnh vực được đặt dưới sự giám sát và kiểm soát của một thiên sứ hay một thiên hoàng ("),i("strong",[n._v("heavenly prince")]),n._v("), tất cả chịu trách nhiệm trực tiếp với Đức Chúa Trời. Thánh Phao-lô cho chúng ta biết đó là những "),i("strong",[n._v('"ngôi vua, chủ quyền, thế lực" cả trong thế giới hữu hình cũng như vô hình (Cô-lô-se 1:16; Ê-phê-sô 1:21)')]),n._v('. Kinh Thánh thường nhắc đến thiên sứ và thiên sứ trưởng cho thấy giữa vòng các hữu thể này có thứ bậc với quyền lớn nhỏ khác nhau. Ma quỉ chắc hẳn phải là một trong số các thiên sứ cao cấp, uy quyền, có lẽ đã được giao trấn nhậm địa cầu. Hữu thể thần linh này được biết tên là Lu-xi-phe, nghĩa là "kẻ mang ánh sáng", chắc hẳn được đứng rất gần Đức Chúa Trời, gần đến nỗi tham vọng phát sinh trong lòng khiến nó quyết định không muốn làm hoàng tử yêu quí của Đức Chúa Trời nữa mà nâng mình lên ngang hàng với Đức Chúa Trời! Lu-xi-phe không đồng bậc với Đức Chúa Trời nhưng chỉ là tạo vật cao cấp tương ứng với Mi-ca-ên hay Gáp-ri-ên. Nói cách khác, Lu-xi-phe chỉ là một thiên sứ sa ngã.')]),n._v(" "),i("p",[n._v("Chính vào giây phút đó, rạn nứt đã xuất hiện trong vũ trụ. Chính giây phút đó toàn thể vũ trụ vốn tốt lành, hài hòa trong ý chỉ Thượng Đế bị tách rời với một thành phần đứng riêng ở thế đối kháng Đức Chúa Trời. Cũng như ngày nay có những thể chế, những phe nhóm phủ nhận sự hiện hữu của Đức Chúa Trời hay chống lại thẩm quyền của Ngài, thì ma quỉ cũng thách đố Đức Chúa Trời như vậy và tìm cách thiết lập thẩm quyền riêng. Nó bỏ phạm vi riêng trong chính quyền của Đức Chúa Trời để tìm xuống các từng trời thấp hơn, tuyên bố rằng nó sẽ như Thượng Đế Tối Cao. Đức Chúa Trời đã đặt nó là vua của trần gian; Ngài chưa đuổi nó khỏi địa vị này dù căn bản để trục xuất nó đã được thiết lập trên sự chết của Chúa Cứu Thế Giê-xu. Cũng kể từ giây phút đó, ma quỉ đã khởi sự tranh đấu với Đức Chúa Trời trên đất.")]),n._v(" "),i("h3",{attrs:{id:"vuong-quoc-cua-ma-qui"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#vuong-quoc-cua-ma-qui","aria-hidden":"true"}},[n._v("#")]),n._v(" Vương Quốc Của Ma Quỉ")]),n._v(" "),i("p",[n._v("Là một vua đầy quyền lực với rất nhiều thiên sứ thuộc hạ, quỉ vương đã thành lập được vương quốc trên đất. Quyền lực và địa vị của nó chính là lý do khiến Kinh Thánh phải được viết ra. Nếu Sa-tan không thách thức Đức Chúa Trời và không chủ tâm chống lại sức mạnh và quyền bính Ngài thì câu chuyện A-đam trong vườn Ê-đen hẳn đã đổi khác. Nếu Sa-tan không đứng ra chống lại Đức Chúa Trời, thì Ngài đã không cần ban bố Mười Giới Răn và Đức Chúa Trời cũng không cần phải sai Con Ngài chịu chết trên thập tự giá.")]),n._v(" "),i("p",[n._v('Chúa Giê-xu và các môn đệ biết rất rõ về ma quỉ. Ma-thi-ơ ghi lại nguyên văn cuộc đối thoại giữa Chúa Giê-xu và ma quỉ trong Ma-thi-ơ 4: 1-10. Đối với phái Pha-ri-si, ma quỉ rất thật, thật đến độ họ dám cáo giác Chúa Giê-xu chính là ma quỉ (Ma-thi-ơ 12:24)! Đối với Chúa Giê-xu sự hiện hữu và quyền lực của ma quỉ hoành hành trên đất là rất thật. Sức mạnh của ma quỉ được trình bày rất rõ trong Giu-đe câu 9, "Vả khi chính mình thiên sứ trưởng Mi-chen chống với ma quỉ giành xác Môi-se, còn chẳng dám lấy lời nhiếc móc mà đoán phạt; người chỉ nói rằng: Cầu Chúa phạt ngươi."')]),n._v(" "),i("p",[n._v('Tất cả những quan niệm lộn xộn ngày nay về ma quỉ phần lớn do hậu quả của cái nhìn méo mó rất phổ biến về ma quỉ từ thời Trung Cổ. Để cho bớt sợ ma quỉ, người thời đó thường chế diễu, vẽ hình ma quỉ là một nhân vật thô lỗ, khờ dại, có sừng, có móng, có đuôi; tay cầm chĩa ba, mặt đểu cáng, rồi tự nhủ, "Sợ gì con quỉ xấu xí dị hợm này?"')]),n._v(" "),i("p",[n._v("Sự thật trái ngược hẳn. Ma quỉ là một tạo vật có trí thông minh xuất chúng, là một hữu thể thần linh mạnh mẽ, tài năng và vô cùng tinh khôn. Chúng ta đã quên rằng ma quỉ có lẽ là thiên sứ cao cấp nhất, được tôn cao nhất trong số các thiên sứ của Đức Chúa Trời. Ma quỉ là một nhân vật cao cấp đã quyết định sử dụng mọi ban tứ của Đức Chúa Trời cho những mục tiêu của chính hắn thay vì cho Ngài. Cách nó lập luận rất khôn ngoan, kế hoạch của nó đầy sáng tạo, lý luận của nó rất khó bẻ bác. Kẻ đối địch mạnh bạo của Đức Chúa Trời không phải là một tạo vật vụng về có sừng, có đuôi, nhưng là một vua uy nghi, vô cùng khôn khéo và quỉ quyệt, có khả năng lợi dụng mọi cơ hội cũng như chuyển đổi mọi tình huống thành có lợi cho mình. Nó là một kẻ tàn nhẫn và ác độc, tuy nhiên nó không toàn năng, không toàn tri cũng không toàn tại.")]),n._v(" "),i("p",[n._v("Ma quỉ có đủ khả năng tạo ra tiên tri giả mà Kinh Thánh đã cảnh cáo. Trên đống đổ nát của lòng vô tín và đức tin chao đảo, ma quỉ sẽ dựng lên kiệt tác của nó là một ông vua giả mạo. Nó sẽ tạo ra tôn giáo không Đấng Cứu Chuộc. Nó sẽ xây dựng một hội thánh không Chúa Cứu Thế. Nó sẽ kêu gọi sự thờ phượng không có Lời Chúa.")]),n._v(" "),i("p",[n._v("Sứ đồ Phao-lô tiên đoán tình trạng này trong "),i("strong",[n._v("2 Côrinhtô 11: 3,4,13")]),n._v(" như sau: "),i("strong",[n._v('"Nhưng tôi ngại rằng như xưa Ê-va bị cám dỗ bởi mưu chước con rắn kia, thì ý tưởng anh em cũng hư đi, mà dời đổi lòng thật thà tinh sạch đối với Chúa Cứu Thế chăng. Vì nếu có người đến giảng cho anh em một Giê-xu khác với Giê-xu chúng tôi đã giảng, hoặc anh em nhận một Thánh Linh khác với Thánh Linh anh em đã nhận, hoặc được một Tin Lành khác với Tin Lành anh em đã nhận, thì anh em chắc dung chịu!...Vì mấy người như vậy là sứ đồ giả, là kẻ làm công lừa dối, mạo chức sứ đồ của Chúa Cứu Thế."')])]),n._v(" "),i("h3",{attrs:{id:"ma-qui-va-ke-dich-lai-chua-cuu-the"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#ma-qui-va-ke-dich-lai-chua-cuu-the","aria-hidden":"true"}},[n._v("#")]),n._v(" Ma quỉ và Kẻ Địch Lại Chúa Cứu Thế")]),n._v(" "),i("p",[n._v("Chúng ta biết rằng kẻ địch lại Chúa Cứu Thế sẽ xuất hiện và cố gắng bẫy bắt tâm trí cũng như tấm lòng con người. Thời điểm đã gần, sân khấu đã sẵn sàng - tình trạng xáo trộn, kinh hoảng, sợ hãi đang lan tràn. Những dấu hiệu về các tiên tri giả đang xuất hiện khắp nơi và có lẽ nhiều người sẽ là nhân chứng sống của giây phút kinh hoàng khi màn cuối của bi kịch trường thiên kéo dài cả thời đại khởi sự. Có thể nó sẽ đến trong thời đại chúng ta vì nhịp độ đang gia tăng, các biến cố xảy ra nhanh hơn và từ mọi phía, chúng ta có thể thấy con người đang cố ý hoặc vô tình chọn chỗ đứng cho mình, hoặc ở phía Đức Chúa Trời hay ở bên ma quỉ.")]),n._v(" "),i("p",[n._v("Đây là cuộc tử chiến trong ý nghĩa đích thực của từ ngữ này, một cuộc chiến không giới tuyến, không khoan nhượng. Giai đoạn đầu của cuộc chiến này khởi sự tại vườn Ê-đen khi ma quỉ dụ hoặc con người từ bỏ Đức Chúa Trời, khiến cho khởi sự từ đó đã có hàng triệu ý chí tranh chấp lẫn nhau, mỗi người đi theo đường riêng. "),i("strong",[n._v('"Chúng ta thảy đều như chiên đi lạc, ai theo đường nấy. Chúa Hằng Hữu đã làm cho tội lỗi của hết thảy chúng ta đều chất trên Người" (Ê-sai 53:6)')]),n._v(". Tình trạng này sẽ cứ tiếp tục cho đến tận cùng thời gian...")]),n._v(" "),i("p",[n._v("Vào thời điểm này của lịch sử cuộc chiến gia tăng cường độ giữa Ba Ngôi Đức Chúa Trời (Đức Chúa Cha, Đức Chúa Con và Đức Thánh Linh) và ngụy ba ngôi mà Sa-tan muốn chúng ta tôn thờ thay vì Ba Ngôi Đức Chúa Trời gồm ma quỉ, kẻ địch lại Chúa Cứu Thế và tiên tri giả được mô tả trong sách "),i("strong",[n._v("Khải Huyền 16:13")]),n._v(": "),i("strong",[n._v('"Tôi thấy từ miệng rồng, miệng thú, miệng tiên tri giả có ba tà thần ra, giống như ếch nhái"')]),n._v(".")]),n._v(" "),i("p",[n._v("Không giây phút nào trong cuộc sống của bạn lại không ở dưới ảnh hưởng của những quyền lực này, và cũng không một giây phút nào bạn lại không có quyết định chọn sống theo một trong hai quyền lực ấy. Ma quỉ luôn luôn đứng bên cạnh thuyết phục, khuyến dụ, dọa dẫm, dụ dỗ bạn, trong khi phía bên kia là Chúa Giê-xu luôn luôn đầy yêu thương, tha thứ đứng chờ bạn quay hướng về Ngài kêu xin trợ giúp. Ngài vẫn đang chờ để ban cho bạn năng lực siêu nhiên chống lại Kẻ Thù. Bạn hoặc thuộc về Ngài hay thuộc về ma quỉ. Bạn sẽ không có vùng đất trung lập dung thân.")]),n._v(" "),i("p",[n._v("Trong những khoảnh khắc lo lắng và sợ hãi nhất của bạn, trong những lúc bạn thấy mình hoàn toàn bất lực trong gọng kìm của những biến cố ngoài tầm kiểm soát, khi chán nản và thất vọng trùm lấp bạn, trong những giây phút đó, ma quỉ nhiều lần bẫy bắt bạn ngay trong chỗ yếu nhất, để xô bạn rơi vào con đường A-đam đã đi.")]),n._v(" "),i("p",[n._v('Trong những khoảng thời gian nguy hiểm này xin nhớ rằng Chúa Cứu Thế không bỏ rơi bạn. Ngài không để bạn trong tình trạng không thể tự bảo vệ. Như chính Ngài đã thắng Sa-tan trong giờ thử thách và cám dỗ, Ngài cũng hứa rằng bạn cũng sẽ thắng hơn Kẻ Cám Dỗ mỗi ngày. Xin nhớ lời Chúa dạy rằng: "Hỡi các con cái bé mọn, phần các con là thuộc về Đức Chúa Trời, đã thắng được họ rồi, vì Đấng ở trong con là lớn hơn kẻ ở trong thế gian" (I Giăng 4:4). Cũng chính Kinh Thánh nhắc đi nhắc lại cho chúng ta về tình yêu của Đức Chúa Trời, thường xuyên cảnh cáo chúng ta rằng ma quỉ là kẻ chen vào ngăn cách chúng ta với Đức Chúa Trời, chực chờ để bẫy bắt linh hồn con người: "Hãy tiết độ và tỉnh thức: kẻ thù nghịch anh em là ma quỉ, như sư tử rống, đi rình mò chung quanh anh em, tìm kiếm người nào nó có thể nuốt được" (I Phi-e-rơ 5:8). Kinh Thánh cũng mô tả ma quỉ là một quỉ vương cầm quyền trên rất đông tà linh với nỗ lực kiểm soát, khống chế mọi hoạt động của con người, chúng là "vua cầm quyền chốn không trung, là thần hiện đương hành động trong con cái ngỗ nghịch" (Ê-phê-sô 2:2).')]),n._v(" "),i("h3",{attrs:{id:"dung-nghi-ngo-su-hien-huu-cua-ma-qui"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#dung-nghi-ngo-su-hien-huu-cua-ma-qui","aria-hidden":"true"}},[n._v("#")]),n._v(" Đừng Nghi Ngờ Sự Hiện Hữu Của Ma Quỉ")]),n._v(" "),i("p",[n._v("Xin bạn đừng bao giờ nghi ngờ sự hiện hữu của ma quỉ! Nó là một nhân vật rất thật, và vô cùng quỉ quyệt! Có lẽ điều khôn khéo nhất nó từng làm là thuyêt phục con người tin rằng nó không hiện hữu. Nếu bạn có thắc mắc gì về cá tính ma quỉ thì xin đọc lại trang đầu của tờ báo hôm nay, hoặc mở máy thâu thanh hay truyền hình ra nghe phần tin tức, xem danh mục các phim trình chiếu tại các rạp hát địa phương, liếc qua quầy sách và tạp chí... Tóm lại, nếu muốn có những bằng chứng cụ thể, bạn chỉ cần nhìn xung quanh là đủ!")]),n._v(" "),i("p",[n._v("Làm thế nào những con người sáng suốt, biết suy nghĩ lại có thể hành động như thế nếu không phải là họ đang ở trong móng vuốt của điều ác? Liệu những tấm lòng đầy ắp tình thương và sự thiện từ Đức Chúa Trời có thể cưu mang và thực hiện những hành vi bạo động, ác độc chúng ta vẫn nghe tường thuật mỗi ngày? Liệu những con người có giáo dục, thông minh và đầy thiện ý họp lại quanh bàn hội nghị thế giới mà lại hoàn toàn không hiểu được những nhu cầu và mục đích của nhau, nếu không phải là những suy nghĩ của họ đã cố tình bị che mờ và làm cho hư hỏng đi?")]),n._v(" "),i("p",[n._v('Mỗi khi tôi nghe nói về một người nào đó của thời đại mệnh danh là "sáng suốt" nhưng lại nghi vấn tính cách hiển nhiên về sự hiện hữu của ma quỉ, thủ lĩnh của vô số tà linh, tôi lại nhớ đến bài thơ sau đây của Alfred J. Hough (được diễn ra văn xuôi):')]),n._v(" "),i("p",[n._v('"Con người ngày nay không tin ma quỉ như cha ông họ ngày xưa. Họ đã mở tung cánh cửa giáo lý phóng khoáng nhất cho ma quỉ đi qua. Trên trời, dưới đất không thấy một dấu chân ma quỉ hay một mũi tên hung hãn nào bắn ra từ chân mày quỉ vương, vì thế gian này cố ý cho như thế".')]),n._v(" "),i("ul",[i("li",[n._v("Nhưng ai là kẻ rình rập bước đi của các thánh đồ lao nhọc và đào hố cho họ sụp chân?")]),n._v(" "),i("li",[n._v("Ai là kẻ gieo cỏ lùng vào cánh đồng mỗi khi Đức Chúa Trời gieo lúa mì?")]),n._v(" "),i("li",[n._v("Người ta đã cố ý bảo rằng ma quỉ không hiện hữu, nhưng như vậy ai là người đang làm những việc chỉ một mình ma quỉ làm được?")]),n._v(" "),i("li",[n._v("Người ta bảo rằng ma quỉ không đi rình mò như sư tử rống đâu, như vậy ai chịu trách nhiệm về tất cả những xáo trộn triền miên trong gia đình, hội thánh, quốc gia và cả ở những vùng xa xôi nhất trên địa cầu?")]),n._v(" "),i("li",[n._v("Nếu người ta đồng loạt bảo rằng không thấy bóng dáng ma quỉ ở đâu thế sao không có ai lên diễn đàn giải thích tại sao bao nhiêu gian dối, tội ác cứ leo thang từng ngày?")]),n._v(" "),i("li",[n._v('Người ta bảo rằng không có ma quỉ và thảng hoặc chúng đã bỏ đi rồi, nhưng điều những con người chất phác muốn biết đó là kẻ nào vẫn đang tiếp tục rắc gieo điều ác?"')])]),n._v(" "),i("p",[n._v("Như vậy ai là người chịu trách nhiệm về những chuyện đồi bại, khủng khiếp, thống khổ xảy ra chung quanh chúng ta? Làm thế nào giải thích được bao nhiêu đau thương chúng ta từng kinh nghiệm nếu đó không phải là do quyền lực của sự gian ác? Thật ra, nền giáo dục hiện đại đã cản trở tâm trí chúng ta. Chính vì những cái mệnh danh là những khám phá của khoa học mà một số người không tin vào quyền lực siêu nhiên của Sa-tan trong khi những người khác lại rơi vào thái cực tôn thờ nó.")]),n._v(" "),i("p",[n._v("George Galloway đã tóm tắt những đóng góp đáng ngờ của nền giáo dục hiện đại bảo rằng: "),i("em",[n._v('"Tâm trí con người hiện đại đã loại bỏ lý thuyết cho rằng trong vũ trụ này có một quyền lực hay một nguyên tắc hữu ngã hay vô ngã vĩnh viễn đối kháng với Đức Chúa Trời."')])]),n._v(" "),i("p",[n._v("Tâm trí hiện đại có thể loại bỏ quyền lực đó nhưng không vì thế mà cái nguyên tắc gian ác kia biến mất! Có lần được hỏi làm cách nào đắc thắng ma quỉ, Martin Luther trả lời, "),i("em",[n._v('"Có gì đâu, khi nó đến gõ cửa tâm hồn tôi và hỏi, '),i("em",[n._v('"Có ai ở nhà không?"')]),n._v(", thì tôi sẽ xin Chúa Giê-xu ra cửa trả lời, "),i("em",[n._v('"Hồi trước Martin Luther ở đây nhưng đã dọn đi rồi. Bây giờ ta ở đây."')]),n._v(' Khi quỉ vương thấy dấu đinh nơi tay Ngài, vết giáo nơi sườn Ngài thì hoảng hồn bỏ chạy."')])]),n._v(" "),i("h3",{attrs:{id:"tinh-cach-chac-chan-cua-toi-loi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#tinh-cach-chac-chan-cua-toi-loi","aria-hidden":"true"}},[n._v("#")]),n._v(" Tính cách chắc chắn của tội lỗi")]),n._v(" "),i("p",[n._v('Tội lỗi hiển nhiên là một sự kiện ghê gớm! Nó đứng sừng sững như một sức mạnh kinh khủng, đánh bạt tất cả những việc thiện lành con người nỗ lực thành đạt. Nó đứng như một bóng đen chực chờ ngăn chặn bất cứ tia sáng nào từ trên cao muốn chiếu rọi xuống chúng ta. Tất cả chúng ta đều biết điều này. Tất cả chúng ta đều thấy bóng đen đó. Tất cả chúng ta đều ý thức từng động tác của nó. Gọi nó là gì đi nữa thì chúng ta vẫn biết sự hiện hữu của nó là rất thật. "Vì chúng ta đánh trận chẳng phải cùng thịt và huyết, bèn là cùng chủ quyền, cùng thế lực, cùng vua chúa của thế gian mờ tối này, cùng ác thần dữ ở các miền trên trời vậy" (Ê-phê-sô 6:12).')]),n._v(" "),i("p",[n._v("Những người phủ nhận sự hiện hữu của ma quỉ và thuộc hạ làm sao có thể giải thích được tốc độ lan tràn của điều ác? Làm sao có thể giải thích được những trở lực vô tận trước lối đi của những người công chính? Làm sao có thể lý giải cho xuôi sự kiện hủy phá và tàn hại xảy ra trong khoảnh khắc trong khi xây dựng và phục hồi thường vô cùng chậm chạp?")]),n._v(" "),i("p",[n._v("Nói một câu dối trá, buông một lời phao vu, tự nhiên những lời đó như có ma thuật bay đi đến tận những xó xỉnh xa xôi nhất. Trong khi một lời chân thật, một hành động lương thiện, quảng đại, vừa thực hiện là có ngay những quyền lực vô hình, tức khắc ngăn chặn, che khuất tia sáng hy vọng le lói này đi.")]),n._v(" "),i("p",[n._v("Khi quyển sách này được viết ra trong ấn bản đầu tiên năm 1953, lúc đó chưa có ai xây đền thờ cho ma quỉ, chưa có toà giảng nào giảng những lời của quỉ, thế mà ngày nay người ta đã làm tất cả những điều đó. Lời của quỉ được nói khắp nơi, lại thường được dịch ra thành những hành động tuyệt vọng hơn hết. Nếu không có những quyền lực vô hình đang hành động làm hư hỏng tâm hồn con người, bóp méo tư tưởng, làm sao bạn có thể giải thích được sự kiện nhân loại tha thiết, thích thú muốn nghe những điều hèn hạ, tầm thường, xấu xa, bẩn thỉu, trong khi lại bịt tai đối với những lời tốt lành, trong sáng, thanh sạch? Mỗi người cần nghe những lời phạm thượng trong những bản nhạc rock mới nhận ra rằng Sa-tan đang sống mạnh giỏi trên địa cầu.")]),n._v(" "),i("p",[n._v("Có người nào trong chúng ta lại từ chối một trái cây tươi chín ngọt để chọn lấy những trái cây thối rữa, sâu bò lúc nhúc nếu không bị thôi thúc bởi một cường lực lớn lao, độc hiểm? Nhưng đây lại đúng là những điều tất cả chúng ta vẫn thường làm. Chúng ta không ngừng loại bỏ những kinh nghiệm đẹp đẽ, phong phú, cao thượng để tìm những cái hào nhoáng, rẻ tiền, tầm thường. Đây là công việc của ma quỉ đang tràn lan khắp mọi nơi!")]),n._v(" "),i("h3",{attrs:{id:"cuoc-tranh-chap-giua-thien-va-ac"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#cuoc-tranh-chap-giua-thien-va-ac","aria-hidden":"true"}},[n._v("#")]),n._v(" Cuộc tranh chấp giữa thiện và ác")]),n._v(" "),i("p",[n._v("Những gì chúng ta đang thấy trên địa cầu chỉ là phản ánh của những cuộc tranh chiến lớn lao hơn giữa thiện và ác trong lãnh vực rộng lớn hơn. Chúng ta thích nghĩ rằng địa cầu là trung tâm vũ trụ và chúng ta bị lôi cuốn quá nhiều vào những biến cố trần gian. Trong niềm kiêu hãnh dại dột, chúng ta nhìn sự việc bằng mắt trần, nhưng cuộc tranh chiến có phạm vi vô cùng rộng lớn hơn đang tiến hành trên cái thế giới chúng ta không thể thấy!")]),n._v(" "),i("p",[n._v('Những người khôn ngoan của thời xưa biết rõ điều này. Họ biết có rất nhiều điều mắt trần không phân định được cũng như nhiều điều tai người không thể nghe ra. Con người hiện đại thích nghĩ rằng mình "sáng tạo" radio, truyền hình và máy vi tính và rằng chính con người có khả năng chuyển âm thanh, hình ảnh qua không gian và tạo ra cũng như lưu trữ những khối lượng dữ kiện vô cùng lớn lao. Tất nhiên, sự thật là những sóng vô tuyến này, tuy con người không biết, nhưng đã hiện hữu từ trước, cũng như biết bao nhiêu kỳ quan vô cùng lớn lao vẫn hiện hữu ở ngoại tầng không gian, mà con người không hề biết. Các nhà tiên tri cổ thời tuy biết những kỳ quan kia có đó, nhưng họ chỉ nói về tính cách vĩ đại của nó một cách mơ hồ, và họ cũng chỉ nắm được những tiếng vang yếu ớt nhất của trận chiến kinh khủng tại các tinh cầu.')]),n._v(" "),i("p",[n._v('Một trong biết bao nhiêu cái giá A-đam phải trả khi nghe lời ma quỉ là bị mất khả năng nhìn thấy các chiều kích thuộc linh. Chính A-đam bị mất, và cũng làm cho toàn thể nhân loại mất khả năng thấy, nghe, hiểu bất cứ điều gì không phải là vật chất. A-đam đã tự ngăn cách chính mình với những kỳ quan và vinh quang vĩnh cửu của thế giới vô hình, đánh mất quyền tiên tri chân chính, đánh mất khả năng nhìn về tương lai là khả năng giúp con người có thể hiểu và thực hiện công việc trong hiện tại. A-đam đã đánh mất giác quan về tính liên tục của sự vật và đã "chết về tội lỗi và quá phạm." A-đam đã tự cắt đứt chính mình với Đức Chúa Trời.')]),n._v(" "),i("p",[n._v('G. Campbell Morgan từng nói: "Khoảng cách của chúng ta với Đức Chúa Trời là sự kiện chúng ta không thể biết và hiểu những cái ở gần. Đó là khoảng cách của người khiếm thị với vẻ đẹp của bức tranh trước mắt. Đó là khoảng cách của người điếc với những âm điệu tuyệt vời của một hoà tấu khúc trong thính phòng. Đó là khoảng cách của một người linh hoạt tách rời với mọi chuyển động của đời sống."')]),n._v(" "),i("p",[n._v("Nhưng cứ thử để cho một thảm kịch hay một bệnh hoạn xảy đến cho chúng ta, hay khi chúng ta lãnh hậu quả tội lỗi của chính mình, tức khắc chúng ta sẽ oán trách Chúa ngay! Chúng ta có thể kiên nhẫn, cảm thông với chiếc máy truyền hình không mấy tốt, nhưng nếu có một bức tranh méo mó nào đó về vũ trụ do Đức Chúa Trời tạo dựng, chúng ta sẽ không ngần ngại trách móc Đức Chúa Trời ngay.")]),n._v(" "),i("p",[n._v("Hãy để khi có người được thăng thưởng trong khi chúng ta bị bỏ quên hay người không xứng đáng lại thành công trong khi chúng ta thất bại, lúc đó chúng ta sẽ kêu la chống lại sự bất công của Đức Chúa Trời. Lúc đó chúng ta sẽ đòi phải được biết tại sao Đức Chúa Trời lại cho phép những điều không công bằng đó xảy ra! Chúng ta không thấy được sự kiện là Đức Chúa Trời giống như trung tâm phát hình, lúc nào cũng truyền đi những hình ảnh toàn hảo về tình thương và sự công chính, nhưng sở dĩ chúng ta chỉ nhận những hình ảnh méo mó là vì trục trặc trong máy thu nơi chính chúng ta!")]),n._v(" "),i("h3",{attrs:{id:"su-ac-trong-nhan-quan-chung-ta"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#su-ac-trong-nhan-quan-chung-ta","aria-hidden":"true"}},[n._v("#")]),n._v(" Sự ác trong nhãn quan chúng ta")]),n._v(" "),i("p",[n._v('Chính điều ác và những méo mó trong chúng ta đã khiến chúng ta không thấy, không kinh nghiệm được thế giới toàn hảo của Đức Chúa Trời. Chính tội lỗi đã làm nhoà hình ảnh, khiến chúng ta thay vì làm con cái trong sạch của Đức Chúa Trời, lại trở thành con cái điều ác. Nói thay cho tất cả chúng ta, Thánh Phao-lô bảo rằng, "tôi không làm điều lành mình muốn nhưng làm điều dữ mình không muốn" (Rô-ma 7:19). Ông nhận ra kẻ thù đáng sợ đó, kẻ thù cường bạo của toàn nhân loại khi kêu lên rằng, "Khốn nạn cho tôi! Ai sẽ cứu tôi thoát khỏi thân thể hay chết này? Cảm tạ Đức Chúa Trời, nhờ Chúa Cứu Thế Giê-xu là Chúa chúng ta! Như vậy, thì chính mình tôi lấy trí khôn phục luật pháp của Đức Chúa Trời, nhưng lấy xác thịt phục luật pháp của tội lỗi" (Rô-ma 7:24-25).')]),n._v(" "),i("p",[n._v("Đối với Phao-lô, hai kẻ thù kinh khủng thật hiển nhiên và ông đã ý thức rất rõ mình đã bị xâu xé giữa hai sức hút đáng sợ này. Quyền lực của điều thiện kéo tâm trí, tấm lòng ông về phía Đức Chúa Trời, trong khi quyền lực của điều ác đang cố lôi thân xác ông vào sự hủy hoại và sự chết. Nếu quan tâm đến điều này, bạn có thể đọc hai cuốn sách sau về vấn đề quỉ ám và sự xuất hiện của các tà giáo: Demon Possession (Quỉ Ám) của John L. Nevius và Unholy Devotion: Why Cults Lure Christians (Tư Duy Phàm Tục: Tại Sao Tà Giáo Dụ Hoặc Cơ Đốc nhân) của Harold L. Bussell.")]),n._v(" "),i("p",[n._v("Bạn cũng bị vướng mắc giữa hai sức mạnh: sự sống và sự chết! Hãy chọn con đường của Đức Chúa Trời là con đường đưa đến sự sống. Chọn con đường của Sa-tan, nó sẽ đưa bạn vào chỗ chết.")]),n._v(" "),i("h2",{attrs:{id:"chuong-6-noi-that-vong-cua-su-co-don"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-6-noi-that-vong-cua-su-co-don","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 6: Nỗi thất vọng của sự cô đơn")]),n._v(" "),i("p",[i("strong",[n._v('"Tôi bị chúng quên đi như kẻ chết mà lòng không còn nhớ đến; tôi giống như một cái bình bể nát." (Thi-thiên 31:12)')])]),n._v(" "),i("p",[n._v("Sau khi chồng qua đời, Nữ-hoàng Victoria bảo rằng "),i("em",[n._v('"Không còn ai để gọi tôi là Victoria"')]),n._v(". Dù là nữ hoàng nhưng bà cũng hiểu thế nào là cô đơn.")]),n._v(" "),i("p",[n._v("H.G. Wells nói trong sinh nhật sáu mươi lăm, "),i("em",[n._v('"Tôi đã 65 tuổi, cô đơn và không tìm thấy bình an."')])]),n._v(" "),i("p",[n._v('Isadora Duncan vũ nữ ba-lê từng trình diễn trước các hoàng gia Au châu và được coi là nghệ sĩ ba-lê danh tiếng nhất đã nói, "Tôi chưa bao giờ phải ở một mình, nhưng lòng tôi đau đớn, mắt tôi ngấn lệ và tay tôi run rẩy mong có sự bình an và niềm vui tôi chưa bao giờ tìm được." Cô nói thêm là ở giữa hàng triệu người ái mộ, cô vẫn thực sự là một phụ nữ vô cùng cô đơn.')]),n._v(" "),i("p",[n._v("Ít năm trước đây, một ngôi sao màn bạc Hollywood trẻ đẹp, có đủ mọi sự nhưng đã quyên sinh. Trong bức thư tuyệt mệnh ngắn ngủi là một lời giải thích vô cùng đơn giản, cô không thể chịu nổi nỗi cô đơn.")]),n._v(" "),i("p",[n._v("Tác giả Thi-thiên bảo rằng: "),i("strong",[n._v('"Tôi trở giống như con chàng bè nơi rừng vắng, khác nào chim mèo ở chốn bỏ hoang. Tôi thao thức, tôi giống như chim sẻ hiu quạnh trên mái nhà" (Thi-thiên 102: 6,7)')]),n._v(". Một chỗ khác, tác giả Thi-thiên nói, "),i("strong",[n._v('"Sự sỉ nhục làm đau thương lòng tôi, tôi đầy sự khổ nhọc; tôi trông đợi có nhiều người thương xót tôi, nhưng chẳng có ai; Tôi mong nhờ người an ủi, song nào có gặp."')])]),n._v(" "),i("h3",{attrs:{id:"noi-co-don-trong-canh-co-lieu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#noi-co-don-trong-canh-co-lieu","aria-hidden":"true"}},[n._v("#")]),n._v(" Nỗi cô đơn trong cảnh cô liêu")]),n._v(" "),i("p",[n._v("Nỗi cô đơn đầu tiên là nỗi cô đơn trong cảnh cô liêu. Tôi đã từng cảm thấy nỗi cô đơn của đại dương, nơi không có một âm thanh nào ngoài tiếng sóng ì ầm vỗ vào bờ đá. Tôi đã từng cảm thấy nỗi cô đơn của đồng hoang nơi chỉ lâu lâu mới nghe một tiếng tru của chó rừng. Tôi đã từng cảm thấy nỗi cô đơn của núi đồi trong tiếng thở than của gió ngàn.")]),n._v(" "),i("p",[n._v("Người lính gác trong chòi canh, hàng ngàn con người trong các bệnh viện tâm thần, những người tù biệt giam hay trong các trại tập trung hiểu rất rõ cô đơn trong cảnh cô liêu là gì.")]),n._v(" "),i("p",[n._v("Louis Zamperini, vận động viên điền kinh Olympic nổi tiếng kể lại về nỗi cô đơn kinh khủng khi phải lênh đênh trên một cái bè trong suốt 48 ngày trong thế chiến thứ hai.")]),n._v(" "),i("p",[n._v('Trong cuốn sách kỳ thú tựa đề Alone (Một Mình), Đô-đốc Richard E. Byrd kể lại thời gian ông sống trong bối rối và trong bóng đêm tàn phá linh hồn. Ông sống một mình trong một cái lều vùi dưới khối băng giá ở Nam Cực ròng rã suốt 5 tháng. Ngày tối như đêm và không có một sinh vật nào hiện hữu trong vòng 100 dậm Anh. Cái lạnh giá kinh khủng đến độ ông có thể nghe được tiếng hơi thở đóng băng khi gió thổi ngang tai. Ông bảo rằng ban đêm trước khi tắt đèn, ông phải tập thói quen hoạch định công việc cho ngày hôm sau để giữ cho tâm trí tỉnh táo. Ông nói, "Còn được phân bố thì giờ như thế này đã là rất hay. Nó đem lại cho tôi một ý thức rất quan trọng về việc làm chủ chính mình. Nếu không có hoạt động liên tục, ngày tháng sẽ không còn mục tiêu, mà không có mục tiêu nó sẽ chấm dứt - vì những ngày như thế luôn luôn kết thúc, kết thúc trong tan rã."')]),n._v(" "),i("h3",{attrs:{id:"noi-co-don-trong-xa-hoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#noi-co-don-trong-xa-hoi","aria-hidden":"true"}},[n._v("#")]),n._v(" Nỗi cô đơn trong xã hội")]),n._v(" "),i("p",[n._v("Có lẽ bạn nghĩ rằng trong vùng đất hoang vu băng giá kia Richard Byrd hẳn là con người cô đơn hơn hết. Tuy nhiên cái cô đơn trong xã hội còn kinh khủng hơn cái cô đơn của ông ta trong cảnh cô liêu nhiều và đây là nỗi cô đơn trong các đô thị lớn.")]),n._v(" "),i("p",[n._v("Có những con người nghèo khổ sống trong các căn nhà ổ chuột dơ bẩn, những người chẳng bao giờ nhận được một lá thư, không bao giờ được nghe một lời khen khích lệ, không bao giờ kinh nghiệm một cái bắt tay thân mật. Nhưng cũng có những lãnh tụ xã hội giàu có đến nỗi có tiền mua mọi thứ trừ tình yêu và hạnh phúc. Cả hai thành phần này đều có nỗi cô đơn ít ai hiểu được.")]),n._v(" "),i("p",[n._v("Có nỗi cô đơn của những con người sống bên hè phố, dưới những mái che bằng bìa các tông, ăn thức ăn thừa bươi từ các đống rác. Đây là nỗi cô đơn độc đáo.")]),n._v(" "),i("p",[n._v('Gần đây có một chương trình truyền hình nói lên nỗi cô đơn tàn nhẫn về những người già bị quên lãng trong các viện dưỡng lão tồi tàn. Hình ảnh những con người ngồi như cái bóng với cặp mắt xa vắng ám ảnh tôi. Họ là những người chết đang sống. Phía sau là một ông già khác, đang dùng một ngón tay gõ lên chiếc đàn dương cầm cũ kỹ bản thánh ca "Chúa Giê-xu Là Bạn Thật".')]),n._v(" "),i("p",[n._v("Trong Phúc Am Giăng đoạn 5 thuật lại việc Chúa Giê-xu đi trên những con đường chật hẹp ở Giê-ru-sa-lem, khi đến vòm cửa Chiên ở ao Bê-tết-đa, Chúa thấy cả một đám đông người tật nguyền đau ốm nằm ngồi la liệt trên bờ ao chờ dịp lội xuống nước. Chợt để ý thấy một người khốn khổ, trông buồn thảm hơn tất cả những người khác, Chúa hỏi, "),i("em",[n._v('"Ngươi có muốn lành không?"')]),n._v(" Anh ta ngước nhìn lên, trả lời, "),i("em",[n._v('"Tôi chẳng có ai để quăng tôi xuống ao trong khi nước động"')]),n._v('. Suốt ba mươi tám năm dài vất vưởng, con người khốn khổ này bị cả một làn sóng người ở Giê-ru-sa-lem ngăn cản, và sau bao nhiêu năm đó, anh đã phải tha với Chúa, "Tôi chẳng có ai..." Anh ta hoàn toàn không bè bạn.')]),n._v(" "),i("p",[n._v("Hôm nay bạn có thể có một người gần gũi khắng khít hơn anh em ruột. Chúa Cứu Thế Giê-xu có thể làm đời bạn vui mừng, thỏa nguyện, tươi sáng. Trên thế giới có hàng triệu người đang yêu mến và hầu việc Chúa Giê-xu, cho nên chính giây phút bạn tiếp nhận Ngài, bạn sẽ trở nên gần gũi với những Cơ Đốc nhân đó hơn cả với những người ruột thịt.")]),n._v(" "),i("p",[n._v("Không một thành phố nào tại Hoa Kỳ không có những nhà thờ ấm cúng, là nơi bạn có thể đến, gặp gỡ những con người thân thiện nhất nước Mỹ. Tại đất nước này có cả một mạng lưới tín đồ chân thật trong tất cả các cộng đồng. Ngay lúc bắt tay họ, bạn sẽ biết rằng bạn đã có những bằng hữu chân tình. Nhưng trước hết bạn cần phải ăn năn, trao dâng tấm lòng và cả cuộc đời bạn cho Chúa Cứu Thế Giê-xu. Hãy để Ngài tha thứ cho bạn mọi lỗi lầm trong quá khứ rồi Ngài sẽ đưa bạn vào trong đại gia đình của Ngài; Ngài sẽ đưa bạn đến ngồi bên lò sưởi, bên ngọn lửa ấm áp của gia đình. Nếu bạn đang cô đơn, tôi mời bạn đến với Chúa Giê-xu để có thể kinh nghiệm mối tương giao thân ái nơi Ngài.")]),n._v(" "),i("h3",{attrs:{id:"noi-co-don-vi-dau-don"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#noi-co-don-vi-dau-don","aria-hidden":"true"}},[n._v("#")]),n._v(" Nỗi cô đơn vì đau đớn")]),n._v(" "),i("p",[n._v('Nỗi cô đơn thứ ba là nỗi cô đơn vì đau đớn. Ít năm trưóc đây chúng tôi nhận được lá thư của một thính giả chương trình phát thanh đã năm năm bị tàn phế vì bệnh thấp khớp. Trong suốt năm năm dài đau đớn, bà không thể duỗi tay chân ra hoặc nằm xuống mà chỉ ngồi. Bà viết, "Đã bao nhiêu ngày tôi ở một mình, nhưng không một ngày nào cô đơn." Tại sao? Đó là vì bà có Chúa Giê-xu. Chúa Cứu Thế là Chúa nhưng cũng là người bạn không rời. Hôm nay, bạn có thể có Ngài, khi đó dù chỉ có một mình, bạn cũng sẽ không bao giờ cô đơn.')]),n._v(" "),i("p",[n._v("Nếu hôm nay bạn là người đang nằm trên giường bệnh, trong nhà thương, đang chịu nỗi cô đơn vì đau đớn, bạn có thể chắc chắn rằng Chúa Cứu Thế Giê-xu có thể ban cho bạn ân sủng và sức mạnh của Ngài. Trong khi nằm đó, bạn vẫn có thể trở thành hữu dụng cho Chúa nếu biết chức vụ cầu thay là chức vụ lớn nhât trên trần gian khi bạn khởi sự cầu nguyện cho người khác.")]),n._v(" "),i("h3",{attrs:{id:"noi-co-don-vi-buon-tham"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#noi-co-don-vi-buon-tham","aria-hidden":"true"}},[n._v("#")]),n._v(" Nỗi cô đơn vì buồn thảm")]),n._v(" "),i("p",[n._v("Nỗi cô đơn thứ tư là cô đơn vì buồn thảm. Phúc Âm Giăng chương 11 ghi lại câu chuyện về Ma-ri và Ma-thê với người anh là La-xa-rơ vừa qua đời mà Chúa Giê-xu chưa đến. Họ đứng bên xác anh, than khóc.")]),n._v(" "),i("p",[n._v('Bạn cũng vậy. Có thể trần gian này đã trở thành một nghĩa trang mênh mông với một ngôi mộ duy nhất. Bạn đang đứng bên giường bệnh, nhìn một người thân yêu quí giá hơn cả trần gian đang đi dần ra khỏi tầm tay. Bạn đang khát khao một mối tương giao. Bạn cần có một bàn tay mạnh mẽ bên cạnh để lau những giọt nước mắt và để gắn lại nụ cười trên môi, và để đem đến cho bạn niềm vui qua cơn sầu khổ. Đây chính là điều Chúa Giê-xu có thể làm cho bạn. Kinh Thánh dạy rằng"Hãy trao mọi lo lắng cho Ngài, vì Ngài hay săn sóc anh em" (I Phi-e-rơ 5:7). Đức Chúa Trời yêu thương con cái Ngài. Nếu bạn sẵn lòng tin cậy, trao dâng chính bạn cho Ngài, Chúa sẽ mang hết mọi nỗi sầu khổ của bạn.')]),n._v(" "),i("h3",{attrs:{id:"noi-co-don-vi-toi-loi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#noi-co-don-vi-toi-loi","aria-hidden":"true"}},[n._v("#")]),n._v(" Nỗi cô đơn vì tội lỗi")]),n._v(" "),i("p",[n._v('Nỗi cô đơn thứ năm là cô đơn vì tội lỗi. Phúc Âm Giăng 13 ghi lại câu chuyện Chúa thiết lập Lễ Tiệc Thánh, trong đó Chúa tiên báo việc một người trong vòng họ sẽ phản bội. Các sứ đồ kinh ngạc nhìn nhau. Giăng hỏi, "Lạy Chúa, người đó là ai?" Chúa trả lời, "Đó là người ta trao cho miếng bánh ta sẽ nhúng đây." Rồi Chúa lấy một miếng bánh, nhúng và trao cho Giu-đa Ích-ca-ri-ốt, con trai Si-môn. Kinh Thánh cho biết sau đó, quỉ Sa-tan nhập vào Giu-đa. Chúa Giê-xu bảo người, "Việc ngươi làm hãy làm mau đi." Sau khi nhận miếng bánh, Giu-đa đi ra, lúc đó trời đã tối.')]),n._v(" "),i("p",[n._v("Có lẽ bạn đã từng biết niềm vui và sự bình an của người được tái sinh vào trong gia đình Đức Chúa Trời, bạn kinh nghiệm được mối thông công ngọt ngào trong cộng đồng dân Chúa. Bạn cũng đã kinh nghiệm niềm hạnh phúc trọn vẹn và thỏa nguyện trong sự hiện diện diện của Chúa, nhưng bạn cũng đã phạm tội. Bạn ra khỏi sự hiện diện của Chúa Cứu Thế, rơi vào đêm tối mênh mông. Bạn mất tương giao với các Cơ Đốc nhân mà cũng không tương giao được với tội các nhân, và lại càng không thể tương giao với Chúa Cứu Thế. Có lẽ không có nỗi cô đơn nào kinh khủng hơn là nỗi cô đơn của người tín đồ sa ngã.")]),n._v(" "),i("p",[n._v('Tuy nhiên sự tha thứ từ Chúa vẫn dành cho bạn. Khi bạn ăn năn xưng nhận và từ bỏ tội lỗi, mối tương giao với Chúa sẽ được phục hồi. Lời Chúa dạy rằng, "Còn nếu chúng ta xưng tội mình thì Ngài thành tín, công bình tha tội cho chúng ta và làm cho chúng ta sạch mọi điều gian ác" (I Giăng 1:9).')]),n._v(" "),i("p",[n._v('Có lẽ bạn bảo rằng phạm tội là những giây phút thích thú, có thể như vậy, vì Kinh Thánh bảo rằng có "những điều vui sướng của tội lỗi". Tuy nhiên đó là những điều ngắn ngủi đem lại hậu quả chết người. Có lẽ bạn đã từng đọc báo cáo của tiến sĩ Kinsey hay những công trình khảo cứu khác và sung sướng thấy rằng có vô số những tội nhân khác giống như bạn, có khi còn tệ hơn bạn. Bạn không cô đơn. Không, bạn đang ở trong thành phần tuyệt đại đa số! Như vậy bạn tự hỏi, nhiều người phạm tội như thế thì cô đơn sao được? Có thể bây giờ bạn đang ở giữa đám đông, nhưng sẽ có một ngày khi mỗi người trong chúng ta phải đứng một mình trước mặt Đức Chúa Trời Toàn năng để chịu phán xét. Đó sẽ là cao điểm của tất cả các nỗi cô đơn trên trần gian, nhưng cũng chỉ là hương vị đầu tiên của nỗi cô đơn trong hỏa ngục.')]),n._v(" "),i("p",[n._v('Đối với tất cả những người đang trẩy bước trên lối mòn tội lỗi, có một màn đêm mênh mông đang ngăn cách họ với tất cả mọi mối tương giao tốt lành, chân thật. Tội lỗi muôn thuở là tăm tối và sẽ mãi mãi là sự tối tăm. Giu-đa cô đơn vì tội lỗi. Trong sách Ô-sê 4:17 Đức Chúa Trời phán rằng, "Ép-ra-im sa mê thần tượng, hãy để mặc nó!" Vì sự tham lam và thờ hình tượng của người Ép-ra-im, Đức Chúa Trời đã phán, "Đừng tương giao gì với nó, hãy để nó một mình!" Tiên tri Ê-sai bảo rằng, "Chúng ta thảy đều như chiên đi lạc, ai theo đường nấy." Đây cũng là một hình ảnh khác của nỗi cô đơn vì tội lỗi.')]),n._v(" "),i("p",[n._v('Một giờ trước cuộc đấu súng với Alexander Hamilton, Aaron Burr ngồi trong thư viện riêng ở Richmond Hill tại Nữu Ước, viết cho con gái những lời như sau, "Một người rất khôn ngoan đã nói rằng ‘Hỡi những kẻ khờ dại, là người nghĩ rằng cô đơn là ở một mình". Ngay cả trước khi phát súng giết người bắn ra và hành vi đẫm máu thực hiện, Aaron Burr đã cảm nhận được nỗi cô đơn của tội lỗi ông ta. Chỉ trong vài giờ sau đó ông ta trở thành một kẻ bôn đào vì lòng thù hận bất ngờ, sâu đậm đối với một công dân khác. Sự nghiệp chính trị của ông ta tiêu tan vĩnh viễn, và tham vọng lớn lao của ông ta hoàn toàn xụp đổ.')]),n._v(" "),i("p",[n._v("Có hàng nghìn những con người cô đơn trong thành phố và trong đất nước này mang những gánh nặng đầy đau thương, lo lắng, ưu sầu, thất vọng, nhưng linh hồn cô đơn nhất là linh hồn của những người có cuộc đời chất chồng tội lỗi. Tôi muốn nói với bạn rằng mỗi tội lỗi bạn cố ý phạm đều trở thành một sức mạnh ghê gớm cầm giữ bạn trong nỗi cô đơn. Càng lớn tuổi, bạn càng cô đơn hơn cho nên tôi xin bạn hãy đến dưới chân thập tự giá, nhận mình là một tội nhân và từ bỏ tội lỗi.")]),n._v(" "),i("p",[n._v("Chúa Cứu Thế Giê-xu có thể ban cho bạn năng quyền đắc thắng tội lỗi cũng như những thói quen phạm tội hàng ngày. Ngài có thể bứt đứt xiềng xích cột trói của tội lỗi, nhưng bạn cần phải ăn năn, xưng tội, cam kết phó thác, giao nạp chính bạn cho Ngài trước. Ngay lúc đó bạn sẽ thấy cuộc đời mình được ổn cố, và chính bạn sẽ kinh nghiệm niềm vui, sự bình an, và mối tương giao với Chúa Cứu Thế.")]),n._v(" "),i("h3",{attrs:{id:"noi-co-don-cua-chua-cuu-the"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#noi-co-don-cua-chua-cuu-the","aria-hidden":"true"}},[n._v("#")]),n._v(" Nỗi cô đơn của Chúa Cứu Thế")]),n._v(" "),i("p",[n._v("Cuối cùng là nỗi cô đơn của Chúa Cứu Thế. Hàng nghìn người chen lấn vây bọc Ngài. Không khí vui mừng, nô nức của mùa lễ Vượt Qua lan khắp nơi, nhưng riêng Chúa Giê-xu là một người "),i("strong",[n._v('"bị khinh dể và chán bỏ, từng trải sự buồn bực, biết sự đau ốm, bị khinh như kẻ mà người ta che mặt chẳng thèm xem; chúng ta cũng chẳng coi người ra gì. Thật người đã mang sự đau ốm của chúng ta, đã gánh sự buồn bực của chúng ta; mà chúng ta lại tưởng rằng người đã bị Đức Chúa Trời đánh và đập và làm cho khốn khổ. Nhưng người đã vì tội lỗi chúng ta mà bị vết, vì sự gian ác chúng ta mà bị thương. Bởi sự sửa phạt người chịu chúng ta được bình an, bởi lằn roi người chúng ta được lành bịnh. Chúng ta thảy đều như chiên đi lạc, ai theo đuờng nấy: Chúa Hằng Hữu đã làm cho tội lỗi của hết thảy chúng ta đều chất trên người" (Ê-sai 53:3-6)')]),n._v(".")]),n._v(" "),i("p",[n._v("Chúa Giê-xu cô đơn. Ngài đã đến với dân Ngài, nhưng họ không tiếp nhận. Phúc Âm Ma-thi-ơ viết như sau, "),i("strong",[n._v('"Nhưng mọi điều ấy phải xảy đến, hầu cho những lời các Đấng tiên tri đã chép được ứng nghiệm. Khi ấy, hết thảy môn đồ bỏ Ngài trốn đi."')]),n._v(' Đám đông vừa mới reo "Hô-sa-na" thì nay đang gào lên '),i("strong",[n._v('"Đóng đinh nó lên cây thập tự! Đóng đinh nó lên cây thập tự!"')]),n._v(" Ngay cả mười hai môn đệ trung thành cũng bỏ trốn.")]),n._v(" "),i("p",[n._v("Cuối cùng, chúng ta nghe Ngài kêu thống thiết, "),i("strong",[n._v('"Đức Chúa Trời tôi ôi! Đức Chúa Trời tôi ôi! Sao Ngài lìa bỏ tôi?" (Mác 15:34)')]),n._v(". Chúa không chỉ bị con người bỏ rơi, mà nay trong giờ cô đơn, cùng quẫn nhất, Chúa Giê-xu vì mang tất cả tội lỗi chúng ta trong thân Ngài trên thập tự, còn bị chính Đức Chúa Trời từ bỏ. Chúa Giê-xu đã chịu tất cả những đau thương và phán xét của địa ngục vì bạn và tôi.")]),n._v(" "),i("p",[n._v("Yếu tính của địa ngục là ngăn cách với Đức Chúa Trời. Địa ngục là nơi cô đơn nhất trên toàn vũ trụ. Chúa Giê-xu đã chịu tất cả những nỗi thống khổ của địa ngục vì bạn, thế chỗ cho bạn. Vì vậy Đức Chúa Trời bảo bạn ăn năn, đặt đức tin nơí Chúa Cứu Thế, tiếp nhận Ngài, bạn sẽ không không bao giờ nếm trải nỗi thống khổ, cô đơn, đau buồn của địa ngục, vì Kinh Thánh khẳng định rằng "),i("strong",[n._v('"ai kêu cầu danh Chúa sẽ được cứu rỗi" (Rô-ma 10:13)')]),n._v(".")]),n._v(" "),i("h2",{attrs:{id:"chuong-7-sau-cai-chet-co-gi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-7-sau-cai-chet-co-gi","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 7: Sau cái chết có gì?")]),n._v(" "),i("p",[i("strong",[n._v('"Chỉ còn một bước cách tôi và sự chết mà thôi" I Sa-mu-ên 20:3')])]),n._v(" "),i("p",[n._v("Người ta đã bảo rằng cả cuộc đời chỉ là để chuẩn bị cho cái chết. Tác giả Thi thiên viết, "),i("strong",[n._v('"Ai là người sống mà sẽ chẳng thấy sự chết ?" (Thi-thiên 89:48)')]),n._v(".")]),n._v(" "),i("p",[n._v("Đây là thời đại tự do tư tưởng cho những cuộc thử nghiệm có tính cách mạng. Chúng ta đã tìm cách thay đổi thế giới và những qui luật cai trị thế giới bằng kiến thức, khoa học, phát minh, khám phá, triết học và những tư duy về vật thể. Chúng ta đã nỗ lực đem tiền bạc, danh vọng, trí thông minh tôn lên làm thần thánh, nhưng dù chúng ta có cố gắng đến đâu, kết cuộc vẫn y như cũ: "),i("strong",[n._v('"Theo như đã định cho loài người phải chết một lần rồi chịu phán xét" (Hê-bơ-rơ 9: 27)')]),n._v(".")]),n._v(" "),i("p",[n._v("Ở chính giữa cuộc đời, chúng ta thấy sự chết khắp nơi. Tiếng còi hụ của xe cứu thương, những bảng hiệu nhà quàn, các nghĩa trang chúng ta thường đi qua và hình ảnh các đoàn xe tang trên đường phố, tất cả nhắc cho chúng ta nhớ rằng tử thần có thể gọi chúng ta bất cứ lúc nào. Không ai trong chúng ta biết đích xác ngày giờ, nhưng tất cả đều ý thức rằng nó có thể đến bất cứ lúc nào.")]),n._v(" "),i("p",[n._v('Có người bảo rằng, "Điều chắc chắn duy nhất về đời sống là cái chết." Oscar Wilde từng nói, "Ngày nay người ta có thể tránh được mọi sự trừ cái chết!" Gần đây sách vở về sự chết và hấp hối phát hành đầy dẫy, trong đó có cả những sách của những người kể rằng họ đã kinh nghiệm cái chết nhưng được hồi sinh. Thay vì tìm cách giải hoà với Đức Chúa Trời, con người hôm nay lại đi học về tình trạng hấp hối và phương cách đối diện với cái chết, chấp nhận đó như là một phần của cuộc sống. Thực ra toàn thể nhân loại đều ở trong danh sách các tử tội. Chết khi nào và chết như thế nào không phải là vấn đề chính. Vấn đề là chúng ta đi về đâu sau khi chết.')]),n._v(" "),i("p",[n._v("Mỗi năm hơn nửa triệu người Mỹ vào ngồi trong xe hơi, không biết rằng đó là chuyến đi cuối cùng trong đời. Năm 1980 có 532,000 người Mỹ chết vì tai nạn xe hơi. Dù ngày càng có nhiều phương tiện và biện pháp bảo vệ an toàn, hàng năm vẫn có đến 469,000 người chết trong các tai nạn ở nhà là lúc họ không hề nghĩ đến cái chết. Vì sự chết không ngừng rình rập loài người và dù y học và các kỹ sư nghiên cứu về các biện pháp an toàn liên tục tuyên chiến với sự chết, nhưng đến cuối cùng, sự chết vẫn luôn luôn là kẻ chiến thắng.")]),n._v(" "),i("p",[n._v("Do cuộc chiến trường kỳ của khoa học với sự chết mà ngày nay chúng ta được lợi thêm một vài năm, nhưng cái chết vẫn đứng chờ ở cuối đường và đời người vẫn không hơn bao nhiêu so với con số bảy, tám mươi năm qui định trong Kinh Thánh.")]),n._v(" "),i("p",[n._v("Những bệnh về tim mạch cũng cướp đi quá nhiều công dân của đất nước chúng ta giữa những đỉnh cao của cuộc đời. Bệnh ung thư đem đau đớn cho thân xác hàng trăm nghìn người khác. Những bệnh nhiễm trùng máu cũng để lại hậu quả nghiêm trọng dù các công trình nghiên cứu y khoa đã giúp giảm bớt số lượng những loại bệnh đó rất nhiều. Những bệnh ngoài da và bệnh liệt kháng (aids) là bệnh của thập niên 80 đang lan tràn khắp thế giới và hiện đã có mặt trên khắp các lục địa. Tuy nhiên cho dù có những thống kê rất lạc quan về tình trạng bệnh tật, dù tuổi thọ con người có tăng lên từ đầu thế kỷ 20, và dù có những số liệu nào đi nữa về các vụ giết người, tự tử và những trường hợp chết thê thảm khác thì điều không thể tránh được là sự chết vẫn không thay đổi - nó vẫn là kinh nghiệm tối hậu của chúng ta trên trần gian!")]),n._v(" "),i("h3",{attrs:{id:"mot-tran-chien-suot-doi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-tran-chien-suot-doi","aria-hidden":"true"}},[n._v("#")]),n._v(" Một trận chiến suốt đời")]),n._v(" "),i("p",[n._v("Từ giây phút đứa trẻ ra đời, tiến trình của sự chết lẫn cuộc chiến đấu chống tử thần khởi sự. Người mẹ bỏ ra bao nhiêu năm tháng chăm sóc, bảo vệ sự sống của con. Bà lo cho con từ thức ăn, quần áo, nơi ở, chỗ chơi, lo cho con đi khám bệnh định kỳ, đi chích ngừa... Nhưng dù bà có chăm sóc kỹ lưỡng đến đâu, con bà vẫn đã bước vào tiến trình của sự chết.")]),n._v(" "),i("p",[n._v("Thời gian trôi qua và rồi không lâu những dấu hiệu rất thật về bản chất yếu đuối của con người trở nên rõ ràng: Đi nha sĩ chữa răng sâu, mắt phải đeo kính mới thấy đường. Da bắt đầu nhăn, vai so và bước đi chậm chạp dần. Xương cốt không còn cứng chắc nữa và sức lực suy giảm. Hiển nhiên, dù có thể không nhận ra, nhưng chúng ta đã tiến gần hơn với cái chết.")]),n._v(" "),i("p",[n._v('Bảo hiểm sức khỏe và bệnh viện sẽ được dùng để giúp chúng ta chống đỡ sức tấn công của sự chết. Người ta mua bảo hiểm nhân thọ để chuẩn bị cho các chi phí hậu sự và rồi bỗng nhiên chúng ta thấy cả cuộc đời chỉ là một cuộc chiến đấu trường kỳ chống lại sự chết. Chúng ta sẽ thấy mọi người tham gia vào một cuộc đua để chỉ dám có một hy vọng lớn nhất là "qua mặt" sự chết, kéo dài cuộc đời thêm một chút thời gian nữa, nhưng rồi đến cuối cùng sự chết vẫn luôn luôn thắng!')]),n._v(" "),i("p",[n._v("Điều bí mật trong kẻ thù này của chúng ta là gì? - Dường như nó cũng bí mật như chính sự sống. Vì chúng ta thấy sự sống đầy dẫy khắp xung quanh, trong cỏ cây, trong thú vật, trong cả con người. Chúng ta không thể tạo ra mà cũng không thể giải thích. Sự chết cũng vậy, không thể giải thích, dù chúng ta ý thức sự hiện diện của nó y như của sự sống, dù chúng ta ít nói hay ít suy nghĩ về tính chất nghiêm trọng của nó đến đâu đi nữa! Khi sự sống đến - như khi một em bé ra đời, chúng ta vui mừng. Nhưng khi sự sống ra đi, khi một người chết, chúng ta cố gắng càng quên nỗi buồn nhanh bao nhiêu, tốt bấy nhiêu.")]),n._v(" "),i("p",[n._v("Ngày nay có khoảng hơn ba tỉ người sống trong hành tinh của chúng ta. Hầu hết số người này sẽ chết trong vòng một trăm năm nữa. Thân xác họ sẽ không còn một cảm xúc nào, nhưng còn linh hồn là phần thiết yếu, vĩnh hằng của sự sống thì sao? Đây chính là điều bí mật. Cái gì bị mất khi con người chết? Cái bị mất đó sẽ đi đâu?")]),n._v(" "),i("h3",{attrs:{id:"tai-sao-con-nguoi-khuoc-tu-duc-chua-troi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#tai-sao-con-nguoi-khuoc-tu-duc-chua-troi","aria-hidden":"true"}},[n._v("#")]),n._v(" Tại sao con người khước từ Đức Chúa Trời?")]),n._v(" "),i("p",[n._v('Vài năm trước đây có một nhà báo đã chết ở Denver, tiểu bang Colorado. Những người dự đám tang được nghe cuốn băng ghi âm lời chính người chết nói, "Đây là đám tang của tôi. Từ lâu tôi là một người vô thần và tôi khinh bỉ thần học. Tu sĩ là những kẻ hèn nhát về phương diện luân lý. Phép lạ là sản phẩm của trí tưởng tượng. Bất cứ bốn phóng viên nào được cử đến một vụ hành quyết mà lại thâu thập dữ kiện lệch lạc méo mó như các sứ đồ trong bản ký thuật của Kinh Thánh, chắc chắn sẽ bị mất việc tức khắc. Tôi không muốn có một bài hát tôn giáo nào trong tang lễ. Đây sẽ là một đám tang hoàn toàn hợp lý."')]),n._v(" "),i("p",[n._v('Tương phản hẳn với những lời trên là một lời thật đẹp của Alfred Lord Tennyson mô tả cái chết trong bài thơ In Memorian của ông, "Ngón tay Chúa chạm vào, rồi anh ta ngủ."')]),n._v(" "),i("p",[n._v("Thời đại nào cũng sản sinh ra những con người vì lòng thù ghét Đức Chúa Trời nên đã dồn đổ mọi chế diễu, ức hiếp lên hội thánh, Kinh Thánh và Chúa Cứu Thế Giê-xu. Họ chẳng cần đưa ra bằng chứng gì cả mà cứ gào lên chống lại tiếng nói của Đức Chúa Trời. Lịch sử đã làm chứng về những Bernard Shaws, những Robert Ingersolls, những B.F. Skinners và nhiều triết gia khác, là những người đã dùng mọi lập luận đả phá nỗi sợ hãi sự chết.")]),n._v(" "),i("p",[n._v('Chúng ta hãy nghe nhà nhân chủng học nói về cái chết trong rừng núi, là nơi không có những "thắc mắc thần học", cũng không ai nghe nói gì về Chúa Cứu Thế Giê-xu. Sự chết ở những nơi này như thế nào? Trong một số các bộ lạc, những người già bị đem ra bỏ trong các bụi rậm mặc cho thú dữ cắn xé để những người trẻ trong bản khỏi phải chứng kiến cái chết. Có bộ lạc lại có thói tục lột hết áo quần của những người than khóc rồi đem sơn trắng. Giờ này qua giờ khác những tiếng khóc than của các phụ nữ cho cả thế giới biết rằng một linh hồn sắp lìa khỏi xác. Sự chết ngoài vòng ảnh hưởng của Cơ Đốc giáo đầy nỗi kinh hoàng và thất vọng, mà khá nhất cũng là thái độ buông xuôi hay lãnh đạm. Thí dụ như giữa vòng những người Hồi giáo, người ta chờ mong, trông đợi sự chết vì họ tin rằng những niềm vui thú lớn đang chờ những người trung tín - nếu họ chết đang khi giết những kẻ vô đạo hoặc đang chiến đấu cho đức tin.')]),n._v(" "),i("p",[n._v("Bây giờ đối chiếu những điều trên với cái chết của Cơ Đốc nhân. Khi Chúa Cứu Thế đến, Ngài đã cho chúng ta cái nhìn mới đối với sự chết. Từ xưa, con người vẫn coi sự chết là kẻ thù, nhưng Chúa Giê-xu bảo rằng Ngài đã đắc thắng sự chết và đã lấy đi nọc độc của sự chết. Chúa Giê-xu đáng bậc thầy của những người theo chủ nghĩa hiện thực khi Ngài thôi thúc con người chuẩn bị cho cái chết là điều chắc chắn sẽ đến với mọi người. Chúa bảo họ đừng lo lắng đến sự chết thân xác, nhưng phải quan tâm đến sự chết vĩnh cửu của linh hồn.")]),n._v(" "),i("p",[n._v('Tôi nghĩ về Helen Morken khi nằm hấp hối, hàng ngày những người thân yêu như chồng và các con đều tụ tập quanh giường hát thánh ca hàng giờ. Nói theo nghĩa đen thì bà đã được đưa vào trong sự hiện diện của Chúa bằng tiếng hát. Tôi nghĩ đến các thánh của Đức Chúa Trời được Alexander Smellie mô tả trong tác phẩm Men Of The Covenant (Những Con Người Của Giao Ước), trong đó ông viết về những vĩ nhân đức tin là những người trong những cuộc "chém giết" tại Tô-cách-lan, coi những cuộc hành quyết và cái chết nhẹ như lông hồng. Lúc đó chưa có ghế điện, chưa có đội hành quyết, chưa có cách chích thuốc độc để khiến cho cái chết càng ít đau đớn càng tốt mà chỉ toàn là tra tấn, khảo đả dã man cho đến chết. Vì vậy, mỗi người Smellie mô tả đều có cái chết thật kinh hoàng. Tuy nhiên, mỗi người đó, khi thực sự bước vào cái chết đều chết trong niềm vui ngây ngất!')]),n._v(" "),i("p",[n._v("Kinh Thánh cho biết thực sự có hai cái chết, cái chết thân xác và cái chết vĩnh hằng. Chúa Giê-xu cảnh cáo rằng chúng ta cần sợ cái chết thứ hai hơn là cái chết thứ nhất. Ngài mô tả cái chết thứ hai là chết trong hỏa ngục, đời đời bị phân cách với Đức Chúa Trời. Chúa cũng dạy rằng cái chết thân xác không có gì đáng kể so với tình trạng linh hồn bị vĩnh viễn lưu đầy xa cách Đức Chúa Trời.")]),n._v(" "),i("h3",{attrs:{id:"cai-chet-cua-mot-thanh-nhan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#cai-chet-cua-mot-thanh-nhan","aria-hidden":"true"}},[n._v("#")]),n._v(" Cái chết của một thánh nhân")]),n._v(" "),i("p",[n._v("Những câu nói cuối cùng của những người hấp hối cung cấp phương tiện tối ưu cho những người đi truy tầm thực tại về cái chết.")]),n._v(" "),i("ul",[i("li",[n._v("Matthew Henry - "),i("em",[n._v('"Tội lỗi cay đắng. Tôi chúc tụng Chúa đã ban cho tôi những sự hỗ trợ hướng nội."')])]),n._v(" "),i("li",[n._v("Martin Luther - "),i("em",[n._v('"Đức Chúa Trời chúng ta là Đấng ban sự cứu rỗi: Đức Chúa Trời là Chúa, nhờ Ngài chúng ta thoát khỏi sự chết"')])]),n._v(" "),i("li",[n._v("John Knox - "),i("em",[n._v('"Sống trong Chúa Cứu Thế, sống trong Chúa Cứu Thế thì thân xác không còn sợ chết nữa."')])]),n._v(" "),i("li",[n._v("John Wesley - "),i("em",[n._v('"Điều tốt nhất là Đức Chúa Trời ở cùng chúng ta. Xin tạm biệt! Xin tạm biệt!"')])]),n._v(" "),i("li",[n._v("Richard Baxter - "),i("em",[n._v('"Tôi đau đớn nhưng có sự bình an. Tôi có sự bình an."')])]),n._v(" "),i("li",[n._v("William Carey, nhà truyền giáo - "),i("em",[n._v('"Khi tôi qua đời, xin hãy nói thật ít về Tiến sĩ Carey, nhưng nói thật nhiều về Chúa của Tiến sĩ Carey."')])]),n._v(" "),i("li",[n._v("Adoniram Judson - "),i("em",[n._v('"Tôi không mệt mỏi vì công việc, cũng không chán trần gian; tuy nhiên khi Chúa Cứu Thế gọi tôi về, tôi sẽ đi với niềm vui của cậu bé tan học về nhà.')])])]),n._v(" "),i("p",[n._v("Như vậy, câu chuyện của một Cơ Đốc nhân đã ăn năn tội, và vận dụng đức tin tiếp nhận Chúa Cứu Thế làm Đấng Cứu Chuộc khác với những câu chuyện khác biết bao nhiêu!")]),n._v(" "),i("p",[n._v("Trong nhiều năm, Tiến sĩ Effie Jane Wheeler dạy Anh văn và văn chương tại trường đại học nơi tôi theo học. Tiến sĩ Wheeler nổi tiếng về lòng nhân ái và kiến văn rộng rãi về những môn bà dạy. Trong ngày kỷ niệm chiến sĩ trận vong vào tháng 5, 1949, tiến sĩ Wheeler viết bức thư sau đây cho Tiến sĩ Edman, lúc đó là khoa trưởng đại học, là bạn đồng lao nhưng cũng là học trò cũ của bà như sau:\n"),i("em",[n._v('"Tôi tri ân ông cho phép đọc những dòng này trong nhà nguyện, vì trước khi ông đi nghỉ hè, tôi muốn ông được biết sự thật về tôi, điều chính tôi cũng chỉ mới biết hôm thứ sáu. Cuối cùng, bác sĩ đã cho biết sự thật kết quả chẩn đoán căn bệnh trong mấy tuần qua - ung thư di căn. Nếu vị bác sĩ này là tín đồ chắc ông đã không ngại ngần tìm cách trì hoãn báo tin như vậy, vì ông ta hẳn đã biết, như ông và tôi biết, rằng sống hay chết đều được chúng ta tiếp đón như nhau khi chúng ta sống trong sự hiện diện của Chúa. Nếu Chúa muốn tôi về với Ngài sớm, tôi rất vui lòng. Xin đừng vì tôi mà phải đau buồn một giây phút nào. Tôi không nói lời vĩnh biệt lạnh lẽo, nhưng là một câu chào ấm áp hẹn tái ngộ trong đất hạnh phúc, nơi tôi có thể được phép vén màn cho ông bước vào. Xin gửi đến từng người trái tim đầy thương mến. (Ký tên) Effie Jane Wheeler."')])]),n._v(" "),i("p",[n._v("Chỉ hai tuần sau khi viết bức thư này Tiến sĩ Wheeler đã buớc vào sự hiện diện của Chúa Cứu Thế, là Đấng đã giữ lời hứa, lấy đi nọc độc của sự chết.")]),n._v(" "),i("p",[n._v("Trong khi soạn chương này, chúng tôi nhận được bốn bức thư. Một bức từ một thánh đồ 94 tuổi tỏ ý mong mau được về với Chúa. Một bức từ một phụ nữ trong danh sách chờ xử tử hình, bà tin Chúa sáu năm trước và bây giờ hướng về ngày xử tử để được bước vào vinh quang trước mặt; hai bức thư còn lại là của hai bà goá sau rất nhiều năm sống trong hạnh phúc gia đình (một bà chồng qua đời có ít ngày trước hôm kỷ niệm 49 năm thành hôn). Cả hai bà đều nhìn thấy ngày vinh quang bên kia cõi chết.")]),n._v(" "),i("p",[n._v("Nhà truyền đạo trứ danh Dwight L. Moody nói trong giờ lâm chung: "),i("em",[n._v('"Đây là chiến thắng của tôi; đây là ngày đăng quang! Ngày vinh hiển!"')])]),n._v(" "),i("p",[n._v("Kinh Thánh dạy rằng bạn là linh hồn bất tử. Linh hồn bạn vĩnh cửu và bạn sẽ sống đời đời. Nói cách khác, con người thật của bạn - cái phần bên trong bạn suy nghĩ, cảm xúc, mơ ước, trông đợi; cái con người thật, vô hình của bạn - không bao giờ chết. Kinh Thánh dạy rằng linh hồn sẽ sống mãi mãi ở một trong hai nơi - thiên đàng hay hỏa ngục. Nếu bạn không phải là một Cơ Đốc nhân và bạn chưa bao giờ được tái sinh, thì Kinh Thánh dạy rằng linh hồn bạn sẽ đi thẳng đến một nơi Chúa Giê-xu gọi là địa ngục, là nơi bạn sẽ phải chờ sự phán xét của Đức Chúa Trời.")]),n._v(" "),i("h3",{attrs:{id:"mot-de-tai-khong-hap-dan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-de-tai-khong-hap-dan","aria-hidden":"true"}},[n._v("#")]),n._v(" Một đề tài không hấp dẫn")]),n._v(" "),i("p",[n._v('Tôi biết nói về địa ngục không phải là chuyện thích thú, vì nó rất không hấp dẫn, dễ gây tranh luận và hiểu lầm. Tuy nhiên trong các chiến dịch truyền giảng Tin Lành của tôi trong cả nước, tôi thường dùng một buổi để thảo luận về đề tài này. Sau phần thảo luận của tôi, nhiều người viết thư cho các nhật báo nhiều ngày sau đó tiếp tục tranh luận, bàn bạc về nhiều khía cạnh của vấn đề, vì Kinh Thánh cũng nói khá nhiều về đề tài này cũng như bất cứ đề tài nào khác. Trong các buổi thảo luận với sinh viên nhiều đại học khắp nước Mỹ, tôi thường hay hỏi câu này và những câu tương tự, "Còn địa ngục thì sao? Có lửa trong địa ngục không?" Là mục sư tôi phải đối diện với vấn đề này, tôi không thể bỏ qua, dù có thể nó làm cho nhiều người khó chịu bực dọc. Tôi cho rằng vấn đề địa ngục là giáo lý khó chấp nhận nhất của Cơ Đốc Giáo.')]),n._v(" "),i("p",[n._v('Có những người dạy rằng mọi người rồi ra sẽ được cứu cả, vì Đức Chúa Trời là Đức Chúa Trời của tình thương nên Ngài sẽ không bỏ ai vào địa ngục. Họ tin rằng từ ngữ vĩnh cửu hay vĩnh viễn không thực sự có nghĩa là đời đời. Tuy nhiên, chính từ ngữ nói về tình trạng vĩnh viễn bị đuổi ra khỏi mặt Đức Chúa Trời cũng được dùng để chỉ sự sống vĩnh hằng trên thiên đàng. Có người đã nói rằng "lẽ công bằng đòi hỏi chúng ta phải làm cho niềm vui của người công chính và sự trừng phạt kẻ ác có hiệu quả, vì cả hai cùng là một từ Hi-lạp và chỉ thị cùng chiều dài thời gian."')]),n._v(" "),i("p",[n._v("Có những người khác lại dạy rằng sau khi chết, những người từ chối chương trình cứu rỗi của Đức Chúa Trời sẽ bị tịch diệt, nghĩa là không còn hiện hữu nữa. Khi tra tìm trong toàn thể Kinh Thánh, tôi không thấy một mảy may bằng chứng nào hỗ trợ cho quan điểm này. Kinh Thánh dạy rằng dù chúng ta được cứu hay hư vong, linh hồn và cá tính chúng ta vẫn tiếp tục ở trong sự hiện hữu vĩnh hằng và đầy đủ ý thức.")]),n._v(" "),i("p",[n._v('Có người lại dạy rằng sau khi chết sẽ vẫn còn có thể được cứu rỗi và Đức Chúa Trời sẽ cho một cơ hội thứ hai. Cho rằng điều này đúng đi nữa thì xin nhớ rằng Kinh Thánh không hề có một chỗ nào ám chỉ điều đó mà không ngừng cảnh cáo rằng "hiện nay là thì thuận tiện, hiện nay là ngày cứu rỗi" (2 Cô-rinh-tô 6:2).')]),n._v(" "),i("h3",{attrs:{id:"dieu-kinh-thanh-day"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#dieu-kinh-thanh-day","aria-hidden":"true"}},[n._v("#")]),n._v(" Điều Kinh Thánh dạy")]),n._v(" "),i("p",[n._v("Người ta có thể trích dẫn hàng chục phân đoạn trong Kinh Thánh hỗ trợ cho sự kiện Kinh Thánh dạy rằng địa ngục dành cho những người cố ý phủ nhận Chúa Cứu Thế Giê-xu là Chúa và Đấng Cứu Chuộc:")]),n._v(" "),i("p",[i("strong",[n._v('"Tôi bị khổ trong lửa này quá đỗi" (Lu-ca 16:24)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v('"Ai mắng anh em mình là đồ điên thì đáng bị lửa địa ngục hình phạt" (Ma-thi-ơ 5:22)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v('"Con Người sẽ sai các thiên sứ Ngài thâu mọi gương xấu và những kẻ làm ác khỏi nước Ngài, và quăng những người đó vào lò lửa, là nơi có khóc lóc và nghiến răng" (Ma-thi-ơ 13:41,42)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v('"Đến ngày tận thế cũng như vầy: các thiên sứ sẽ đến và chia kẻ ác với người công bình ra, ném những kẻ ác vào lò lửa; ở đó sẽ có khóc lóc và nghiến răng" (Ma-thi-ơ 13:49,50)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v('"Kế đó Ngài sẽ phán cùng những người ở bên tả rằng: Hỡi kẻ bị rủa, hãy lui ra khỏi ta; đi vào lửa đời đời đã sắm sẵn cho ma quỉ và những quỉ sứ nó" (Ma-thi-ơ 25:41)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v('"Ngài sẽ đốt rơm rạ trong lửa chẳng hề tắt" (Ma-thi-ơ 3: 12)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v('"...Đức Chúa Giê-xu từ trời hiện đến với các thiên sứ của quyền phép Ngài, giữa ngọn lửa hừng, báo thù những kẻ chẳng hề nhận biết Đức Chúa Trời và không vâng phục Tin Lành của Chúa Cứu Thế Giê-xu chúng ta. Họ sẽ bị hình phạt hư mất đời đời, xa cách mặt Chúa và sự vinh hiển của quyền phép Ngài..." (II Tê-sa-lô-ni-ca 1:8,9)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v('"Nếu ai thờ phượng con thú cùng tượng nó, và chịu dấu nó ghi trên trán hay trên tay, thì người ấy cũng vậy, sẽ uống rượu thịnh nộ không pha của Đức Chúa Trời rót trong chén thịnh nộ Ngài, và sẽ chịu đau đớn trong lửa và diêm ở trước mặt các thiên sứ thánh và trước mặt Chiên Con. Khói của sự đau đớn chúng nó bay lên đời đời. Những kẻ thờ lạy con thú và tượng nó, cùng những kẻ chịu dấu của tên nó ghi, thì cả ngày lẫn đêm không lúc nào được yên nghỉ" (Khải Huyền 14: 10-11)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v('"Đoạn sự chết và âm phủ bị quăng xuống hồ lửa. Hồ lửa là sự chết thứ hai. Kẻ nào không được biên vào sách sự sống đều bị ném xuống hồ lửa" (Khải Huyền 20: 14,15)')]),n._v(".")]),n._v(" "),i("p",[i("strong",[n._v('"Còn những kẻ hèn nhát, kẻ chẳng tin, kẻ đáng gớm ghét, kẻ giết người, kẻ dâm loạn, kẻ phù phép, kẻ thờ thần tượng, và phàm kẻ nào nói dối, phần của chúng nó ở trong hồ có lửa và diêm cháy bừng bừng: đó là sự chết thứ hai" (Khải Huyền 21:8)')])]),n._v(" "),i("p",[n._v("Nhưng tôi lại nghe có người nói, "),i("em",[n._v('"Tôi không tin có hỏa ngục. Tôn giáo của tôi là Bài Giảng Trên Núi."')])]),n._v(" "),i("p",[n._v("Chúng ta hãy nghe một phân đoạn trong Bài giảng trên núi:")]),n._v(" "),i("p",[i("strong",[n._v('"Vậy nếu con mắt bên hữu xui cho ngươi phạm tội, thì hãy móc mà quăng nó cho xa ngươi đi; vì thà chịu một phần thân thể ngươi phải hư, còn hơn là cả thân thể bị ném vào địa ngục. Lại nếu tay hữu xui cho ngươi phạm tội, thì hãy chặt mà liệng nó cho xa ngươi đi, vì thà một phần thân thể ngươi phải hư còn hơn là cả thân thể vào địa ngục" (Ma-thi-ơ 5:29,30)')]),n._v(".")]),n._v(" "),i("p",[n._v("Tại đây chúng ta thấy Chúa Giê-xu dạy thật rõ rằng có địa ngục. Thật ra Chúa kể chuyện và đưa ra những hình ảnh minh hoạ về đề tài này và nhiều lần cảnh cáo con người về cái ngu dại của cuộc sống giả hình và tội lỗi trên đất.")]),n._v(" "),i("h3",{attrs:{id:"dia-nguc-tren-tran-gian"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#dia-nguc-tren-tran-gian","aria-hidden":"true"}},[n._v("#")]),n._v(" Địa ngục trên trần gian")]),n._v(" "),i("p",[n._v('Hiển nhiên nhiều kẻ ác phải sống đau khổ như trong địa ngục trên trần gian. Kinh Thánh dạy, "phải biết rằng tội chắc sẽ đổ lại trên ngươi" (hay tội của ngươi sẽ tìm ra ngươi) (Dân số ký 32:23). Trong thư Ga-la-ti 6:7 cũng chép, "ai gieo giống chi lại gặt giống ấy." Tuy nhiên cũng có những bằng chứng quanh ta cho thấy một số kẻ ác dường như vẫn thịnh vượng trong khi những người công chính chịu khổ vì sự công chính của họ. Kinh Thánh dạy rằng sẽ có lúc mọi sự phải được phân xử công bình khi công lý được thực hiện. Có người lại bảo rằng "những kẻ gian ác kia chính là biện pháp trừng phạt tội lỗi chúng ta." Cả hai ý tưởng trên đều đúng.')]),n._v(" "),i("p",[n._v("Liệu một Đức Chúa Trời yêu thương có bỏ con người vào địa ngục không? Câu trả lời là có, vì Ngài công chính, nhưng Ngài không vui lòng khi phải làm như vậy. Con người tự lên án cho mình khi từ khước con đường cứu rỗi của Đức Chúa Trời. Với tình yêu và lòng thương xót, Đức Chúa Trời đã ban cho con người một lối thoát, một con đường cứu rỗi, một hy vọng về những điều tốt lành hơn. Nhưng con người trong tình trạng mù loà, ngu dại, cứng cổ, vị kỷ, yêu thích những thú vui tội lỗi, từ khước phương cách đơn sơ của Đức Chúa Trời giúp thoát khỏi nỗi đau đớn của tình trạng bị lưu đầy vĩnh viễn, xa cách Ngài.")]),n._v(" "),i("p",[n._v("Giả sử tôi bị đau, mời bác sĩ đến khám bệnh, cho thuốc, nhưng sau đó suy nghĩ lại, tôi dẹp bỏ thuốc men sang một bên, không uống. Mấy hôm sau bác sĩ trở lại, thấy tình trạng sức khoẻ của tôi tồi tệ hơn. Lúc đó tôi có thể đổ thừa bác sĩ, bắt ông ta phải chịu trách nhiệm không? Ông đã cho tôi toa thuốc, ông đã định phương thức chữa trị, nhưng tôi đã khước từ!")]),n._v(" "),i("p",[n._v("Cũng vậy, Đức Chúa Trời đã định sẵn phương thuốc cho căn bịnh của nhân loại. Phương thuốc đó là tin và ký thác cuộc đời cho Chúa Cứu Thế Giê-xu. Phương thuốc đó là tái sinh hay sinh lại mà chúng ta sẽ đề cập đến trong một chương khác. Nếu cố ý từ khước chúng ta sẽ phải lãnh hậu quả và không thể đổ thừa Đức Chúa Trời. Làm sao có thể bảo đó là lỗi của Đức Chúa Trời khi chúng ta từ chối phương thuốc của Ngài?")]),n._v(" "),i("p",[n._v('Người không chịu tin có đời sống đàng sau cõi chết, không tin có thiên đàng hay hoả ngục, không tin Lời Chúa nói về những thực tại này, khi bước sang thế giới bên kia mới bừng tỉnh biết mình sai lầm thì anh ta đã mất tất cả. Trong tạp chí "Người Dân" (People), Lem Banker là một trong những tay cờ bạc hàng đầu được trích dẫn nói rằng, "Đừng bao giờ đánh cá nhắm vào số tiền anh muốn thắng mà chỉ đánh cá dựa vào số tiền cao nhất anh có thể chịu nổi khi thua." Bạn có thể chịu nổi khi mất linh hồn vĩnh cửu không?')]),n._v(" "),i("p",[n._v("Có người lại hỏi, "),i("em",[n._v('"Bản chất địa ngục là gì?"')]),n._v(' Có bốn từ ngữ trong Kinh Thánh được dùng để dịch "địa ngục".')]),n._v(" "),i("ul",[i("li",[n._v("Chữ thứ nhất là "),i("strong",[n._v("Sheol")]),n._v(' dùng trong Cựu Ước được dịch đến 31 lần là "địa ngục" (trong bản tiếng Anh là "hell"). Từ này có nghĩa là "tình trạng chưa thấy". Những từ ngữ như lo buồn, đau đớn và huỷ hoại được dùng liên hệ với từ địa ngục này.')]),n._v(" "),i("li",[n._v("Chữ thứ hai là "),i("strong",[n._v("Hades")]),n._v(', dịch từ nguyên ngữ Hy-lạp có nghĩa là âm phủ được nhắc đến mười lần trong Tân Ước, và cùng có nghĩa như từ "Sheol" trong Cựu Ước, có liên quan đến đau đớn và phán xét.')]),n._v(" "),i("li",[n._v("Chữ thứ ba là "),i("strong",[n._v("Tartarus")]),n._v(" chỉ được dùng một lần trong "),i("strong",[n._v("2 Phi-e-rơ 2:4")]),n._v(', được dịch là "vực sâu", là nơi giam giữ các thiên sứ phạm tội, không vâng phục. Từ ngữ này chỉ thị một nơi phán xét như nhà giam, hầm tối, nơi chỉ có bóng tối dầy đặc.')]),n._v(" "),i("li",[n._v("Chữ thứ tư là "),i("strong",[n._v("Ghehena")]),n._v(" được dùng 11 lần và được dịch là địa ngục trong Tân Ước. Đây là hình ảnh minh họa được Chúa dùng chỉ Thung Lũng Hinnom, một địa điểm đốt rác thường xuyên ở ngoại thành Giê-ru-sa-lem.")])]),n._v(" "),i("p",[n._v("Những người khác lại hỏi, "),i("em",[n._v('"Kinh Thánh có dạy rằng trong địa ngục thực sự có lửa không?"')]),n._v(" Nếu không phải là lửa theo nghĩa đen thì chắc phải là thứ gì khác kinh khủng hơn lửa, vì Chúa Giê-xu không nói quá. Tất nhiên trong Kinh Thánh nhiều lần lửa được dùng theo nghĩa biểu tượng, tuy nhiên Đức Chúa Trời cũng có thứ lửa cháy nhưng không thiêu rụi.")]),n._v(" "),i("p",[n._v("Khi Môi-se thấy bụi gai cháy, ông ngạc nhiên vì bụi gai không tàn. Ba thanh niên Hê-bơ-rơ bị quăng vào lò lửa, bị đốt nhưng không cháy, cả đến tóc trên đầu họ cũng không một sợi nào bị xém.")]),n._v(" "),i("p",[n._v('Mặt khác, Kinh Thánh nói về những cái lưỡi "bị lửa địa ngục đốt cháy" (Gia-cơ 3:6), mỗi khi chúng ta nói xấu người khác, nhưng tất nhiên không có nghĩa là mỗi lần như vậy là có lửa cháy theo nghĩa đen. Dù lửa được nói theo nghĩa đen hay nghĩa bóng nó không hề thay đổi tính hiện thực của địa ngục. Nếu không có lửa theo nghĩa đen, chắc chắn Chúa cũng đã dùng từ ngữ này để chỉ một điều kinh khủng hơn cả lửa.')]),n._v(" "),i("h3",{attrs:{id:"phan-cach-voi-duc-chua-troi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#phan-cach-voi-duc-chua-troi","aria-hidden":"true"}},[n._v("#")]),n._v(" Phân cách với Đức Chúa Trời")]),n._v(" "),i("p",[n._v("Yếu tính của địa ngục là sự phân cách với Đức Chúa Trời. Đây là sự chết thứ hai, được mô tả là tình trạng bị lưu đầy có ý thức, vĩnh viễn xa cách với tất cả những gì liên quan đến sự sáng, niềm vui, sự thiện, sự công chính và hạnh phúc. Nhiều chỗ trong Kinh Thánh mô tả tình trạng kinh khủng này, nơi linh hồn con người sẽ rơi vào chỉ một phút sau khi chết. Điều kỳ lạ là con người thường lo xa, chuẩn bị cho mọi chuyện trừ cái chết. Chúng ta chuẩn bị cho sự học hành, cho công việc làm ăn, cho nghề nghiệp. Chúng ta chuẩn bị cho việc hôn nhân, cho tuổi già. Chúng ta chuẩn bị cho mọi sự trừ ra việc chuẩn bị cho giây phút chúng ta phải đi vào cõi chết, trong khi Kinh Thánh long trọng cảnh cáo rằng đã định cho chúng ta phải chết một lần rồi chịu phán xét.")]),n._v(" "),i("p",[n._v("Sự chết dường như là chuyện không tự nhiên khi xảy ra cho mình nhưng lại hoàn toàn tự nhiên khi xảy ra cho người khác. Sự chết kéo mọi người xuống cùng một hạng. Nó tước đoạt của người giàu bạc triệu, nhưng lại cất hết những cái rách rưới của người nghèo. Nó làm nguội lòng tham và dập tắt dục vọng. Mọi người đều muốn làm ngơ với cái chết nhưng rồi tất cả đều phải đối diện với nó, dù là vua chúa hay nông dân, hạng thất học hay hàng triết gia, kẻ sát nhân cũng như thánh nhân. Sự chết không biết đến giới hạn tuổi tác cũng không biết thiên vị là gì nhưng nó là cái mọi người đều sợ.")]),n._v(" "),i("p",[n._v('Vào cuối đời Daniel Webster có lần thuật lại chuyện ông tham dự một buổi lễ nhà thờ tại một làng quê nhỏ. Mục sư là một cụ già đơn sơ, tin kính. Sau những nghi lễ mở đầu giờ thờ phượng, ông đứng lên giảng lời Chúa, rồi với một giọng vô cùng tha thiết nhưng đơn sơ ông nói, "Thưa các bạn, tất cả chúng ta đều chỉ có thể chết một lần."')]),n._v(" "),i("p",[n._v('Ít lâu sau, nhận định về bài giảng này, Daniel Webster nói, "Những lời trên dù có vẻ yếu ớt, lạnh lùng, nhưng đối với tôi, nó tức khắc đem lại một thức tỉnh sâu xa chưa từng có."')]),n._v(" "),i("h3",{attrs:{id:"hen-gap-tu-than"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hen-gap-tu-than","aria-hidden":"true"}},[n._v("#")]),n._v(" Hẹn gặp tử thần")]),n._v(" "),i("p",[n._v("Nghĩ đến việc người khác phải giữ ngày hẹn với thần chết thì dễ, nhưng rất khó nhớ chính mình cũng không tránh được cái hẹn này. Khi tiễn những người lính ra trận, đọc báo viết về một tử tội chờ ngày hành quyết hay đi thăm một người bạn hấp hối, chúng ta đều ý thức về tính cách nghiêm trọng đang vây quanh những con người đó. Cái chết được định cho mọi người và việc nó xảy ra khi nào chỉ là vấn đề thời gian. Đối với tất cả những cái hẹn khác trong cuộc đời như hẹn gặp các thú vui chẳng hạn, chúng ta có thể bỏ qua, thất hứa và chấp nhận hậu quả, nhưng đối với cái chết, không ai có thể tránh né hay lỗi hẹn! Đối với tử thần, mỗi người chỉ có một lần hẹn- một lần bắt buộc!")]),n._v(" "),i("p",[n._v("Nếu hậu quả của cuộc đời xa cách Đức Chúa Trời chỉ là cái chết thân xác, chúng ta không sợ lắm, nhưng Kinh Thánh cảnh cáo rằng còn có cái chết thứ hai, là cuộc lưu đầy đời đời xa cách Đức Chúa Trời. Dù Kinh Thánh có nói đến hoả ngục cho tội nhân, nhưng cũng còn khía cạnh tươi sáng hơn khi Kinh Thánh công bố thiên đàng cho thánh nhân. Thánh nhân được định nghĩa là một tội nhân đã được tha thứ. Đề tài thiên đàng so với hỏa ngục tất nhiên dễ được chấp nhận hơn rất nhiều, và Kinh Thánh dạy về cả hai đề tài đó.")]),n._v(" "),i("p",[n._v("Khi dọn đến một căn nhà mới, bạn muốn biết hết về khu phố mình sẽ sống. Nếu dời sang một thành phố khác, bạn cũng muốn biết hết về thành phố đó - về hệ thống xe điện, các công viên, sông, hồ, trường học... Vì chúng ta rồi sẽ đi đến một nơi nào đó trong cõi vĩnh hằng, chúng ta cũng cần biết đôi điều về nơi ấy. Kiến thức về thiên đàng được trình bày trong Kinh Thánh cho nên suy nghĩ, bàn luận về thiên đàng là việc cần làm. Khi nói về thiên đàng, đem so sánh, chúng ta sẽ thấy trần gian bỗng trở nên xập xệ, tầm thường. Những buồn đau và nan đề của chúng ta tại đây dường như vơi bớt rất nhiều khi chúng ta có lòng thực sự trông đợi tương lai. Chính vì thế, trong một nghĩa đặc biệt nào đó, Cơ Đốc nhân đã có thể có thiên đàng trên đất - sự bình an trong linh hồn, bình an trong lương tâm và bình an với Đức Chúa Trời. Giữa bao nhiêu khó khăn, bối rối, Cơ Đốc nhân có sự bình an và niềm vui sâu xa hoàn toàn không tùy thuộc vào hoàn cảnh.")]),n._v(" "),i("h3",{attrs:{id:"thien-dang-co-that"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#thien-dang-co-that","aria-hidden":"true"}},[n._v("#")]),n._v(" Thiên đàng có thật")]),n._v(" "),i("p",[n._v('Kinh Thánh hứa cho Cơ Đốc nhân thiên đàng trong đời sau. Vào năm ông John Quincy Adams 94 tuổi, một buổi sáng kia có người hỏi ông thấy thế nào, ông trả lời, "Tốt, tốt lắm, nhưng căn nhà tôi đang ở thì không được tốt mấy." Cho dù "căn nhà" chúng ta ở có thể đang đau ốm, bệnh hoạn, chúng ta vẫn có thể thấy tinh thần phấn chấn, mạnh mẽ nếu chúng ta là Cơ Đốc nhân. Chúa Giê-xu dạy rằng thật có thiên đàng dành sẵn cho chúng ta.')]),n._v(" "),i("p",[n._v('Chúng ta có thể trưng dẫn nhiều chỗ trong Kinh Thánh nói về thiên đàng, nhưng câu mô tả linh động nhất vẫn là Giăng 14:2, "Trong nhà Cha ta có nhiều chỗ ở, bằng chẳng vậy ta đã nói cho các ngươi rồi, ta đi sắm sẵn cho các ngươi một chỗ, khi ta đã đi và đã sắm sẵn cho các ngươi một chỗ rồi, ta sẽ trở lại đem các ngươi đi với ta, hầu cho ta ở đâu thì các ngươi cũng ở đó." Phao lô rất chắc chắn về thiên đàng đến nỗi ông đã viết, "Vậy tôi nói, chúng ta đầy lòng tin cậy, muốn lìa bỏ thân thể này để ở cùng Chúa thì hơn (II Cor. 5:8).')]),n._v(" "),i("p",[n._v('Viễn ảnh về đời sau của Cơ Đốc nhân và của một nhà bất khả tri như Bob Ingersoll thật là khác biệt. Bên mộ anh ông ta nói rằng, "Sự sống là cái màn thật mỏng giữa hai đỉnh núi lạnh lẽo hoang vu của cõi vĩnh hằng. Chúng ta nỗ lực uổng công khi cố nhìn ra xa hơn hai đỉnh núi này. Cất tiếng khóc, chúng ta chỉ nghe lời đáp duy nhất là những tiếng vang vọng thê thảm tiếng khóc của chính mình"')]),n._v(" "),i("p",[n._v('Nhiều lần sứ đồ Phao-lô bảo, "Chúng ta biết", "Chúng ta tin chắc," "Chúng ta luôn chắc rằng." Kinh Thánh bảo rằng Áp-ra-ham "tìm kiếm một thành có nền vững chắc Đức Chúa Trời đã xây cất và sáng lập."')]),n._v(" "),i("p",[n._v('Nhiều người hỏi, "Ông có tin rằng thiên đàng thực sự là một nơi theo nghĩa đen không? Vâng, đó là điều chúng ta tin, vì Chúa Giê-xu bảo rằng, "Ta đi chuẩn bị cho các con một chỗ." Kinh Thánh dạy rằng Ê-nóc và Ê-li với thân xác thật đã được cất lên một nơi có thật y như những nơi có thật trên trần gian như Hawaii, Thụy sĩ, Virgin Islands.')]),n._v(" "),i("p",[n._v('Nhiều người từng hỏi, "Thiên đàng ở đâu?" Kinh Thánh không cho biết mà đó cũng không phải là vấn đề quan trọng miễn là có thiên đàng và Chúa Cứu Thế Giê-xu ở đó để đón chúng ta về.')]),n._v(" "),i("h3",{attrs:{id:"mot-noi-tuyet-my"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-noi-tuyet-my","aria-hidden":"true"}},[n._v("#")]),n._v(" Một nơi tuyệt mỹ")]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng thiên đàng sẽ là nơi tuyệt đẹp, được mô tả trong Kinh Thánh là "nhà của Đức Chúa Trời", là "thành phố" - một "quê hương tốt hơn" - "một cơ nghiệp" - "một nơi đầy vinh quang."')]),n._v(" "),i("p",[n._v('Có thể bạn sẽ hỏi, "Liệu trên thiên đàng chúng ta có còn nhận ra nhau không?" Một số chỗ trong Kinh Thánh cho biết sẽ có một cuộc hội ngộ vĩ đại của tất cả những người đi trước.')]),n._v(" "),i("p",[n._v('Người khác hỏi, "Trẻ con có được cứu rỗi không?" Có, vì Kinh Thánh cho biết Đức Chúa Trời không gán trách nhiệm đối với tội lỗi cho trẻ con trước khi chúng đến tuổi hiểu biết. Có nhiều chỗ trong Kinh Thánh hàm ý sự chuộc tội phủ bao mọi tội lỗi cho đến khi chúng đến tuổi phải chịu trách nhiệm cá nhân về những hành vi đúng cũng như sai.')]),n._v(" "),i("p",[n._v("Kinh Thánh cũng cho biết thiên đàng sẽ là nơi chúng ta có những hiểu biết sâu rộng với kiến thức về những sự vật chúng ta chưa bao giờ học được trên trần gian.")]),n._v(" "),i("p",[n._v('Sir Isaac Newton khi về già, nói với một người khen ngợi sự khôn ngoan của ông rằng, "Tôi chỉ như là một đứa trẻ đi trên bờ biển lượm lặt đây đó viên cuội, vỏ sò, còn cả đại dương chân lý vẫn trải rộng mênh mông trước mặt."')]),n._v(" "),i("p",[n._v("Rồi Thomas Edition cũng từng nói, "),i("em",[n._v('"Tôi không biết được đến một phần triệu của một phần trăm về bất cứ điều gì."')])]),n._v(" "),i("p",[n._v('Nhiều điều bí ẩn về Đức Chúa Trời, những nỗi đau lòng, những thử thách, những nỗi thất vọng, những thảm kịch và sự im lặng của Đức Chúa Trời ở giữa bao nhiêu thống khổ sẽ được phơi bày. Eli Wiesel bảo rằng cõi vĩnh hằng là "...nơi những vấn nạn và câu trả lời là một." Trong Giăng 16:23 Chúa Giê-xu phán, "Ngày đó các ngươi sẽ không còn hỏi ta điều gì nữa." Tất cả những câu hỏi của chúng ta sẽ được trả lời!')]),n._v(" "),i("p",[n._v('Nhiều người hỏi "Như vậy chúng ta sẽ làm gì trên thiên đàng? Không lẽ chỉ ngồi và vui hưởng mọi lạc thú cao sang của sự sống?" Không. Kinh Thánh cho biết chúng ta sẽ phục vụ Đức Chúa Trời. Chúng ta sẽ có vô số việc làm cho Đức Chúa Trời. Chính con người chúng ta sẽ ca ngợi Chúa. Kinh Thánh dạy rằng, "Chẳng còn có sự nguyền rủa nữa; ngôi của Đức Chúa Trời và Chiên Con sẽ ỏ trong thành; các tôi tớ Ngài sẽ hầu hạ Ngài" (Khải Huyền 22:3).')]),n._v(" "),i("p",[n._v("Đó sẽ là thời gian chúng ta được trọn vẹn trong niềm vui, trong sự phục vụ, được ca hát vui cười và ca ngợi Đức Chúa Trời. Chúng ta thử tưởng tượng nghĩ đến tình trạng đời đời được phục vụ Chúa không hề mỏi mệt!")]),n._v(" "),i("h3",{attrs:{id:"vao-trong-su-hien-dien-cua-chua-cuu-the"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#vao-trong-su-hien-dien-cua-chua-cuu-the","aria-hidden":"true"}},[n._v("#")]),n._v(" Vào trong sự hiện diện của Chúa Cứu Thế")]),n._v(" "),i("p",[n._v("Kinh Thánh dạy rằng chúng ta cần ra khỏi thân thể để có thể ở với Chúa. Chính giây phút qua đời Cơ Đốc nhân đi thẳng ngay vào trong sự hiện diện của Chúa Cứu Thế rồi ở đó chờ ngày phục sinh là lúc linh hồn và thân thể sẽ lại được kết hợp trở lại.")]),n._v(" "),i("p",[n._v('Nhiều người từng hỏi, "Làm thế nào những thân thể đã tan rữa hay đã hỏa thiêu có thể làm cho sống lại?" Chỉ một mình Đức Chúa Trời biết điều đó, tuy nhiên thân thể mới chúng ta sẽ có là một thân thể vinh hiển giống như thân thể phục sinh của Chúa Cứu Thế Giê-xu và đó là thân thể vĩnh hằng. Với thân thể vinh hiển đó, chúng ta sẽ không còn than khóc, đau buồn, bệnh tật, đau khổ, mệt nhọc hay chết chóc nữa. Chúng ta sẽ có thân thể mới, nhưng vẫn còn nhận ra người đó là ai.')]),n._v(" "),i("p",[n._v("Đó là bức tranh về hai thế giới vĩnh hằng nổi lên trong không gian. Mỗi hậu tự của A-đam sẽ ở một trong hai thế giới đó. Xung quanh cả hai bao trùm một màn bí mật mênh mông, nhưng qua Kinh Thánh chúng ta có thể tìm thấy một ít tia sáng chỉ thị rằng một nơi sẽ đầy bi thương đau khổ còn nơi kia đầy ánh sáng và vinh quang.")]),n._v(" "),i("p",[n._v("Như vậy chúng ta đã thấy nan đề của nhân loại. Bề ngoài chúng có vẻ phức tạp, nhưng trong căn bản chúng đơn giản hơn nhiều và có thể tóm gọn vào trong một chữ - tội lỗi. Chúng ta cũng đã thấy tương lai con người hoàn toàn vô vọng nếu không có Đức Chúa Trời. Tuy nhiên nếu chỉ dùng lý trí để phân tích vấn đề và tìm hiểu chương trình của Đức Chúa Trời thì không đủ. Nếu muốn được Đức Chúa Trời cứu giúp, con người phải hội đủ một số điều kiện của Ngài. Trong những chương sau chúng ta sẽ tìm hiểu những điều kiện đó.")]),n._v(" "),i("h2",{attrs:{id:"chuong-8-tai-sao-chua-gie-xu-den"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-8-tai-sao-chua-gie-xu-den","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 8: Tại sao Chúa Giê-xu đến?")]),n._v(" "),i("h3",{attrs:{id:"di-tim-giai-phap"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#di-tim-giai-phap","aria-hidden":"true"}},[n._v("#")]),n._v(" Đi Tìm Giải Pháp")]),n._v(" "),i("p",[i("strong",[n._v('"Con Người đã đến tìm và cứu kẻ bị mất." Luca 19:10')])]),n._v(" "),i("p",[n._v("Chúng ta đã thấy sự kiện kinh khủng nhất, có tác hại tàn phá nhất trong vũ trụ là tội lỗi. Nguyên nhân của mọi rắc rối, gốc rễ của mọi đau thương, nỗi kinh hoàng của mọi người nằm trong một chữ nhỏ - tội lỗi. Tội lỗi làm bản chất con người què quặt. Tội lỗi hủy phá tính chất hài hòa bên trong con người, cướp mất tính cao thượng và khiến con người rơi vào bẫy rập của ma quỉ.")]),n._v(" "),i("p",[n._v("Tất cả những xáo trộn tâm trí, tất cả bệnh tật, tất cả mọi hình thức suy đồi, hủy phá, chiến tranh, đều có gốc rễ khởi nguyên từ tội lỗi. Tội lỗi gây điên loạn cho trí não và tạo ra nọc độc trong lòng. Trong Kinh Thánh tội lỗi được mô tả là một thứ bệnh chết người cần phải có một phương thuốc chữa trị triệt để. Nó là cơn lốc cuồng loạn, là hỏa diệm sơn điên cuồng. Nó là một người điên vượt ngục, là một tên du đãng đang tung hoành. Nó là sư tử rống săn mồi, là cú sét đang đánh thẳng xuống địa cầu. Tội lỗi là bãi cát lầy đang nhanh chóng rút con người xuống, là bệnh ung thư đang lan vào tận trong linh hồn. Nó là một cơn lũ đang dâng lên cuốn sạch mọi thứ phía trước. Nó là một hầm cầu hư hoại làm ô nhiễm tất cả mọi lãnh vực của đời sống.")]),n._v(" "),i("p",[n._v("Nhưng như có người từng nói, "),i("em",[n._v('"Tội lỗi có thể ngăn bạn đến với Kinh Thánh- hay Kinh Thánh có thể giữ bạn không phạm tội."')])]),n._v(" "),i("p",[n._v("Từ bao đời, con người đã hư vong trong bóng tối tâm linh, bị bệnh tật tội lỗi làm đui mù khiến cho phải lần dò, mò mẫm - lục lạo, hỏi, tìm lối thoát. Nhân loại cần có người dẫn ra khỏi tình trạng rối loạn về tinh thần cũng như luân lý, cần có người mở được cửa và cứu ra khỏi ngục tù của ma quỉ. Nhân loại với trái tim đói khát, với tâm trí khô cằn, với tâm linh rạn vỡ đang đứng tuyệt vọng mở to mắt, lắng tai trông chờ. Trong khi đó ma quỉ đang hả hê đứng nhìn chiến thắng lẫy lừng của nó tại vườn Ê-đen.")]),n._v(" "),i("p",[n._v('Từ những con người sơ khai trong rừng rậm đến những người của các nền văn minh hùng mạnh Ai-cập, Hy-lạp và La-mã, tất cả đang bối rối hỏi cùng những câu giống nhau, "Làm sao thoát ra? Làm sao khá hơn? Tôi có thể làm gì? Tôi sẽ đi về hướng nào? Làm sao dứt được căn bệnh kinh khủng này? Làm sao ngăn được dòng nước lũ ghê gớm kia? Làm sao ra khỏi tất cả những thảm bại kinh khủng này trong cuộc đời? Nếu có lối thoát, liệu tôi có tìm được không?"')]),n._v(" "),i("h3",{attrs:{id:"giai-dap-cua-thanh-kinh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#giai-dap-cua-thanh-kinh","aria-hidden":"true"}},[n._v("#")]),n._v(" Giải Đáp Của Thánh Kinh")]),n._v(" "),i("p",[n._v("Chúng ta đã thấy Kinh Thánh dạy về Đức Chúa Trời là Đức Chúa Trời của tình thương. Ngài muốn cứu loài người, muốn giải phóng con người khỏi sự rủa sả của tội lỗi, nhưng Ngài đã hành động như thế nào? Đức Chúa Trời là Đức Chúa Trời công chính. Ngài vừa công chính vừa thánh khiết và ngay từ đầu Ngài đã cảnh cáo con người rằng nếu nghe theo ma quỉ mà trái lời Ngài, con người sẽ chết cả phần xác lẫn phần tâm linh; vậy mà con người đã cố ý không vâng lời Chúa. Con người phải chết còn nếu không thì Đức Chúa Trời nói dối, vì Ngài không thể nuốt lời. Chính bản chất Đức Chúa Trời không cho phép Ngài nói dối và lời Ngài phải được thực hiện. Vì vậy khi cố ý bất tuân, cố ý chọn con đường của ma quỉ, con người đã bị đuổi khỏi sự hiện diện của Đức Chúa Trời.")]),n._v(" "),i("p",[n._v('Phải có một đường lối, một phương cách nào khác vì con người đang sa lầy thật vô vọng, đang hư vong không lối thoát. Bản chất con người bị đảo ngược khiến con người chống lại Đức Chúa Trời. Tật bệnh của tội lỗi khiến con người đui mù đến độ phủ nhận cả sự hiện hữu của Đức Chúa Trời. Nhưng ngay từ trong vuờn Ê-đen, Đức Chúa Trời đã hé mở cho thấy Ngài sẽ can thiệp. Ngài hứa với con người và cảnh cáo ma quỉ rằng, "Ta sẽ làm cho mày cùng người nữ, dòng dõi mày cùng dòng dõi người nữ nghịch thù nhau. Người sẽ giày đạp đầu mày, còn mày sẽ cắn gót chân người" (Sáng Thế 3:15). Tại đây chúng ta thấy tia sáng lóe ra từ thiên đàng. Đầu bị giày đạp nói đến vết thương trí mạng, còn vết thương ở gót chân chỉ là thương tích nhỏ, tạm thời. Đây chính là lời hứa cho con người nắm lấy. Đức Chúa Trời hứa rằng một ngày kia Đấng Cứu Chuộc sẽ đến, một Đấng Giải Phóng sẽ xuất hiện. Đức Chúa Trời đã ban cho con người hy vọng và suốt qua bao nhiêu thế kỷ, con người đã nắm chặt lấy chút hy vọng đó!')]),n._v(" "),i("p",[n._v("Nhưng không phải chỉ có thế, vì suốt qua hàng nghìn năm lịch sử, Đức Chúa Trời đã chiếu những tia sáng khác từ thiên đàng. Suốt qua Cựu Ước, Đức Chúa Trời ban cho con người lời hứa cứu rỗi nếu đặt đức tin nơi Đấng Cứu Chuộc vị lai. Vì vậy Ngài khởi sự dạy dân Ngài rằng con người chỉ có thể được cứu do sự thay thế hay thế chỗ. Phải có người chịu án phạt thay thế thì con người mới được cứu chuộc.")]),n._v(" "),i("h3",{attrs:{id:"tro-lai-vuon-e-den"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#tro-lai-vuon-e-den","aria-hidden":"true"}},[n._v("#")]),n._v(" Trở Lại Vườn Ê-Đen")]),n._v(" "),i("p",[n._v('Bây giờ mời bạn dùng trí tưởng tượng cùng tôi trở lại vườn Ê-đen. Đức Chúa Trời căn dặn con người, "Ngày nào ngươi ăn trái cấm chắc sẽ chết." Con người đã ăn, và đã chết.')]),n._v(" "),i("p",[n._v('Giả sử sau đó Đức Chúa Trời bảo rằng, "Này A-đam, con đã sai phạm rồi! Nhưng thôi, ta tha cho, đừng tái phạm đấy!" Nếu vậy, Đức Chúa Trời là Đấng nói dối, Ngài không phải là Đấng thánh khiết cũng không công chính. Chính bản chất Đức Chúa Trời khiến Ngài nói phải giữ lời, nếu không công lý của Ngài sẽ bị đe dọa. Con người phải chết cả thể xác lẫn tâm linh. Tội lỗi cách ly con người với Đức Chúa Trời khiến cho con người đau khổ. Con người phải trả giá cho tội ác mình. Như chúng ta đã thấy, vì A-đam là đầu nhân loại cho nên khi A-đam phạm tội, tất cả chúng ta đều phạm tội. "Cho nên như bởi một người mà tội lỗi vào trong thế gian, lại bởi tội lỗi mà có sự chết, thì sự chết đã trải qua hết thảy mọi người như vậy, vì mọi người đếu phạm tội" (Rô-ma 5:12).')]),n._v(" "),i("p",[n._v('Bây giờ vấn đề nóng bỏng của chúng ta là, "Làm thế nào Đức Chúa Trời có thể vẫn công chính khi Ngài công chính hóa tội nhân?" Chúng ta cần nhớ rằng từ ngữ công chính hóa có nghĩa là "giải tỏa linh hồn khỏi tình trạng mắc tội". Sự công chính hóa có tính cách rộng lớn hơn sự tha tội, nghĩa là tội lỗi phải được giải trừ và coi như chưa hề có. Con người cần phải được phục hồi để không còn một chút tì vết nào của tội lỗi. Nói cách khác, con người phải được đem trở lại địa vị trước khi sa ngã, suy đồi.')]),n._v(" "),i("p",[n._v('Suốt bao nhiêu thế kỷ, trong tình trạng mù lòa, con người đã cố gắng lần mò trở lại vườn Ê-đen, nhưng họ không bao giờ có thể tìm đến nơi. Họ đã thử đi nhiều lối nhưng tất cả đều thất bại. C. S. Lewis bảo rằng "Tất cả các tôn giáo hoặc chỉ là bản khái lược của Cơ Đốc Giáo hoặc là một hình thức Cơ Đốc Giáo suy đồi".')]),n._v(" "),i("p",[n._v('Giáo dục quan trọng, nhưng giáo dục không đem con người trở lại với Đức Chúa Trời. Những tôn giáo giả hiệu là một loại nha phiến cố gắng giữ con người khỏi cảnh khốn cùng hiện tại trong khi hứa hẹn một tương lai huy hoàng, mà không bao giờ đưa con người đến đích. Trong một thế giới kình địch, sự hiện diện của tổ chức Liên Hiệp Quốc là cần thiết và chúng ta biết ơn tổ chức này trong việc dàn xếp các tranh chấp trong lãnh vực bang giao quốc tế tránh việc sử dụng chiến tranh; nhưng nếu Liên Hiệp Quốc có thể đem lại hòa bình trường cửu thì con người có thể nói với Đức Chúa Trời, "Chúng tôi không cần Ngài nữa. Chúng tôi đã tạo lập được hòa bình trên đất và đã tổ chức được nhân loại sống đời công chính." Tất cả những kế hoạch này là những phương thuốc vá víu mà thế giới bệnh hoạn, hấp hối của chúng ta phải dùng trong khi chờ đợi vị Đại Lương Y. Trở lại lịch sử, chúng ta biết nỗ lực đầu tiên kết hợp con người đã kết thúc trong tình trạng rối loạn ngôn ngữ tại tháp Ba-bên. Lần nào con người cố hành động không có Đức Chúa Trời thì cũng đều thất bại và sẽ còn thất bại nữa.')]),n._v(" "),i("p",[n._v('Vấn đề còn lại là "Làm thế nào Đức Chúa Trời công chính- nghĩa là công chính trong bản chất và trong sự thánh khiết, mà vẫn có thể công chính hóa tội nhân?" Bởi vì mỗi người đều mang tội của chính mình, và toàn thể nhân loại không ai đủ tư cách giúp người khác, vì mỗi người đều đã bị nhiễm cùng một thứ bệnh.')]),n._v(" "),i("p",[n._v('Giải pháp duy nhất là phải có một nhân vật vô tội tình nguyện chịu chết cả thể xác lẫn tâm linh thay thế cho con người trước Đức Chúa Trời. Nhân vật vô tội này phải đứng ra lãnh nhận sự phán xét, án phạt và sự chết của con người. Nhưng tìm đâu ra một nhân vật như thế? Chắc chắn không có một con người toàn hảo nào trên đất, vì Kinh Thánh khẳng định "Mọi người đều đã phạm tội" (Rô-ma 3:23). Chỉ còn một điều khả dĩ duy nhất đó là Con Đức Chúa Trời, Đấng duy nhất trong toàn vũ trụ có thể mang trên thân Ngài tội lỗi của toàn thể nhân loại. Chắc chắn thủ lĩnh thiên sứ Gáp-ri-ên hay Mi-ca-ên cũng có thể xuống để chết thay cho một người, nhưng chỉ có Đấng vô hạn là Con Đức Chúa Trời mới có thể chết thay cho mọi người.')]),n._v(" "),i("h3",{attrs:{id:"thuong-de-ba-ngoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#thuong-de-ba-ngoi","aria-hidden":"true"}},[n._v("#")]),n._v(" Thượng Đế Ba Ngôi")]),n._v(" "),i("p",[n._v("Kinh Thánh dạy rằng Đức Chúa Trời có ba Ngôi Vị và đây là một mầu nhiệm không bao giờ chúng ta có thể hiểu được. Kinh Thánh không dạy có ba Đức Chúa Trời mà chỉ có một và Đức Chúa Trời độc nhất này thể hiện trong ba Ngôi Vị là Đức Chúa Cha, Đức Chúa Con và Đức Thánh Linh.")]),n._v(" "),i("p",[n._v("Ngôi Hai trong Ba Ngôi là Chúa Cứu Thế Giê-xu, Con Đức Chúa Trời. Ngài đồng đẳng với Đức Chúa Trời là Cha. Ngài không phải là con Đức Chúa Trời trong nghĩa tạo vật, nhưng là Con Đức Chúa Trời trong nghĩa chia xẻ bản tính của Chúa Trời. Ngài là Con Vĩnh Hằng của Đức Chúa Trời, là Thượng Đế Ngôi Hai trong Đức Chúa Trời Ba Ngôi Chí Thánh, là Thượng Đế thể hiện trong thân xác, là Đấng Cứu Thế Hằng Sống.")]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng Chúa Cứu Thế Giê-xu không có khởi đầu. Ngài không phải là tạo vật mà là tạo hóa vì tất cả vạn vật do Ngài tạo dựng (Giăng 1:1-3). Cả từng trời với muôn vàn tinh tú, vô số mặt trời cũng đều do Ngài sáng tạo. Trái đất hiện hữu từ công việc của ngón tay Ngài. Sự giáng sinh của Chúa Cứu Thế Giê-xu chúng ta vẫn kỷ niệm vào dịp cuối năm không phải là khởi đầu của Ngài. Mỗi khi suy tưởng, tìm tòi về nguồn gốc Đức Chúa Trời chúng ta thấy bối rối như thế nào thì nguồn gốc của Chúa Giê-xu cũng che phủ trong cùng một màn bí mật đó. Kinh Thánh dạy chúng ta rằng, "Ban đàu có Ngôi Lời, Ngôi Lời ở cùng Đức Chúa Trời và Ngôi Lời Là Đức Chúa Trời" (Giăng 1:1).')]),n._v(" "),i("p",[n._v('Nói về Chúa Cứu Thế, Kinh Thánh dạy rằng, "Ay chính Ngài là hình ảnh của Đức Chúa Trời không thấy được, là Đấng sinh ra đầu hết thảy mọi vật dựng nên. Vì muôn vật đã được dựng nên trong Ngài, bất luận trên trời, dưới đất, vật thấy được, vật không thấy được, hoặc ngôi vua, hoặc quyền cai trị, hoặc chấp chánh, hoặc cầm quyền đếu là bởi Ngài và vì Ngài mà được dựng nên cả. Ngài có trước muôn vật và muôn vật đứng vững trong Ngài" (Cô-lô-se 1:15-17).')]),n._v(" "),i("p",[n._v('Câu cuối cùng của phân đoạn Kinh Thánh trên, "muôn vật đứng vững trong Ngài" chỉ thị sự kiện Ngài bảo tồn muôn vật, hay nói cách khác, toàn thể vũ trụ sẽ tan vỡ thành muôn triệu mảnh nếu không có sức mạnh bảo tồn, kết hợp của Chúa Cứu Thế Giê-xu. Trong một chỗ khác, Kinh Thánh nói rằng, "Hỡi Chúa, ban đầu trước hết Chúa đã dựng nền trái đất, và các từng trời cũng là công việc của tay Chúa. Trời đất sẽ hư đi nhưng Chúa hằng có; Trời đất sẽ cũ đi như cái áo; Ngài sẽ cuốn nó lại như cái áo choàng, rồi trời đất sẽ biến đổi, nhưng Chúa vẫn y nguyên, các năm của Chúa không hề cùng" (Hê-bơ-rơ 1:10-12).')]),n._v(" "),i("h3",{attrs:{id:"chua-cuu-the-gie-xu-dang-cuu-chuoc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chua-cuu-the-gie-xu-dang-cuu-chuoc","aria-hidden":"true"}},[n._v("#")]),n._v(" Chúa Cứu Thế Giê-xu, Đấng Cứu Chuộc")]),n._v(" "),i("p",[n._v('Chúa Giê-xu cũng từng nói về Ngài, "Ta là An-pha và Ô-mê-ga, đầu tiên và cuối cùng."Chỉ một mình Ngài khả dĩ có quyền năng đưa con người trở lại với Đức Chúa Trời. Nhưng liệu Ngài có hành động không? Nếu có, Ngài sẽ phải bước vào trần gian, phải mang hình hài của một kẻ tôi đòi, Ngài phải trở nên giống như con người, Ngài sẽ phải hạ mình xuống vâng phục cho đến chết. Ngài sẽ phải vật lộn với tội lỗi, phải đương đầu và chiến thắng Sa-tan, là kẻ tử thù của linh hồn con người. Ngài sẽ phải chuộc tội nhân ra khỏi khu chợ nô lệ của tội lỗi. Ngài sẽ phải trả một giá mới có thể phá tung xiềng xích để giải phóng tù nhân, giá đó là chính sự chết của Ngài. Ngài sẽ bị con người khinh dể và chán bỏ, Ngài trở thành một người khốn khổ tràn ngập đau buồn. Ngài sẽ bị đánh đập và ngăn cách với Đức Chúa Trời. Ngài sẽ bị thương vì tội lỗi con người, chịu bầm dập vì những gian ác của nhân loại, chịu đổ huyết ra chuộc tội cho loài người. Ngài phải giải hòa Đức Chúa Trời với con người. Ngài sẽ là Đấng Thay Thế, Đấng Trung Bảo lớn trong lịch sử. Ngài sẽ phải chết thế chỗ cho tội nhân. Tất cả những điều này Ngài đều phải làm một cách tự nguyện.')]),n._v(" "),i("p",[n._v("Đó chính là những điều đã thực sự xảy ra! Từ trên thiên đàng Ngài thấy địa cầu đang đung đưa trong không gian, chờ bị kêu án, chờ bị hủy hoại và trên đường lao xuống hỏa ngục. Ngài thấy bạn và tôi đang quằn quại dưới gánh nặng tội lỗi, bị cột trói trong giây thừng và xiềng xích của tội ác cho nên trong tòa nghị luận của Đức Chúa Trời, Ngài đã quyết định ra đi. Hàng muôn hàng ngàn thiên binh thiên sứ hạ mình cúi đầu kinh ngạc khi Vua của muôn Vua, Chúa của muôn Chúa, là Đấng phán một lời tạo thành vũ trụ, đang lên lên xe đi qua cổng ngọc trai, vượt qua những tầng cao vút của bầu trời, và vào một đêm đen tại miền Giu-đê, trong khi các tinh tú hòa ca, các thiên thần vang khúc tôn vinh, Ngài đã bước ra khỏi xe song mã, vứt bỏ áo bào và trở thành một người!")]),n._v(" "),i("p",[n._v('Sự kiện này giống nhưng trường hợp giả sử tôi đang đi trên đường lỡ đạp nhằm tổ kiến. Tôi cúi xuống nói với đàn kiến đang cuống cuồng rối loạn, "Ta vô cùng ân hận đã làm vỡ tổ các bạn. Ta đã phá tan nhà các bạn. Ước gì ta có thể nói với các bạn rằng ta không cố ý làm hại, mà muốn thật lòng giúp các bạn xây sửa lại."')]),n._v(" "),i("p",[n._v('Có người sẽ phản đối bảo rằng, "Vô lý, làm gì có chuyện đó, vì kiến làm sao hiểu được ngôn ngữ loài người!" Đó chính là điều tôi muốn nói. Tôi sẽ sung sướng đến chừng nào nếu có thể trở thành con kiến dù chỉ trong khoảnh khắc để dùng ngôn ngữ loài kiến, nói cho chúng biết điều tôi suy nghĩ!')]),n._v(" "),i("p",[n._v("Đây chính là điều Chúa Cứu Thế đã làm. Ngài đến để bày tỏ Đức Chúa Trời cho con người. Ngài là Đấng cho chúng ta biết Đức Chúa Trời yêu thương chúng ta và rất quan tâm đến cuộc đời chúng ta. Chính Ngài là Đấng nói cho chúng ta biết về lòng thương xót, lòng nhịn nhục và ân sủng của Đức Chúa Trời. Chính Ngài là Đấng hứa ban cho chúng ta sự sống vĩnh hằng.")]),n._v(" "),i("p",[n._v('Nhưng không phải chỉ có thế, Chúa Cứu Thế Giê-xu còn mang lấy thịt và huyết, nghĩa là mặc lấy thân xác con người để có thể chịu chết (Hê-bơ-rơ 2:14). "Ngài đã hiện ra để cất tội lỗi đi" (I Giăng 3:5). Mục đích chính Chúa Cứu Thế bước vào trần gian là để có thể dâng chính sự sống của Ngài làm sinh tế chuộc tội cho con người. Ngài đến để chịu chết. Bóng sự chết của Ngài giống như tấm vải phủ áo quan che suốt ba mươi năm trên đất.')]),n._v(" "),i("p",[n._v("Đêm Chúa Giê-xu giáng sinh Sa-tan run rẩy. Nó đã tìm phương giết Chúa trước khi Ngài giáng thế, và cố giết Ngài khi Chúa ra đời. Sắc chiếu của Hê-rốt ra lệnh giết tất cả con trai hai tuổi trở xuống chỉ có mục đích là để triệt hạ cho kỳ được Chúa Giê-xu.")]),n._v(" "),i("h3",{attrs:{id:"chua-con-vo-toi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chua-con-vo-toi","aria-hidden":"true"}},[n._v("#")]),n._v(" Chúa Con Vô Tội")]),n._v(" "),i("p",[n._v('Suốt những năm tháng trên trần gian, Ngài không hề phạm tội. Ngài là con người duy nhất vô tội. Ngài có thể đứng trước tất cả loài người hỏi rằng, "Trong các ngươi có ai bắt ta thú tội được chăng?" (Giăng 8:46). Ngài bị kẻ thù rình rập ngày đêm, nhưng họ không tìm được nơi Ngài bất cứ lỗi lầm nào. Ngài hoàn toàn không tì vết.')]),n._v(" "),i("p",[n._v("Chúa Giê-xu đã sống cuộc đời khiêm hạ, Ngài không màng danh vọng cũng không nhận vinh dự từ con người. Ngài đã sinh ra trong chuồng chiên, được trưởng dưỡng rồi làm nghề thợ mộc trong một làng quê nhỏ - làng Na-xa-rét. Ngài qui tụ quanh mình một nhóm ngư dân chất phác. Ngài sống bình dị giữa mọi người và là một thành phần trong đó. Chưa từng có ai đã sống khiêm hạ như Ngài.")]),n._v(" "),i("p",[n._v('Chúa Giê-xu đã truyền dạy đầy thẩm quyền đến nỗi những người đương thời phải thốt lên rằng, "Chưa hề có ai nói như người này" (Giăng 7:46). Mỗi lời Chúa nói đều hoàn toàn đúng về phương diện lịch sử. Mỗi lời Chúa nói đều đúng về phương diện khoa học. Mỗi lời Chúa nói đều đúng về phương diện đạo đức. Không hề có một khuyết điểm nào về ý niệm cũng như cách diễn đạt trong những lời dạy của Chúa Giê-xu. Quan điểm đạo đức của Ngài hoàn toàn đúng, đúng trong thời đại của Ngài cũng như trong mọi thời đại hậu lai.')]),n._v(" "),i("p",[n._v("Những lời nói của Con Người đầy ân phúc này hoàn toàn đúng về phương diện tiên tri. Ngài đã tiên báo nhiều điều, có những điều còn ở trong tương lai. Các luật gia đặt nhiều câu hỏi thử Ngài, nhưng họ không có cách nào làm Ngài rối trí. Những câu Chúa trả lời cho đối thủ của Ngài rất rõ ràng, minh bạch. Những lời khẳng định của Chúa không một chút ngờ vực, không một mảy may lắt léo, và không có gì phải do dự. Vì Ngài biết rất rõ cho nên Ngài đã từ tốn phán dạy đầy thẩm quyền. Ngài cũng phán dạy những lời thật đơn sơ đến nỗi cả những người mộc mạc hơn hết cũng hiểu và vui lòng nghe Ngài dạy. Dù Ngài dạy rất sâu sắc nhưng lời dạy lại dung dị, dễ hiểu. Lời Ngài đơn sơ trong sáng nhưng cũng thật mạnh mẽ đến nỗi làm kẻ thù choáng váng. Đối phó với những vấn đề lớn của thời đại dù đơn sơ hay phức tạp, Ngài đều có cách giải quyết tinh tế đến độ mọi người đều có thể hiểu dễ dàng.")]),n._v(" "),i("p",[n._v("Chúa Giê-xu chữa lành người đau, người què, người bại, người mù. Ngài chữa lành người mắc bệnh phung cùi và gọi người chết sống lại. Ngài đuổi quỉ, làm im tiếng gió, dẹp tan cơn bão. Ngài đem vui mừng, bình an, hy vọng cho hàng ngàn người đến với Ngài.")]),n._v(" "),i("p",[n._v('Chúa không bao giờ tỏ vẻ sợ hãi. Không lúc nào Ngài phải vội vã cũng không có biến cố nào là bất ngờ đối với Ngài. Mọi hành động của Chúa được phối hợp hoàn toàn và rất chính xác. Ngài có tư cách vô cùng khoan nhã. Ngài không phải trì hoãn cũng không lo lắng đối với những việc phải làm. Dù Ngài không chữa lành tất cả mọi người đau, cứu sống mọi người chết, mở mắt mọi người mù hay nuôi ăn mọi người đói, nhưng đến cuối đời, Ngài vẫn có thể nói với Đức Chúa Trời là Cha, "Con đã làm xong công việc Cha giao cho làm."')]),n._v(" "),i("p",[n._v('Ngài đã đứng trước mặt Phi-lát bình thản bảo rằng, "Nếu không phải từ trên ban cho ngươi, ngươi không có quyền gì trên ta" (Giăng 19:11). Ngài cũng nói với các môn đồ đang hoảng sợ rằng Ngài có thể điều khiển cả binh đội thiên sứ trên trời.')]),n._v(" "),i("p",[n._v('Ngài tiến đến thập tự giá trong vẻ uy nghi và bình thản, biết chắc rằng Ngài đang hoàn thành lời tiên tri đã viết về Ngài cách tám trăm năm trước đó: "Ngài như chiên con bị dắt đến lò sát sinh, như chiên câm trước mặt kẻ hớt lông, Ngài chẳng hề mở miệng" (Ê-sai 53:7).')]),n._v(" "),i("h3",{attrs:{id:"danh-bai-ma-qui"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#danh-bai-ma-qui","aria-hidden":"true"}},[n._v("#")]),n._v(" Đánh Bại Ma Quỉ")]),n._v(" "),i("p",[n._v("Ngài đã tiến đến sứ mạng cần phải hoàn tất với ý thức rõ ràng một cách quả cảm, đầy vinh quang. Ngài đã đến để cứu vớt tội nhân. Ngài đến để hóa giải cơn giận của Đức Chúa Trời. Ngài đến để vĩnh viễn đánh bại ma quỉ. Ngài đến để chiến thắng sự chết và hỏa ngục. Trước mặt Ngài chỉ có một con đường, một phương thức để thành đạt những mục tiêu đó.")]),n._v(" "),i("p",[n._v("Sự chết của Ngài đã được tiên báo hàng ngàn năm trước, như chúng ta đã thấy trước tiên ở vườn Ê-đen, rồi sau đó là là trong những lời tiên tri, trong những câu chuyện và những lời giảng về sự chết của Đấng Cứu Thế trong nhiều thời đại trước. Áp-ra-ham thấy trước sự chết của Ngài khi chiên con bị giết. Tuyển dân Y-sơ-ra-ên giết chiên con làm biểu tượng cho sự chết của Ngài. Mỗi lần huyết chiên con đổ ra trên bàn thờ đều là để tượng trưng cho Chiên Con của Đức Chúa Trời một ngày kia sẽ đến giải quyết vấn đề tội lỗi. Trong một số các thi thiên tiên tri, vua Đa-vít đã nói rất chi tiết về sự chết của Ngài. Ê-sai cũng dùng cả một chương tiên báo tỉ mỉ về việc Ngài chịu thương khó.")]),n._v(" "),i("p",[n._v('Nói về thẩm quyền phó sự sống của chính Ngài, Chúa Cứu Thế Giê-xu tuyên bố, "Người chăn thiện hảo bỏ sự sống vì chiên" (Giăng 10:11). Ngài cũng nói, "Xưa Môi-se treo con rắn lên nơi đồng hoang thế nào thì Con người cũng phải bị treo lên như vậy để hễ ai tin đến Ngài đều được sự sống đời đời" (Giăng 3:14,15). Chúa Cứu Thế Giê-xu đã phải đối diện với viễn ảnh thập tự giá từ những thời xa xưa trong cõi vĩnh hằng. Qua bao nhiêu thời đại trước giáng sinh, Chúa biết rằng sự chết của Ngài đang nhanh chóng đến gần. Khi được một trinh nữ sinh ra, Ngài đã sinh ra với bóng thập tự giá che phủ đường đi. Ngài đã mang lấy thân xác loài người để có thể chết. Từ lúc nằm nôi cho đến khi lên thập tự giá, chết là mục tiêu của đời Ngài.')]),n._v(" "),i("p",[n._v('Có người đã mô tả những đau thương của Ngài chưa từng có con người nào phải chịu như sau: "Một đêm thức trắng tại vườn Ghết-sê-ma-nê trong ánh đuốc bập bùng, cái hôn của kẻ bội phản, bị bắt, cuộc xét xử trước thầy cả thượng phẩm, những giờ chờ đợi trong tòa tổng trấn La-mã, chuyến đi đến cung điện Hê-rốt, lúc bị bọn lính tàn ác của Hê-rốt hành hạ, khung cảnh ghê gớm lúc Phi-lát cố tìm cách tha trong khi bọn thầy tế lễ và dân chúng gào lên đòi huyết Ngài, trận đòn nát thịt, những tiếng gào thét của đám đông, chặng đường từ Giê-ru-sa-lem đến Gô-gô-tha, những cây đinh đóng vào tay, vào chân Ngài, mão gai trên đầu, những tiếng kêu nhạo báng nhiếc móc của hai tên trộm cướp hai bên, "Ngươi đã cứu kẻ khác, bây giờ hãy tự cứu mình đi."')]),n._v(" "),i("p",[n._v("Thỉnh thoảng có người hỏi tôi tại sao Chúa Cứu Thế chết nhanh quá, chỉ trong có 6 giờ, trong khi đó những nạn nhân khác phải chịu cực hình trên thập tự từ hai đến ba ngày hoặc lâu hơn. Ngài đã yếu và kiệt lực từ khi đến nơi hành quyết. Ngài đã bị đánh đòn, thể lực hoàn toàn suy kiệt. Tuy nhiên khi chết, Ngài đã chết một cách tự nguyện. Ngài đã chọn đúng thời điểm khi trút linh hồn.")]),n._v(" "),i("p",[n._v('Trên thập giá Ngài treo thân giữa khoảng trời và đất. Sau khi đã chịu đau thương cùng cực, Ngài không than van, không cầu khẩn, mà chỉ thốt lên tiếng kêu bộc lộ nỗi đau đớn kinh khủng trong thân xác: "Ta khát!" Có một nhà thơ vô danh đã viết như sau:\n')]),i("p",{attrs:{align:"center"}},[i("em",[n._v("Cái hành hạ Ngài nơi đó kinh khủng hơn đau đớn")]),i("br"),n._v(" "),i("em",[n._v("Là cái khát thâm sâu thăm thẳm lòng Thiên Chúa,")]),i("br"),n._v(" "),i("em",[n._v("Khát khao gào thét cứu vớt linh hồn,")]),i("br"),n._v(" "),i("em",[n._v("Mà Chúa ơi, chính con là một trong những linh hồn đó!")]),i("br")]),i("p"),n._v(" "),i("h3",{attrs:{id:"dang-thay-the-toi-nhan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#dang-thay-the-toi-nhan","aria-hidden":"true"}},[n._v("#")]),n._v(" Đấng Thay Thế Tội Nhân")]),n._v(" "),i("p",[n._v("Đức Chúa Trời đòi hỏi tội lỗi phải trả bằng cái chết - cái chết của chính tội nhân hay của người thay thế! Chúa Cứu Thế chính là người thay thế đó! Thiên sứ trưởng Gáp-ri-ên và muời đạo binh thiên sứ gươm giáo tuốt trần đang bay lượn chờ trực quanh vòng đai vũ trụ. Chỉ cần một ánh mắt hiệu lệnh trên gương mặt đầy ân phúc của Chúa Cứu Thế là đội binh thiên sứ sẽ xô tất cả đám người cuồng nộ đang hò hét kia xuống địa ngục. Không phải những cái đinh đã giữ Ngài trên thập tự giá, nhưng chính là những sợi dây yêu thương đã cột trói Ngài chặt hơn bất cứ cái đinh nào của con người. "),i("strong",[n._v('"Đức Chúa Trời tỏ lòng yêu thương Ngài đối với chúng ta, khi chúng ta còn là tội nhân thì Chúa Cứu Thề vì chúng ta chịu chết" (Rô-ma 5:8)')]),n._v(".")]),n._v(" "),i("p",[n._v('Vì bạn! Vì tôi! Ngài đã mang tội lỗi chúng ta trong thân thể Ngài trên cây gỗ. Như có người từng bảo rằng, "Nhìn Ngài trên cây thập tự, đầu thánh gục xuống, Ngài gom hết vào trong tim tội lỗi của toàn nhân loại trong viễn ảnh kinh khủng bị cô lập và phân cách với Đức Chúa Trời, nhưng qua việc nhận tội thay thế đó Ngài có thể ban sự sống cho những người Ngài đã thay thế." Chúng ta đứng sững trước nỗi đau thương cùng cực của Chúa Giê-xu, cảm thấy chính mình không thể hiểu, cũng không thể giải thích nổi, và với ý thức thật sâu sắc về sức mạnh với vẻ uy nghi tràn ngập xung quanh khung cảnh đó, chúng ta nghe được tiếng kêu từ miệng Chúa, '),i("strong",[n._v('"Mọi sự đã trọn!"')])]),n._v(" "),i("p",[n._v("Nhưng sự đau đớn trong thân xác Chúa Cứu Thế Giê-xu không phải là nỗi thống khổ thật. Nhiều người trước Ngài đã chết. Có những người khác đã bị treo lên thập tự giá lâu hơn Ngài. Nhiều người đã chịu tử đạo. Nỗi đau thương chính yếu của Chúa Giê-xu là cái chết thuộc linh. Chúa Giê-xu đã đối diện với vấn đề cuối cùng là tội lỗi, đã kinh nghiệm được chiều sâu hơn hết của sự thống khổ khi Ngài kêu lên rằng, "),i("strong",[n._v('"Đức Chúa Trời tôi ôi! Đức Chúa Trời tôi ôi! Sao Ngài lìa bỏ tôi?"')]),n._v(" Tiếng kêu này là một bằng chứng cho thấy Chúa Cứu Thế Giê-xu đã trở nên tội lỗi vì chúng ta, đã thực sự chết về phương diện thân xác và vì vậy, Ngài mất hết các tri giác về sự hiện diện của Cha Ngài vào giây phút đó. Một mình cô đơn trong giờ phút tối thượng đó của lịch sử nhân loại, Chúa Cứu Thế đã thốt lên những lời này! Anh sáng đã lóe lên cho chúng ta thoáng thấy nỗi thống khổ của Ngài, nhưng ánh sáng đó quá chói lọi như G. Campbell Morgan bảo rằng, "),i("em",[n._v('"không mắt nào dám nhìn."')]),n._v(" Ông cũng nhận xét rất tinh tế, bảo rằng những lời Chúa Cứu Thế đã thốt ra trên thập tự giá, "),i("em",[n._v('"để con người thấy rằng còn bao nhiêu điều khác họ chưa từng biết."')])]),n._v(" "),i("p",[n._v("Trong thư "),i("strong",[n._v("Ga-la-ti 3:13")]),n._v("; "),i("strong",[n._v("Mác 15:34")]),n._v("; "),i("strong",[n._v("2 Côrinhtô 5:21")]),n._v(" cho chúng ta biết Đấng không hề biết tội lỗi đã vấy bẩn tội lỗi vì cớ chúng ta để trong Ngài chúng ta có thể trở nên sự công chính của Đức Chúa Trời. Trên thập tự giá Chúa Cứu Thế đã trở thành tội lỗi, Ngài đã bị Đức Chúa Trời từ bỏ. Bởi vì Ngài không hề biết tội lỗi cho nên giá trị sự trừng phạt Ngài chịu vượt quá mọi khả năng hiểu biết vì sự trừng phạt đó thật ra không cần áp dụng cho Ngài. Nếu vì là Đấng vô tội không cần phải chịu hình phạt thì Ngài đã chịu trừng phạt để tạo ra giá trị chuộc tội đó cho ai?")]),n._v(" "),i("p",[n._v("Làm thế nào giá trị chuộc tội đó đã hoàn tất trong hố sâu của tăm tối là điều con người không bao giờ hiểu được, nhưng tôi chỉ biết một điều - Ngài đã mang tội lỗi tôi trong thân thể Ngài trên cây thập tự. Ngài đã bị treo lên nơi đáng lẽ chính tôi phải ở đó. Tất cả những nỗi đớn đau của địa ngục thuộc phần tôi đều đã chất trên Ngài. Bây giờ tôi có thể hưởng thiên đàng cùng mọi đặc quyền không do tôi mà hoàn toàn là của Ngài. Tất cả mọi biểu mẫu, mọi tế lễ, mọi hình bóng và mọi biểu tượng trong Cựu Ước đã được thành nghiệm. Các thấy tư tế không còn cần mỗi năm một lần vào nơi chí thánh nữa vì sinh tế đã hoàn tất.")]),n._v(" "),i("p",[n._v("Bây giờ khi nền tảng của sự cứu chuộc đã đặt xong, tất cả các tội nhân ý thức tội lỗi của mình cần đặt đức tin nơi Con Đức Chúa Trời để có thể giải hòa với Đức Chúa Trời và có sự bình an trong Ngài. "),i("strong",[n._v('"Vì Đức Chúa Trời yêu thương nhân loại đến nỗi đã ban Con Một của Ngài, hầu cho hễ ai tin Con ấy, không bị hư vong mà được sự sống vĩnh hằng" (Giăng 3:16)')]),n._v(".")]),n._v(" "),i("h4",{attrs:{id:"ba-dieu-tren-thap-tu-gia"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#ba-dieu-tren-thap-tu-gia","aria-hidden":"true"}},[n._v("#")]),n._v(" Ba Điều Trên Thập Tự Giá")]),n._v(" "),i("p",[n._v("Trên thập tự giá của Chúa Cứu Thế Giê-xu tôi thấy ba điều:")]),n._v(" "),i("ul",[i("li",[i("p",[n._v("Trước hết là chiều sâu tội lỗi con người được phơi bày. Xin đừng đổ cho những người đương thời đã treo Chúa Cứu Thế lên cây gỗ vì chính bạn và tôi cùng mang trách nhiệm về tội đó. Không phải đoàn dân hay bọn lính La-mã đóng đinh Chúa, nhưng chính vì tội lỗi của bạn và tôi đã khiến cho Ngài phải tình nguyện chịu chết.")])]),n._v(" "),i("li",[i("p",[n._v("Thứ hai, trên thập tự giá tôi thấy được tình thương chan hòa của Đức Chúa Trời. Nếu có bao giờ bạn nghi ngờ tình yêu của Ngài, xin hãy nhìn thật kỹ, thật lâu lên thập tự giá, vì trên đó bạn có thể thấy cách Đức Chúa Trời thể hiện tình thương. của Ngài.")])]),n._v(" "),i("li",[i("p",[n._v("Thứ ba, trên thập tự giá tôi thấy con đường cứu rỗi duy nhất. Chúa Giê-xu phán, "),i("strong",[n._v('"Ta là con đường, chân lý và sự sống: không bởi ta thì không ai đến được với Thượng Đế" (Giăng 14:6)')]),n._v(". Không có cách nào khác khả dĩ cứu bạn khỏi tội lỗi và hỏa ngục ngoài ra cách phó mình cho Chúa Cứu Thế trên thập tự giá. Nếu có cách nào khác cứu bạn, chắc chắn Chúa cũng đã tìm ra. Nếu nhờ cố gắng tu tỉnh, sửa đổi tâm tính và sống cuộc sống đạo đức tốt hơn có thể cứu bạn thì Chúa Giê-xu không cần phải chết. Cần phải có một người thế chỗ cho bạn. Con người không muốn bàn đến việc này, họ không muốn nghe chuyện có người phải chết thay cho mình, vì nó làm tổn thương tự ái. Nó phơi bầy bản ngã.")])])]),n._v(" "),i("p",[n._v('Nhiều người bảo rằng, "Tôi có thể được cứu nếu sống đúng theo Luật Vàng không? Hay theo các giáo huấn của chính Chúa Giê-xu? Hay sống cuộc đời đạo đức Chúa dạy?" Cho dù có thể được cứu do sống cuộc sống Chúa dạy, chúng ta rồi ra sẽ vẫn là tội nhân. Chúng ta lại sẽ tiếp tục thất bại, vì không một người nào trong chúng ta, từ khi sinh ra cho đến khi qua đời từng sống được cuộc đời Chúa dạy. Chúng ta đã thất bại, đã phạm giới răn, đã không vâng phục, đã phạm tội. Như vậy chúng ta phải làm gì đối với tội lỗi? Chỉ có một điều duy nhất phải làm là đem tội lỗi đó đến thập tự giá để tìm ơn tha thứ.')]),n._v(" "),i("p",[n._v("Nhiều năm trước vua Charles đệ Ngũ được một đại thương gia ở Antwerp cho vay một số tiền rất lớn, nhưng khi đáo hạn, nhà vua vỡ nợ không trả nổi. Vị đại thương gia liền tổ chức một bữa dạ yến mời vua và một số đông quan khách đến dự. Khi mọi người đã yên vị và trước khi thức ăn được mang ra, người thương gia cho đem một khay lớn đặt trên bàn với một ngọn nến thắp sẵn, rồi lấy trong túi ra tờ giấy nợ đốt thành tro trước mặt mọi người.")]),n._v(" "),i("p",[n._v("Cũng vậy, tất cả chúng ta đều mắc nợ Đức Chúa Trời. Món nợ đã đáo hạn nhưng chúng ta không thể trả. Hai ngàn năm trước Đức Chúa Trời đã mời cả một thế giới suy đồi đến trước chân thập tự giá. Tại đó Ngài đã đem đốt tất cả tội lỗi của bạn và tôi cho đến khi tất cả cháy tiêu, không còn lại một vết tích nào.")]),n._v(" "),i("p",[n._v("Kinh Thánh khẳng định, "),i("strong",[n._v('"Không đổ huyết thì không có sự tha thứ" (Hê-bơ-rơ 9:22)')]),n._v(". Nhiều người đã nói với tôi, "),i("em",[n._v('"Thật là kinh khủng! Tôi không nghĩ ông lại có thể tin vào một tôn giáo sát sinh như thế!"')]),n._v(" Có người lại thắc mắc, "),i("em",[n._v('"Tôi không hiểu vì sao Đức Chúa Trời lại đòi phải đổ huyết."')]),n._v(" Nhiều người khác lại bảo, "),i("em",[n._v('"Tôi không hiểu vì sao Chúa Cứu Thế lại chết cho tôi."')]),n._v(" Ngày nay ở nhiều tòa giảng, khái niệm về huyết Chúa Cứu Thế đổ ra đang trở thành cũ kỹ, lỗi thời. Nhưng đây là khái niệm của Kinh Thánh. Đây là trái tim của Cơ đốc giáo. Khía cạnh đặc thù của Cơ Đốc giáo nằm ở huyết chuộc tội, vì nếu không có, chúng ta không thể được cứu. Huyết thực sự là biểu tượng về sự chết của Chúa Cứu Thế.")]),n._v(" "),i("p",[n._v('Cách đây không lâu khi đang đứng bên bàn nhận bệnh tại dưỡng đường Mayo Clinic ở Rochester, Minnesota tôi thấy có một cái hộp vuông trong đó có một số tập giấy có hàng chữ đỏ "Tặng Phẩm Huyết" in thành hình một giọt máu lớn. Thoạt tiên tôi tưởng đó là một truyền đạo đơn, nhưng nhìn kỹ hơn tôi mới biết đó là tài liệu cổ động, khuyến khích hỗ trợ chương trình hiến máu. Đối với một người nằm trong bệnh viện, máu có một ý nghĩa lớn lao đem lại khác biệt giữa sống và chết. Không một người nào từng được tiếp máu mà lại không nhìn máu với lòng biết ơn. Có người bảo rằng lấy máu ra có vẻ nghịch thường, nhưng cho máu là hành vi đầy ân phúc!')]),n._v(" "),i("p",[n._v("Sự kiện cơ bản đó là huyết đại diện cho sự sống như trong "),i("strong",[n._v("Lê-vi-ký 17:11")]),n._v(" chép, "),i("strong",[n._v('"hồn sống của xác thịt ở trong huyết; ta đã cho các ngươi huyết rưới trên bàn thờ để làm lễ chuộc tội cho linh hồn mình; vì nhờ hồn sống mà huyết mới chuộc tội được."')]),n._v(" Như vậy chúng ta thấy huyết sinh tế đã chảy thấm khắp Cựu Ước, là biểu tượng báo trước sinh tế toàn hảo là Chúa Cứu Thế.")]),n._v(" "),i("h3",{attrs:{id:"nam-dieu-do-huyet-dem-lai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#nam-dieu-do-huyet-dem-lai","aria-hidden":"true"}},[n._v("#")]),n._v(" Năm Điều Do Huyết Đem Lại")]),n._v(" "),i("ul",[i("li",[i("p",[n._v('Kinh Thánh dạy rằng ơn ích đầu tiên huyết đem lại là sự cứu chuộc như được khẳng định trong I Phi-e-rơ 1:18-19, "vì biết rằng chẳng phải bởi vật hay hư nát như bạc hoặc vàng mà anh em đã được chuộc khỏi sự ăn ở không ra chi của tổ tiên truyền lại cho mình , bèn là bởi huyết báu Chúa Cứu Thế, dường như huyết của Chiên Con không lỗi không vít...". Chúng ta không chỉ được cứu khỏi bàn tay ma quỉ, nhưng còn được cứu khỏi cả bàn tay của Giới Luật truyền xuống từ Đức Chúa Trời qua Môi-se. Sự chết của Chúa Cứu Thế trên thập tự giá đem chúng ta ra khỏi quyền lực của giới luật. Giới Luật kết án tôi nhưng Chúa Cứu Thế thỏa đáp mọi yêu cầu là điều không có khối lượng vàng bạc, châu báu nào trên đất đủ để chuộc mạng cho tôi. Điều vàng bạc không làm được thì sự chết của Chúa Cứu Thế đã hoàn tất. Cứu chuộc có nghĩa là "mua lại." Chúng ta đã bị bán không cho ma quỉ nhưng Chúa Cứu Thế đã cứu chuộc và đem chúng ta trở lại.')])]),n._v(" "),i("li",[i("p",[n._v("Thứ hai, huyết đem chúng ta đến gần, "),i("strong",[n._v('"Nhưng trong Chúa Cứu Thế Giê-xu, anh em là kẻ ngày trước cách xa, hiện nay đã nhờ huyết Chúa Cứu Thế mà được gần rồi" (Ê-phê-sô 2:13)')]),n._v(". Khi chúng ta "),i("strong",[n._v('"bị ngoại quyền công dân trong Y-sơ-ra-ên, không dự vào giao ước của lời hứa, ở thế gian không có hy vọng và không có Đức Chúa Trời"')]),n._v(" thì Chúa Cứu Thế Giê-xu đem chúng ta đến gần Đức Chúa Trời. "),i("strong",[n._v('"Cho nên hiện nay không có sự đoán phạt nào cho những người ở trong Chúa Cứu Thế Giê-xu" (Rô-ma 8:1)')]),n._v(". Tội nhân đã được cứu chuộc sẽ không bao giờ phải đối diện với sự phán xét của Đức Chúa Trời toàn năng vì Chúa Cứu Thế Giê-xu đã nhận thay sự phán xét rồi.")])]),n._v(" "),i("li",[i("p",[n._v('Thứ ba, huyết đem lại hòa bình. Thánh Phao-lô trong Cô-lô-se 1:20 viết như sau, "bởi huyết Ngài trên thập tự giá thì đã làm nên hòa bình, khiến muôn vật dưới đất, trên trời đều nhờ Ngài mà hòa thuận cùng chính mình Đức Chúa Trời ." Trần gian sẽ không bao giờ có thể biết hòa bình thật sự cho đến khi tìm đến thập tự giá của Chúa Cứu Thế Giê-xu. Bạn sẽ không bao giờ biết sự bình an với Chúa, bình an trong lương tâm, bình an trong tâm trí và bình an trong linh hồn cho đến khi bạn đứng tại chân thập tự giá và bởi đức tin liên kết chính mình với Chúa Cứu Thế. Bí quyết của sự bình an là bình an với Đức Chúa Trời.')])]),n._v(" "),i("li",[i("p",[n._v('Thứ tư, huyết đem lại sự công chính. Rô-ma 5:9 chép, "Huống chi nay chúng ta đã nhờ Ngài được xưng công bình, thì sẽ nhờ Ngài được cứu khỏi cơn thạnh nộ là dường nào!" Huyết thay đổi vị thế của chúng ta trước mặt Đức Chúa Trời, từ địa vị mắc tội, bị kết án bước sang địa vị được ân xá và tha thứ. Tội nhân được tha thứ không giống như một tù nhân được tha sau khi mãn hạn vẫn không có quyền công dân, trong khi tội nhân ăn năn, được tha thứ nhờ huyết Chúa Cứu Thế Giê-xu, được phục hồi toàn thể tư cách công dân nước Trời, "Ai sẽ kiện kẻ lựa chọn của Đức Chúa Trời? Đức Chúa Trời là Đấng xưng công bình những kẻ ấy. Ai sẽ lên án họ ư? Chúa Cứu Thế Giê-xu là Đấng đã chết, và cũng đã sống lại nữa, Ngài đang ngự bên hữu Đức Chúa Trời, cầu nguyện thế cho chúng ta" (Rô-ma 8:33-34).')])]),n._v(" "),i("li",[i("p",[n._v('Thứ năm, huyết thanh tẩy. I Giăng 1:7 chép như sau, "Nhưng nếu chúng ta đi trong sự sáng cũng như chính mình Ngài ở trong sự sáng thì chúng ta giao thông cùng nhau và huyết của Chúa Cứu Thế Giê-xu Con Ngài, làm sạch mọi tội chúng ta." Chữ chìa khóa trong câu này là "mọi tội". Không phải một phần tội chúng ta nhưng tất cả mọi tội. Nghĩa là tất cả mọi lời dối trá, tất cả mọi hành vi xấu xa, hèn hạ, dơ bẩn chúng ta đã làm, tất cả những hành vi giả hình, những tư tưởng tham dục, đều được tẩy sạch do sự chết của Chúa Cứu Thế.')])])]),n._v(" "),i("h3",{attrs:{id:"con-nguoi-khong-che-dau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#con-nguoi-khong-che-dau","aria-hidden":"true"}},[n._v("#")]),n._v(" Con Người Không Che Dấu")]),n._v(" "),i("p",[n._v('Câu chuyện đã được kể nhiều năm trước về một việc xảy ra tại Luân Đôn. Có một buổi hội lớn qui tụ toàn những nhân vật danh tiếng, trong đó cũng có một mục sư tiếng tăm là Caesar Milan. Trong chương trình, có một thiếu nữ trẻ vừa đàn vừa hát được mọi người tán thưởng nồng nhiệt. Với giọng thật ôn tồn, nhẹ nhàng nhưng can đảm vị mục-sư tiến lên khi tiếng đàn vừa dứt. Ông nói, "Khi nghe bài hát tối nay tôi tự nghĩ với khả năng và giọng hát của cô như thế nếu cô đem dâng cho Chúa Cứu Thế Giê-xu thì chính nghĩa của Ngài sẽ được ích lợi rất nhiều. Chắc cô cũng đã biết rằng, trước mặt Chúa, cô cũng chỉ là một tội nhân như một người say rượu ngồi bên cống rãnh hay một gái làng chơi ngoài phố chợ. Nhưng tôi rất vui báo cho cô một tin mừng rằng huyết của Chúa Cứu Thế Giê-xu, Con Đức Chúa Trời, làm sạch mọi tội chúng ta."')]),n._v(" "),i("p",[n._v('Cô thiếu nữ giận dữ bác bỏ câu nói "vơ đũa cả nắm" của vị mục-sư, nhưng ông ôn tồn trả lời, '),i("em",[n._v('"Thưa cô, tôi không có ý làm cô giận, nhưng xin Đức Thánh Linh cáo trách trong lòng cô."')])]),n._v(" "),i("p",[n._v("Mọi người đều về nhà. Cô thiếu nữ đi nghỉ, nhưng đêm ấy không ngủ được. Gương mặt nhà truyền đạo và những lời ông nói cứ văng vẳng bên tai. Hai giờ sáng, cô ở trong giường vùng ngồi dậy lấy giấy, viết ra, mặt đầm đìa nước mắt, Chalotte Elliott ghi xuống bài thơ nổi tiếng:\n")]),i("p",{attrs:{align:"center"}},[i("em",[n._v("Tuyệt nhiên không cách chi bào chữa tôi")]),i("br"),n._v(" "),i("em",[n._v("Chỉ huyết Giê-xu đã đổ ra rồi,")]),i("br"),n._v(" "),i("em",[n._v("Ngài yêu tôi lắm, hằng khuyên tôi đến")]),i("br"),n._v(" "),i("em",[n._v("Kính thưa Chiên Con, tôi nguyện đến liền.")]),i("br"),n._v(" "),i("em",[n._v("Tuyệt nhiên tôi chẳng cậy một pháp môn")]),i("br"),n._v(" "),i("em",[n._v("Có thể gội sạch vết uế tâm hồn,")]),i("br"),n._v(" "),i("em",[n._v("Nhờ huyết Giê-xu tội khiên tiêu biến")]),i("br"),n._v(" "),i("em",[n._v('Kính thưa Chiên Con, tôi nguyện đến liền!"')]),i("br")]),i("p"),n._v(" "),i("p",[n._v("Nhưng sự chết của Chúa Giê-xu không phải là chung cuộc. Chúng ta không để Chúa Cứu Thế treo thân trên cây gỗ, với máu từ tay, chân và sườn Ngài tuôn chảy đầm đìa. Ngài được hạ xuống từ cây thập tự, đem liệm và chôn cất trong mộ với một tảng đá lớn lăn ra chận trước cửa hang. Một toán lính được cắt cử canh gác cẩn mật. Suốt ngày thứ bảy, các môn đệ ngồi ủ rũ, buồn thảm trên phòng cao, hai người trong vòng họ đã lên đường đến làng Em-ma-út. Tất cả chìm ngập trong âu lo sợ hãi. Vào buổi sáng sớm ngày thứ nhất trong tuần, Ma-ri, Ma-ri Ma-đơ-len và Sa-lô-mê cùng đi đến mộ để xức dầu thơm cho xác Chúa. Khi đến nơi họ kinh ngạc thấy trong mộ trống rỗng. Theo Alfred Edersheim là một học giả Do Thái thì, "),i("em",[n._v('"Khung cảnh trong mộ không có một dấu hiệu vội vã nào, nhưng tất cả đều ngăn nắp, trật tự, để lại ấn tượng rằng Đấng nằm đó đã từ tốn cởi bỏ những tấm vải liệm không còn thích hợp cho Ngài."')]),n._v(" Một thiên sứ đứng phía đầu huyệt đá hỏi, "),i("strong",[n._v('"Các ngươi tìm ai?"')]),n._v(" Họ trả lời, "),i("strong",[n._v('"Chúng tôi tìm Giê-xu Na-xa-rét"')]),n._v(". Thiên sứ đã thông báo một tin tức quan trọng, vinh quang hơn hết cho toàn thể nhân loại, "),i("strong",[n._v('"Ngài không ở đây đâu, Ngài sống lại rồi."')])]),n._v(" "),i("h3",{attrs:{id:"su-kien-phuc-sinh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#su-kien-phuc-sinh","aria-hidden":"true"}},[n._v("#")]),n._v(" Sự kiện phục sinh")]),n._v(" "),i("p",[n._v('Xây dựng trên sự kiện vĩ đại trên là toàn thể chương trình cứu rỗi của Đức Chúa Trời. Không có sự phục sinh thì không có sự cứu rỗi. Nhiều lần Chúa Cứu Thế nói trước về sự phục sinh, như có lần Ngài tuyên bố, "Vì Giô-na đã bị ở trong bụng cá lớn ba ngày ba đêm, cũng một thể ấy, Con người sẽ ở trong lòng đất ba ngày ba đêm" (Ma-thi-ơ 12:40). Đúng như lời báo trước này, Chúa đã phục sinh!')]),n._v(" "),i("p",[n._v("Có một số qui luật về bằng chứng trong việc thiết định bất cứ biến cố lịch sử nào. Trước hết, biến cố đó cần phải có trong các tài liệu do chính các chứng nhân đáng tin đương thời ghi chép lại. Những tài liệu về sự phục sinh của Chúa Giê-xu còn nhiều hơn những tài liệu về hoàng đế La-mã Julius Ceasar hay về A-lịch-sơn Đại Đế của Hi-lạp chết năm 33 tuổi. Nhưng điều kỳ lạ là các sử gia đã chấp nhận hàng ngàn sự kiện dựa trên những bằng chứng rất manh mún, trong khi sự phục sinh của Chúa Cứu Thế Giê-xu có vô số bằng chứng mạnh mẽ lại bị họ nhìn với con mắt hoài nghi và ngờ vực về phương diện trí thức. Nan đề của những người này là họ không muốn tin. Nhãn quan thuộc linh của họ mù loà đến độ hoàn toàn bị thành kiến che phủ đến nỗi họ không thể chấp nhận sự kiện phục sinh của Chúa Cứu Thế Giê-xu dưạ trên một mình lời chứng của Kinh Thánh.")]),n._v(" "),i("ul",[i("li",[i("p",[n._v("Trước hết sự phục sinh có nghĩa là Chúa Cứu Thế Giê-xu chính là Đức Chúa Trời là sự kiện không thể chối cãi được. Ngài chính là Đấng Ngài đã tuyên xưng, Chúa Cứu Thế là Thượng Đế trong thân xác.")])]),n._v(" "),i("li",[i("p",[n._v('Thứ hai, sự phục sinh có nghĩa là Đức Chúa Trời đã chấp nhận công trình chuộc tội của Chúa Giê-xu trên thập tự giá, là công trình thiết yếu cho sự cứu rỗi của chúng ta. "Ngài đã bị nộp vì tội lỗi chúng ta, và sống lại vì sự công bình của chúng ta" (Rô-ma 4:25).')])]),n._v(" "),i("li",[i("p",[n._v('Thứ ba, sự phục sinh đoan chắc cho nhân loại về một sự phán xét công chính, "Vì như bởi sự không vâng phục của một người mà mọi người khác đều thành ra kẻ có tội, thì cũng một lẽ ấy, bởi sự vâng phục của một người mà mọi người khác sẽ thành ra công bình" (Rô-ma 5:19).')])]),n._v(" "),i("li",[i("p",[n._v('Thứ tư, sự phục sinh bảo đảm rằng thân thể chúng ta đến cuối cùng rồi cũng sẽ sống lại. "Nhưng bây giờ Chúa Cứu Thế đã từ kẻ chết sống lại, Ngài là trái đầu mùa của những kẻ ngủ" (I Cô-rinh-tô 15:20). Kinh Thánh dạy rằng, là Cơ Đốc nhân, thân thể chúng ta dù phải bỏ trong mồ mả, nhưng rồi chúng ta sẽ sống lại trong ngày phục sinh lớn. Lúc đó sự chết sẽ bị nuốt mất trong sự đắc thắng. Kết quả do sự phục sinh của Chúa Cứu Thế Giê-xu đem lại là cái nọc của sự chết bị tiêu diệt và Chúa Cứu Thế Giê-xu là Đấng cầm chìa khóa. Chúa phán, "Ta là Đấng sống, ta đã chết, kìa nay ta sống đời đời, cầm chìa khoá của sự chết và âm phủ" (Khải 1:18). Chúa Cứu Thế cũng hứa rằng "Vì ta sống thì các ngươi cũng sẽ sống."')])]),n._v(" "),i("li",[i("p",[n._v("Thứ năm, sự phục sinh cho thấy rằng đến cuối cùng, sự chết sẽ bị loại trừ. Quyền lực của sự chết đã bị loại bỏ và tinh thần sợ sự chết đã bị cất đi. Bây giờ chúng ta có thể nói như tác giả Thi-thiên, "),i("strong",[n._v('"Dầu khi tôi đi trong trũng bóng chết tôi sẽ chẳng sợ tai hoạ nào, vì Chúa ở cùng tôi" (Thi-thiên 23:4)')]),n._v(". Thánh Phao-lô nhìn về sự chết với lòng trông mong lớn lao nơi sự phục sinh của Chúa Cứu Thế, ông nói, "),i("strong",[n._v('"Vì Chúa Cứu Thế là sự sống của tôi và sự chết là điều ích lợi cho tôi vậy" (Phi-líp 1:21)')]),n._v(", cũng như Velma Barfield là một tử tội chờ hành quyết ở tiểu bang North Carolina từng nói: "),i("em",[n._v('"Tôi yêu mến Ngài đến nỗi thấy thời gian chờ đợi gặp Ngài thật quá dài."')])])])]),n._v(" "),i("p",[n._v("Không có sự phục sinh của Chúa Cứu Thế cũng sẽ không có hy vọng cho tương lai. Kinh Thánh hứa rằng một ngày kia chúng ta sẽ đứng trước Chúa Cứu Thế phục sinh mặt đối mặt, và chúng ta sẽ có thân thể mới như thân thể của chính Ngài.\n")]),i("p",{attrs:{align:"center"}},[i("em",[n._v("Khi tôi đối mặt cùng Cứu Chúa tôi,")]),i("br"),n._v(" "),i("em",[n._v("Phút vinh quang kia thuật sao rồi.")]),i("br"),n._v(" "),i("em",[n._v("Ôi vui vẻ thay nhìn Chân Chúa kìa,")]),i("br"),n._v(" "),i("em",[n._v("Giê-xu Christ chết thế tôi kia.")]),i("br"),n._v(" "),i("em",[n._v("Khi tôi đối mặt Ngài vui bấy,")]),i("br"),n._v(" "),i("em",[n._v("Xa tít bên kia tinh cầu này")]),i("br"),n._v(" "),i("em",[n._v("Ôi, tôi đối mặt Ngài đầy vinh hiển,")]),i("br"),n._v(" "),i("em",[n._v("Mặt gặp mặt hưởng phước vô biên.")]),i("br"),n._v(" "),i("strong",[n._v("Dịch lời của Carrie E. Breck")]),i("br")]),i("p"),n._v(" "),i("h2",{attrs:{id:"chuong-9-khoi-su-nhu-the-nao-va-tu-dau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-9-khoi-su-nhu-the-nao-va-tu-dau","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 9: Khởi sự như thế nào và từ đâu?")]),n._v(" "),i("p",[i("strong",[n._v('"Quả thật ta nói cùng các ngươi, nếu các ngươi không đổi lại và nên như đứa trẻ thì chẳng được vào thiên đàng đâu" Ma-thi-ơ 18:3')])]),n._v(" "),i("p",[n._v("Chúng ta nhận ra rằng có một nguyên tắc tự nhiên kéo chúng ta xuống bình diện loài vật - làm đui mù lý trí, chai lì lương tâm và tê liệt ý chí. Đó là sức kéo của dẫn lực thuộc linh. Chúng ta bị kết án bởi chính những hành động của mình. Đức Chúa Trời là Đức Chúa Trời thánh khiết và công chính, Ngài không thể dung thứ tội lỗi. Tội lỗi phân cách chúng ta với Đức Chúa Trời, khiến cho cơn giận của Đức Chúa Trời trút xuống linh hồn con người. Con ngưòi đã mất hết ý thức đạo đức, sự khôn sáng cũng như ý thức thuộc linh về Đức Chúa Trời vì người đã mất Đức Chúa Trời. Người sẽ không thể nào tìm được Đức Chúa Trời cho đến khi tìm được con đuờng trở về với Ngài.")]),n._v(" "),i("p",[n._v("Con đường trở về với Đức Chúa Trời không phải con đuờng của lý trí, cũng không phải con đường luân lý. Bạn không thể nào nghĩ ra đường trở về với Đức Chúa Trời vì lối suy nghĩ của con người không phù hợp với lối suy nghĩ của Đức Chúa Trời và tâm trí trần tục luôn luôn chống nghịch Ngài. Bạn không thể dùng sự thờ phượng để trở lại với Đức Chúa Trời vì con người là kẻ nổi loạn thuộc linh chống lại sự hiện diện của Đức Chúa Trời. Bạn không thể nào dùng đạo đức trở lại với Đức Chúa Trời vì tâm tính con người đã vấy bẩn vì tội lỗi.")]),n._v(" "),i("h3",{attrs:{id:"con-duong-tro-lai-voi-duc-chua-troi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#con-duong-tro-lai-voi-duc-chua-troi","aria-hidden":"true"}},[n._v("#")]),n._v(" Con Đường Trở Lại Với Đức Chúa Trời")]),n._v(" "),i("p",[n._v('Những câu hỏi tự nhiên sau đây đến với bạn - Tôi phải làm gì? Tôi sẽ khỏi sự từ đâu? Tôi sẽ bắt đầu từ chỗ nào? Tôi phải đi đường nào để trở về với Đức Chúa Trời? Chỉ có một con đường duy nhất trở lại với Đức Chúa Trời. Chúa Giê-xu dạy, "Nếu các ngươi không đổi lại và nên như đứa trẻ, thì không được vào nước thiên đàng đâu" (Ma-thi-ơ 18: 3). Hiển nhiên Chúa Giê-xu không bảo trẻ con trở thành môn đệ, nhưng bảo môn đệ Chúa trở thành trẻ con. Do đức tin như trẻ thơ mỗi người có cơ hội từ những người có tâm trí yếu kém trở thành những bậc đại trí thức. Như vậy, Chúa đòi con người phải hoán cải và đây là chỗ khởi đầu. Bạn cần phải được hoán cải!')]),n._v(" "),i("p",[n._v('Nhiều người lẫn lộn sự hoán cải hay qui đạo với việc tuân giữ các giới luật. Luật Môi-se được đưa ra với những điều kiện đặc biệt trong Kinh Thánh và mục đích của các giới luật này rất rõ ràng. Luật Môi-se không được đưa ra như một loại thuốc trị bá bệnh cho các bệnh tật trần gian, nhưng là để chẩn đoán, để chỉ ra nguyên nhân bệnh tật chứ không phải để cứu chữa. Kinh Thánh dạy, "Chúng ta biết rằng những điều luật pháp nói, là nói cho mọi kẻ ở dưới luật pháp, hầu cho miệng nào cũng phải ngậm lại, cả thiên hạ đều nhận tội trước mặt Đức Chúa Trời" (Rô-ma 3:19). Giới luật đã bày tỏ sự không công chính của con người và Kinh Thánh bảo rằng, "vì chẳng có một người nào bởi việc làm theo giới luật mà sẽ được coi là công chính trước mặt Ngài" (Rô-ma 3:20). Con người không thể do tuân giữ giới luật mà được cải hoán vì Kinh Thánh dạy rằng, "vì giới luật cho người ta biết tội lỗi." Giới luật giống như tấm gương đạo đức, là dụng cụ giúp con người thấy mình đã sa ngã đến đâu và như thế nào. Giới luật chỉ lên án chứ không thể cải hoán con người được. Giới luật thách thức nhưng không thể đem lại thay đổi, vạch trần tội lỗi mà không bày tỏ lòng thương xót. Trong giới luật không có sự sống mà chỉ có sự chết vì lời công bố của giới luật là, "Ngươi sẽ chết." Giới luật chính là cây thước cứng ngắc để bên cạnh bản chất cong quẹo của con người.')]),n._v(" "),i("p",[n._v("Nhiều người bảo rằng tôn giáo của họ là Bài Giảng Trên Núi, nhưng chưa hề có một con người nào sinh trên trần gian có thể sống đạt được tiêu chuẩn của Bài Giảng này. Kinh Thánh khẳng định rằng mọi người đều đã phạm tội, thiếu hụt sự vinh hiển của Đức Chúa Trời.")]),n._v(" "),i("p",[n._v("Trước khi bảo rằng mình toàn hảo không chê trách được và không cần phải hoán cải, xin bạn hãy kiểm điểm lại mọi động cơ của đời sống mình. Xin hãy nhìn sâu vào lòng bạn một cách chân thành, không sợ hãi trước khi bảo rằng việc hoán cải hay qui đạo cần thiết cho người khác chứ không cần cho bạn.")]),n._v(" "),i("h3",{attrs:{id:"cau-hoi-chung"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#cau-hoi-chung","aria-hidden":"true"}},[n._v("#")]),n._v(" Câu Hỏi Chung")]),n._v(" "),i("p",[n._v('Khi tôi giảng tại Hollywood, một nhóm người thuộc giới điện ảnh yêu cầu tôi nói về những kinh nghiệm tôn giáo. Sau phần trình bày, chúng tôi có giờ thảo luận và câu hỏi đầu tiên là, "Hoán cải là gì?" Ít lâu sau đó tôi cũng được hân hạnh trình bày trước một nhóm lãnh tụ chính trị ở Hoa Thịnh Đốn. Lúc khởi sự phần thảo luận, một lần nữa, câu hỏi đầu tiên cũng là, "Hoán cải là gì?" Trong hầu hết những đại học tôi có dịp đến diễn giảng, trong giờ thảo luận, cùng câu hỏi trên vẫn thường được hỏi trong những hình thức khác nhau như, "Sinh lại hay tái sinh là gì?" Trong cuốn How To Be Born Again (Làm Sao Để Được Tái Sinh), tôi đã trình bày diễn tiến dó như sau:')]),n._v(" "),i("p",[n._v("Sự sống mới này xảy ra trong nhiều cách thế khác nhau, qua một khoảng thời gian nhưng cũng có thể trong giây lát. Những con đường nhiều người sử dụng để đạt tới có thể là đưòng thẳng trực tiếp nhưng cũng có thể là đường vòng. Nhưng dù là đường nào đi nữa, chúng ta luôn luôn gặp Chúa Cứu Thế ở cuối đường. Chính giây phút gặp Chúa Cứu Thế cũng là lúc phát sinh sự sống mới, là khởi đầu hành trình của cuộc sống ở dưới sự tể trị của Ngài. Đời sống có nhiều thay đổi, hôn nhân được cải thiện đầy hứng khởi, xã hội hưởng được nhiều ảnh hưởng tốt - tất cả chỉ do từng đợt sóng đơn sơ bao gồm những con người kinh nghiệm sự tái sinh.")]),n._v(" "),i("p",[n._v('Trong nền kinh tế hôm nay chúng ta nghe về những loại xe cộ, máy móc được "tái sinh" hay tân trang, nhưng đó không phải ý nghĩa tái sinh tôi nói ở đây. Có lẽ cách đơn giản nhất để trình bày về sự tái sinh là bảo rằng tái sinh là sinh ra vào trong gia đình Đức Chúa Trời. Có lẽ tái sinh là một trong những vấn nạn có nhiều cách trả lời khác nhau hơn hầu hết những vấn nạn khác liên quan đến tôn giáo. Như vậy, hoán cải là gì? Có những yếu tố nào liên hệ với sự hoán cải? Làm sao đạt được sự hoán cải? Hiệu quả của sự hoán cải là gì? Tại sao bạn cần phải được hoán cải để có thể vào thiên đàng?')]),n._v(" "),i("p",[n._v("Khái niệm hoán cải thật ra không phải là một khái niệm mới lạ trong xã hội chúng ta. Bất cứ nhân viên chào hàng giỏi nào cũng biết rất rõ rằng anh ta phải thay đổi lối suy nghĩ về món hàng anh ta giới thiệu. Mục đích chính của việc quảng cáo là khiến cho giới tiêu thụ quyết định thay đổi mặt hàng. Chúng ta cũng nói đến các lãnh tụ chính trị thay đổi từ quan điểm này sang một quan điểm chính trị khác. Trong cuộc đại chiến thế giới vừa qua, chúng ta nghe nói rất nhiều đến việc chuyển đổi từ một nền kỹ nghệ thời bình sang nền kỹ nghệ thời chiến và hầu hết những lò dầu trong các tư gia đổi thành lò dùng than, và rồi gần đây lại đổi thành dùng hơi đốt. Chúng ta cũng bàn nhiều đến tỉ số hối đoái chuyển đổi tiền trong nước thành ngoại tệ.")]),n._v(" "),i("p",[n._v('Thật ra từ hoán cải có nghĩa là "đổi lại," hay "quay lại" "thay đổi ý kiến," hay là "trở lại." Trong phạm trù tôn giáo từ này đã được giải thích là "hối cải," "tân sinh," "nhận ân sủng," "kinh nghiệm tôn giáo," "được bảo đảm."')]),n._v(" "),i("p",[n._v('Tôi nhớ có một tay nghiện rượu đến trong mấy buổi nhóm đầu của một chiến dịch truyền giảng, anh ta nói với tôi, "Thưa Mục-sư Graham, tôi không biết trong những gì ông giảng, có lời nào là chân lý hay không, nhưng tôi muốn thử Chúa Cứu Thế Giê-xu của ông, chỉ cần Ngài có một phần quyền năng như những gì ông giảng, tôi sẽ trở lại đây ký thác cả đời tôi cho Ngài!"')]),n._v(" "),i("p",[n._v("Vài tuần sau, nói với tôi, anh ta bảo không hiểu sao mỗi lần định uống rượu anh thấy như có cái gì hay một người nào giữ anh ta lại. Chúa Cứu Thế đã ban cho anh quyền năng đắc thắng một thói quen xấu xa ghê gớm. Anh đã trở lại với gia đình, và bây giờ đang sống cho Chúa. Nói cách khác, người thanh niên này đã quay lại, đã đổi hướng, đã thay đổi cách suy nghĩ - anh ta đã đuợc hoán cải!")]),n._v(" "),i("h3",{attrs:{id:"ban-chat-su-hoan-cai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#ban-chat-su-hoan-cai","aria-hidden":"true"}},[n._v("#")]),n._v(" Bản Chất Sự Hoán Cải")]),n._v(" "),i("p",[n._v("Hoán cải có nhiều hình thức khác nhau. Nó xảy ra như thế nào tuỳ thuộc rất nhiều vào từng cá nhân - cá tính, cảm xúc, môi trường sống, lối sống và những điều kiện sống trước đó. Sự hoán cải có thể xảy ra theo sau một biến cố lớn trong đời sống một người, hay cũng có thể xảy ra sau khi tất cả những giá trị trước đó trong đời sống bị quét sạch, sau khi có một kinh nghiệm lớn, khi do có nhiều tài sản mà mất ý thức về quyền lực, hay mất mục tiêu của lòng yêu mến. Khi một người dồn mọi quan tâm vào chuyện tiền bạc, vào những quyền lợi xã hội hoặc nghề nghiệp, vào tình cảm đối với một người, người đó sẽ kinh nghiệm ý thức về sự mất mát khi bị từ khước những điều đem lại ý nghĩa cho đời sống mình.")]),n._v(" "),i("p",[n._v("Trong những giờ bi thảm đó, khi một người thấy mình bị mất hết mọi quyền lực trần gian, khi người thân yêu ra đi vĩnh viễn, người đó sẽ nhận ra tính cách kinh khủng và toàn diện của nỗi cô đơn mình phải đối diện thực sự như thế nào. Trong giây phút đó, Đức Thánh Linh có thể làm cho những tấm màn che thuộc linh rơi khỏi mắt khiến cho người đó thấy rõ lần đầu tiên trong đời. Người đó nhận ra rằng Đức Chúa Trời là nguồn năng lực duy nhất và là nguồn suối yêu thương, thông cảm bền vững duy nhất.")]),n._v(" "),i("p",[n._v("Hay sự hoán cải cũng có thể xảy ra ngay vào cao điểm của sự thịnh vượng và quyền lực cá nhân, nghĩa là khi mọi chuyện tốt đẹp và những sự thương xót dồi dào của Chúa tuôn đổ trên đời sống. Tất cả những phước lành của Chúa khiến cho bạn nhận ra rằng tất cả những gì mình đang có là do Chúa ban, và chính đó là điều đưa bạn đến chỗ ăn năn quay lại với Ngài (Rô-ma 2:4). Sự hoán cải trong những giờ phút như thế có thể bất ngờ và mạnh mẽ như sự hoán cải của Phao-lô trên đường đi Đa-mách.")]),n._v(" "),i("p",[n._v('Nhưng không phải tất cả mọi sự hoán cải đều đến bất ngờ như một luồng ánh sáng loé soi trong linh hồn như vậy, mà chúng ta gọi là "hoán cải cấp tính". Có nhiều trường hợp sự hoán cải xảy ra sau một cuộc tranh chiến khó khăn, lâu dài với những động cơ sâu kín bên trong. Với một số người khác, sự hoán cải đến như một cao điểm sau một thời gian dài được thôi thúc, cảnh tỉnh về nhu cầu cũng như về sự mặc khải của chương trình cứu rỗi. Tiến trình lâu dài này đưa đến kết quả là người đó chấp nhận Chúa Cứu Thế Giê-xu là Đấng Cứu Chuộc chính mình và bằng lòng giao phó cuộc đời mình cho Ngài.')]),n._v(" "),i("p",[n._v('Trong tập tự truyện thuộc linh, C.S. Lewis mô tả kinh nghiệm hoán cải của ông như sau:\nBạn phải muờng tượng ra cái cảnh tôi một mình trong đại học Magdalen, suốt đêm này sang đêm khác, nghĩa là bất cứ giây phút nào không nghĩ đến công việc, là tôi lại cảm thấy ngay cái bước tiến lại gần vững chắc, cương quyết của một Đấng tôi hết sức không muốn gặp. Chính điều tôi sợ nhất đó cuối cùng đã xảy ra. Vào khóa mùa Xuân năm 1929 tôi chịu thua, công nhận rằng Đức Chúa Trời thật quả là Đức Chúa Trời và quỳ xuống cầu nguyện: có lẽ tối hôm đó tôi là kẻ hoán cải bi đát và do dự nhất nước Anh. Lúc đó tôi không thấy được những điều bây giờ tôi cho là hiển nhiên và tươi sáng nhất; lòng khiêm hạ của Đức Chúa Trời đã chấp nhận một kẻ hoán cải trong những điều kiện đó. Đứa con hoang đàng ít ra cũng đã tự đưa buớc trở về. Nhưng đàng này làm sao có thể thán phục một Tinh Yêu sẵn sàng mở rộng cửa cho đứa con hoang đàng trở về mà khi được dẫn vào nó lại tỏ thái độ bực bội, vùng vẫy, đạp đá, mắt ngó dáo dác tìm dịp chạy trốn? Từ ngữ "ép mời vào nhà" đã bị những con người gian ác chúng ta kinh tởm lạm dụng, nhưng khi hiểu thật đúng, họ chính là phương tiện để biểu minh chiều sâu lòng thương xót của Đức Chúa Trời. Khía cạnh cứng cỏi của Đức Chúa Trời hiền từ hơn cái êm ái của con người, và sự ép buộc của Ngài lại là sự giải phóng của chúng ta.')]),n._v(" "),i("p",[n._v("Như vậy chúng ta có thể bảo rằng sự hoán cải là một biến cố tức thời, một biến động qua đó một người nhận được sự mặc khải rõ ràng về tình yêu của Đức Chúa Trời; hay sự hoán cải cũng có thể là một sự khai mở tiệm tiến đi kèm với cao điểm là lúc tiến đến giao điểm giữa sáng và tối. Giữa cái chết và sự sống vĩnh hằng.")]),n._v(" "),i("p",[n._v("Sự hoán cải cũng không luôn luôn xảy ra đúng như vậy. Thí dụ như nhà tôi đã không nhớ chính xác ngày giờ bà trở thành Cơ Đốc nhân, nhưng bà biết chắc rằng có giây phút đó trong đời, giây phút bà thực sự vượt lằn ranh. Nhiều thanh thiếu niên lớn lên trong những gia đình tín hữu, được trưởng dưỡng trong Cơ Đốc giáo thường không ý thức thời điểm chính xác mình ký thác đời mình cho Chúa Cứu Thế. Có người bảo rằng chúng ta không biết thời điểm chính xác mặt trời mọc, nhưng một khi nó đã mọc, chúng ta biết chắc. Có những người khác lại biết rõ lúc mình công khai tuyên xưng đức tin. Hầu hết các trường hợp hoán cải ký thuật trong Tân Ước đều có tính cách của một biến động rất rõ ràng.")]),n._v(" "),i("h3",{attrs:{id:"quan-diem-cua-tam-ly-hoc-doi-voi-su-hoan-cai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#quan-diem-cua-tam-ly-hoc-doi-voi-su-hoan-cai","aria-hidden":"true"}},[n._v("#")]),n._v(" Quan Điểm Của Tâm Lý Học Đối Với Sự Hoán Cải")]),n._v(" "),i("p",[n._v("Trong nhiều năm ngành tâm lý học không đả động gì đến sự hoán cải và các kinh nghiệm tôn giáo, tuy nhiên trong hơn năm mươi năm qua, các nhà tâm lý đã khởi sự nghiên cứu toàn thể tiến trình hoán cải. Họ nêu lên rằng hoán cải không chỉ là kinh nghiệm riêng của Cơ Đốc nhân, nhưng cũng có trong những tôn giáo khác. Hơn thế nữa, đó không nhất thiết là một hiện tượng tôn giáo nhưng cũng xảy ra ở những lãnh vực ngoài tôn giáo nữa. Các sinh viên ngành tâm lý đều đồng ý rằng có ba bước trong sự hoán cải: Trước hết là ý thức bối rối, và khó chịu; thứ hai, là cao điểm và quyết định đổi hướng; cuối cùng là giai đoạn dãn xả thể hiện bằng sự vui mừng và nghỉ ngơi.")]),n._v(" "),i("p",[n._v('Trong một bài viết nhan đề, Tại Sao Cảm Thấy Buồn Lại Là Điều Tốt (Why It’s Good To Feel So Bad) được đăng trong báo The New York Times số ngày 29 tháng 11, 1983) tác giả nêu lên rằng "Ý thức đau khổ vì không đạt được tiêu chuẩn của chính mình hay còn gọi là mặc cảm mắc tội, chính là người giám hộ cho tính thiện hảo trong chúng ta. Nó cần cho sự phát triển lương tâm trong trẻ con và để tránh tác phong chống xã hội." Bài báo đó cũng giải thích tiếp, "Trẻ thơ có tác phong tốt phần lớn là do cha mẹ đã dạy con biết sợ hình phạt khi phạm lỗi. Nhưng khi đứa trẻ lớn lên, cái "bản ngã lý tưởng" của nó - trong hình ảnh người cha - được khắc sâu vào tâm khảm, trở thành mẫu mực cho tác phong đúng đắn ...và đến khi trưởng thành, con người sẽ tự trừng phạt mình mỗi khi sống bội phản với mẫu mực đó. Tiến sĩ Gaylin thấy sự kiện thiếu các gương mẫu về hình ảnh người cha ở trên đã là nguyên nhân cho sự gia tăng thái độ chai lì, nổi loạn chống xã hội trong giới trẻ ngày nay." Chính cảm xúc mắc tội đã tạo ra lòng khao khát đối với một điều tốt hơn chỉ có thể thấy trong mối tương giao tốt đẹp với Chúa Cứu Thế.')]),n._v(" "),i("p",[n._v("Các nhà tâm lý học bảo rằng có hai loại hoán cải. Một loại kèm theo ý thức mãnh liệt về tội lỗi, loại kia đi kèm với cảm xúc của tình trạng không hoàn tất, một cuộc vật lộn tìm kiếm cuộc đời rộng rãi hơn và mong ước được sự soi sáng tâm linh.")]),n._v(" "),i("p",[n._v("Giá trị của các nghiên cứu tâm lý về sự hoán cải đã bị đánh giá quá thấp. Chúng ta không thể dẹp những nghiên cứu này sang một bên hay coi như không có. Các tâm lý gia đã rọi vào vấn đề khá nhiều ánh sáng, tuy nhiên hầu hết không sẵn sàng chấp nhận rằng sự hoán cải theo nghĩa của Kinh Thánh là một kinh nghiệm siêu nhiên.")]),n._v(" "),i("p",[n._v("Thật ra sự hoán cải theo Kinh Thánh bao gồm ba bước - hai bước tích cực và một bước tiêu cực. Trong khía cạnh tích cực, hoán cải liên quan đến sự ăn năn và đức tin. An năn là hoán cải nhìn từ khởi điểm, từ chỗ lìa bỏ cuộc đời cũ. Đức tin chỉ thị khía cạnh khách quan của sự hoán cải, là quay trở về với Đức Chúa Trời. Bước thứ ba có tính cách thụ động, chúng ta gọi là sự tân sinh hay tái sinh, là sinh ra lần thứ hai, sinh vào gia đình Đức Chúa Trời.")]),n._v(" "),i("p",[n._v('Chúa Giê-xu dạy rằng để vào thiên đàng bạn phải được hoán cải. Đây không phải là điều tôi đặt ra nhưng đây là điều chính Chúa Giê-xu nói. Đây không phải là ý kiến của con người nhưng là quan điểm của Đức Chúa Trời! Chúa Giê-xu phán rằng, "Nếu các ngươi không đổi lại và nên như đưa trẻ thì không được vào nước thiên đàng" (Ma-thi-ơ 18:3).')]),n._v(" "),i("p",[n._v('Sự hoán cải thật phải bao gồm trí óc, tình cảm và ý chí. Có hàng ngàn người đã hoán cải, đến với Chúa Cứu Thế về phương diện lý trí, họ tin toàn thể Kinh Thánh, họ tin nhiều điều về Chúa Giê-xu nhưng chưa bao giờ thực sự hoán cải để đến với Ngài. Kinh Thánh cho chúng ta biết ma quỉ cũng "tin" theo cách đó và "run sợ" (Gia-cơ 2:19).')]),n._v(" "),i("h3",{attrs:{id:"khac-biet-giua-niem-tin-cua-ly-tri-va-su-hoan-cai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#khac-biet-giua-niem-tin-cua-ly-tri-va-su-hoan-cai","aria-hidden":"true"}},[n._v("#")]),n._v(" Khác Biệt Giữa Niềm Tin Của Lý Trí Và Sự Hoán Cải")]),n._v(" "),i("p",[n._v('Trong Phúc Âm Giăng có một chỗ mô tả về hàng trăm người theo Chúa Giê-xu trong thời gian Chúa khởi đầu sứ mạng, "có nhiều người thấy phép lạ Ngài làm thì tin danh Ngài. Nhưng Đức Chúa Giê-xu không phó thác mình cho họ, vì Ngài nhận biết mọi người và không cần ai làm chứng về người nào, bởi Ngài tự hiểu thấu mọi điều trong lòng người ta" (Giăng 2:23-25). Tại sao Chúa Giê-xu không phó thác Ngài cho họ? Chúa biết họ chỉ tin Ngài bằng lý trí chứ không nhận Ngài bằng tấm lòng.')]),n._v(" "),i("p",[n._v("Có một khác biệt lớn lao giữa niềm tin của lý trí và sự hoán cải toàn diện cứu được linh hồn. Để chắc chắn cần phải có một thay đổi trong suy nghĩ của chúng ta và sự chấp nhận Chúa Cứu Thế về phương diện lý trí.")]),n._v(" "),i("p",[n._v("Có hàng ngàn người từng kinh nghiệm một hình thức xúc cảm nào đó mà họ coi là sự hoán cải, nhưng lại là những người chưa từng thực sự quay trở lại với Chúa Cứu Thế. Chúa đòi hỏi bạn phải thay đổi lối sống, và nếu đời sống bạn không chuyển đổi theo kinh nghiệm kia thì bạn có đầy đủ lý do để nghi ngờ kinh nghiệm đó. Chắc chắn cũng sẽ có sự thay đổi trong những yếu tố cấu thành xúc cảm khi bạn đến với Chúa Cứu Thế như ghét và yêu chẳng hạn. Bạn sẽ ghét tội lỗi và yêu mến sự công chính. Tình thương của bạn sẽ trải qua những đổi thay quan trọng. Lòng tận hiến cho Chúa sẽ không còn biên cương và tình yêu của bạn dành cho Chúa không thể mô tả được.")]),n._v(" "),i("p",[n._v('Nhưng dù bạn đã chấp nhận Chúa về phương diện lý trí và về phương diện xúc cảm đã có kinh nghiệm với Chúa, nhưng như vậy vẫn chưa đủ, còn cần phải có sự hoán cải của ý chí! Cần phải có quyết tâm đi theo và vâng lời Chúa Cứu Thế. Bạn sẽ phải thuận theo ý chỉ Đức Chúa Trời và bản ngã phải được đóng đinh vào thập tự giá. Nhiều người trong chúng ta có thể cũng suy nghĩ như một thiếu nữ đã viết cho chúng tôi rằng cô không thấy việc "treo bản ngã lên thập tự giá là chuyện dễ." Thật ra chưa một người nào trong chúng ta có thể làm được việc đó, tuy nhiên mong ước chính của chúng ta là làm vui lòng Chúa. Đây là một sự ký thác toàn diện.')]),n._v(" "),i("p",[n._v("Trong sự hoán cải, khi bạn đứng dưới chân thập tự giá, Đức Thánh Linh sẽ khiến bạn nhận ra mình là một tội nhân . Ngài sẽ hướng đức tin bạn về Chúa Cứu Thế là Đấng chết thế chỗ cho bạn . Bạn phải mở lòng ra để Ngài bước vào và ngay chính giây phút đó Đức Thánh Linh thi hành một phép lạ gọi là sự tân sinh. Bạn thực sự trở thành một tạo vật mới và đó chính là lúc bản chất thiêng liêng mới được trồng trong bạn. Bạn trở thành người được dự phần trong chính sự sống của Đức Chúa Trời và Chúa Cứu Thế Giê-xu qua Đức Thánh Linh sẽ ngự vào lòng bạn.")]),n._v(" "),i("p",[n._v('Sự hoán cải đơn giản đến độ một em bé cũng có thể được hoán cải, nhưng cũng sâu sắc đến độ các thần học gia suốt qua bao nhiêu thế kỷ đã phải cố công suy tìm ý nghĩa thâm sâu trong đó. Đức Chúa Trời đã làm cho con đường cứu rỗi rõ ràng đến độ "ai đi trên đường đó, dầu khờ dại cũng không lầm lạc" (Ê-sai 35:8). Không một người nào bị ngăn cấm vào nước Trời chỉ vì thiếu khả năng hiểu biết. Người giàu cũng như người nghèo, người đơn sơ cũng như người sâu sắc, tất cả đều có thể được hoán cải.')]),n._v(" "),i("p",[n._v('Tóm lại, nói một cách đơn giản, hoán cải là "thay đổi." Khi một người được hoán cải, người đó có thể vẫn yêu thích những vật anh ta thích trước đây, nhưng sẽ có những thay đổi trong lý do yêu thích. Có thể anh cũng sẽ không đến với những người bạn cũ nữa không phải vì anh không thích họ, vì trong số đó có nhiều người đơn sơm dễ mến, nhưng vì có sức thu hút mạnh mẽ hơn trong cộng đồng Cơ Đốc gồm những người có đồng tâm chí.')]),n._v(" "),i("p",[n._v("Người được hoán cải sẽ yêu mến điều thiện lành trước đây anh từng ghét và ghét tội lỗi trước đây anh từng yêu thích. Tấm lòng đối với Đức Chúa Trời cũng thay đổi. Ở những chỗ trước đây anh không quan tâm gì đến Đức Chúa Trời, luôn luôn sống trong lo âu, sợ hãi và chống nghịch Đức Chúa Trời thì bây giờ anh thấy mình sống trong tâm trạng hoàn toàn ký thác, tin cậy, tôn kính và vâng lời Ngài. Trong lòng anh sẽ xuất hiện lòng kính sợ Đức Chúa Trời, thái dộ luôn luôn biết ơn Chúa, tùy thuộc Chúa và lòng trung thành mới đối với Ngài. Trước khi hoán cải có thể là cuộc sống chiều theo xác thịt, chỉ biết theo đuổi các mục tiêu văn hóa, học thức hay tìm kiếm sự giàu sang, coi đó là những ưu tiên hàng đầu. Bây giờ thì tấm lòng công chính và thánh khiết, và việc sống đời Cơ Đốc được đặt lên trên tất cả mọi mối quan tâm khác, vì bây giờ, mục tiêu quan trọng nhất trong đời là làm vui lòng Chúa Cứu Thế. Nói cách khác, hoán cải là sự thay đổi toàn diện trong đời sống của một cá nhân.")]),n._v(" "),i("h3",{attrs:{id:"mot-truong-hop-hoan-cai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-truong-hop-hoan-cai","aria-hidden":"true"}},[n._v("#")]),n._v(" Một Trường Hợp Hoán Cải")]),n._v(" "),i("p",[n._v('Tôi nhớ rất rõ về một thiếu nữ trẻ làm việc tại Nữu Ước đến thành phố Los Angeles để chuẩn bị lập gia đình. Cô và vị hôn phu gặp nhau trong thời gian cùng làm việc chung trong một công ty quảng cáo lớn tại Nữu Ước. Giai đoạn tiền hôn nhân của họ diễn tiến trong bối cảnh của những buổi dạ vũ vui chơi trong các hộp đêm. Hôn phu của cô là một người đầy tham vọng với "nghề nghiệp đang lên", anh đã xin chuyển sang làm tại một văn phòng ở Los Angeles, biết rằng trong vòng sáu tháng cô cũng sẽ thu xếp sang đây và rồi họ sẽ thành hôn.')]),n._v(" "),i("p",[n._v("Khoảng một tuần sau khi đến Los Angeles, mong sẽ chuẩn bị khởi đầu một cuộc sống vui sướng thì cô khám phá ra rằng hôn phu của cô đã yêu một diễn viên màn bạc nhưng lại không đủ can đảm viết thư trước khi cô rời Nữu Ước!")]),n._v(" "),i("p",[n._v("Bây giờ cô đang ở đây, cô đơn trong một thành phố không có ai quen biết. Tất cả mọi chương trình của cô gãy đổ, lòng kiêu hãnh bị chà nát, tương lai trải dài trước mặt trống rỗng, vô vọng. Cô xuất thân trong một gia đình không tin đạo nên trong giờ cực kỳ khủng hoảng này cô không biết một chỗ nào để tìm sự an ủi, khuyên lơn, hướng dẫn.")]),n._v(" "),i("p",[n._v('Trong khi cô lang thang trên những con đường không quen, cố vượt qua cú sốc và tâm trạng bị sỉ nhục, cô đi qua "lều giáo đường" là một khu nhà rạp chúng tôi dựng lên để tổ chức chiến dịch truyền giảng Tin Lành năm 1949. Cô bảo rằng không hiểu có điều gì đã thôi thúc trong lòng, nhưng cô cứ vào, ngồi ủ dột suốt buổi. Tối kế tiếp cô lại đến và cứ thế suốt một tuần. Dù cô bị cả một đám mây cay đắng, khổ sở bao phủ, Đức Chúa Trời đã khiến cho cô nghe được lời Ngài, cô đã bước lên phía trước thổ lộ nhu cầu cần được cứu rỗi.')]),n._v(" "),i("p",[n._v("Khi gánh nặng mặc cảm mắc tội và bị phụ bạc được cất khỏi lòng cô do đức tin nơi Chúa Cứu Thế Giê-xu, cô nhận ra rằng tình yêu cô vừa bị mất thật ra chỉ là bậc thềm đưa cô vào một tình thương lớn lao, phong phú hơn rất nhiều. Mặc cảm hổ thẹn ngăn cản cô trở lại với công việc cũ ở Nữu Ước biến mất, và cuộc sống thay vì mất hết ý nghĩa thì lúc trở về, cô lại thấy nó đầy trọn hơn rất nhiều. Thay vì phung phí tâm trí và khả năng tổ chức vào những cuộc họp mặt vui chơi bất tận, cô trở nên vô cùng tích cực trong bước đường theo Chúa và phục vụ người khác.")]),n._v(" "),i("p",[n._v('Trí tưởng tượng trước đây cô dùng "giúp vui" cho đám đông trong sở làm thì bây giờ được cô tận dụng để làm các chuyện tích Kinh Thánh trở nên sống động đối với giới trẻ. Khả năng chuyên môn của cô trong lãnh vực gây quĩ bây giờ được dùng cho sự phục vụ Chúa, và vị mục-sư của cô bảo rằng những ý kiến cô đóng góp rất có giá trị trong việc gia tăng số người tham dự các lễ thờ phượng Chúa. Thay vì bị từ chối, hất hủi, bây giờ cô là người được các con cái Chúa trong hội thánh tìm đến nhiều hơn hết. Nhưng điều quan trọng hơn cả là ý thức cô đơn trong cô đã biến mất vì cô biết rằng từ bây giờ Chúa Cứu Thế Giê-xu sẽ ở luôn bên cạnh, sẵn sàng ủi an, nâng đỡ và bảo vệ cô.')]),n._v(" "),i("p",[n._v("Tất cả những điều này là kết quả của sự hoán cải - nghĩa là quay lưng lại với con đường trần gian hoang vu, trống vắng, để đến với Chúa và Cứu Chúa của cô là Chúa Cứu Thế Giê-xu! Cô đã tìm được sự bình an với Chúa.")]),n._v(" "),i("h2",{attrs:{id:"chuong-10-hoi-cai-la-gi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-10-hoi-cai-la-gi","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 10: Hối cải là gì?")]),n._v(" "),i("p",[i("strong",[n._v('"Ta nói cùng các ngươi, trên trời cũng như vậy, sẽ vui mừng cho một kẻ có tội ăn năn hơn là chín mươi chín kẻ công bình không cần phải ăn năn" (Lu-ca 15:7)')])]),n._v(" "),i("p",[n._v("Chúng ta đã thấy Chúa Giê-xu đòi con người phải hoán cải. Chúng ta cũng thấy ba yếu tố của sự hoán cải là hối cải, đức tin và sự sinh lại hay tái sinh. Trình tự của ba yếu tố này có thể là vấn đề tranh luận, nhưng nói chung, người ta đồng ý rằng cả ba rất có thể xảy ra cùng một lúc.")]),n._v(" "),i("p",[n._v('Nếu sự hối cải có thể mô tả bằng hai từ, tôi sẽ dùng hai từ "quay lại." Bạn sẽ hỏi quay lại với điều gì? Câu trả lời là "tội lỗi." Như chúng ta đã thấy, Kinh Thánh dạy rằng tội lỗi là sự vi phạm giới luật. Tội lỗi là loại bỏ mọi thẩm quyền và khước từ mọi bổn phận đối với Đức Chúa Trời. Tội lỗi chính là cái nguyên tắc gian ác đã đến trong vườn Ê-đen khi A-đam và Ê-va bị cám dỗ và sa ngã. Kể từ thảm họa trong vườn Ê-đen, thứ độc dược gian ác này đã ảnh hưởng tất cả mọi người đến nỗi "mọi người đều đã phạm tội," và "không có người công chính nào hết, dẫu một người cũng không." Tội lỗi đã hủy phá mối tương giao của chúng ta với Đức Chúa Trời, hậu quả là làm xáo trộn mối tương giao của chúng ta với nhau, và ngay cả với chính mình.')]),n._v(" "),i("p",[n._v('Chúng ta sẽ không thể nào có bình an với Đức Chúa Trời hay bình an với nhau trong trần gian, hay ngay cả bình an bên trong chúng ta cho đến khi điều "Đức Chúa Trời gớm ghét" được loại trừ. Chúng ta không những phải quay lưng lại với tội, mà còn phải từ bỏ mọi tội lỗi - tất cả tội lỗi. Chúng ta phải loại trừ mọi ảnh hưởng xấu xa của thế gian, xác thịt và ma quỉ. Không thể có thương lượng, mặc cả, thỏa hiệp hay do dự. Chúa Cứu Thế đòi hỏi lòng trung thành tuyệt đối của chúng ta.')]),n._v(" "),i("h3",{attrs:{id:"hoi-cai-va-duc-tin"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hoi-cai-va-duc-tin","aria-hidden":"true"}},[n._v("#")]),n._v(" Hối cải và đức tin")]),n._v(" "),i("p",[n._v("Tại đây chúng ta lại thấy liên quan đến nguyên tắc về tình thương vì khi bạn hoàn toàn tuyệt đối yêu mến Chúa, bạn sẽ không muốn làm những điều Ngài ghét và ghê tởm. Bạn sẽ tự động từ bỏ mọi tội lỗi trong đời sống khi lấy đức tin đầu phục Ngài. Vì vậy hối cải và đức tin luôn luôn đi với nhau như bóng với hình. Bạn không thể có đức tin đưa đến sự cứu rỗi nếu không có sự hối cải chân thật.")]),n._v(" "),i("p",[n._v('Ngày nay từ ngữ hối cải không còn được nhắc đến nhiều trên tòa giảng nữa và đó là điều thật đáng buồn. Hối cải cũng là một từ không được ưa thích. Bài giảng đầu tiên của Chúa Giê-xu là "Hãy hối cải: vì nước thiên đàng đã đến gần" (Ma-thi-ơ 4:17). Đây là sứ điệp Đức Chúa Trời phán dạy qua Con Ngài. Chúa Giê-xu đến với một tấm lòng đầy ắp tình yêu và lòng thương cảm, nhưng Ngài cũng tức khắc khẳng định tội lỗi và tình trạng mắc tội của con người. Ngài kêu gọi con người thừa nhận tội lỗi và quay lại khỏi cuộc sống không tôn thờ Chúa. Ngài dạy rằng sự ăn năn phải đến trước khi Ngài có thể tuôn đổ tình thương, ân sủng và lòng thương xót xuống cho con người. Chúa Giê-xu không chấp nhận việc lấp liếm tội lỗi mà Ngài nhấn mạnh đến việc tự kiểm, đến thái độ quay hẳn lưng lại với cuộc đời cũ. Ngài cương quyết đòi con người phải có một thái độ mới trước khi Ngài có thể bày tỏ tình thương của Đức Chúa Trời.')]),n._v(" "),i("p",[n._v('Một ngày kia đoàn dân kể cho Chúa Giê-xu việc mấy người Ga-li-lê bị Phi-lát giết, lấy huyết trộn với sinh tế trong dịp quân La-mã dẹp yên một cuộc nổi dậy của người Do Thái. Họ cũng kể lại chuyện tháp Si-lô-ê sập đổ đè chết nhiều người. Trả lời họ, Chúa Giê-xu bảo rằng, "Các ngươi tưởng mấy người đó vì chịu khốn nạn như vậy, có tội nặng hơn mọi người Ga-li-lê khác sao? Ta nói cùng các ngươi, không phải; song nếu các ngươi không ăn năn thì hết thảy sẽ hư mất như vậy" (Lu-ca 13:3). Nói cách khác, Chúa Giê-xu bảo rằng dù con người có chết tức tưởi, bất đắc kỳ tử hay chết bình yên thì số phận hư vong vẫn giống nhau trừ khi họ ăn năn hối cải quay trở lại với Đức Chúa Trời. Nếu không hối cải thì tuyệt đối không thể có đức tin thật. Điều này không giới hạn ân sủng Đức Chúa Trời, nhưng sự hối cải dọn đường cho ân sủng Chúa.')]),n._v(" "),i("h3",{attrs:{id:"su-hoi-cai-va-an-sung-duc-chua-troi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#su-hoi-cai-va-an-sung-duc-chua-troi","aria-hidden":"true"}},[n._v("#")]),n._v(" Sự hối cải và ân sủng Đức Chúa Trời")]),n._v(" "),i("p",[n._v('Chúng ta đã biết sự cứu rỗi hoàn toàn dựa trên ân sủng của Đức Chúa Trời. Kinh Thánh dạy rằng trước mặt Đức Chúa Trời không một người nào được kể là công chính do tuân giữ giới luật mà khẳng định, "Người công chính sẽ sống bởi đức tin" (Rô-ma 9:17). Sự cứu rỗi, sự tha thứ, sự công chính hóa đều hoàn toàn dựa trên sự chuộc tội của Chúa Cứu Thế. Tuy nhiên để sự hy sinh của Chúa Cứu Thế trên thập tự giá trở thành hiệu nghiệm cho mọi người mọi hạng tuổi thì chính con người đó phải ăn năn tội và tiếp nhận Chúa Cứu Thế bằng đức tin.')]),n._v(" "),i("p",[n._v('Giô-na giảng về sự hối cải cho dân thành Ni-ni-ve cho đến khi cả thành ăn năn. Ê-xê-chi-ên giảng về sự ăn năn, nói rằng, "Chúa Giê-hô-va phán: Hỡi nhà Y-sơ-ra-ên, ta sẽ đoán xét các ngươi, mỗi người theo việc làm của nó. Hãy trở lại và xây bỏ mọi sự phạm pháp của các ngươi; vậy thì sự gian ác không trở nên cớ hư nát cho các ngươi" (Ê-xê-chi-ên 18:30).')]),n._v(" "),i("p",[n._v('Sứ điệp chính của Giăng Báp-tít là về sư hối cải. Ong giảng như sau, "Hãy ăn năn: vì nước thiên đàng đã đến gần" (Ma-thi-ơ 3:2).')]),n._v(" "),i("p",[n._v('Sự hối cải hay ăn năn được nói đến bảy mươi lần trong Tân Ước. Chúa Giê-xu phán "Nếu các ngươi không ăn năn thì tất cả sẽ hư mất như vậy." Bài giảng của Phi-e-rơ trong ngày lễ Ngũ Tuần là "Hãy hối cải, ai nấy phải nhân danh Chúa Cứu Thế Giê-xu chịu báp tem để được tha tội" (Công-vụ 2:38). Phao-lô giảng về sự hối cải bảo rằng ông "giảng cho người Giu-đa cũng như cho người Gờ-réc về sự ăn năn đối với Đức Chúa Trời và đức tin trong Chúa Cứu Thế Giê-xu, Chúa chúng ta" (Công-vụ 20:21). Kinh Thánh cũng dạy rằng Đức Chúa Trời truyền bảo con người phải hối cải, "Đức Chúa Trời đã bỏ qua các đời ngu muội đó mà nay bảo tất cả các ngươi trong mọi nơi đều phải ăn năn" (Công-vụ 17:30). Đây là mệnh lệnh, một mệnh lệnh bắt buộc. Đức Chúa Trời truyền phán, "Hãy hối cải hay là chết!" Bạn đã hối cải chưa? Bạn có chắc chắn không?')]),n._v(" "),i("p",[n._v('Có nhiều thí dụ trong Kinh Thánh về sự hối cải giả hiệu, thí dụ như Pha-ra-ôn nói với tuyển dân Y-sơ-ra-ên đang tìm cách ra khỏi Ai-cập đi đến đất hứa, "Lần này trẫm đã phạm tội..." (Xuất Ai-cập 9:27). Hiển nhiên đó là cách nói lên lòng hối tiếc hay hối hận mà không phải là hối cải thật. Đây cũng chính là thái độ của Sau-lơ như ký thuật trong I Sa-mu-ên 15: 24, 30 và 26: 21. Trong khi đó, khi Đa-vít nói với Na-than "Tôi đã phạm tội", Đa-vít thực sự ăn năn (2 Sa-mu-ên 15:24,30 và 26:21; cũng xem Thi-thiên 51).')]),n._v(" "),i("h3",{attrs:{id:"dinh-nghia-cua-chua-gie-xu-ve-su-an-nan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#dinh-nghia-cua-chua-gie-xu-ve-su-an-nan","aria-hidden":"true"}},[n._v("#")]),n._v(" Định Nghĩa Của Chúa Giê-xu Về Sự ăn Năn")]),n._v(" "),i("p",[n._v('Chúa muốn nói gì trong từ ăn năn ? Tại sao từ ngữ này cứ được dùng đi dùng lại trong suốt Kinh Thánh? Tra tự điển, bạn sẽ thấy "ăn năn" là "cảm thấy buồn hay tiếc vì lầm lỡ." Nhưng trong nguyên ngữ Chúa dùng, "ăn năn" có ý nghĩa rộng lớn hơn nhiều. Đó không chỉ là hối tiếc hay cảm thấy buồn vì lầm lỗi mà theo nghĩa của Kinh Thánh, ăn năn là "thay đổi" hay "quay trở lại". Đó là một từ rất mạnh chỉ hành động, nói về một sự chuyển hướng hoàn toàn trong đời sống cá nhân. Khi Kinh Thánh kêu gọi chúng ta ăn năn tội, có nghĩa là chúng ta phải quay lưng lại với tội lỗi và đi về một hướng ngược lại hướng của tội lỗi cũng như tất cả những gì dính dấp đến tội lỗi.')]),n._v(" "),i("p",[n._v("Chúa Giê-xu kể dụ ngôn Đứa Con Phóng Đãng để linh động hóa ý nghĩa của từ ngữ ăn năn. Khi đứa con phóng đãng ăn năn, nó không chỉ ngồi đó hối tiếc về bao nhiêu tội lỗi đã phạm. Nó không tiêu cực, thụ động, không ngồi ủ dột giữa đàn heo mà đứng dậy lên đường. Nó quay bước, đổi hướng để về nhà, tìm cha, hạ mình xuống trước mặt cha. Nó đã được cha tha thứ.")]),n._v(" "),i("p",[n._v('Nhiều Cơ Đốc nhân hôm nay đã không còn thấy được ý nghĩa của sự ăn năn theo Kinh Thánh dạy. Họ tưởng rằng ăn năn chỉ là lắc đầu chép miệng, tỏ ý ân hận về lầm lỗi đã phạm, bảo rằng, "Thật đáng tiếc!" và tiếp tục cuộc sống y như cũ.')]),n._v(" "),i("p",[n._v('An năn thật có nghĩa là "thay đổi, quay lưng lại để đi về một hướng mới." Hối tiếc chưa đủ gọi là ăn năn. Sự hối tiếc của Giu-đa mới chỉ đủ để khiến người môn đệ này đi treo cổ tự tử. Hối tiếc là nhìn nhận tội lỗi mà không ăn năn thật. Ngay cả nỗ lực cải cách cũng không đủ. Không một hình khổ nào bạn bắt thân xác phải chịu hay một nỗi thống khổ nào tâm trí bạn phải nhận có thể làm vừa lòng Đức Chúa Trời Toàn Năng. Tất cả tội lỗi chúng ta được chuộc nhờ Chúa Cứu Thế trên thập tự giá, tại đó Ngài chịu án phạt của tội lỗi. Không một nỗi đau thương nào chúng ta chịu có thể đưa chúng ta đến chỗ ăn năn.')]),n._v(" "),i("h3",{attrs:{id:"an-nan-khong-chi-la-xuc-cam"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#an-nan-khong-chi-la-xuc-cam","aria-hidden":"true"}},[n._v("#")]),n._v(" Ăn Năn Không Chỉ Là Xúc Cảm")]),n._v(" "),i("p",[n._v('Khi nói về sự ăn năn, tôi không nói đến việc hãm mình than khóc. Nhiều người được dạy rằng để ăn năn, bạn phải dành một số thì giờ nào đó than vãn thì mới mong nhận được sự cứu rỗi. Một người kể lại cho tôi vào đêm anh gặp Chúa Cứu Thế cách đây ít năm, anh tiến lên phía trước trong một buổi nhóm truyền thống, quì trước bàn thờ, cố gắng tìm gặp Đức Chúa Trời. Đang lúc đó, một chị em đến vỗ vai bảo, "Ráng lên, nếu anh muốn tìm gặp Đức Chúa Trời thì cứ tiếp tục kiên trì bám lấy Ngài." Vài phút sau, một chức viên trong nhà thờ cũng đến vỗ vai bảo rằng, "Đừng căng thẳng như thế! Hãy buông mình vào tay Chúa!" Một chị em tín hữu khác lại đến bảo, "Vào đêm tôi được hoán cải, có một luồng sáng lớn chiếu vào mặt khiến cho cả người tôi lạnh toát." Sau tất cả những lời khuyên đó, người này nói, "Cùng lúc tôi vừa cố nắm chặt, vừa cố thả lỏng, đồng thời cố nhìn vào luồng ánh sáng lớn. Tôi hoàn toàn bối rối và không biết mình đã làm cái gì!"')]),n._v(" "),i("p",[n._v("Một Cơ Đốc nhân rất thông minh thuộc thành phần lãnh đạo có lần kể rằng vào lúc ông qui đạo, thái độ của hội chúng và của nhà truyền đạo mong ông bày tỏ xúc cảm, đã suýt khiến ông khựng lại không muốn đến với Chúa nữa.")]),n._v(" "),i("p",[n._v("Chủ trương gây xúc cảm giả tạo trong một số các buổi nhóm phục hưng đã trở thành hòn đá vấp chân cho nhiều linh hồn chân thành tìm kiếm Chúa. Nhưng tại đây, loại ăn năn tôi sẽ trình bày là loại ăn năn dựa vào theo đúng lời dạy của Kinh Thánh bao gồm ba phương diện: lý trí, xúc cảm và ý chí.")]),n._v(" "),i("h3",{attrs:{id:"ba-phuong-dien-cua-su-an-nan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#ba-phuong-dien-cua-su-an-nan","aria-hidden":"true"}},[n._v("#")]),n._v(" Ba Phương Diện Của Sự Ăn năn")]),n._v(" "),i("p",[n._v('Trước hết cần phải hiểu rõ về tội lỗi. Kinh Thánh dạy, "Mọi người đều đã phạm tội, thiếu mất sự vinh hiển của Đức Chúa Trời" (Rô-ma 3:23). Khi tiên tri Ê-sai bị cáo trách về tội lỗi ông kêu lên, "Khốn nạn cho tôi!...Tôi là một người có môi dơ dáy" (Ê-sai 6:5). Khi Gióp được thoáng nhìn thấy sự thánh khiết của Đức Chúa Trời ông bảo, "Tôi lấy làm gớm ghê tôi" (Gióp 42:6). Khi Phi-e-rơ bị cáo trách về tội lỗi ông nói, "Tôi là một người tội lỗi" (Lu-ca 5:8). Khi Phao-lô bị cáo trách về tội lỗi, ông gọi mình là "kẻ đứng đầu tội nhân" (I Ti-mô-thê 1:15).')]),n._v(" "),i("p",[n._v("Chính Đức Thánh Linh là Đấng đem lại sự cáo trách này. Thật ra sự ăn năn không thể xảy ra trước khi có tác động của Đức Thánh Linh trong lòng, trong trí con người. Đức Thánh Linh có thể dùng lời cầu nguyện của một người mẹ, lời giảng của mục sư, một chương trình phát thanh Tin lành, hình ảnh một tháp chuông nhà thờ, cái chết của một người thân yêu để đem lại sự cáo trách cần thiết này. Tuy nhiên, tôi từng thấy có những người trong một số các buổi nhóm, đứng run rẩy vì bị cáo trách nhưng vẫn không chịu ăn năn tội. Có thể có những trường hợp một người bị cáo trách về tội lỗi, biết rõ mình là một tội nhân, thậm chí khóc lóc nữa nhưng vẫn không ăn năn.")]),n._v(" "),i("p",[n._v('Thứ hai, xúc cảm có trong sự ăn năn cũng như trong những kinh nghiệm thực sự khác. Phao-lô bảo rằng sự buồn rầu chân thật mở đường cho sự ăn năn. Có người từng nói, "Nhiều người ghê tởm tất cả các loại xúc cảm, một số nhà phê bình nghi ngờ bất cứ sự hoán cải nào thiếu tính chất sáng suốt, bình thản. Có nhiều hiểm họa trong chủ trương nhấn mạnh đến xúc cảm, coi xúc cảm là cứu cánh. Tuy nhiên không phải vì thế mà chúng ta loại trừ xúc cảm thật và những cảm xúc sâu sắc, chân thành."')]),n._v(" "),i("p",[n._v('Như Tiến sĩ W. E. Sangster, một mục sư nổi tiếng người Anh của giáo hội Methodist viết trong cuốn Let Me Commend (Cho Tôi Nhận Định), "Người hò hét trong trận đấu bóng hay dã cầu nhưng lại khó chịu khi nghe tiếng khóc ăn năn tại thập tự giá và nhỏ to khích bác xúc cảm chủ nghĩa, thì không đáng tin tưởng."')]),n._v(" "),i("p",[n._v("Horace Walpole có lần cáo buộc John Wesley đã có lối giảng biểu lộ xúc cảm quá đáng, dầu vậy Wesley đã đưa được hàng ngàn người trở về với Đức Chúa Trời.")]),n._v(" "),i("p",[n._v("Thứ ba, sự ăn năn liên quan đến ý chí.\nChỉ khi đụng đến lãnh vực ý chí chúng ta mới tìm được trung tâm của sự ăn năn. Cần phải có quyết tâm từ bỏ tội lỗi, nghĩa là thay đổi thái độ đối với bản ngã, đối với tội lỗi và đối với Đức Chúa Trời; thay đổi xúc cảm, thay đổi ý chí, quan niệm và mục tiêu.")]),n._v(" "),i("p",[n._v('Chỉ có Thần Linh của Đức Chúa Trời mới có thể ban cho bạn quyết tâm cần thiết đi đến chỗ ăn năn thật, nghĩa là không phải ăn năn giống như lời cầu nguyện của một bé gái, "Xin Chúa giúp con ngoan ngoãn, không phải thật ngoan nhưng ngoan đủ để khỏi bị đòn."')]),n._v(" "),i("p",[n._v('Có hàng ngàn người Mỹ có tên trong sổ hội thánh, thỉnh thoảng đi nhà thờ, cũng dâng tiền và hỗ trợ một số hoạt động của hội thánh. Họ bắt tay mục sư sau buổi lễ và khen ngợi bài giảng. Họ dùng ngôn ngữ tín đồ và nhiều người thuộc làu nhiều câu Kinh Thánh, nhưng họ chưa bao giờ thực sự kinh nghiệm sự ăn năn thật. Họ có thái độ dửng dưng, "thế-nào-cũng-được" đối với tôn giáo. Khi gặp khó khăn họ đến với Chúa để cầu nguyện, nhưng lúc bình thường họ không bận tâm nghĩ đến Chúa. Kinh Thánh dạy rằng khi đến với Chúa Cứu Thế con người sẽ được thay đổi, và những thay đổi đó thể hiện ra ở tất cả mọi hành động.')]),n._v(" "),i("h3",{attrs:{id:"de-an-nan-can-phai-dau-phuc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#de-an-nan-can-phai-dau-phuc","aria-hidden":"true"}},[n._v("#")]),n._v(" Để Ăn Năn Cần Phải Đầu Phục")]),n._v(" "),i("p",[n._v("Không có một câu Kinh Thánh nào chỉ thị rằng là một tín hữu bạn muốn sống như thế nào cũng được. Khi Chúa Cứu Thế ngự vào lòng người, Ngài muốn ngự vào để làm Thầy, làm Chúa. Ngài đòi bạn phải đầu phục hoàn toàn. Ngài đòi quyền kiểm soát toàn thể tâm trí bạn. Ngài đòi cả thân xác bạn phải tùng phục. Ngài mong bạn giao nộp mọi tài năng cho Ngài. Ngài mong không phải chỉ một phần mà tất cả mọi công lao, sức lực của bạn đều được thực hiện trong danh Ngài.")]),n._v(" "),i("p",[n._v("Nhiều người hôm nay mệnh danh là Cơ Đốc nhân nhưng sẵn sàng bỏ giờ thờ phượng Chúa chứ không muốn bỏ dịp đi mua một cái tủ lạnh mới. Cho lựa chọn giữa việc đặt tiền cọc mua xe và dâng tiền cho một dự án xây cất Trường Chúa Nhật của hội thánh, không phải là một quyết định khó, chúng ta có thể đoán ngay ra quyết định đó là gì. Hàng nghìn tín đồ trên danh nghĩa, bỏ tiền ra đắp vào cho cuộc sống tiện nghi hơn là dâng hiến cho việc giảng dạy lời Chúa. Chúng ta tìm được thì giờ cho việc xem phim, chơi dã cầu hay bóng bầu dục, nhưng không tìm ra thì giờ dành cho Đức Chúa Trời. Chúng ta có thể để dành tiền mua một căn nhà mới hay một máy truyền hình lớn hơn, nhưng lại cảm thấy mình không thể nào dâng hiến một phần mười. Đây là tội thờ hình tượng.")]),n._v(" "),i("p",[n._v("Cần phải có một sự thay đổi! Chúng ta chỉ tay về thế giới ngoại giáo và lên án những người thờ hình tượng thời xưa, trong khi đó khác biệt duy nhất là hình tượng của chúng ta ngày nay thay vì cẩn kim cương hay vàng bạc, lại làm bằng sắt thép mạ kền bóng loáng và có hệ thống xả đông tự động. Thay vì làm bằng vàng, hình tượng của chúng ta làm bằng chất sứ vừa bền vừa dễ lau chùi và chúng ta thực sự đã nâng niu thờ phụng chúng không khác gì hình tượng và cảm thấy rằng chúng ta không thể sống thiếu chúng. Chúng ta đã đi đến chỗ thờ lạy đồ vật, địa vị, danh tiếng, tiền bạc và một cuộc sống an toàn. Chúng ta cần nhớ rằng bất cứ điều gì xen vào giữa chúng ta và Đức Chúa Trời thì đó là sự thờ hình tượng.")]),n._v(" "),i("p",[n._v("Chúa Giê-xu đòi cầm quyền làm chủ trên tất cả những điều đó. Ngài muốn bạn giao nộp mọi sự liên quan đến đời sống xã hội, gia đình, công ăn việc làm cho Ngài. Ngài phải được dành chỗ đầu tiên trong mọi việc bạn làm, nói năng hay suy nghĩ, vì khi thực sự ăn năn, bạn sẽ hướng mọi sự về Ngài.")]),n._v(" "),i("p",[n._v('Chúa Giê-xu đã cảnh cáo rằng Ngài sẽ không thể nhận chúng ta vào thiên đàng nếu chúng ta không sẵn sàng buông bỏ mọi sự, cho đến khi chúng ta sẵn sàng từ bỏ mọi tội lỗi trong đời sống. Đừng cố gắng làm nửa vời. Đừng nói rằng, "Tôi sẽ bỏ một số tội lỗi trước và tiếp tục vướng mắc với một số tội khác. Tôi sẽ sống một phần cuộc đời cho Chúa và phần còn lại tôi sống cho những đam mê riêng." Chúa Giê-xu muốn chúng ta đầu phục một trăm phần trăm và một khi đã trao hết, Chúa sẽ ban thưởng cho chúng ta gấp ngàn lần. Nhưng xin chúng ta đừng mong đợi Chúa sẽ ban thưởng năm trăm lần khi chúng ta đầu phục năm mươi phần trăm! Đức Chúa Trời không hành động theo cách đó, Ngài đòi chúng ta giao nộp tất cả.')]),n._v(" "),i("p",[n._v("Khi bạn đã quyết định dứt khoát với tội lỗi, từ bỏ tội lỗi và giao nộp mọi sự cho Chúa Cứu Thế, bạn đã tiến tới một bước mới để có bình an với Đức Chúa Trời.")]),n._v(" "),i("p",[n._v('Tên cướp ăn năn trên thập tự giá nói với Chúa Giê-xu, "Lạy Chúa, xin nhớ đến tôi..." (Lu-ca 23:42). Từ ngữ tên cướp này dùng, "Lạy Chúa," hàm ý thái độ tùng phục Chúa Cứu Thế Giê-xu. Anh đã đầu phục Chúa hoàn toàn và thực sự hoán cải, và đây chính là kết quả của sự ăn năn thật. Hòa lòng với tác giả bài thánh ca, tội nhân ăn năn có thể hát như sau:')]),n._v(" "),i("p",[n._v("Tôi đầu phục Chúa Giê-xu hoàn toàn, trao tất cả mọi sự cho Ngài,\nTôi sẽ mãi mãi tin cậy, yêu mến Ngài, sống trong sự hiện diện của Ngài mỗi ngày.\nTôi hoàn toàn đầu phục Chúa Giê-xu, xin Chúa Cứu Thế khiến con trọn vẹn thuộc Ngài.\nXin cho con cảm nhận được sự hiện diện của Đức Thánh Linh để thực sự biết Ngài thuộc con.")]),n._v(" "),i("h2",{attrs:{id:"chuong-11-duc-tin-la-gi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-11-duc-tin-la-gi","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 11: Đức tin là gì?")]),n._v(" "),i("p",[i("strong",[n._v("Ấy là nhờ ân điển, bởi đức tin mà anh em được cứu, điều đó không phải đến từ anh em nhưng là sự ban cho của Đức Chúa Trời. (Cô-lô-se 2:8-9)")])]),n._v(" "),i("p",[n._v("Đến bây giờ bạn đã sẵn sàng đi bước kế tiếp trong việc tìm kiếm bình an với Chúa. Bây giờ bạn đã sẵn sàng từ bỏ cuộc đời tội lỗi cũ và quyết tâm thực hiện thay đổi này trong đời sống. Bạn không còn quay lưng lại với Đức Chúa Trời nữa nhưng đang tiến bước hướng về tình yêu, lòng thương xót và sự bảo vệ của Ngài. Bạn đã quyết định. Bạn đã hối cải, đã chọn con đường chính đáng, dù đó có thể là con đường cam go. Bạn đã chọn con đường Môi-se đi gần 3,500 năm trước, khi ông từ bỏ ngôi vua Ai-cập và quyết định chọn Đức Chúa Trời!")]),n._v(" "),i("p",[n._v("Khi được 40 tuổi, Môi-se đã phải bỏ trốn khỏi Ai-cập để bảo toàn sự sống. Bốn mươi năm sau ông trở lại lãnh đạo tuyển dân ra khỏi Ai-cập. Điều gì đã thay đổi? Môi-se đã làm một quyết định lớn trong đời và kết luận rằng đức tin và chân lý cùng đi với khó khăn và thống khổ vẫn hơn hẳn giàu có và danh vọng mà thiếu vắng tình yêu của Đức Chúa Trời. Trong lịch sử ít có người nào phải đối diện với quyết định khó khăn hơn quyết định của Môi-se.")]),n._v(" "),i("h3",{attrs:{id:"con-nguoi-cua-duc-tin"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#con-nguoi-cua-duc-tin","aria-hidden":"true"}},[n._v("#")]),n._v(" Con Người Của Đức Tin")]),n._v(" "),i("p",[n._v('Môi-se là một người có học vấn, một người giàu có và thế lực. Là con nuôi công chúa Pha-ra-ôn, ông quen nếp được mọi người tôn trọng, sống trong cảnh xa hoa với tất cả mọi đặc quyền. Ngôi vua Ai-cập, một đế quốc hùng cường, thịnh vượng vào bậc nhất đương thời ở trong tầm tay Môi-se. Nhưng Kinh Thánh ký thuật rằng, "Bởi đức tin, Môi-se lúc đã khôn lớn, bỏ danh hiệu mình là con trai của công chúa Pha-ra-ôn, đành cùng dân Đức Chúa Trời chịu hà hiếp, hơn là tạm hưởng sự vui sướng của tội lỗi: người coi sự sỉ nhục về Chúa Cứu Thế là quí hơn châu báu Ai-cập, vì người ngửa trông sự ban thưởng. Bởi đức tin, người lìa Ai-cập không sợ vua giận; vì người đứng vững như thấy Đấng không thấy được" (Hê-bơ-rơ 11:24-27). Phân đoạn Kinh Thánh này đề cập đến Môi-se sau bốn mươi năm trong sa mạc với Đức Chúa Trời - không phải một tên sát nhân trẻ tuổi hung hăng sợ chết chạy trốn Pha-ra-ôn hồi trước.')]),n._v(" "),i("p",[n._v('Chúng ta lưu ý Kinh Thánh nói rằng Môi-se "bỏ" và "từ chối" - đây chính là sự ăn năn thật. Kinh Thánh cũng bảo rằng Môi-se đã quyết định như thế là "bởi đức tin"! Đức tin chính là bước kế tiếp. Môi-se đi đến quyết định này không vào lúc xúc cảm vỡ bờ như một số các tâm lý gia coi là điều kiện cần thiết cho kinh nghiệm tôn giáo. Động cơ khiến Môi-se hành động như vậy cũng không phải do chán nản hay thất vọng. Môi-se không phải là một con nguời bất đắc chí hay thất cơ lỡ vận. Môi-se không chọn con đường của Đức Chúa Trời để bù cho những mất mát thiệt thòi của ông trong cuộc đời; ông cũng không trở lại với tôn giáo vì thấy cuộc đời buồn nản, vô vị. Ong không đến với Chúa vì lợi lộc hay để tìm vui thú, tìm chốn giải khuây.')]),n._v(" "),i("h3",{attrs:{id:"van-de-chon-lua"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#van-de-chon-lua","aria-hidden":"true"}},[n._v("#")]),n._v(" Vấn Đề Chọn Lựa")]),n._v(" "),i("p",[n._v("Tất cả những lập luận này và những lập luận khác nữa thường được đưa ra làm lý do để tìm kiếm đời sống với Chúa đều không có giá trị trong trường hợp Môi-se. Ong không bị buộc phải chạy trốn xác thịt hay ma quỉ. Ong bỏ đi do quyết định cá nhân. Chắc chắn Môi-se không phải là con người kém cỏi hay hèn yếu. Ong không phải là một đứa trẻ bám víu nương nhờ vào sự an toàn của trật tự xã hội. Ong không phải là một tiểu tốt đi tìm danh vọng. Ong không phải là một trong bất cứ điều gì mà những kẻ chế diễu tôn giáo gán ghép, bảo rằng vì những điều đó mà Môi-se phải cần có sự cứu rỗi. Những điều Môi-se có hơn cả những giấc mơ nhiều người mong ước, nhưng với một nhận định trưởng thành trong giai đoạn sung mãn nhất của cuộc đời, ông quay lưng lại với sự giàu có, địa vị, danh vọng để chọn đức tin nơi Đức Chúa Trời.")]),n._v(" "),i("p",[n._v('Mỗi lần tôi nghe người ta bảo rằng chỉ có những người tuyệt vọng, vô vọng, và chỉ những người thất cơ lỡ vận mới cần có sự an ủi của "tôn giáo", tôi liền nghĩ đến Môi-se.')]),n._v(" "),i("p",[n._v("Tôi có cơ hội nói chuyện với nhiều người về những nan đề tâm linh, tôi học được điều này, đó là khi những người khôn ngoan sáng suốt từ chối tiếp nhận Chúa Cứu Thế Giê-xu làm Chủ và làm Đấng Cứu Chuộc, họ từ chối không phải vì khám phá ra rằng giáo lý Cơ Đốc không hợp lý về phương diện trí tuệ, nhưng chỉ vì họ muốn tránh né những trách nhiệm và bổn phận của cuộc sống Cơ Đốc nhân. Điều ngăn cản họ đến với Chúa Cứu Thế không phải là trí óc sáng suốt, nhưng chính là tấm lòng yếu ớt của họ. Họ không sẵn lòng tùng phục và giao nạp mọi điều cho Chúa. Điều lý thú đáng để ý là hai nhân vật được Đức Chúa Trời sử dụng nhiều nhất trong Kinh Thánh (một trong Cựu Ước, một trong Tân Ước) lại là hai người học thức nhất: Môi-se và Phao-lô.")]),n._v(" "),i("p",[n._v("Môi-se xem xét kỹ lưỡng những đòi hỏi và bổn phận Đức Chúa Trời đưa ra cho ông. Năm bốn muơi tuổi, Môi-se chạy trốn như một kẻ sát nhân, đến năm tám mươi ông trở về làm lãnh tụ. Môi-se ý thức rằng nếu chọn Đức Chúa Trời, ông sẽ phải chấp nhận hy sinh những điều hầu hết mọi người ôm ấp, trân quí. Ong không suy xét vội vàng. Ong không đi đến những kết luận thiếu chín chắn do những thôi thúc bất ngờ hay do phản ứng của xúc cảm. Ong đoán biết những gì có thể gặp hiểm nguy và chỉ kết luận sau khi đã vận dụng mọi khả năng của một tâm trí sắc bén đã được tinh luyện. Lựa chọn cuối cùng của Môi-se không phải là một thí nghiệm có tính cách tạm thời. Ong không chọn đức tin như một biện pháp giai đoạn, nhưng do lòng tin quyết chín chắn với một mục tiêu không thay đổi, một lòng tin quyết không rúng động bởi những biến thiên của thuận cảnh hay của những thử thách lâu dài. Môi-se thận trọng đốt cả cầu lẫn thuyền là những phương tiện có thể khiến Môi-se triệt thoái từ vị trí mới. Trong biến động lớn Môi-se đối diện năm 80 tuổi, ông hoàn toàn phó thác chính mình cho Đức Chúa Trời, ở dưới sự điều động của Ngài, không giữ lại chút gì trong bất cứ thời gian hay hoàn cảnh nào.")]),n._v(" "),i("p",[n._v('Phẩm chất quyết định của Môi-se thật khác biệt với quyết định của tiểu sử gia nổi tiếng Gamaliel Bradford là người vào cuối đời đã viết rằng, "Tôi không dám đọc Tân Ước, sợ rằng sẽ đánh thức cơn bão lo lắng, nghi ngờ, hoảng sợ vì đã chọn lầm đường, đã trở thành kẻ phản bội một Đức Chúa Trời hiển hiện, đơn sơ."')]),n._v(" "),i("p",[n._v('Môi-se không có nỗi sợ hãi đó. Bạn cũng sẽ không sợ nếu với đức tin, bạn hết lòng phó thác chính mình cho Chúa Cứu Thế bây giờ và mãi mãi. Xin đừng đến với Chúa bảo rằng, "Tôi thử đi theo Chúa Cứu Thế trong một thời gian, nếu thấy được tôi sẽ tiếp tục, nhưng nếu không, tôi vẫn còn đủ thì giờ để chọn một một lối sống khác." Khi bạn đến với Chúa Cứu Thế, mọi cây cầu sau lưng bạn đều phải phá sập để đừng bao giờ nghĩ rằng bạn sẽ có ngày quay lại.')]),n._v(" "),i("h3",{attrs:{id:"roi-bo-chien-thuyen"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#roi-bo-chien-thuyen","aria-hidden":"true"}},[n._v("#")]),n._v(" Rời Bỏ Chiến Thuyền")]),n._v(" "),i("p",[n._v("Nhiều năm trước khi cánh con đại bàng hung hãn Rô-ma phủ bóng đe dọa trên thế giới thì những tay chiến sĩ liều lĩnh dưới quyền Sê-sa đã giương buồn đi chinh phạt Anh Quốc. Khi các chiến thuyền địch quân xuất hiện ở chân trời thì hàng ngàn người Anh can đảm tập trung trên các cao điểm để bảo vệ mảnh đất quê hương. Lúc đó họ rất ngạc nhiên khi thấy biển và thủy triều đã nhận chìm hầu hết các chiến thuyền La-mã và như vậy là đường triệt binh duy nhất của đạo quân xâm lăng liều lĩnh đã bị cắt đứt. Chúng chiến đấu một cách man dại vì không còn đường rút. Với tinh thần bất khuất đó, làm sao họ không chiến thắng! Vì vậy không có gì đáng ngạc nhiên khi ngôi làng nhỏ bé bên bờ sông Tiber đã trở thành Bà Hoàng của thế giới!")]),n._v(" "),i("p",[n._v('Cũng vậy, chúng ta sẽ không thể nào được Chúa Cứu Thế chấp nhận nếu không đầu phục hoàn toàn với lòng sùng mộ tuyệt đối. Phúc Âm Lu-ca 9:62 chép như sau: "Đức Chúa Giê-xu phán rằng: Ai đã tra tay cầm cầy còn ngó lại đàng sau, thì không xứng đáng với nước Đức Chúa Trời."')]),n._v(" "),i("p",[n._v("Khi đứng giữa ngã ba đường đời Môi-se đã có một lựa chọn rúng động linh hồn. Tâm trí quen phán đoán của ông cân nhắc tất cả các dữ kiện trước khi quyết định. Ong nhìn thật sâu, thật cẩn thận xuống từng con đường cho đến tận cùng. Ong xem xét tất cả từng điểm lợi điểm hại và lúc đó mới quyết định đặt lòng tin cậy và đức tin nơi Đức Chúa Trời.")]),n._v(" "),i("h3",{attrs:{id:"moi-se-dot-cau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#moi-se-dot-cau","aria-hidden":"true"}},[n._v("#")]),n._v(" Môi-se Đốt Cầu")]),n._v(" "),i("p",[n._v("Trước hết, ông nhìn xuống cuối con đường thênh thang, rạng rỡ, đầy quyền uy, xa xỉ, đầy trò vui, rượu say, tràn ngập những thứ trần gian coi là lạc thú. Đối với Môi-se, đây là con đường thật quen thuộc ông biết rõ, con đuờng ông đã đi suốt bốn mươi năm và biết rằng nó sẽ kết thúc ở chỗ hủy diệt và chỉ đưa đến hỏa ngục.")]),n._v(" "),i("p",[n._v("Rồi Môi-se nhìn xuống con đường kia, con đường hẹp và khó đi hơn nhiều. Ong thấy đầy những đau thương khổ ải, nhục nhã, thất vọng. Ong thấy đầy gian nguy, thử thách, buồn thảm và đau đớn, nhưng với đức tin, ông cũng thấy cả chiến thắng và phần thưởng là sự sống vĩnh hằng.")]),n._v(" "),i("p",[n._v("Một người kém trí phán đoán và ít kinh nghiệm hơn Môi-se rất có thể đã bị cám dỗ đi con đường thứ nhất. Ai-cập lúc đó là một cường quốc hùng mạnh nhất hoàn cầu, trấn giữ đồng bằng sông Nile màu mỡ, là vựa lúa của thế giới. Quân đội Ai-cập không thể đánh bại, các trường cao đẳng và đại học Ai-cập đưa ra những mẫu mực con người hàng bao nhiêu thế kỷ sau vẫn phải theo!")]),n._v(" "),i("p",[n._v("Rất ít người trong chúng ta bị đòi phải từ bỏ nhiều như Đức Chúa Trời đòi Môi-se từ bỏ. Ít người trong chúng ta phải đứng trước nhiều cám dỗ đa diện như Môi-se rồi bị đòi phải chống lại những cám dỗ đó. Ít người trong chúng ta phải chứng kiến những thú vui trần tục phơi bầy trước mắt, và Kinh Thánh cũng xác nhận rằng ngay cả tội lỗi cũng có những thú vui của nó, những thú vui tạm bợ, mau qua và cũng không lưu lại một dấu vết an ủi nào.")]),n._v(" "),i("p",[n._v("Chọn Đức Chúa Trời, Môi-se đã chấp nhận hy sinh rất lớn, nhưng cũng đã được phần thưởng lớn không kém. Trong thời Môi-se những tài sản lớn rất hiếm cho nên cũng rất ít người có cơ hội như Môi-se, trở thành người giàu nhất thế giới.")]),n._v(" "),i("h3",{attrs:{id:"su-giau-co-cua-tran-gian"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#su-giau-co-cua-tran-gian","aria-hidden":"true"}},[n._v("#")]),n._v(" Sự Giàu Có Của Trần Gian")]),n._v(" "),i("p",[n._v("Ngày nay nhiều người có thể thâu gom được những tài sản kếch sù. Năm 1923 (thời gian việc tích lũy tài sản là mối quan tâm hàng đầu trên đất nước này), có một nhóm các nhà tài phiệt thành công nhất thế giới họp nhau tại Khách sạn Edgewater Beach Hotel ở Chicago. Ngay cả đối với thời kỳ cực thịnh của thập niên 20, buổi họp đó cũng là một tập hợp quyền lực và tài sản đáng nể. Ngồi chung trong cái bàn lớn đó là chủ tịch một công ty thép độc lập lớn nhất thế giới, chủ tịch công ty tiện ích lớn nhất, chủ tịch Ngân Hàng Chứng Khoán Nữu Ước, một nhân viên nội các của Tổng Thống Mỹ, chủ tịch Ngân Hàng Giao Dịch Quốc Tế, một nhà kinh doanh lớn nhất Wall Street, và một nhân vật nữa là người đứng đầu một công ty độc quyền lớn nhất thế giới. Tất cả những nhân vật trên kiểm soát một số tài chánh nhiều hơn cả ngân khố Hoa Kỳ! Bất cứ em học sinh nào cũng biết những câu chuyện thành công của họ. Họ là những mẫu mực cho người khác noi theo. Họ là những nhà tài chánh và kỹ nghệ gia đứng đầu của nước Mỹ!")]),n._v(" "),i("p",[n._v("Năm 1923 những câu chuyện được quảng bá rộng rãi về những nhân vật này thật phấn khích, đầy thích thú, kích thích trí tưởng tượng, khơi dậy lòng ganh đua, thôi thúc nhiều người cố gắng vươn tới như họ! Nhưng trong năm 1923, câu chuyện đời họ mới chỉ được kể có một nửa còn những chương kết thúc chưa viết xong.")]),n._v(" "),i("p",[n._v("Vào lúc tám nhân vật trên ngồi với nhau trong khách sạn tại Chicago, mỗi người trong đời sống riêng, đang đứng ở chỗ của Môi-se trước ngã ba đường. Hai hướng đi trải dài trước mặt mỗi người, nhưng có lẽ đó là những lối đi họ không thấy, những lối đi họ không quan tâm, và chắc chắn đó cũng không phải là những lối đi họ chọn, và hôm nay tiểu sử những người này đã hoàn tất. Hôm nay chúng ta đã biết những chương cuối, chúng ta có thể ôn lại cuộc đời họ như chúng ta đã ôn lại cuộc đời Môi-se để xem đâu là cuộc đời thành công nhất, đâu là cuộc đời khôn ngoan nhất.")]),n._v(" "),i("p",[n._v('Charles Schwab, chủ tịch công ty thép sống những năm cuối đời trong nợ nần và chết không một xu dính túi. Arthur Cutten, thương gia lúa mì lớn nhất đã chết khánh tận ở ngoại quốc. Richard Whitney, chủ tịch Ngân Hàng Chứng Khoán Nữu Ước ngồi tù ở cải hối thất Sing Sing. Albert Fall, nhân viên nội các bị tù nhưng được ân giảm để có thể về chết ở nhà. Jessie Livermore, "con gấu" của Wall Street; Leon Frazer, Chủ tịch Ngân Hàng Giao Dịch Quốc Tế; và Ivon Kreuger, người đứng đầu một công ty độc quyền lớn nhất thế giới, cả ba đều đã tự sát!')]),n._v(" "),i("p",[n._v("Những người này có tất cả tiền tài, quyền lực, danh vọng, ưu quyền, trí thông minh và học vấn, nhưng tất cả đều thiếu một thuộc tính đem lại ý nghĩa đích thực và mục tiêu cho đời sống. Họ thiếu một thuộc tính thiết yếu đối với giáo lý và tác phong Cơ Đốc, một thuộc tính đưa đến sự hoán cải và khiến cho sự tái sinh trở thành thực hữu. Họ không chịu tin! So sánh cuộc đời những người này với cuộc đời những giáo sĩ đã bỏ mọi sự theo Chúa, là những người có thể chết trong đớn đau, trong cảnh thanh bần, nhưng đó là cái chết có ý nghĩa!")]),n._v(" "),i("p",[n._v("Những nhân vật giàu có trên không có đức tin, hoặc giả có đức tin đi chăng nữa thì họ đã không chịu hành động thể hiện đức tin đó. Những chương cuối cuộc đời họ sẽ khác biết chừng nào nếu họ coi đức tin nơi Chúa Cứu Thế là một trong những tài sản quí.")]),n._v(" "),i("h3",{attrs:{id:"moi-se-quay-lung-voi-cua-cai-tran-gian"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#moi-se-quay-lung-voi-cua-cai-tran-gian","aria-hidden":"true"}},[n._v("#")]),n._v(" Môi-se Quay Lưng Với Của Cải Trần Gian")]),n._v(" "),i("p",[n._v("Môi-se đã từ bỏ cuộc sống sang giàu trong Ai-cập là vì đức tin. Chính đức tin đã giúp ông biết rằng dù phải chịu bao thiệt thòi suốt những năm còn lại của cuộc đời trần gian, đến cuối cùng, ông sẽ nhận được phần thưởng lớn hơn hết là sự sống vĩnh hằng.")]),n._v(" "),i("p",[n._v('Những người như Cutten và Schwab có thể nghĩ Môi-se là một kẻ dại, bảo rằng, "Một con chim trong tay hơn hai con trong bụi." Nếu nói được với Môi-se họ sẽ nói như sau: "Này Môi-se, ông biết tất cả những gì ông có trong Ai-cập và biết một người với đầu óc như ông có đủ mánh lới để thâu đoạt tiền của và quyền lực. Nếu biết cách, ông có thể đưa Ai-cập vào vị trí điều khiển thế giới, triệt hạ tất cả những tiểu quốc khác, chấm dứt mọi cạnh tranh để toàn quyền thao túng." Đó là những điều họ có thể nói với Môi-se, vì đó là cách họ suy nghĩ, cách họ hành xử và cũng là cách nhiều người trong vòng họ đã dùng để thâu đoạt, tích lũy tài sản. Họ sẽ cười vào mũi người nào nhờ cậy Đức Chúa Trời và đặt đức tin nơi Chúa Cứu Thế Giê-xu. Họ sẽ bảo rằng, "Đức tin không phải là cách làm ăn khéo léo, khôn ngoan."')]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng đức tin là phương cách duy nhất đến với Đức Chúa Trời, "vì người đến gần Đức Chúa Trời phải tin rằng có Đức Chúa Trời và Ngài là Đấng hay thưởng cho kẻ tìm kiếm Ngài" (Hê-bơ-rơ 11:6b). Kinh Thánh cũng dạy rằng đức tin là điều làm vui lòng Đức Chúa Trời hơn nất cứ điều gì khác, "Không có đức tin thì không thể nào ở cho đẹp ý Ngài" (He-bơ-rơ 11:6).')]),n._v(" "),i("p",[n._v("Con người trên khắp thế giới đang tự hành hạ chính mình, mặc những kiểu áo quần dị hợm, ăn chay, ép xác, bỏ nhiều thì giờ cầu nguyện, khắc khổ thân thể mong nhờ đó sẽ được chấp nhận trước mặt Đức Chúa Trời. Tất cả những điều này có thể cũng là những điều tốt, nhưng điều lớn nhất chúng ta có thể làm để được vui lòng Đức Chúa Trời là tin cậy Ngài.")]),n._v(" "),i("p",[n._v("Tôi có thể khen ngợi bạn tôi đủ điều, nhưng sau tất cả những mỹ từ kia tôi thổ lộ rằng tôi không tin anh ta thì tất cả những câu khen ngợi hoa mỹ trên hoàn toàn vô ích. Tôi đã nâng anh ta lên chỉ là để vùi dập anh ta xuống.")]),n._v(" "),i("h3",{attrs:{id:"duc-tin-thiet-yeu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#duc-tin-thiet-yeu","aria-hidden":"true"}},[n._v("#")]),n._v(" Đức Tin Thiết Yếu")]),n._v(" "),i("p",[n._v('Cách tốt nhất chúng ta có thể làm vui lòng Đức Chúa Trời là tin Lời Ngài. Chúa Cứu Thế gần như đã nài nỉ các môn đệ thể hiện đức tin, khi Chúa bảo rằng, "Khi ta nói rằng ta ở trong Cha và Cha ở trong ta, thì hãy tin ta, bằng chẳng, hãy tin bởi công việc ta" (Giăng 14:11). Kinh Thánh công bố rằng đức tin là điều cần thiết tuyệt đối. Bạn sẽ hỏi, "Nếu đức tin cần thiết như thế thì đức tin là gì? Xin giải thích và cho định nghĩa của đức tin. Làm sao biết tôi có một đức tin đúng đắn? Tôi phải có lượng đức tin nào?" Tôi sẽ lần lượt giải thích những vấn nạn trên.')]),n._v(" "),i("p",[n._v('Kinh Thánh nhiều chỗ dạy rằng chúng ta chỉ có thể có sự cứu rỗi qua đức tin:\n"Hãy tin Chúa Cứu Thế Giê-xu thì ngươi và cả nhà sẽ được cứu rỗi" (Công-vụ 16:31).\n"Nhưng hễ ai nhận Ngài thì Ngài ban cho quyền phép trở nên con cái Đức Chúa Trời, là ban cho những kẻ tin danh Ngài" (Giăng 1:12).\n"Còn kẻ chẳng làm việc chi hết nhưng tin Đấng xưng người có tội là công bình, thì đức tin của kẻ ấy kể là công bình cho mình" (Rô-ma 4:5).\n"Vậy chúng ta đã được xưng công bình bởi đức tin, thì được hòa thuận với Đức Chúa Trời, bởi Chúa Cứu Thế Giê-xu, Chúa chúng ta" (Rô-ma 5:1).\n"Về phần chúng ta, nào phải là kẻ lui đi cho hư mất đâu, bèn là kẻ giữ đức tin cho linh hồn được cứu rỗi" (Hê-bơ-rơ 10:39).\n"Vả, ấy là nhờ ân điển, bởi đức tin mà anh em được cứu, điều đó không phải đến từ anh em, bèn là sự ban cho của Đức Chúa Trời" (Ê-phê-sô 2:8).')]),n._v(" "),i("h3",{attrs:{id:"ban-chat-duc-tin"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#ban-chat-duc-tin","aria-hidden":"true"}},[n._v("#")]),n._v(" Bản Chất Đức Tin")]),n._v(" "),i("p",[n._v('Chúng ta có thực sự được cứu bởi đức tin không? Không, chúng ta được cứu nhờ ân sủng, qua đức tin. Đức tin chỉ là phương tiện qua đó chúng ta tiếp nhận ân sủng của Đức Chúa Trời. Đức tin là cánh tay vươn dài để nhận món quà tình yêu của Ngài. Thư Hê-bơ-rơ 11:1 viết như sau, "Đức tin là sự biết chắc vững vàng của những điều đang hy vọng, là bằng chứng của những điều chưa thấy." Weymouth đã dịch như sau giúp chúng ta dễ hiểu hơn, "Đức tin là sự bảo đảm chắc chắn điều chúng ta đang hy vọng, là lòng tin quyết tính chất thực hữu của những điều chúng ta không thấy." Đức tin theo nghĩa đen là "bỏ, đầu hàng hay ký thác." Đức tin là lòng tin cậy hoàn toàn.')]),n._v(" "),i("p",[n._v("Tôi chưa bao giờ đặt chân lên Bắc Cực, tuy nhiên tôi tin rằng có Bắc Cực. Làm sao tôi biết? Tôi đọc sách lịch sử nói về Bắc Cực, tôi thấy bản đồ Bắc Cực, và tôi tin những người viết những quyển sách đó.Tôi chấp nhận bằng đức tin.")]),n._v(" "),i("p",[n._v('Kinh Thánh dạy, "Đức tin đến là do nghe lời Đức Chúa Trời được rao giảng" (Rô-ma 10:17). Chúng ta tin những gì Đức Chúa Trời nói về sự cứu rỗi và chúng ta chấp nhận những lời đó không thắc mắc.')]),n._v(" "),i("p",[n._v('Martin Luther dịch Hê-bơ-rơ 11: 27 như sau, "vì Môi-se bám chặt lấy Đấng không thấy được như thể ông đã nhìn thấy Ngài."')]),n._v(" "),i("p",[n._v("Đức tin không phải là một phẩm tính đặc biệt, bí mật mà chúng ta phải cố sức thâu đạt. Chúa Giê-xu dạy rằng chúng ta phải trở thành trẻ thơ, và như trẻ thơ tin cậy cha mẹ thể nào, chúng ta cũng phải tin cậy Đức Chúa Trời như thế.")]),n._v(" "),i("p",[n._v("Giả dụ tôi đang lái xe trên đường với vận tốc 75 cây số một giờ, khi lên đỉnh dốc, có bao giờ tôi thắng gấp, ra khỏi xe, đi bộ tới phía trước để kiểm soát tình trạng con đường hay không? Tất nhiên là không. Tôi tin tưởng sở công chánh của tiểu bang, và tôi cứ tiếp tục lái xe bình thường ở tốc độ 75 cây số/giờ, tin chắc rằng con đường sẽ còn tiếp tục dẫn tới trước, dù tôi không thấy. Tôi chấp nhận bằng đức tin. Đức tin nơi Chúa Cứu Thế cũng tương tự như vậy!")]),n._v(" "),i("h3",{attrs:{id:"ba-phuong-dien-cua-duc-tin"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#ba-phuong-dien-cua-duc-tin","aria-hidden":"true"}},[n._v("#")]),n._v(" Ba Phương Diện Của Đức Tin")]),n._v(" "),i("p",[n._v("Cũng như sự ăn năn, đức tin liên quan đến ba điều trọng yếu. Trước hết, phải có kiến thức về những điều Đức Chúa Trời phán dạy, và đó là lý do tại sao bạn phải đọc Kinh Thánh. Đó là lý do tại sao bạn cần biết một số điều Kinh Thánh dạy về sự cứu rỗi và linh hồn. Chỉ cần biết rằng bạn là một tội nhân và Chúa Cứu Thế Giê-xu chịu chết thay cho bạn được kể là biết đủ. Chỉ biết câu Kinh Thánh Giăng 3:16 cũng kể là đủ. Nhiều người đã từng được hoán cải với một kiến thức ít hơn như thế nữa! Tuy nhiên, đối với một điều quan trọng như đức tin, bạn cần hiểu biết càng nhiều càng tốt, và chỗ duy nhất bạn có thể học biết về sự cứu rỗi là Kinh Thánh!")]),n._v(" "),i("p",[n._v('Nhiều người từng nói, "Kinh Thánh khó hiểu quá cho nên tôi không muốn đọc." Đây không phải là thái độ khôn ngoan. Tôi không hiểu nhiều điều trong Kinh Thánh vì tâm trí hữu hạn của tôi không bao giờ có thể hiểu hết về cái vô hạn. Tôi không biết hết về vô tuyến truyền hình, nhưng đó không phải là lý do khiến tôi từ chối mở máy xem đài. Tôi chấp nhận bằng đức tin.')]),n._v(" "),i("p",[n._v('Đức Chúa Trời không đòi hỏi một điều bất khả. Trong sự hoán cải, Ngài không đòi bạn phải buông mình nhảy vào đêm tối. Việc bạn đặt đức tin nơi Chúa Cứu Thế dựa trên một bằng chứng vững chắc nhất trên thế giới, đó là Kinh Thánh. Dù bạn không hiểu hết Kinh Thánh, bạn vẫn có thể chấp nhận Kinh Thánh dựa trên gía trị tự tại của Kinh Thánh vì đó là lời Đức Chúa Trời phán. Một trong những cuộc tấn công đầu tiên của ma quỉ là khiến con người nghi ngờ Lời Đức Chúa Trời: "Mà chi! Đức Chúa Trời há có phán dặn các ngươi..." (Sáng Thế Ký 3:1). Nếu bạn khởi sự nghi ngờ và đặt dấu hỏi vào Lời Đức Chúa Trời thì bạn đang gặp rắc rối. Bạn phải biết mình là một tội nhân. Bạn phải biết Chúa Cứu Thế đã chịu chết thay thế cho bạn và Ngài đã phục sinh để bạn được kể là công chính. Sự chết, chôn và phục sinh của Chúa Cứu Thế Giê-xu là trái tim của Tin Lành. Đây là những điều tối thiểu bạn phải tin và chấp nhận để được hoán cải.')]),n._v(" "),i("p",[n._v('Thứ hai, đức tin cũng liên hệ đến xúc cảm. Kinh Thánh dạy rằng, "Kính sợ Chúa Hằng Hữu là khởi đầu sự khôn ngoan" (Châm Ngôn 1:7). Phao-lô nói, "tình thương của Chúa Cứu Thế cảm động chúng tôi" (2 Cô-rinh-tô 5:14). Mong ước, yêu thương, kính sợ - tất cả đều là xúc cảm. Xúc cảm không thể bị tách rời khỏi cuộc sống và không một người thông minh nào bảo rằng "Chúng ta hãy dẹp hết cảm xúc đi!" Loại bỏ mọi cá tính khỏi những xúc cảm sâu đậm là điều bất khả. Chúng ta không thể tưởng tượng nổi có một cuộc sống nào lại thiếu tính chất ấm áp của xúc cảm, giả dụ như sinh hoạt trong một gia đình mọi người đều cư xử với nhau đúng bổn phận nhưng thật lạnh lùng, hay chúng ta thử tưởng tượng có một thanh niên ngỏ ý cầu hôn với một thiếu nữ sau khi giải thích cho cô ấy biết rằng anh ta không có một chút rung động nào đối với cô ta cả!')]),n._v(" "),i("p",[n._v('Giống như Tiến sĩ Sangster từng nói, "Tôn giáo cũng có cùng nguyên tắc này. Nếu sứ giả của Đức Chúa Trời đi ra công bố ân huệ của Vua: tha thứ dồi dào và ban phước dư dật, nhưng nghiêm cấm việc đưa bất cứ niềm vui nào vào lời công bố cũng như vào việc tiếp nhận, thì đây là một điều không thể có được."')]),n._v(" "),i("p",[n._v("Trong lòng sẽ có sự giằng co, và xúc cảm có thể thay đổi theo kinh nghiệm tôn giáo. Một số người theo chủ trương khắc kỷ, những người khác bộc lộ tình cảm ra bên ngoài, dù thế nào, xúc cảm vẫn có đó.")]),n._v(" "),i("p",[n._v("Khi thủ tướng Churchill đọc những bài diễn văn lớn cho dân Anh trong thời chiến, ông đưa ra những lập luận vững chắc, nhưng đồng thời, ông cũng làm cho thính giả cảm xúc theo ông. Tôi nhớ có lần được nghe ông diễn thuyết tại Glassglow. Ong thách thức lý trí tôi nhưng ông cũng làm tôi cảm thấy muốn đứng bật dậy hô hào cổ võ và phất cờ! Khi bạn yêu mến Chúa Giê-xu, xúc cảm của bạn không thể nào không được khơi dậy.")]),n._v(" "),i("p",[n._v('Phương diện thứ ba của đức tin và cũng là phương diện quan trọng hơn hết đó là ý chí. Chúng ta có thể coi ba khía cạnh này như ba nhân vật - một người là "Lý trí", người thứ hai là "Xúc cảm" và người thứ ba là "Ý chí". Lý trí cho biết Tin Lành hợp lý. Xúc cảm áp lực trên Ý chí bảo rằng. "Tôi yêu mến Chúa Cứu Thế," hay "Tôi sợ sự phán xét." Người đứng giữa mệnh danh là Ý chí, đóng vai trò trọng tài. Anh ta ngồi đó chống tay dưới cầm suy nghĩ, cố gắng quyết định. Chính ý chí là yếu tố quyết định cuối cùng có tính cách chắc chắn, lâu bền. Người ta có thể tin bằng lý trí với tất cả thiện cảm trong tâm hồn nhưng vẫn chưa thực sự hoán cải để trở thành môn đồ thật của Chúa Cứu Thế. Đức tin thật phải kèm theo hành động, "Đức tin không có việc làm là đức tin chết" (Gia-cơ 2: 20).')]),n._v(" "),i("h3",{attrs:{id:"mot-thi-du-ve-duc-tin"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-thi-du-ve-duc-tin","aria-hidden":"true"}},[n._v("#")]),n._v(" Một Thí Dụ Về Đức Tin")]),n._v(" "),i("p",[n._v('Mấy năm trước tôi có nghe kể lại về một nhà làm xiếc đẩy chiếc xe cút kít trên một sợi dây cáp giăng ngang qua sông Niagara. Hàng ngàn người bên dưới hoan nghênh cổ võ. Ong ta bỏ lên xe một bao cát 200 cân Anh, đẩy qua rồi đẩy lại. Sau đó ông hỏi đám đông, "Có bao nhiêu người trong số quí vị tin rằng tôi có thể chở một người đẩy ngang qua sợi dây cáp này?" Mọi người reo hò ủng hộ, trong đó có một người đứng hàng đầu tin tưởng và cổ võ mạnh mẽ hơn cả. Nhà biểu diễn chỉ vào ông giáo sư đó tuyên bố, "Mời ông lên xe!"')]),n._v(" "),i("p",[n._v("Bạn có thể tưởng tượng giây phút kế tiếp là những giây phút hổ thẹn đến chừng nào cho vị giáo sư kia không! Ong ta thực sự tin vào khả năng nhà biểu diễn. Ong ta công bố đức tin và cũng nghĩ rằng mình tin, nhưng lại không muốn ngồi vào chiếc xe một bánh đó!")]),n._v(" "),i("p",[n._v("Đối với Chúa Giê-xu cũng vậy. Rất nhiều người bảo rằng họ tin Ngài, nhiều người bảo rằng họ theo Ngài, nhưng không bao giờ dám ngồi vào xe cút kít! Họ chưa bao giờ thực sự cam kết phó thác cả trăm phần trăm cuộc đời cho Ngài.")]),n._v(" "),i("p",[n._v('Có nhiều người hỏi rằng, "Như vậy lượng đức tin tôi cần có là bao nhiêu?" Chúa Giê-xu bảo rằng chỉ cần có "đức tin bằng hạt cải."')]),n._v(" "),i("p",[n._v('Người khác lại hỏi, "Phải có loại đức tin nào?" Đây không phải là vấn đề loại đức tin đặc biệt, vì chỉ có một loại đức tin. Chính đối tượng của đức tin mới quan trọng. Đối tượng đức tin của bạn là gì? Đối tượng đó phải là Chúa Cứu Thế Giê-xu. Không phải đức tin nơi nghi thức, đức tin nơi các sinh tế, đức tin vào luân lý, đức tin vào chính mình hay vào bất cứ điều gì khác ngoài Chúa Cứu Thế Giê-xu!')]),n._v(" "),i("p",[n._v("Kinh Thánh dạy rằng đức tin tự thể hiện qua ba phương thức. Đức tin thể hiện trong giáo lý, nghĩa là trong điều bạn tin. Đức tin thể hiện trong sự thờ phượng, nghĩa là trong sự tương thông với Đức Chúa Trời và sự thông công với Hội thánh. Đức tin cũng tự thể hiện trong đời sống luân lý, nghĩa là cách bạn sống và cư xử hàng ngày. Chúng ta sẽ bàn trong những chương khác.")]),n._v(" "),i("p",[n._v('Kinh Thánh cũng dạy rằng đức tin không chấm dứt sau khi một người đã đặt lòng tin nơi Chúa Cứu Thế để nhận sự cứu rỗi, nhưng cứ tiếp tục tiến tới. Đức tin tăng trưởng, lúc đầu có thể yếu ớt, nhưng sẽ mạnh mẽ hơn khi bạn khởi sự đọc Kinh Thánh, cầu nguyện, đi nhà thờ và kinh nghiệm sự thành tín của Đức Chúa Trời trong cuộc sống Cơ Đốc nhân. Sau khi bạn đã ăn năn tội, tiếp nhận Chúa Giê-xu bằng đức tin, bạn phải tin cậy, phó thác cho Ngài giữ gìn bạn, thêm sức cho bạn, nâng đỡ bạn, bảo tồn bạn. Bạn sẽ ngày càng học biết hơn cách thức tùy thuộc nơi Chúa Cứu Thế trong mọi nhu cầu, đối phó với mọi hoàn cảnh và mọi thử thách. Bạn sẽ học nói được như Phao-lô, "Tôi đã bị đóng đinh vào thập tự giá với Chúa Cứu Thế, mà tôi sống, không phải là tôi sống nữa nhưng Chúa Cứu Thế sống trong tôi. Nay tôi còn sống trong thân xác, là sống trong đức tin nơi Con Đức Chúa Trời, là Đấng đã yêu tôi và đã phó chính mình Ngài vì tôi" (Ga-la-ti 2: 20).')]),n._v(" "),i("p",[n._v("Khi bạn đã có đức tin để được cứu rỗi nơi Chúa Cứu Thế Giê-xu, bạn đã bước thêm được một bước nữa tiến đến cuộc sống bình an với Chúa.")]),n._v(" "),i("h2",{attrs:{id:"chuong-12-nguoi-cu-va-nguoi-moi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-12-nguoi-cu-va-nguoi-moi","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 12: Người cũ và người mới")]),n._v(" "),i("p",[i("strong",[n._v('"Nếu một người không sinh lại thì không thể thấy nước Đức Chúa Trời". (Giăng 3:3)')])]),n._v(" "),i("p",[n._v('Giá như tôi có thể đến nhà bạn, ngồi trong phòng khách tâm tình, có lẽ bạn sẽ thú nhận như sau, "Lòng tôi đang rối như tơ vò, không biết nghĩ sao. Tôi đã vi phạm pháp luật của Đức Chúa Trời. Tôi đã sống ngược hẳn lại với giới răn Ngài. Tôi tưởng mình vẫn có thể sống an nhiên không cần Ngài giúp đỡ. Tôi cố sống theo những luật lệ tự đặt ra nhưng đã thất bại. Những bài học đắng cay tôi đã học là qua những kinh nghiệm bi thảm và đau khổ. Mất gì để được sinh lại tôi cũng chịu! Mất gì để để được trở về làm lại từ đầu tôi cũng sẵn lòng. Nếu trở lại được thì con đường đời tôi sẽ khác biết bao nhiêu!"')]),n._v(" "),i("p",[n._v("Nếu những lời này cũng làm rung động tâm hồn bạn, nếu nó là âm hưởng của những suy nghĩ đã từng thoáng qua tâm trí bạn, thì tôi xin báo cho bạn một tin thật vui. Chúa Giê-xu tuyên bố rằng bạn có thể được sinh lại! Bạn có thể có một khởi đầu tốt đẹp hơn và hoàn toàn mới mẻ như điều bạn cầu mong. Bạn có thể bỏ đi cái bản ngã tội lỗi, nhuốc nhơ cũ để bước tới trong một con người mới, một con người trong sạch, an bình mà bao nhiêu tội ô đã được tẩy sạch.")]),n._v(" "),i("h3",{attrs:{id:"loi-thoat"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#loi-thoat","aria-hidden":"true"}},[n._v("#")]),n._v(" Lối Thoát")]),n._v(" "),i("p",[n._v("Dù quá khứ bạn có nhơ nhớp đến đâu, hiện tại của bạn có rắc rối đến đâu và tương lai bạn có vô vọng đến đâu, thì vẫn còn lối thoát. Vẫn còn một lối thoát an toàn, chắc chắn, vĩnh viễn, và chỉ có một lối thoát duy nhất mà thôi! Bạn chỉ có một lựa chọn duy nhất. Bạn chỉ có một con đường duy nhất để theo, thay vì con đường đau thương, vô vọng bạn đã trải qua.")]),n._v(" "),i("p",[n._v("Hoặc bạn có thể cứ tiếp tục sống trong khốn khổ, không thỏa lòng, sợ hãi, không hạnh phúc, bất mãn với chính mình và với đời mình hay bạn có thể quyết định ngay giờ này rằng bạn muốn được sinh lại. Ngay bây giờ bạn có thể quyết định xóa sạch tội lỗi quá khứ, bắt đầu một khởi sự mới mẻ, đúng đắn. Ngay bây giờ bạn có thể quyết định trở thành con người như Chúa Giê-xu đã hứa.")]),n._v(" "),i("h3",{attrs:{id:"lam-sao-toi-tim-duoc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#lam-sao-toi-tim-duoc","aria-hidden":"true"}},[n._v("#")]),n._v(" Làm Sao Tôi Tìm Được?")]),n._v(" "),i("p",[n._v('Câu hỏi hợp lý tiếp theo bạn có thể đặt ra là, "Làm thế nào tôi có sự tái sinh này? Làm sao tôi có thể được sinh lại? Làm sao tôi có thể khởi sự từ đầu?" Đây là câu Ni-cô-đem đã hỏi Chúa Giê-xu dưới bầu trời phương Đông vào một đêm cách đây hai ngàn năm trước. Tuy nhiên, sự tái sinh mang nhiều ý nghĩa phong phú hơn chứ không chỉ đơn thuần là một khởi đầu mới mẻ, một sự cải cách hay lật qua một trang mới. Như chúng ta đã thấy, Kinh Thánh dạy rằng lần đầu bạn được sinh vào thế giới vật lý nhưng bản chất tâm linh của bạn đã sinh ra trong tội lỗi. Kinh Thánh tuyên bố rằng bạn "chết trong gian ác và tội lỗi" (Ê-phê-sô 2:1).')]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng không có một điều gì trong bản chất hư hoại và tội lỗi của bạn có thể tạo ra sự sống. Đang đắm đuối trong tội lỗi, bạn không thể nào sống một cuộc đời công chính. Nhiều người chưa được tái sinh nhưng vẫn cố gắng xây dựng cuộc sống thánh khiết, thiện lành, chính trực. Họ không thể nào thành công được vì một xác chết không thể tạo ra bất cứ điều gì. Kinh Thánh dạy rằng "tội lỗi khi đã thành sẽ đem lại sự chết" (Gia-cơ 1:15). Về phương diện thuộc linh tất cả chúng ta đều chết.')]),n._v(" "),i("p",[n._v('Bản chất cũ của bạn không thể phục vụ Đức Chúa Trời. Kinh Thánh dạy rằng, "Con người thiên nhiên không thể nhận được những sự thuộc về Thánh Linh của Đức Chúa Trời ... vì không thể hiểu được" (I Cô-rinh-tô 2:14). Trong tình trạng tự nhiên, chúng ta thực sự thù nghịch với Đức Chúa Trời. Chúng ta không tùng phục luật pháp của Ngài mà cũng không thể tùng phục được như Rô-ma 8:7 khẳng định.')]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng bản chất cũ của chúng ta hoàn toàn hư hỏng, "từ bàn chân cho đến đỉnh đầu không có chỗ nào lành, đầy những vết thương, vết xưng cùng lằn mới" (Ê-sai 1:6). Giê-rê-mi 17:9 cho biết, "Lòng người ta dối trá hơn mọi vật, và rất xấu xa: ai có thể biết được?" Lòng người hư hoại, ở dưới sự thống trị của sự tham dục đầy gian trá.')]),n._v(" "),i("p",[n._v("Kinh Thánh dạy rằng bản chất cũ của chúng ta là bản chất duy ngã không có khả năng cải tiến hay canh tân và cũng theo lời dạy của Kinh Thánh, khi chúng ta được tái sinh, chúng ta vứt bỏ con người cũ chứ không giữ lại để sửa đổi hay vá víu. Bản chất cũ phải bị loại trừ, không thể cải thiện. Chúa Giê-xu dạy rằng rửa bên ngoài chén và mâm không thể làm sạch được bên trong.")]),n._v(" "),i("h3",{attrs:{id:"ban-phai-duoc-sinh-lai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#ban-phai-duoc-sinh-lai","aria-hidden":"true"}},[n._v("#")]),n._v(" Bạn Phải Được Sinh Lại!")]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng nếu không kinh nghiệm sự tân sinh, chúng ta không thể vào nước thiên đàng. Về việc này Chúa Giê-xu khẳng định, "Các ngươi phải được sinh lại" (Giăng 3:7). Vấn đề này không có gì mơ hồ, cũng không phải là chuyện tùy ý. Người nào muốn vào thiên đàng phải được tái sinh.')]),n._v(" "),i("p",[n._v('Sự cứu rỗi không chỉ là tu sửa bản ngã nguyên thủy nhưng là một bản chất mới được Đức Chúa Trời tạo dựng trong sự công chính và thánh khiết chân thật. Sự tái tạo không chỉ là thay đổi bản chất hay thay đổi tấm lòng. Được tái sinh không phải là được thay đổi mà là được tái tạo, được trỏ thành một nòi giống mới. Đó là sự sinh ra lần thứ hai. "Các con phải được sinh lại."')]),n._v(" "),i("p",[n._v('Đức Chúa Trời không chấp nhận bất cứ điều gì thuộc bản chất cũ, vì nó không toàn thiện. Bản chất cũ quá yếu đuối không thể đi theo Chúa Cứu Thế. Thánh Phao-lô bảo rằng "Anh em không thể làm được điều mình muốn" (Ga-la-ti 5:17). Với bản tính xác thịt cũ, con người không thể phục vụ Đức Chúa Trời. Gia-cơ hỏi, "Có lẽ nào một cái suối kia đồng một mạch mà ra cả nước ngọt và nước đắng sao? Hỡi anh em, cây vả có ra trái ô-liu được, cây nho có ra trái vả được chăng?" (Gia-cơ 3:11,12). Con người cũ được mô tả trong Rô-ma như sau, "Họng chúng nó như huyệt mả mở ra; chúng dùng lưỡi mình để phỉnh gạt; Dưới môi chúng nó có nọc rắn hổ mang. Miệng chúng nó đầy những lời nguyền rủa và cay đắng. Chúng nó có chân lẹ làng để làm đổ máu. Trên đường lối chúng nó toàn những sự tàn hại và khổ nạn. Chúng nó không hề biết con đường hòa bình. Chẳng có sự kính sợ Đức Chúa Trời ở trước mặt chúng nó" (Rô-ma 3:13-18).')]),n._v(" "),i("p",[n._v('Bạn có thể làm gì để cải cách, sửa chữa hay thay đổi những cổ họng, miệng lưỡi, chân và mắt của những con người này? Đây là điều bất khả. Chúa Giê-xu biết là không thể thay đổi, vá víu, cải cách, cho nên Ngài bảo rằng bạn cần phải được tái sinh hoàn toàn mới. Chúa phán "Các ngươi phải sinh lại. Hễ chi sinh bởi xác thịt là xác thịt." Trong một trường hợp khác, Kinh Thánh dạy rằng, "Người Ê-thi-ô-pi có thay được da mình hay con báo có thay đổi được cái đốm của nó không? (Giê-rê-mi 13:23). Cũng trong Rô-ma, Kinh Thánh dạy rằng "Người có tánh xác thịt không thể làm vừa lòng Đức Chúa Trời". Phao-lô cũng thú nhận rằng "điều lành không ở trong tôi (nghĩa là trong xác thịt tôi" (Rô-ma 7:18). Kinh Thánh cũng nói rằng, "nếu không nên thánh thì không ai được thấy Đức Chúa Trời" (Hê-bơ-rơ 12:14).')]),n._v(" "),i("p",[n._v("Sức sống do sự tái sinh không thể đạt được bằng sự phát triển tự nhiên hay nỗ lực của bản ngã. Theo bản chất tự nhiên con người không thể nào có được sự thánh khiết Đức Chúa Trời đòi hỏi để vào thiên đàng. Chính qua sự tái sinh, con người mới khởi đầu có sự sống thánh khiết đó. Để sống bằng sự sống thiên thượng, con người cần có bản chất của Đức Chúa Trời.")]),n._v(" "),i("h3",{attrs:{id:"dieu-duc-chua-troi-lam"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#dieu-duc-chua-troi-lam","aria-hidden":"true"}},[n._v("#")]),n._v(" Điều Đức Chúa Trời Làm")]),n._v(" "),i("p",[n._v("Giống như đồng tiền có hai mặt, vấn đề nhận sự sống mới có hai phương diện, phương diện Đức Chúa Trời và phương diện con người. Chúng ta đã thấy phương diện con người trong chương nói về sự hoán cải, chúng ta đã thấy điều chúng ta phải làm. Bây giờ chúng ta sẽ xem điều Đức Chúa Trời làm.")]),n._v(" "),i("p",[n._v('Được sinh lại hoàn toàn là công việc của Đức Thánh Linh và chúng ta không thể làm gì để nhận được sự sống mới. Kinh Thánh khẳng định rằng "Nhưng hễ ai đã nhận Ngài thì Ngài ban cho quyền phép trở nên con cái Đức Chúa Trời, là ban cho những kẻ tin danh Ngài, là kẻ không sinh bởi khí huyết, bởi tình dục hoặc bởi ý người, nhưng sinh bởi Đức Chúa Trời" (Giăng 1:12-13). Nói cách khác, chúng ta không thể tái sinh do máu huyết, nghĩa là chúng ta không thể có sự tân sinh do di truyền. Đức Chúa Trời không có cháu.')]),n._v(" "),i("p",[n._v("Chúng ta không thể thừa hưởng Cơ Đốc giáo từ cha mẹ. Cha mẹ chúng ta có thể là Cơ Đốc nhân, nhưng điều đó không nhất thiết có nghĩa là con cái sinh ra đương nhiên sẽ là Cơ Đốc nhân. Tôi có thể ra đời trong nhà để xe, nhưng không có nghĩa là tôi sẽ thành xe hơi! Đức Chúa Trời chỉ có con cái mà không có cháu.")]),n._v(" "),i("p",[n._v("Kinh Thánh bảo rằng chúng ta không thể được tái sinh do ý muốn của xác thịt, hay nói cách khác, không ai có thể làm gì để tạo ra sự tái sinh. Người không tin Chúa là người chết. Một người chết không có sự sống để làm bất cứ điều gì.")]),n._v(" "),i("p",[n._v("Con người cũng không được tái sinh do ý người. Sự tân sinh này không thể do kế hoạch hay phương tiện của con người tạo ra. Nhiều người nghĩ rằng họ sẽ tự động được tái sinh khi gia nhập hội thánh, khi nhận thánh lễ, hay khi đưa ra hứa nguyện quyết tâm vào dịp đầu năm hoặc khi dâng hiến một số tiền lớn cho một cơ quan từ thiện danh tiếng. Tất cả những điều trên đều tốt nhưng không thể tạo ra sự tái sinh.")]),n._v(" "),i("h3",{attrs:{id:"cong-viec-cua-duc-chua-troi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#cong-viec-cua-duc-chua-troi","aria-hidden":"true"}},[n._v("#")]),n._v(" Công Việc Của Đức Chúa Trời")]),n._v(" "),i("p",[n._v("Chúa Giê-xu bảo chúng ta phải được sinh lại. Động từ được sinh lại ở dây dùng trong thể thụ động cho thấy đây không phải là điều chúng ta có thể tự làm lấy. Không ai tự sinh mình ra mà cần phải được sinh ra. Sự tân sinh hoàn toàn xa lạ với ý muốn chúng ta. Nói cách khác, sự tân sinh là công việc của Đức Chúa Trời - chúng ta được Đức Chúa Trời sinh ra.")]),n._v(" "),i("p",[n._v('Ni-cô-đem không hiểu làm thế nào có thể sinh ra lần thứ hai. Ông bối rối hỏi Chúa, "Làm sao điều đó thực hiện được?" Dù sự tái sinh dường như là một điều bí ẩn nhưng đó không phải là lý do làm nó không thật. Có nhiều điều chúng ta không hiểu về điện lực, nhưng chúng ta biết nó đem lại ánh sáng trong nhà, chạy máy truyền hình, máy thâu thanh. Chúng ta không hiểu loài cừu mọc lông như thế nào hay loài chim đổi lông vũ ra sao nhưng chúng ta biết chúng thay lông. Chúng ta không hiểu nhiều điều bí nhiệm, nhưng chúng ta chấp nhận bằng đức tin rằng vào giây phút chúng ta ăn năn tội và hướng đức tin vào Chúa Cứu Thế Giê-xu, chúng ta được tái sinh.')]),n._v(" "),i("p",[n._v("Đó là biến cố truyền sự sống thiêng liêng của Đức Chúa Trời vào linh hồn con người. Đó là sự trồng hay cấy bản chất thiên thượng vào linh hồn con người nhờ đó chúng ta trở thành con cái Đức Chúa Trời và chúng ta nhận được hơi sống của Ngài. Chúa Cứu Thế qua Đức Thánh Linh ngự vào cư trú trong tâm hồn chúng ta khiến chúng ta được kết hiệp với Đức Chúa Trời cho đến đời đời, nghĩa là nếu đã được tái sinh, bạn sẽ sống vô cùng như Đức Chúa Trời vì bạn được chia xẻ chính sự sống vĩnh cửu của Ngài. Mối tương giao của con người với Đức Chúa Trời bị mất từ lâu trong vuờn Ê-đen nay đã được phục hồi.")]),n._v(" "),i("h3",{attrs:{id:"nhung-ket-qua-cua-su-tan-sinh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#nhung-ket-qua-cua-su-tan-sinh","aria-hidden":"true"}},[n._v("#")]),n._v(" Những Kết Quả Của Sự Tân Sinh")]),n._v(" "),i("p",[n._v('Có nhiều kết quả theo sau sự tái sinh: Trước hết, sự tái sinh gia tăng tầm nhìn và sự thông hiểu của bạn. Kinh Thánh nói rằng, "Đức Chúa Trời là Đấng truyền lệnh cho sự sáng phải soi từ trong sự tối tăm, đã làm cho sự sáng Ngài chói rạng trong lòng chúng tôi, để sự thông biết về vinh hiển Đức Chúa Trời soi sáng nơi mặt Chúa Cứu Thế Giê-xu" (2 Cô-rinh-tô 4:6). Kinh Thánh cũng nói rằng, "con mắt hiểu biết của anh em được soi sáng" (Ê-phê-sô 1:18). Những điều bạn từng chê cười là dại dột bây giờ bạn chấp nhận bằng đức tin. Tất cả lối suy nghĩ của bạn thay đổi và Đức Chúa Trời trở thành trung tâm của mọi sinh hoạt trí tuệ. Bây giờ Ngài là tâm điểm và bản ngã đã bị hạ bệ.')]),n._v(" "),i("p",[n._v('Thứ hai, tấm lòng bạn sẽ trải qua một cuộc cách mạng. Kinh Thánh ghi lại lời Chúa phán, "Ta sẽ ban lòng mới cho các ngươi, và đặt thần mới trong các ngươi. Ta sẽ cất lòng bằng đá khỏi thịt các ngươi, và ban cho các ngươi lòng bằng thịt" (Ê-xê-chi-ên 36:26). Tình cảm của bạn cũng sẽ trải qua một thay đổi tận gốc rễ. Bản chất mới trong bạn sẽ yêu mến Đức Chúa Trời và những điều thuộc về Ngài. Bạn sẽ yêu mến những điều tốt đẹp nhất, cao cả nhất trong đời sống và từ khước tất cả những gì tầm thường, thấp thỏi. Bạn sẽ có một thái độ ghi nhận mới đối với những nan đề xã hội xung quanh và bạn sẽ có lòng cảm thương sâu xa hơn đối với những người kém may mắn hơn.')]),n._v(" "),i("p",[n._v('Thứ ba, ý chí của bạn cũng sẽ trải qua một cuộc thay đổi lớn. Quyết tâm của bạn cũng khác, những động cơ trong cuộc sống thay đổi. Kinh Thánh bảo rằng, "Đức Chúa Trời của sự bình an... sẽ khiến anh em nên trọn vẹn trong mọi sự lành đặng làm thành ý muốn Ngài, và làm ra sự đẹp ý Ngài trong chúng ta..." (Hê-bơ-rơ 13: 20, 21).')]),n._v(" "),i("p",[n._v("Vì bản chất mới do Đức Chúa Trời ban được uốn nắn theo ý muốn Ngài cho nên bạn có khuynh hướng mong làm theo ý Chúa và hết lòng tận tụy với Ngài. Trong bạn xuất hiện quyết tâm mới, ý hướng mới, mong muốn mới, một nguyên tắc sống mới và những chọn lựa mới. Bạn sẽ cố gắng sống vinh hiển danh Chúa, cố gắng kết thân với các Cơ Đốc nhân khác trong hội thánh. Bạn sẽ yêu thích Kinh Thánh và dành nhiều thì giờ cầu nguyện với Đức Chúa Trời. Toàn thể ý hướng đời sống bạn thay đổi. Cuộc sống bạn trước đây từng đầy ắp lòng vô tín, là cội rễ và nền tảng của mọi tội lỗi, và bạn cũng đã từng nghi ngờ Đức Chúa Trời thì giờ đây bạn tin Ngài, đặt trọn lòng tin tuyệt đối nơi Đức Chúa Trời và Lời Ngài.")]),n._v(" "),i("p",[n._v("Đã có lúc kiêu ngạo từng là trung tâm điểm đời sống bạn với bao nhiêu tham vọng vị kỷ về quyền lực, dục vọng, với bao nhiêu mục tiêu muốn đạt được, nhưng bây giờ ý hướng đó đã bắt đầu thay đổi. Đã có lúc lòng thù ghét ngự trị đời sống bạn với những tư tưởng ghen tị, bất mãn, gian giảo đầy ắp tâm trí, nhưng bây giờ những điều này đang dần dần bị loại trừ.")]),n._v(" "),i("p",[n._v("Đã có lúc bạn từng nói dối như uống nước, đời sống đầy những tư tưởng, lời nói, hành động gian giảo, giả hình. Có thời gian bạn từng sống chiều theo dục vọng xác thịt nhưng bây giờ lối sống này thay đổi. Bạn đã được sinh lại. Bạn có thể vấp ngã vào những bẫy rập ma quỉ giăng ra, nhưng liền sau đó bạn ăn năn, xưng tội xin Chúa tha thứ, vì bạn đã được tái sinh. Chính bản chất của bạn đã thay đổi.")]),n._v(" "),i("h3",{attrs:{id:"con-heo-va-con-chien"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#con-heo-va-con-chien","aria-hidden":"true"}},[n._v("#")]),n._v(" Con Heo Và Con Chiên")]),n._v(" "),i("p",[n._v("Có một câu chuyện cổ về con heo và con chiên. Một nông gia đem một con heo vào nhà, tắm rửa thật sạch sẽ, chà móng, xức nước hoa, cột nơ quanh cổ và dắt vào phòng khách. Trông con heo cũng được, không đến nỗi nào đối với khách khứa đến chơi nhà ít ra là được vài ba phút đầu. Tuy nhiên khi cửa vừa mở, nhanh như chớp, con heo phóng ra và đẵm ngay vào vũng bùn đầu tiên nó gặp. Tại sao? Vì lòng của nó vẫn là tấm lòng một con heo! Bản chất nó không hề thay đổi. Nó chỉ mới thay đổi hình thức bên ngoài mà không có gì biến đổi bên trong.")]),n._v(" "),i("p",[n._v("Bây gì đến lượt con chiên đặt trong phòng khách rồi dắt nó ra sau vườn. Nó sẽ cố tránh tất cả mọi vũng bùn. Tại sao? Vì bản chất của nó là bản chất một con chiên.")]),n._v(" "),i("p",[n._v("Bạn có thể bảo một người ăn mặc chỉnh tề đến nhà thờ ngồi vào băng đầu. Trông anh ta sẽ như một ông thánh. Anh ta có thể làm cho người bạn thân nhất nhầm trong ít lâu, nhưng phải chờ quan sát anh ta trong sở làm hay trong các hộp đêm vào tối thứ bảy, bạn sẽ thấy bản chất thật của anh ta xuất hiện. Tại sao anh ta hành xử như vậy? Vì bản chất anh ta chưa thay đổi. Anh ta chưa được sinh lại.")]),n._v(" "),i("h3",{attrs:{id:"y-nghia-cua-su-cong-chinh-hoa"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#y-nghia-cua-su-cong-chinh-hoa","aria-hidden":"true"}},[n._v("#")]),n._v(" Ý Nghĩa Của Sự Công Chính Hóa")]),n._v(" "),i("p",[n._v("Chính giây phút bạn nhận được sự sống mới, chính giây phút bạn được sinh lại, chính giây phút bạn được phú ban bản chất mới, bạn được kể là công chính trước mặt Đức Chúa Trời. Được công chính hóa có nghĩa là được kể như bạn chưa từng phạm tội. Công chính hóa là hành vi pháp lý của Đức Chúa Trời qua đó Ngài tuyên bố một người vô đạo là toàn hảo trong khi anh ta vẫn là một người vô đạo. Đức Chúa Trời để bạn ở trước mặt Ngài như thể bạn chưa từng phạm một tội nào.")]),n._v(" "),i("p",[n._v('Như Phao-lô nói, "Ai sẽ kiện kẻ lựa chọn của Đức Chúa Trời? Đức Chúa Trời là Đấng công chính hóa những kẻ ấy" (Rô-ma 8:33). Tội lỗi bạn được tha thứ và Đức Chúa Trời đã đem chôn tất cả những tội lỗi đó dưới đáy biển sâu và quăng chúng ra đàng sau kho quên lãng của Ngài. Từng tội lỗi được Ngài xóa sạch. Bạn đứng trước Đức Chúa Trời như một con nợ tội lỗi, nhưng Đức Chúa Trời đã tuyên bố bạn trắng án, bạn được giải hòa với Đức Chúa Trời, vì trước đây bạn thực sự là kẻ thù của Ngài. Kinh Thánh bảo rằng, "Mọi điều đó đến bởi Đức Chúa Trời, Ngài đã làm cho chúng ta nhờ Chúa Cứu Thế mà được hòa thuận lại cùng Ngài, và đã giao chức vụ giảng hòa cho chúng ta" (2 Cor. 5:18).')]),n._v(" "),i("p",[n._v('Nhưng hơn hẳn tất cả những điều đó, bạn đã được nhận vào làm con trong gia đình Đức Chúa Trời. Bây giờ bạn là con cái Ngài. "Bởi sự thương yêu của Ngài đã định trước cho chúng ta được trở nên con nuôi của Ngài bởi Chúa Cứu Thế Giê-xu theo ý tốt Ngài" (Ê-phê-sô 1:5). Bây giờ bạn là một thành viên hoàng tộc trên thiên quốc, bạn có huyết thống hoàng gia, bạn là hoàng tử, là con Vua Trời. Bạn bè của bạn sẽ khởi sự nhận ra những thay đổi trong đời sống bạn. Bạn đã được tái sinh.')]),n._v(" "),i("h3",{attrs:{id:"con-nguoi-cu-va-con-nguoi-moi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#con-nguoi-cu-va-con-nguoi-moi","aria-hidden":"true"}},[n._v("#")]),n._v(" Con Người Cũ Và Con Người Mới")]),n._v(" "),i("p",[n._v("Một khi đã được tái sinh cuộc đời bạn sẽ có nhiều biến đổi. Trước hết là thái độ đối với tội lỗi. Bạn sẽ kinh khủng ghê tởm tội lỗi và sẽ dần dần thấy ghét tội lỗi như chính Đức Chúa Trời ghét.")]),n._v(" "),i("p",[n._v('Tại thành phố Houston, tiểu bang Texas có một người tin Chúa, được tái sinh vào một trong các buổi truyền giảng của chúng tôi. Ông là chủ một tiệm bán rượu. Sáng hôm sau ông đem treo trước cửa tấm bảng đề chữ "Dẹp Tiệm." Cách đây ít lâu tôi cũng nghe câu chuyện về một người được tái sinh trong một buổi truyền giảng Tin Lành. Ông này là một tay nghiện rượu cả thành phố biết tiếng và người ta gọi ông là "Lão John" hay là "John Cũ." Hôm sau gặp ông ngoài đường có người gọi, "chào Lão John". Ông bảo, "Này, anh bạn nói chuyện với ai đấy? Tôi không còn là John Cũ đâu mà là John Mới đây!" Một cuộc cách mạng triệt để đã xảy ra trong cuộc đời con người này.')]),n._v(" "),i("p",[n._v('Thứ hai, bạn sẽ biết bạn được tái sinh vì bạn muốn vâng lời Đức Chúa Trời, "Nầy tại sao chúng ta biết mình đã biết Ngài, ấy là tại chúng ta giữ các điều răn của Ngài"(I Giăng 2:3).')]),n._v(" "),i("p",[n._v('Thứ ba, bạn sẽ phân rẽ với thế gian. Kinh Thánh dạy rằng, "Chớ yêu thế gian cũng đừng yêu các vật ở thế gian nữa; nếu ai yêu thế gian thì sự kính mến Đức Chúa Cha chẳng ở trong người ấy" (I Giăng 2:15). Một nhà thơ khuyết danh đã viết như sau:\n')]),i("p",{attrs:{align:"center"}},[i("em",[n._v('"Nước trên khắp địa cầu (All the water in the world)')]),i("br"),n._v(" "),i("em",[n._v("Dù cố sức đến đâu (However hard it tried)")]),i("br"),n._v(" "),i("em",[n._v("Không sao làm chìm được (Could never sink a ship)")]),i("br"),n._v(" "),i("em",[n._v("Nếu không lọt vào tầu (Unless it got inside.)")]),i("br"),n._v(" "),i("em",[n._v("Toàn sự ác trần gian (All the evil of the world)")]),i("br"),n._v(" "),i("em",[n._v("Tội lỗi với gian tham (The wickedness and sin)")]),i("br"),n._v(" "),i("em",[n._v("Không bao giờ hại được (Could never sink the soul’s craft)")]),i("br"),n._v(" "),i("em",[n._v('Nếu không nhập tâm can" (Unless it got inside)')]),i("br")]),i("p"),n._v(" "),i("p",[n._v("Thứ tư, bạn sẽ có một tình yêu mới đối với tha nhân. Kinh Thánh bảo rằng, "),i("strong",[n._v('"Chúng ta biết rằng mình đã vượt khỏi sự chết qua sự sống, vì chúng ta yêu anh em mình" (I Giăng 3:14)')]),n._v(".")]),n._v(" "),i("p",[n._v("Thứ năm, chúng ta sẽ không tiếp tục ghì mài trong tội lỗi. Kinh Thánh dạy rằng, "),i("strong",[n._v('"Chúng ta biết rằng ai sinh bởi Đức Chúa Trời, thì hẳn chẳng phạm tội, nhưng ai sinh bởi Đức Chúa Trời, thì tự giữ lấy mình, ma quỉ không làm hại người được" (I Giăng 5:18)')]),n._v(". Chúng ta sẽ không can dự vào nếp sống sa đọa.")]),n._v(" "),i("p",[n._v('Ở tiểu bang Texas có câu chuyện kể về một người sáng nào cũng đến cột ngựa phía trước một tiệm rượu. Một buổi sáng kia chủ quán rượu đi ra thấy người này không cột ngựa trước quán nữa mà lại cột phía trước nhà thờ. Nhìn anh ta từ đường trên đi xuống chủ quán gọi to, "Này anh bạn! Sao sáng nay lại cột ngựa trước nhà thờ?" Người kia quay lại trả lời, "Tôi đã trở lại với Chúa trong buổi nhóm phục hưng tối qua nên hôm nay muốn đổi chỗ cột ngựa."')]),n._v(" "),i("p",[n._v("Đây chính là ý nghĩa tái sinh. Đây là ý nghĩa của việc qui đạo. Đây là ý nghĩa của sự phân rẽ với trần gian. Đó là đổi chỗ cột ngựa.")]),n._v(" "),i("h2",{attrs:{id:"chuong-13-lam-sao-toi-biet-chac"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-13-lam-sao-toi-biet-chac","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 13: Làm sao tôi biết chắc")]),n._v(" "),i("p",[i("strong",[n._v('"Ta đã viết những điều này cho các con hầu cho các con biết mình có sự sống đời đời, là kẻ nào tin đến danh Con Đức Chúa Trời" (I Giăng 5:13)')])]),n._v(" "),i("p",[n._v("Mỗi tuần tôi nhận được hàng chục lá thư từ những người cho biết họ có những điều nghi ngại và không chắc chắn liên quan đến cuộc sống Cơ Đốc. Trong số đó có những tín hữu thật nhưng dường như lại không có được niềm vui trong đức tin Cơ Đốc, hay không cảm thấy được bảo đảm vì họ đã không hiểu được chân lý căn bản về kinh nghiệm Cơ Đốc. Dù Kinh Thánh đã khẳng định rằng "),i("strong",[n._v('"những việc này đã chép để các ngươi tin rằng Đức Chúa Giê-xu là Đấng Christ, tức là Con Đức Chúa Trời, và để khi các ngươi tin thì nhờ danh Ngài mà được sự sống" (Giăng 20:31)')]),n._v(", nhưng nhiều người vẫn không cảm thấy vững chắc, an lòng.")]),n._v(" "),i("p",[n._v("Chúng ta sẽ dùng chương này để tóm tắt lại những gì đã xảy ra trong tiến trình cứu rỗi. Chúng ta đã hiểu rõ ý nghĩa của sự ăn năn, việc đặt đức tin nơi Chúa và sự được sinh lại hay tái sinh. Nhưng làm sao chúng ta có thể biết chắc tất cả những điều này đã xảy ra cho chúng ta ? Nhiều người nói chuyện với tôi đã từng ăn năn, tin nhận Chúa Cứu Thế Giê-xu và đã tái sinh, nhưng vẫn cảm thấy không chắc chắn về sự kiện mình qui đạo.")]),n._v(" "),i("p",[n._v('Chúng ta hãy đi trở lại một số điều đã học. Trước hết, sự kiện trở thành một Cơ Đốc nhân có thể là một biến động lớn trong đời sống hay cũng có thể là một tiến trình đưa đến một cao điểm mà bạn có thể ý thức hay không. Xin bạn đừng hiểu lầm điểm này. Việc bạn trở thành Cơ Đốc nhân không phải là kết quả của một tiến trình giáo dục. Vài năm trước một mục sư có tiếng tăm bảo rằng, "Chúng ta phải giáo dục và huấn luyện thanh niên trong lối sống Cơ Đốc như thế nào để họ không bao giờ biết thời gian họ chưa phải là Cơ Đốc nhân." Nhiều triết lý giáo dục tôn giáo đã dựa vào tiền đề này và có lẽ một số không ít đã lạc mất yếu tính của kinh nghiệm Cơ Đốc, vì tất cả chỉ lo huấn luyện mà thôi cho nên trong lòng không hề có một biến đổi nào.')]),n._v(" "),i("p",[n._v("Đến đầu thế kỷ 20, giáo sư Starbuck, một lý thuyết gia hàng đầu trong lãnh vực tâm lý, quan sát và nhận thấy rằng những công tác viên Cơ Đốc thường là những người được tuyển trong hàng ngũ những người đã có một kinh nghiệm hoán cải sinh động. Ông cũng nhận thấy rằng những người có khái niệm rõ ràng về sự hoán cải thường là những người xuất thân từ các vùng quê ít được huấn luyện về tôn giáo hay có nhưng không qui mô.")]),n._v(" "),i("p",[n._v("Không phải chúng ta phê phán việc huấn luyện tôn giáo, nhưng những nhận định trên có thể coi là lời cảnh cáo đối với hiểm họa sử dụng những trang bị tôn giáo không thích đáng thay thế cho kinh nghiệm tái sinh.")]),n._v(" "),i("h3",{attrs:{id:"ton-giao-khong-phai-la-tai-sinh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#ton-giao-khong-phai-la-tai-sinh","aria-hidden":"true"}},[n._v("#")]),n._v(" Tôn Giáo Không Phải Là Tái Sinh")]),n._v(" "),i("p",[n._v("Chúa Giê-xu đã nói với Ni-cô-đem, một trong những người sùng đạo nhất đương thời, "),i("strong",[n._v('"Nếu một người không sinh lại thì không thể thấy nước Đức Chúa Trời" (Giăng 3:3)')]),n._v(". Ni-cô-đem không thể lấy kiến thức tôn giáo sâu sắc của ông thay thế cho sự tái sinh và cho đến thế hệ hôm nay chúng ta vẫn chưa vượt qua được giới hạn đó.")]),n._v(" "),i("p",[n._v("Con sâu xấu xí dùng hầu hết thời gian để tăng trưởng và thay đổi trong kén mà không ai biết đến. Dù tiến trình đó có chậm chạp và kéo dài bao lâu đi nữa cũng sẽ đến lúc nó phải trải qua một biến động, từ đó xuất hiện một con bướm đẹp đẽ. Những tuần lễ tăng trưởng trong yên lặng rất quan trọng, nhưng nó không thể thay thế cho kinh nghiệm khi cái xác cũ kỹ, xấu xí bị lột ra để lại đàng sau và một hình dáng mới rực rỡ xuất hiện.")]),n._v(" "),i("p",[n._v("Có hàng nghìn Cơ Đốc nhân không biết rõ đích xác ngày giờ người đó biết Chúa Cứu Thế, nhưng đức tin và đời sống họ minh chứng rằng họ đã được hoán cải, đã đến với Chúa Cứu Thế một cách ý thức hay vô thức. Dù họ có nhớ hay không thì vẫn có một giây phút họ đã vượt lằn ranh từ chết qua sống.")]),n._v(" "),i("p",[n._v('Có lẽ ai cũng có những điều ngờ vực, những điều cảm thấy không chắc chắn trong kinh nghiệm tôn giáo. Khi Môi-se lên núi Si-na-i nhận hai bảng luật Chúa ban, ông vắng mặt nơi tuyển dân trong một khoảng thời gian nên họ nóng lòng trông chờ ông trở lại. Cuối cùng dân chúng ngờ vực, nói với A-rôn, "Nào hãy làm các thần để đi trước chúng tôi đi vì về Môi-se, là người đã dẫn chúng tôi ra khỏi xứ Ai-cập chúng tôi không biết việc gì đã xảy đến cho người rồi" (Xuất Ai-cập 32:1b). Sự bội nghịch của tuyển dân là hậu quả của lòng nghi ngờ và thiếu xác quyết.')]),n._v(" "),i("p",[n._v("Tình trạng thiếu xác quyết đáng sợ trên ám ảnh linh hồn vô số người phát xuất từ sự hiểu lầm những yếu tố của kinh nghiệm Cơ Đốc. Nhiều người dường như không hiểu gì về bản chất kinh nghiệm Cơ Đốc trong khi một số khác lại mong đợi và tìm kiếm những kinh nghiệm không hề được Kinh Thánh bảo đảm.")]),n._v(" "),i("p",[n._v('Không kể nhiều lần hàm ý, từ đức tin được minh thị nói đến hơn ba trăm lần trong Tân-ước liên hệ đến sự cứu rỗi. Tác giả thư Hy-bá viết, "Kẻ đến gần Đức Chúa Trời phải tin rằng có Đức Chúa Trời, và Ngài là Đấng ban thưởng cho người chuyên cần tìm kiếm Ngài" và, "Không có đức tin thì không thể nào ở cho đẹp ý Ngài" (Hy-bá 11:6).')]),n._v(" "),i("p",[n._v("Chính vì lẫn lộn đức tin với cảm xúc cho nên ngày nay nhiều người đã kinh nghiệm tình trạng khó khăn và thiếu xác quyết thường có giữa vòng những người nhận mình là Cơ Đốc nhân.")]),n._v(" "),i("p",[n._v('Đức tin luôn luôn hàm ý phải có đối tượng, nghĩa là chúng ta phải đặt đức tin vào một người nào hay một vật gì. Người hay vật đó chúng ta gọi là sự kiện. Tôi xin đưa ra ở đây ba từ ngữ hướng dẫn chúng ta ra khỏi tình trạng ngờ vực để bước vào cuộc sống một Cơ Đốc nhân đầy lòng tin quyết. Ba từ ngữ đó là sự kiện, đức tin và cảm xúc theo đúng thứ tự này. Nếu lẫn lộn thứ tự hoặc bỏ bớt hoặc thêm vào, chúng ta sẽ rơi vào trong vũng lầy của thất vọng và tiếp tục lần mò trong bóng mờ, không có được niềm vui và lòng tin quyết của một người có thể tuyên bố rằng, "Tôi biết tôi đã tin Đấng nào" (2 Timôthê 1:12).')]),n._v(" "),i("h3",{attrs:{id:"su-kien"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#su-kien","aria-hidden":"true"}},[n._v("#")]),n._v(" Sự kiện")]),n._v(" "),i("p",[n._v('Chúng ta được cứu khỏi tội hoàn toàn chỉ do đức tin cá nhân vào Phúc Âm của Chúa Cứu Thế Giê-xu được trình bày trong Kinh Thánh. Dù điều này đối với bạn, ban đầu có vẻ giáo điều và hẹp hòi, nhưng sự kiện hiển nhiên còn lại đó là không có một con đường nào khác. Kinh Thánh ghi lại lời Thánh Phao lô viết, "Trước hết, tôi đã dạy anh em điều chính tôi đã nhận lãnh, ấy là Chúa Cứu Thế chịu chết vì tội chúng ta theo lời Kinh Thánh; Ngài đã bị chôn, đến ngày thứ ba, Ngài sống lại theo lời Kinh Thánh" (I Cô-rinh-tô 15:3,4). Kinh Thánh bảo rằng chúng ta được cứu khi đức tin chúng ta ở trong sự kiện khách quan này. Công việc của Chúa Cứu Thế là sự kiện, thập tự giá của Ngài là sự kiện, ngôi mộ của Ngài là sự kiện và sự phục sinh của Ngài là sự kiện.')]),n._v(" "),i("p",[n._v("Không thể nào chỉ do tin tưởng mà người ta có thể làm cho một điều gì hiện hữu. Phúc Âm không hiện hữu do con người tin tưởng. Ngôi mộ trống không còn xác chết vào ngày Lễ Phục Sinh đầu tiên không phải vì có những người trung thành tin như vậy. Sự kiện luôn luôn có trước đức tin. Con người được tạo dựng về phương diện tâm lý, không thể tin nếu không có đối tượng của đức tin.")]),n._v(" "),i("p",[n._v("Kinh Thánh không kêu gọi bạn tin vào điều gì không thể tin, nhưng tin vào sự kiện lịch sử mà trong thực tại vượt qua tất cả mọi dòng lịch sử. Kinh Thánh kêu gọi bạn tin vào công tác của Chúa Cứu Thế giải quyết tội lỗi cho tội nhân là công tác có hiệu nghiệm cho tất cả mọi người dám liều mình phó thác linh hồn cho Chúa. Tin cậy Chúa để được sự cứu rỗi vĩnh cửu là tin cậy vào một sự kiện.")]),n._v(" "),i("h3",{attrs:{id:"duc-tin"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#duc-tin","aria-hidden":"true"}},[n._v("#")]),n._v(" Đức tin")]),n._v(" "),i("p",[n._v("Đức tin theo thứ tự là chữ thứ nhì trong ba từ ngữ trên. Hiển nhiên về phương diện lý trí đức tin không thể có được nếu không có gì để tin. Đức tin cần phải có đối tượng. Đối tượng đức tin Cơ Đốc là Chúa Cứu Thế. Đức tin không chỉ là chấp nhận bằng lý trí những lời tuyên xưng của Chúa Cứu Thế nhưng còn liên quan đến ý chí. Đức tin có tính cách quyết định chọn lựa. Đức tin đòi hỏi hành động. Nếu chúng ta thực sự tin, chúng ta sẽ sống theo niềm tin tưởng đó. Đức tin không việc làm là đức tin chết. Đức tin thực sự có nghĩa là thuận phục, ký thác và tận hiến cho những điều Chúa Cứu Thế tuyên xưng. Đức tin bao gồm việc nhận tội và quay trở về với Chúa Cứu Thế. Chúng ta không thể biết Chúa Cứu Thế bằng ngũ quan nhưng chúng ta có thể biết Ngài bằng giác quan thứ sáu mà Đức Chúa Trời ban cho mọi người, đó là khả năng tin.")]),n._v(" "),i("h3",{attrs:{id:"kinh-nghiem-duc-tin"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#kinh-nghiem-duc-tin","aria-hidden":"true"}},[n._v("#")]),n._v(" Kinh nghiệm đức tin")]),n._v(" "),i("p",[n._v('Trong khi tôi cẩn thận đọc qua Kinh Thánh Tân Ước để xem bạn có thể có loại kinh nghiệm nào, tôi thấy Tân Ước chỉ đưa ra có một loại kinh nghiệm mà thôi. Đó là kinh nghiệm bạn có thể tìm, kinh nghiệm bạn có thể mong đợi và đó là kinh nghiệm đức tin. Tin là một kinh nghiệm rất thật như mọi kinh nghiệm, vậy mà nhiều người vẫn còn muốn tìm thêm những điều khác, như xúc cảm mãnh liệt hay những thể hiện phi thường khác. Nhiều người được bảo phải đi tìm những xúc cảm đó, nhưng Kinh Thánh dạy rất rõ rằng người ta được "công chính hóa" bởi đức tin chứ không nhờ xúc cảm. Một người được cứu là do tin cậy công tác chuộc tội viên mãn của Chúa Giê-xu trên thập tự giá chứ không do những khích động thể lý hay tình trạng ngây ngất trong tôn giáo.\nNhưng rồi bạn có thể nói với tôi, "Thế còn xúc cảm thì sao? Trong đức tin cứu rỗi không có chỗ nào cho xúc cảm sao?" Có xúc cảm trong đức tin cứu rỗi nhưng chúng ta không được cứu nhờ xúc cảm. Bất cứ xúc cảm nào chúng ta có chỉ là kết quả của đức tin cứu rỗi. Xúc cảm không cứu chúng ta!')]),n._v(" "),i("h3",{attrs:{id:"xuc-cam"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#xuc-cam","aria-hidden":"true"}},[n._v("#")]),n._v(" Xúc cảm")]),n._v(" "),i("p",[n._v("Xúc cảm là chữ cuối cùng trong ba từ đã nêu lên (sự kiện, đức tin và xúc cảm) và là phương diện ít quan trọng nhất. Tôi tin rằng tình trạng bất ổn trong đời sống tôn giáo và tâm trạng bấp bênh không xác quyết xảy ra là do những người nhiệt thành, chân chất đi tìm sự cứu rỗi đã quả quyết trước rằng họ phải có một tâm trạng sốt sắng nào đó thì mới kinh nghiệm được sự hoán cải.")]),n._v(" "),i("p",[n._v("Những người đi tìm sự cứu rỗi theo như Kinh Thánh trình bày sẽ muốn biết loại kinh nghiệm nào Kinh Thánh muốn dẫn vào. Tôi thường nói những điều sau đây cho những người đáp ứng lời mời tin nhận Chúa, hoặc trước bục giảng hay trong các phòng vấn nạn, hay khi quì gối ngay bên cạnh máy thâu thanh hay truyền hình. Tôi bảo rằng, bạn đã nghe sứ điệp tin mừng, bạn đã biết mình là một tội nhân đang cần có một Đấng Cứu Chuộc, bạn đã nhận ra rằng cuộc đời bạn về phương diện thuộc linh hoàn toàn đổ vỡ, bạn đã cố theo mọi kế hoạch của con người để tự cải tiến, tự cải cách nhưng tất cả đều thất bại. Trong tình trạng hư vong và tuyệt vọng, bạn ngước lên nhìn Chúa Cứu Thế trông đợi ơn cứu rỗi. Bạn tin rằng Ngài có đủ quyền năng và chắc sẽ cứu bạn. Bạn từng được nghe hay đọc lời mời của Ngài trong Kinh Thánh rằng, "),i("strong",[n._v('"Hỡi những kẻ mệt mỏi và gánh nặng, hãy đến cùng ta, ta sẽ cho các con được yên nghỉ" (Ma-thi-ơ 11:28)')]),n._v(". Bạn cũng đã đọc lời Chúa hứa, "),i("strong",[n._v('"Người nào đến cùng Ta thì Ta không bỏ ra ngoài đâu" (Giăng 6:37)')]),n._v(", và bạn cũng đã đọc lời Chúa bảo rằng, "),i("strong",[n._v('"Nếu người nào khát hãy đến cùng ta mà uống" (Giăng 7:37)')]),n._v(".")]),n._v(" "),i("h3",{attrs:{id:"xuc-cam-den-sau-duc-tin"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#xuc-cam-den-sau-duc-tin","aria-hidden":"true"}},[n._v("#")]),n._v(" Xúc cảm đến sau đức tin")]),n._v(" "),i("p",[n._v('Khi tôi hiểu đôi điều về tình yêu của Chúa Cứu Thế dành cho tôi là một tội nhân, tôi đáp ứng với Ngài cũng bằng tình yêu - và tình yêu thì có kèm theo cảm xúc. Nhưng tình yêu đối với Chúa Cứu Thế vượt lên trên tình cảm của con người dù cả haui có những điểm tương đồng. Đây là loại tình yêu giải phóng chúng ta khỏi bản ngã. Trong hôn nhân có sự cam kết nhưng cũng có xúc cảm. Tuy nhiên xúc cảm đến rồi đi, còn cam kết không dời đổi. Chúng ta là những người ký thác với Chúa Cứu Thế cũng có những xúc cảm đến rồi đi, xúc cảm vui mừng, yêu mến, biết ơn..., nhưng sự cam kết tiếp tục vững bền. Xúc cảm quan trọng nhưng không thiết yếu. Kinh Thánh bảo rằng, "tình yêu thương trọn vẹn cất bỏ mọi sợ hãi" (I Giăng 4:18) cho nên lòng tin cậy Chúa Cứu Thế của những người yêu mến Ngài nâng họ vượt lên trên mọi sợ hãi. Các nhà tâm lý học cho biết có những nỗi sợ hãi gây tổn hại nhưng cũng có những sự sợ hãi lành mạnh. Nỗi sợ hãi lành mạnh khiến chúng ta chăm sóc chính mình và những người thân. Chúa Giê-xu dạy chúng ta cần e sợ, cảnh giác đối với Sa-tan.')]),n._v(" "),i("p",[n._v('Khi hiểu rằng Chúa Cứu Thế trong sự chết của Ngài đã tuyệt đối đắc thắng tội lỗi và sự chết, thì tôi không còn sợ sự chết nữa. Kinh Thánh dạy rằng, "Vì con cái có phần về huyết và thịt, nên chính Chúa Cứu Thế Giê-xu cũng có phần vào đó, hầu cho Ngài bởi sự chết mình mà phá diệt kẻ cầm quyền sự chết là ma quỉ, lại cho giải thoát mọi người vì sợ sự chết, bị cầm trong vòng tôi mọi trọn đời" (Hy-bá 2:14,15). Hiển nhiên sợ hãi là một xúc cảm và thắng sự sợ hãi với can đảm và lòng tin cậy trước sự chết là xúc cảm và kinh nghiệm. Nhưng tôi cần nhắc lại điểm này, điều cứu chúng ta không phải là xúc cảm can đảm hay sự an tâm mà là đức tin, trong khi đó can đảm và sự an tâm là kết quả của đức tin đặt nơi Chúa Cứu Thế. Trong Kinh Thánh suốt từ Sáng Thế Ký đến Khải Huyền đều dạy chúng ta phải kính sợ Đức Chúa Trời. Chính lòng kính sợ Chúa này đã khiến cho chúng ta nhận định đúng tất cả những nỗi sợ hãi khác.')]),n._v(" "),i("h3",{attrs:{id:"tinh-trang-mac-toi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#tinh-trang-mac-toi","aria-hidden":"true"}},[n._v("#")]),n._v(" Tình trạng mắc tội")]),n._v(" "),i("p",[n._v("Có một lương tâm mang mặc cảm phạm tội là một kinh nghiệm. Các nhà tâm lý học có thể định nghĩa đó là kinh nghiệm mắc tội và có thể tìm cách biện bạch để xóa bỏ mặc cảm mắc tội này. Tuy nhiên, một khi lương tâm đã thức tỉnh do đối diện với giới răn của Đức Chúa Trời thì không một lời biện minh nào có thể trấn át tiếng nói dai dẳng của lương tâm. Có nhiều tội nhân cuối cùng đã ra đầu thú nộp mình cho chính quyền vì sự lên án, cáo trách của lương tâm mắc tội còn kinh khủng hơn các chấn song nhà tù.")]),n._v(" "),i("p",[n._v("Trong một bài báo nói về sự mắc tội được đăng trên tờ The New York Times (Số ra ngày 29 tháng 11, 1983), Tiến sĩ Helen Block Lewis, một tâm lý gia và nhà phân tích tâm lý của đại học Yale, đã mô tả cảm thức mắc tội là một cảm xúc "),i("em",[n._v('"giúp con người giữ liên lạc"')]),n._v(" với đồng loại. Bà giải thích rằng "),i("em",[n._v('"Cảm thức mắc tội là chất keo kết hợp chúng ta lại với nhau và giúp chúng ta duy trì tính người. Nếu bạn là người gây tổn hại cho người khác, cảm thức mắc tội sẽ thúc đẩy bạn phải tìm cách sửa đổi, hàn gắn lại."')])]),n._v(" "),i("p",[n._v("Samuel Rutherford bảo rằng, "),i("em",[n._v('"Hãy xin Chúa ban cho bạn một ý thức mạnh mẽ, sống động về tội lỗi. Ý thức về tội lỗi càng sâu sắc, càng ít tội."')]),n._v(" Ý thức về tội lỗi chính là cảm thức mắc tội. Nó không chỉ cho bạn biết lúc bạn rơi vào vòng tội lỗi, nhưng cũng giống như cảm giác về sự đau đớn, nó giữ bạn tránh xa hiểm họa. Thiếu cảm giác đau đớn, người ta có thể vô tình đưa tay vào lò lửa mà không cảm thấy gì cả. Vai trò sinh động nhất của cảm giác đau đớn được trình bày cặn kẽ trong cuốn Được Tạo Dựng Đáng Sợ lạ Lùng (Fearfully and Wonderfully Made) của Paul Brand và Philip Yancey. Hai tác giả này đã giải thích rằng không phải chính bệnh phong cùi đã làm cho nhiều người mắc bệnh này thường bị khuyết tật, nhưng vì tình trạng mất cảm giác đau đớn đã khiến họ bị thương tật (như bị cháy, phỏng khi đưa tay vào lửa mà không biết...), đưa đến hậu quả bị cưa, cắt, liên quan đến loại bệnh kinh khủng này.")]),n._v(" "),i("p",[n._v("Kinh Thánh dạy rằng Chúa Cứu Thế Giê-xu là Đấng thanh tẩy lương tâm, như trong thư "),i("strong",[n._v("Hy-bá 9:13,14")]),n._v(" chép như sau, "),i("strong",[n._v('"Vì nếu huyết của dê đực cùng tro bò cái tơ mà người ta rưới trên kẻ ô uế còn làm sạch được phần xác thịt họ và nên thánh thay, huống chi huyết của Chúa Cứu Thế là Đấng nhờ Đức Thánh Linh đời đời, dâng chính mình không tì tích cho Đức Chúa Trời, thì sẽ làm sạch lương tâm anh em khỏi công việc chết, đặng hầu việc Đức Chúa Trời hằng sống là dường nào!"')])]),n._v(" "),i("p",[n._v("Được tẩy sạch khỏi lương tâm mắc tội và được giải phóng khỏi tình trạng thường xuyên bị cáo trách, lên án, là mộ kinh nghiệm, nhưng không phải việc thanh tẩy lương tâm cứu rỗi bạn nhưng chính đức tin nơi Chúa Cứu Thế cứu rỗi bạn, và lương tâm được thanh tẩy là kết quả của việc bước vào mối tương giao tốt đẹp với Đức Chúa Trời.")]),n._v(" "),i("p",[n._v("Vui mừng là một xúc cảm. Sự bình an sâu kín trong lòng là xúc cảm. Tình thương tha nhân là xúc cảm. Mối quan tâm đối với những người hư vong cũng là một xúc cảm.")]),n._v(" "),i("p",[n._v('Điểm cuối cùng, có lẽ có người bảo rằng, "Tôi tin vào những sự kiện lịch sử của phúc âm, nhưng tôi vẫn chưa được cứu." Có thể như thế, vì đức tin cứu rỗi có một phẩm tính đặc biệt - đức tin cứu rỗi là đức tin đưa đến lòng vâng phục, đó là đức tin tạo ra một lối sống. Một số người rất thành công trong việc bắt chước lối sống này trong một thời gian, nhưng những người tin cậy Chúa Cứu Thế để được cứu rỗi thì đức tin đó tạo ra trong họ một ước muốn sống thể hiện kinh nghiệm đức tin trong lòng. Đó là năng quyền đem đến kết quả trong cuộc sống thánh thiện, đầu phục Chúa.')]),n._v(" "),i("p",[n._v("Chúng ta hãy để đức tin lý trí, đức tin lịch sử bạn đang có qui phục Chúa Cứu Thế một cách hoàn toàn. Hãy nhiệt thành ước muốn sự cứu rỗi của Ngài, và dựa trên thẩm quyền của Lời Đức Chúa Trời bạn trở thành con cái Ngài. "),i("strong",[n._v('"Nhưng hễ ai nhận Ngài thì Ngài ban cho quyền trở nên con cái Đức Chúa Trời, là ban cho những kẻ tin danh Ngài" (Giăng 1:12)')]),n._v(".")]),n._v(" "),i("h2",{attrs:{id:"chuong-14-ke-thu-cua-co-doc-nhan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-14-ke-thu-cua-co-doc-nhan","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 14: Kẻ thù của Cơ Đốc nhân")]),n._v(" "),i("p",[i("strong",[n._v('"Vì chúng ta đánh trận, chẳng phải cùng thịt và huyết, bèn là cùng chủ quyền, cùng thế lực, cùng vua chúa của thế gian mờ tối này, cùng các thần dữ ở các miền trên trời vậy". (Ê-phê-sô 6:12)')])]),n._v(" "),i("p",[n._v('Như vậy là bạn đã quyết định, bạn đã được tái sinh, bạn đã được hoán cải, đã được kể là công chính, được gọi là con cái Đức Chúa Trời, nhưng bước kế tiếp là gì? Tất cả chỉ có vậy thôi sao? Có phải chỉ cần một phút quyết định và như thế là xong? Bạn sẽ hỏi, "Tôi còn có những bổn phận nào nữa?"')]),n._v(" "),i("p",[n._v("Vâng, bạn vừa khởi đầu cuộc sống Cơ Đốc. Bạn vừa được sinh vào một thế giới mới - thế giới thuộc linh. Đối với bạn, mọi sự hoàn toàn mới mẻ. Bạn thật sự là một em bé thuộc linh, bạn cần được nâng niu, chăm sóc, yêu thương, nuôi dưỡng. Bạn cần được bảo vệ. Đó là một số những lý do tại sao Chúa cứu Thế đã thiết lập hội thánh, vì bạn không thể nào sống cuộc sống Cơ đốc đơn lẻ. Hầu hết chúng ta đều cần tương giao và cần được giúp đỡ. Cơ Đốc nhân sơ sinh giống như em bé mới chào đời cần được yêu thương.")]),n._v(" "),i("p",[n._v("Trong thời gian hiệu đính lại cuốn sách này, nhà tôi và tôi đi nghỉ ở một đảo nhỏ với vợ chồng con gái lớn. Con tôi đem theo cháu bé thứ bảy tên là Anthony mới có ba tháng, là cháu ngoại thứ mười sáu của chúng tôi. Trong suốt tuần lễ ở chung, chúng tôi thấy cháu khóc có hai lần. Tại sao? Vì vây quanh cháu là bao nhiêu sự nâng niu, chăm sóc, yêu thương, nuôi dưỡng cho nên cháu chỉ có việc ăn, ngủ và cười. Cũng vậy, điều kiện lý tưởng nhất là ngay khởi đầu kinh nghiệm thuộc linh, Cơ đốc nhân sơ sinh cần có sự nuôi dưỡng này. Tuy nhiên đáng tiếc là thế giới chúng ta đang sống không có những điều kiện đó cho nên trong chương trình và mục đích của Đức Chúa Trời, hội thánh chính là nơi thích hợp cho đời sống Cơ Đốc khởi đầu.")]),n._v(" "),i("p",[n._v("Rất có thể bạn đã phát hiện ra mình có nhiều kẻ thù và đây là những kẻ thù nguy hiểm, quỉ quyệt sẽ dùng mọi thủ đoạn tấn công, khiến bạn thất bại trong nếp sống Cơ Đốc. Ngay từ giây phút quyết định tin Chúa, bạn đã thấy những kẻ thù này khởi sự hoạt động: hoặc bạn sẽ bị cám dỗ phạm một tội lỗi nào đó hay bạn sẽ cảm thấy nản chí ngã lòng. Tuy nhiên điều chắc chắn là khi quyết định tiếp nhận Chúa Cứu Thế, bạn sẽ thấy mọi sự đầy hứng khởi, còn sự kiện bạn có những nghi ngờ, nan đề, thắc mắc, cám dỗ, nản lòng và những khó khăn là chuyện bình thường. Kinh Thánh dạy rằng bạn có ba kẻ thù sẽ thường xuyên gây hấn với bạn suốt đời cho nên bạn cần chuẩn bị đối phó để loại trừ chúng.")]),n._v(" "),i("p",[n._v("Trước hêt, hãy xem những kẻ thù chúng ta phải đối diện là ai. Chúng ta phải lột mặt nạ để thấy rõ chúng là những gì, là những kẻ nào và cách chúng hoạt động ra sao.")]),n._v(" "),i("h3",{attrs:{id:"ma-qui"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#ma-qui","aria-hidden":"true"}},[n._v("#")]),n._v(" Ma quỉ")]),n._v(" "),i("p",[n._v('Kẻ thù đầu tiên là ma quỉ. Chúng ta đã thấy ma quỉ là một hữu thể quyền lực, chống lại Đức Chúa Trời và cám dỗ dân Ngài. Dù nó đã bị Chúa Cứu Thế đánh bại tại thập tự giá, nhưng nó vẫn còn sức mạnh cám dỗ con người làm điều ác. Kinh Thánh gọi nó là "kẻ ác", "ma quỉ", "kẻ giết người", "kẻ nói dối và cha sự dối trá", "kẻ thù nghịch" tìm cách cắn nuốt, "con rắn xưa" và "kẻ kiện cáo anh em chúng ta" (Ma-thi-ơ 13:19; Lu-ca 4:33; Giăng 8:44; 1 Phi-e-rơ 5:8; Khải Huyền 12:9-10).')]),n._v(" "),i("p",[n._v("Giây phút bạn tiếp nhận Chúa Cứu Thế là lúc Sa-tan thảm bại. Nó giận dữ điên cuồng và từ nay sẽ tìm đủ cách cám dỗ, cố sức dẫn bạn vào con đường phạm tội. Nhưng xin bạn đừng hoảng sợ vì nó không thể cướp mất sự cứu rỗi của bạn hay lấy đi sự bảo đảm và đắc thắng Chúa ban cho bạn. Nó sẽ làm mọi cách trong giới hạn quyền lực của nó để gieo nghi ngờ, khiến bạn không biết chắc sự hoán cải của bạn có phải là một thực tại hay không. Bạn không thể tranh luận với nó, vì nó là một tay biện bác mưu lược nhất của mọi thời đại.")]),n._v(" "),i("p",[n._v('Giây phút thử thách đã đến với chước cám dỗ đầu tiên. Bạn cần nhớ đừng bao giờ dựa vào xúc cảm vì nó sẽ thay đổi như phong kỳ trong gió loạn. Phương sách kế tiếp của nó có thể sẽ là làm cho bạn cảm thấy kiêu hãnh như mình là một người quan trọng - khiến bạn tin vào sức mạnh riêng, vào những tham vọng, những ước muốn và mục tiêu riêng. Cũng có khi nó đặt lòng thù ghét vào lòng bạn. Nó sẽ cám dỗ bạn nói những lời thô lỗ, bần tiện về người khác. Nó sẽ để sự ghen tị, bất mãn và độc ác trong lòng bạn. Rồi cũng có lúc nó cám dỗ bạn nói dối, khiến cho bạn tự nhiên đứng vào chỗ của một kẻ giả hình. Dối trá là một trong những tội lỗi tệ hại nhất và có thể phạm bằng tư tưởng, lời nói hay việc làm. Bất cứ điều gì làm với ý định phỉnh gạt người khác là dối trá. Ma quỉ sẽ cố hết sức biến bạn thành một kẻ dối trá. Nó cũng sẽ cố sức khiến bạn làm việc cho nó bằng cách cám dỗ người khác phạm tội - cố làm cho các tín hữu khác lầm lạc. Nếu không cẩn trọng bạn sẽ rơi vào chỗ phục vụ ma quỉ mà không biết. Ma quỉ rất mạnh, quỉ quyệt, tinh ma, mưu lược. Nó được gọi là "chúa đời này", "vua chúa thế gian này", "vua cầm quyền chốn không trung" (2 Cô-rinh-tô 4:4; Giăng 12:31; Ê-phê-sô 2:2).')]),n._v(" "),i("p",[n._v("Ma quỉ sẽ cố sức làm bạn nản lòng, làm bạn lạc hướng; nó sẽ tìm cách khiến cho lời chứng của bạn giảm giá trị; nó sẽ tìm đủ cách phá hủy mối tương giao của bạn với Chúa Cứu Thế, cũng như ảnh hưởng của bạn đối với người khác.")]),n._v(" "),i("p",[n._v('Bạn hỏi "Làm sao tôi thắng được ma quỉ ? Tôi phải làm gì? Tôi phải đi hướng nào? Có lối thoát nào không?"')]),n._v(" "),i("p",[n._v('Thánh Phao-lô trong I Cô-rinh-tô 10:13 viết như sau, "Những sự cám dỗ đến cho anh em, chẳng có sự nào quá sức loài người. Đức Chúa Trời là thành tín, Ngài chẳng hề cho anh em bị cám dỗ quá sức mình đâu; nhưng trong sự cám dỗ, Ngài cũng mở đường cho ra khỏi, để anh em có thể chịu được."')]),n._v(" "),i("p",[n._v("Nhiều năm trước, tôi nghe một người bạn, ông J. Edwin Orr so sánh Cơ Đốc nhân bị Sa-tan tấn công giống như con chuột bị bà nội trợ giơ chổi đập. Con chuột không đứng đó giương mắt nhìn bà nội trợ hay nhìn cái cán chổi, nhưng tức khắc nó tìm một chỗ hiểm hóc nào đó chạy trốn, nghĩa là tìm lối thoát. Vì vậy, Cơ Đốc nhân trước sức tấn công của Sa-tan cũng phải tìm một lối thoát.")]),n._v(" "),i("p",[n._v('Trong câu Kinh Thánh trên Đức Chúa Trời bảo rằng "Ngài sẽ mở đường cho ra khỏi". Xin bạn ghi nhớ điều này: sự cám dỗ của ma quỉ không phải là dấu hiệu của một đời sống không vừa lòng Chúa, mà là dấu hiệu của một đời sống được Chúa vui lòng. Bị cám dỗ không phải là tội lỗi. Cũng xin bạn nhớ rằng, Đức Chúa Trời không cám dỗ con cái Ngài. Chúa không bao giờ khiến cho con cái Ngài nghi ngờ. Mọi nghi ngờ và mọi cám dỗ đều đến từ ma quỉ. Tuy nhiên, Sa-tan chỉ có thể cám dỗ mà không thể buộc bạn đầu hàng cám dỗ, vì bạn cần nhớ rằng nó đã bị Chúa Cứu Thế đánh bại. Quyền lực của nó trở thành vô dụng trong đời sống một Cơ Đốc nhân hoàn toàn tin cậy, đầu phục và tùy thuộc Đức Chúa Trời. Một nhà thơ từng viết, "Ma quỉ run sợ khi thấy dù chỉ là một thánh nhân yếu ớt nhất cầu nguyện."')]),n._v(" "),i("p",[n._v('Khi bảo rằng Sa-tan bị đẩy lui lúc chúng ta đọc Kinh Thánh và nó sẽ chạy như chó bị phỏng khi bị chống cự là quá đơn giản hóa vấn đề. Tuy nhiên, chúng ta có thể nương dựa vào huyết Chúa Cứu Thế khi bị tấn công. Có những lúc chúng ta chỉ có thể ẩn nấp đằng sau Chúa Cứu Thế và xin Ngài lo liệu cho nan đề của chúng ta. Thánh Giu-đe viết như sau, "khi chính mình thiên sứ trưởng Mi-chen chống với ma quỉ giành xác Môi-se còn chẳng dám lấy lời trách móc mà đoán phạt; người chỉ nói rằng: Cầu Chúa phạt ngươi" (c.9). Đó chính là điều chúng ta cần làm - kêu cầu Đức Chúa Trời.')]),n._v(" "),i("p",[n._v('Kinh Thánh cũng dạy rằng chúng ta phải "chống cự ma quỉ thì nó sẽ lánh xa" chúng ta (Gia-cơ 4:7). Nhưng trước đó, Chúa bảo rằng chúng ta phải "tùng phục Đức Chúa Trời". Nếu bạn hoàn toàn tùng phục Chúa, đầu phục Chúa Cứu Thế trăm phần trăm thì lời hứa trong Kinh Thánh quả quyết rằng ma quỉ sẽ bỏ chạy. Ma quỉ run sợ khi bạn cầu nguyện. Nó sẽ bị đánh bại khi bạn trưng dẫn Kinh Thánh và nó sẽ lìa xa khi bạn kháng cự.')]),n._v(" "),i("h3",{attrs:{id:"the-gian"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#the-gian","aria-hidden":"true"}},[n._v("#")]),n._v(" Thế gian")]),n._v(" "),i("p",[n._v('Kẻ thù thứ nhì của bạn là thế gian- thế gian trong nghĩa toàn thể cơ chế trần gian. Thế gian có khuynh hướng đưa chúng ta đến chỗ phạm tội - khiến chúng ta kết bạn với tội ác, với lạc thú, thời trang, những quan niệm và mục tiêu trần tục. Bạn sẽ thấy trong kinh nghiệm tái sinh những lạc thú của bạn đã được nâng lên một phạm trù hoàn toàn mới, vô cùng vinh diệu. Nhiều người bên ngoài thường chê trách đời sống Cơ Đốc nhân chỉ toàn là những qui tắc luật lệ, những điều cấm kỵ, những điều bị cấm đoán va những cái phải khước từ. Đây lại là một lời dối trá khác của ma quỉ. Cơ Đốc Giáo không phải là hàng loạt những những "giới cấm" mà là vô số những điều tích cực phải làm. Bạn sẽ rất bận rộn với những công tác của Chúa Cứu Thế, vô cùng thỏa nguyện với những điều của Ngài khiến cho bạn không còn thì giờ nào cho những điều thuộc về trần gian, giống như sau khi đã ăn một bữa tiệc thịnh soạn, có người mời ăn một củ khoai bạn sẽ trả lời, "cám ơn, tôi đã no lắm rồi."')]),n._v(" "),i("p",[n._v('Các bạn tín hữu trẻ thân mến, đó chính là bí quyết của đời sống Cơ Đốc. Khi trong bạn đầy ắp những điều tốt lành của Chúa Cứu Thế, bạn mến yêu những điều thuộc về Đức Chúa Trời, bạn sẽ không còn thì giờ hay ham thích nào cho những lạc thú tội lỗi của trần gian. Kinh Thánh nói, "Kẻ no nê giày đạp tàng mật dưới chân mình; song điều gì đắng cũng lấy làm ngọt cho kẻ đói khát" (Châm Ngôn 27:7). Tuy nhiên, cái gọi là tinh thần thế gian đã bị rất nhiều tín hữu hiểu lầm và cần được làm sáng tỏ. Đây có lẽ là một trong những khó khăn lớn nhất một tín hữu non trẻ, ít kinh nghiệm phải đương đầu.')]),n._v(" "),i("p",[n._v('Tiến sĩ W.H. Griffith Thomas từng nói, "Có một số yếu tố trong đời sống hàng ngày tự thân không phải là tội lỗi, nhưng có khuynh hướng dẫn đến tội lỗi nếu bị lạm dụng. Lạm dụng nghĩa đen là sử dụng thái quá, cho nên trong nhiều trường hợp, sử dụng thái quá một điều hợp pháp là phạm tội. Vui hưởng lạc thú là chuyện hợp pháp, nhưng nếu lạm dụng sẽ trở thành bất hợp pháp. Ước vọng là phần thiết yếu của một cá tính chân thật, nhưng nó phải được hướng về những mục tiêu hợp pháp và được vận dụng một cách quân bằng. Nghề nghiệp hàng ngày, việc đọc sách, ăn mặc, bè bạn, và những sinh hoạt tương tự khác trong đời sống đều là những điều hợp pháp, nhưng cũng rất dễ trở thành bất hợp pháp, không cần thiết và nguy hại. Suy nghĩ về những nhu cầu thiết yếu của đời sống là cần, nhưng những suy nghĩ này rất dễ thoái hóa trở thành lo lắng và trong một ẩn dụ Chúa nhắc chúng ta rằng nỗi lo lắng về cuộc sống làm nghẹt ngòi hạt giống thuộc linh trong lòng. Kiếm tiền là việc cần thiết cho sự sống hàng ngày nhưng nó rất dễ suy thoái trở thành lòng mê tham tiền bạc và rồi tính chất lừa phỉnh của sự giàu sang sẽ lẻn vào cướp mất đời sống thuộc linh. Như vậy, tinh thần thế gian không chỉ giới hạn vào một cấp độ, một cách thế hay một hoàn cảnh nào của đời sống khiến chúng ta không thể tách biệt các thể loại đó ra và bảo rằng như thế này là trần tục và như thế kia là thanh cao... như thế này là thuộc linh và như thế kia là không thuộc linh. Tinh thần thế gian là một thái độ, một khung cảnh sống, một loại ảnh hưởng thẩm thấu vào toàn thể đời sống và xã hội con người mà chúng ta cần nỗ lực thường xuyên cảnh giác."')]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng "Chớ yêu thế gian cũng đừng yêu các vật trong thế gian" (I Giăng 2:15). Kinh Thánh cũng cảnh cáo rằng thế gian và "những sự tham dục của nó " đều qua đi, "nhưng ai làm theo ý muốn Đức Chúa Trời sẽ còn lại đời đời" (I Giăng 2:17).')]),n._v(" "),i("p",[n._v('Tuy nhiên trong một số những điều kiện, tinh thần thế gian có thể trở thành những nan đề phức tạp trong cuộc sống của thế giới hôm nay. Nhiều bạn trẻ đến với tôi hỏi làm việc này, làm điều kia có sai không, có phải là tội lỗi không. Tôi bảo rằng đến chín mươi phần trăm những nan đề đại loại như trên có thể được giải quyết nếu họ sử dụng câu hỏi đơn giản sau đây: "Chúa Cứu Thế muốn tôi làm gì?" Và câu hỏi nữa là, "Tôi có thể xin Chúa ban phước cho tôi trong việc này không? Chúa nghĩ gì về những trò vui, trò giải trí tôi tham dự, những sách vở tôi đọc, những người bạn tôi giao tiếp, những chương trình TV tôi xem? Tôi có thể mời Chúa cùng tham dự với tôi trong chỗ này hay không?" Vì là Đấng toàn năng cho nên dù thế nào thì Đức Chúa Trời cũng có mặt tại đó, nhưng vấn đề là bạn có nên ở những chỗ như thế hay không?')]),n._v(" "),i("p",[n._v("Điều đó không có nghĩa là trong xã hội, chúng ta trở thành những kẻ hợm hĩnh hay có mặc cảm tự tôn có thể khiến chúng ta rơi vào hiểm họa của thái độ kiêu ngạo thuộc linh còn tệ hại hơn hơn bất cứ tinh thần thế gian nào. Nhưng ngày nay có quá nhiều người xưng mình là Cơ đốc nhân nhưng lại sống thỏa hiệp với thế gian đến nỗi bạn không thể phân biệt được người đó có phải là tín đồ hay không. Đây là điều không thể chấp nhận được.")]),n._v(" "),i("p",[n._v("Cơ Đốc nhân phải nổi bật lên như viên kim cương lóng lánh trên một cái nền thô thiển, phải toàn vẹn hơn bất cứ con người thế gian nào, phải điềm đạm, nhã nhặn, lịch thiệp, ân hậu nhưng cương quyết với những việc mình quyết định làm hay không làm. Cơ Đốc nhân phải tươi tắn, rạng rỡ nhưng cũng phải là người từ khước không để cho trần gian kéo xuống ngang bằng mức thấp thỏi của nó.")]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng "làm điều gì không bởi đức tin là tội lỗi" (Rô-ma 14:23), và Kinh Thánh cũng dạy rằng người nào nghi ngờ là đã tự lên án trong điều mình làm. Nói cách khác, chúng ta không bao giờ làm điều gì không hoàn toàn thấy rõ và không hoàn toàn chắc chắn. Nếu bạn có chút ngờ vực nào về một việc khiến bạn áy náy không biết đó có phải là việc làm theo thế gian hay không thì chính sách tốt nhất là "đừng làm".')]),n._v(" "),i("h3",{attrs:{id:"su-me-tham-cua-xac-thit"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#su-me-tham-cua-xac-thit","aria-hidden":"true"}},[n._v("#")]),n._v(" Sự mê tham của xác thịt")]),n._v(" "),i("p",[n._v('Kẻ thù thứ ba bạn phải tức thời đối đầu là "sự mê tham của xác thịt". Xác thịt đây được hiểu là khuynh hướng gian ác của bản ngã sâu kín bên trong. Dù sau khi bạn đã thực sự được hoán cải thì những khao khát tội lỗi cũ đôi lúc vẫn trở lại khiến cho bạn kinh ngạc không hiểu chúng đến từ đâu. Kinh Thánh dạy rằng bản chất cũ hư hoại vẫn còn đó và những cám dỗ xấu xa kia đã xuất phát từ đó chứ không từ đâu khác. Nói cách khác, bên trong mỗi chúng ta đều có "một kẻ nội thù", là cái "khuynh hướng khốn kiếp lúc nào cũng có mặt để kéo chúng ta xuống." Cuộc chiến đã bùng nổ và ở trong bạn hai bản chất tranh chấp quyết liệt trong một cuộc chiến dành quyền thống trị.')]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng "xác thịt có những điều ưa muốn trái với những điều của Thánh Linh, Thánh Linh có những điều ưa muốn trái với của xác thịt" (Ga-la-ti 5:17), và đây là trận chiến giữa sự sống theo bản ngã và sự sống theo Chúa Cứu Thế. Bản chất cũ của bản ngã không thể nào làm vừa lòng Đức Chúa Trời. Nó không thể được hoán cải cũng không thể vá víu. Tạ ơn Đức Chúa Trời khi Chúa Cứu Thế Giê-xu chết, Ngài đã đưa bạn đến với Ngài và bản chất cũ có thể làm cho bất khiển dụng và bạn có thể "coi mình như chết về tội lỗi" (Rô-ma 6:11). Đây là điều thực hiện bởi đức tin.')]),n._v(" "),i("p",[n._v("Tuy nhiên bạn cần thận trọng phân biệt giữa tận dụng và lạm dụng - giữa những điều được phép làm và những điều không nên làm. Một số những điều này khi bất ngờ xuất hiện có thể là những tham muốn tội lỗi nhưng cũng có khi không phải.")]),n._v(" "),i("p",[n._v('Như Tiến sĩ W. H. Griffith Thomas từng nói, "nguyên nghĩa từ ngữ tham dục là "ước muốn mãnh liệt" và không nhất thiết là ước muốn tội lỗi, vì chúng ta vẫn có những ước muốn tự nhiên của thân xác như đói, khát, là những ước muốn chung của thế giới động vật, tự nó không có gì là tội lỗi. Chỉ khi nào những ước muốn đó bị lạm dụng, chúng mới thành điều ác. Đói là một ham muốn tự nhiên, nhưng tham ăn là ham muốn tội lỗi. Khát là một ước muốn tự nhiên, nhưng quá chén, không tiết độ là một tham muốn tội lỗi. Không nên nhầm lẫn đau ốm, mệt mỏi với biếng nhác. Hôn nhân là xu hướng theo ý muốn Chúa và là thôi thúc của bản chất con người đáp ứng cho các nhu cầu thân xác, tâm trí và xã hội, trong khi đó tà dâm là tội lỗi chống lại ý muốn Đức Chúa Trời, nghịch lại với tất cả những gì là tinh sạch trong thân xác, tâm trí và tâm hồn. Nhưng cũng có những tham dục của xác thịt vốn đã là tội lỗi về tính chất cũng như trong bản chất, thí dụ như ước muốn bằng mọi giá thỏa mãn lòng oán hận cừu thù. Vì vậy, chúng ta phải rất thận trọng phân biệt giữa một dục vọng thật ra chỉ là một ước muốn mạnh mẽ với cùng dục vọng đó nhưng lại là một tham dục tội lỗi. Có thể nói trong một số phương diện, những tội lỗi của bản ngã xác thịt là những tội lỗi kinh khủng hơn hết vì nó đại diện cho những khao khát của một bản chất muốn làm điều ác. Không phải ma quỉ hay thế gian, cũng không phải tấm lòng gian ác có thể ép buộc chúng ta phạm tội, mà phải có sự đồng tình thỏa thuận của ý chí, và chính nơi ý chí mà bản chất xấu xa của chúng ta xuất hiện với điều ác và sức mạnh đáng sợ của nó."')]),n._v(" "),i("p",[n._v('Phao-lô nói rằng ông không để lòng tin cậy nơi xác thịt bao giờ. Ông cũng bảo, "đừng chăm lo cho xác thịt" (Rô-ma 13:14). Trong một chỗ khác ông bảo, "tôi đãi thân thể tôi nghiêm khắc" (I Cô-rinh-tô 9:27). Chúng ta cũng phải tái đầu phục, tái cung hiến chính mình cho Đức Chúa Trời để chúng ta bởi đức tin, có thể coi bản chất cũ thật sự chết cho tội lỗi.')]),n._v(" "),i("h3",{attrs:{id:"chien-dau-chong-quan-thu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chien-dau-chong-quan-thu","aria-hidden":"true"}},[n._v("#")]),n._v(" Chiến đấu chống quân thù")]),n._v(" "),i("p",[n._v('Như vậy ma quỉ, thế gian và bản ngã xác thịt là ba kẻ thù của chúng ta. Là Cơ Đốc nhân, thái độ của chúng ta đối với những kẻ thù này có thể tóm tắt trong chữ từ bỏ. Sẽ không có mặc cả, tương nhượng hay do dự. Ở trong Chúa Cứu Thế như được dạy trong Giăng 15 là con đường khả thể duy nhất cho Cơ Đốc nhân là những người "ở trong" thế gian nhưng không "thuộc về" thế gian. Có người bảo rằng thánh Phao-lô đã đứng trước tình huống tiến thoái lưỡng nan này khi viết thư cho tín hữu Ê-phê-sô, "Phao-lô, sứ đồ của Chúa Cứu Thế Giê-xu...viết cho các thánh đồ...tại Ê-phê-sô, là những người trung tín trong Chúa Cứu Thế Giê-xu..." (Ê-phê-sô 1:1). Ê-phê-sô là địa chỉ giao dịch, nhưng "trong Chúa Cứu Thế" mới là địa chỉ nhà! Đối với ma quỉ, chúng ta chỉ có thể chống cự chúng khi chúng ta tùng phục Đức Chúa Trời. Đối với thế gian, Kinh Thánh dạy rằng, "sự đắc thắng thế gian là đức tin của chúng ta" (I Giăng 5:4). Đối với bản ngã xác thịt, Kinh Thánh dạy rằng, "Hãy bước đi theo Thánh Linh, chớ hề làm trọn những điều ưa muốn của xác thịt" (Ga-la-ti 5:16).')]),n._v(" "),i("p",[n._v('Đây chính là tin mừng cho bạn là những người đã tham dự vào cuộc chiến đấu chống cám dỗ. Chúa không đòi bạn phải chiến đấu một mình. Kinh Thánh trong Rô-ma 8:13 dạy rằng bạn nhờ Thánh Linh sẽ làm chết các việc làm của thân thể. Xin nhớ rằng Chúa Giê-xu đã hứa Ngài không bào giờ lìa chúng ta, không bao giờ bỏ chúng ta. Ngài cũng hứa rằng sau khi từ giã trấn gian, Ngài sẽ ban cho chúng ta một Đấng Yên Ủi khác, là Ngôi Thứ Ba trong Ba Ngôi Đức Chúa Trời, là Đức Thánh Linh (Đấng Yên Ủi nghĩa đen là "Đấng đến sát bên cạnh để giúp đỡ"). Ngài sẽ ở với chúng ta cho đến đời đời (Giăng 14:16) . Chúa Giê-xu phán rằng, "Ta không để cho các con mồ côi đâu, ta sẽ đến cùng các con trong thân vị Đức Thánh Linh." Chúa Giê-xu mô tả Ngài là cây nho và những người tin Ngài là những cành nho liền gốc (Giăng 15).')]),n._v(" "),i("p",[n._v('Đức Thánh Linh là Hữu Thể quyền uy nhất trong trần gian ngày nay. Thời đại Cựu Ước là thời đại của Thượng Đế là Cha. Trong thời gian Chúa Giê-xu tại thế là thời đại Thượng Đế Ngôi Hai là Đức Chúa Con. Kể từ Lễ Ngũ Tuần cho đến nay, chúng ta đang sống trong thời đại Thượng Đế Ngôi Ba là Đức Thánh Linh. Kinh Thánh dạy chúng ta "phải đầy dẫy Đức Thánh Linh" (Ê-phê-sô 5:18), nghĩa đen là, "Hãy được đổ đầy..." và đây là một tiến trình đang tiến hành liên tục. Như Chúa Cứu Thế đã đến bày tỏ cho con người thấy, biết Đức Chúa Trời và để cứu chuộc nhân loại thì Đức Thánh Linh cũng đến để làm Chúa Cứu Thế hiển hiện trong đời sống tín hữu, giúp Cơ Đốc nhân đem sự cứu chuộc của Chúa Cứu Thế đến cho thế giới hư vong đang hấp hối.')]),n._v(" "),i("p",[n._v('Kinh Thánh cho biết ngay giây phút bạn tiếp nhận Chúa Cứu Thế làm Đấng Cứu Chuộc thì Đức Thánh Linh tức khắc ngự vào cư trú trong lòng bạn. Thân thể bạn bây giờ trở thành "đền thờ của Đức Thánh Linh đang ngự trong lòng bạn" (I Cô-rinh-tô 6:19). Thánh Phao-lô cũng cảnh cáo rằng nếu ai không có Thánh Linh của Chúa Cứu Thế thì người đó không thuộc về Ngài. Nhưng rồi bạn bảo rằng, "Tôi không cảm thấy có gì khác lạ trong lòng tôi. Tôi không cảm thấy có Đức Thánh Linh của Đức Chúa Trời trong tôi."')]),n._v(" "),i("h3",{attrs:{id:"duc-tin-la-su-kien"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#duc-tin-la-su-kien","aria-hidden":"true"}},[n._v("#")]),n._v(" Đức tin là sự kiện")]),n._v(" "),i("p",[n._v('Bạn đừng để ý gì hết đến xúc cảm vì bạn không được cứu nhờ xúc cảm. Đối với Đức Thánh Linh, bạn có thể "cảm nhận" được về Ngài hay không. Hãy dựa vào đức tin và chấp nhận sự hiện diện của Ngài là một sự kiện. Ngay giờ này Ngài đang sống trong bạn để giúp bạn sống cuộc sống Cơ đốc nhân. Ngài đang sống trong bạn để tôn đại, tôn vinh, tôn cao Chúa Cứu Thế trong bạn để bạn có thể sống cuộc đời hạnh phúc, đắc thắng, chói sáng, một cuộc sống tôn kính Chúa.')]),n._v(" "),i("p",[n._v('Mạng lệnh trong Kinh Thánh là "Hãy đầy dẫy Đức Thánh Linh" (Ê-phê-sô 5:18). Nếu được đổ đầy bởi Đức Thánh Linh, bạn sẽ sản sinh ra trái Thánh Linh là "yêu thương, vui mừng, bình an, nhịn nhục, nhân từ hiền lành trung tín, mềm mại, tiết độ" (Ga-la-ti 5:22,23). Đầy dẫy Đức Thánh Linh không phải là điều tùy ý, nhưng là một mệnh lệnh phải tuân thủ, một bổn phận phải thi hành.')]),n._v(" "),i("p",[n._v('Làm sao bạn biết mình được đầy dẫy Đức Thánh Linh? Và làm sao để được đầy dẫy? Có cần phải trải qua một kinh nghiệm xúc cảm nào không? Không nhất thiết. Khi bạn trao tất cả mọi điều trong con người mình cho Ngài, lúc đó bởi đức tin bạn có thể tuyên xưng rằng bạn được đầy dẫy Thánh Linh của Đức Chúa Trời nghĩa là Ngài hoàn toàn chiếm hữu bạn. Cam kết thật ra là đầu phục- đầu phục hoàn toàn, tuyệt đối, vô điều kiện và bất khả phục hồi. Đó là điều Phao-lô viết trong thư Rô-ma 12:1, "Hỡi anh em, tôi lấy sự thương xót của Đức Chúa Trời khuyên anh em dâng thân thể mình làm của lễ sống và thánh, đẹp lòng Đức Chúa Trời, ấy là sự thờ phượng phải lẽ của anh em" (Rô-ma 12:1).')]),n._v(" "),i("p",[n._v('Chỉ có Cơ Đốc nhân tận hiến cho Chúa, được đầy dẫy Đức Thánh Linh mới có thể đắc thắng thế gian, xác thịt và ma quỉ. Chính Đức Thánh Linh sẽ chiến đấu cho bạn. "Chúng ta đánh trận không phải với thịt và huyết nhưng với chủ quyền, thế lực, cùng vua chúa thế gian mờ tối này..."(Ê-phê-sô 6:12). Đây là cuộc chiến tâm linh, bạn không thể chống lại ba kẻ thù trên bằng khí giới thông thường, vì chỉ khi chúng ta trở thành những ống dẫn, để Đức Thánh Linh chiến đấu qua chúng ta, chúng ta mới có đắc thắng hoàn toàn. Đối với Chúa Cứu Thế, đừng tiếc giữ bất cứ điều gì. Hãy để Ngài hoàn toàn làm Chúa và làm Chủ cuộc đời bạn, vì Chúa Giê-xu từng nói với các môn đệ, "Các người gọi ta là Thầy, là Chúa, các ngươi nói phải, vì ta thật vậy" (Giăng 13:13).')]),n._v(" "),i("p",[n._v('Tôi tin rằng ý thức quên mình là đặc điểm của trái Thánh Linh. Người bảo rằng "Tôi được đầy dẫy Đức Thánh Linh" là tự đặt mình vào chỗ dễ bị người ta kiểm chứng. Có sứ đồ hay môn đồ nào tự vỗ ngực bảo "Tôi đầy dẫy Đức Thánh Linh" đâu? Trái lại, người ta ghi nhận nhiều người trong số đó "được đầy dẫy Đức Thánh Linh". Người tự ý thức là mình yêu thương, tự ý thức mình vui mừng, tự ý thức mình bình an là người thể hiện mùi bản ngã rõ nhất, như một tín hữu nhận định rất hay rằng "Bản ngã là "cái mùi thân thể thuộc linh" của một người."')]),n._v(" "),i("p",[n._v('Ngày kia có chú bé đút tay vào một chiếc bình quí rồi không rút tay ra được. Người cha tìm đủ cách giúp con kéo tay ra nhưng không được. Cuối cùng, trước khi tính chuyện đập vỡ bình, người cha bảo, "con thử mở bàn tay ra, để xuôi ngón tay như ba làm đây xem có rút được không?" Trước sự ngạc nhiên của mọi người cậu bé nói, "Mở tay ra như thế rơi mất đồng tiền của con thì sao?" Có lẽ bạn cười về cái dại dột trẻ thơ này, nhưng nhiều người trong chúng ta đã hành động y như vậy, lo nắm chặt những cái vô giá trị của trần gian mà không buông bỏ để có thể được giải phóng. Tôi van nài bạn hãy dứt bỏ những cái vặt vãnh của đời sống. Hãy đầu phục Chúa! Hãy buông bỏ tất cả, để Đức Chúa Trời thể hiện đường lối Ngài trong đời sống bạn.')]),n._v(" "),i("p",[n._v('Bây giờ, sau khi đã hoàn toàn giao thác chính bạn cho Chúa Cứu Thế, xin hãy nhớ rằng Ngài chấp nhận những gì bạn dâng phó cho Ngài vì Chúa hứa rằng "kẻ nào đến cùng ta, ta không bỏ ra ngoài đâu" (Giăng 6:37). Bạn đã đến với Ngài và đã được tiếp nhận. Ngài không bao giờ loại trừ, không bao giờ xua đuổi bạn!')]),n._v(" "),i("h3",{attrs:{id:"trai-thanh-linh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#trai-thanh-linh","aria-hidden":"true"}},[n._v("#")]),n._v(" Trái Thánh Linh")]),n._v(" "),i("p",[n._v('Bạn không chỉ có lòng can trường mà còn phải thể hiện ra trái Thánh Linh. Nhưng cần nhớ rằng những trái Thánh Linh này là của Đức Thánh Linh chứ không do bạn tự sinh ra. Căn nguyên những trái Thánh Linh này là từ Đức Chúa Trời. Theo Ga-la-ti chương 5, trái đầu tiên là yêu thương và từ gốc yêu thương này sản sinh ra tất cả những trái khác. Chúa Giê-xu dạy rằng, "Giới răn của ta là các ngươi hãy yêu nhau như ta đã yêu các ngươi... Nếu các ngươi giữ giới răn ta thì sẽ ở trong sự yêu thương của ta, cũng như ta giữ các giới răn của Cha ta và ở trong sự yêu thương của Ngài" (Giăng 15:12,10). Chúng ta cần phân biệt giữa các ân tứ Thánh Linh và trái Thánh Linh. Ân tứ được ban cho, nhưng trái thì được sinh ra. Để có thể sinh ra trái, cành cây và gốc phải gắn liền trong một mối liên lạc tư riêng, thiết cốt, chặt chẽ. Để kết quả, một người cần được trồng và bén rễ trong Chúa Cứu Thế.')]),n._v(" "),i("p",[n._v("Như chúng ta đã nói, một trong những đặc tính của Đức Thánh Linh là ý thức vô kỷ (unself-consciousness). Khi một người ý thức mình có trái Thánh Linh thì tức khắc đã bốc mùi bản ngã!")]),n._v(" "),i("p",[n._v('Một trái khác của Thánh Linh là vui mừng. Một trong những đặc tính của Cơ Đốc nhân là niềm vui nội tâm không tùy thuộc hoàn cảnh bên ngoài. Nê-hê-mi bảo rằng, "Sự vui vẻ của Chúa hằng Hữu là sức lực các ngươi" (Nê-hê-mi 8:10). Mục-sư S.D. Gordon là một tác giả viết sách dưỡng linh nổi tiếng của thế hệ trước nói về sự vui mừng như sau: "Vui mừng là khía cạnh đặc biệt trong cuộc sống Cơ Đốc và cũng là một từ ngữ Cơ Đốc độc đáo. Nó ngược lại với hạnh phúc. Hạnh phúc là kết quả của một sự việc thuận lợi xảy ra trong khi vui mừng có những nguồn mạch chìm sâu bên dưới, là những nguồn mạch không bao giờ cạn tắt cho dù có biến động nào. Chỉ một mình Chúa Giê-xu có thể ban cho chúng ta niềm vui đó. Ngài có một niềm vui sung mãn, sâu xa ngay cả khi ở dưới bóng thập tự giá. Vui mừng là một sự kiện và là một từ ngữ không ai hiểu được nếu không có Ngài tác động bên trong."')]),n._v(" "),i("p",[n._v('Một nhà truyền giáo là Sir Wilfred Grenfeld từng nói: "Niềm vui thật không đến từ sự giàu có hay những dễ dãi của đời sống... hoặc từ lời ca ngợi của con người, nhưng từ những việc làm có giá trị." Alexander MacLaren thì bảo, "Tìm kiếm, theo đuổi niềm vui sẽ mất. Con đường duy nhất được an vui là bước đi vững chắc trên con đường của bổn phận mà không bận tâm nghĩ đến niềm vui, lúc đó niềm vui giống như con cừu, sẽ đến, không cần nhọc công tìm kiếm. Khi chúng ta "ở trên đường" của Chúa, chính niềm vui sâu xa sẽ tìm gặp chúng ta."')]),n._v(" "),i("p",[n._v('Trái của Thánh Linh cũng là sự bình an. Sứ đồ Phao-lô nói, "Chúng tôi bị ép đủ cách nhưng không đến cùng; bị túng thế nhưng không ngã lòng; bị bắt bớ nhưng không đến bỏ; bị đánh đập nhưng không bị chết mất" (2 Cô-rinh-tô 4: 8-9). Chúng ta có thể đi một lượt qua hết phần còn lại của bản danh sách siêu nhiên này trong Ga-la-ti 5, nhịn nhục, nhân từ, hiền lành, trung tín, mềm mại, tiết độ, để có thể thấy tất cả những trái Thánh Linh này chín rộ trong đời sống những người thật sự đầu phục Chúa và được đầy dẫy Thánh Linh.')]),n._v(" "),i("p",[n._v('Chiến thắng thuộc về bạn, hãy đòi cho kỳ được vì đó là quyền cơ bản. Nhà thơ Browning bảo đây là "Điều tuyệt hảo chưa thành." Như vậy không có nghĩa là Cơ Đốc nhân sẽ không bao giờ thất bại hay không bao giờ kinh nghiệm những giai đoạn xuống dốc trong đời sống nhưng có nghĩa là Chúa Cứu Thế đồng đi với bạn dù nan đề là gì. Sự bình an sẽ đến dù bạn ở giữa muôn ngàn khó khăn.')]),n._v(" "),i("h2",{attrs:{id:"chuong-15-nhung-chi-dan-cho-cuoc-song-co-doc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-15-nhung-chi-dan-cho-cuoc-song-co-doc","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 15: Những chỉ dẫn cho cuộc sống Cơ Đốc")]),n._v(" "),i("p",[i("strong",[n._v('"Các ngươi muốn người ta làm cho mình thể nào, hãy làm cho người ta thể ấy." (Lu-ca 6: 31)')])]),n._v(" "),i("p",[n._v("Dù khi chơi, lái xe hoặc làm bếp đều có những qui luật chúng ta phải theo để được an toàn cũng như thành công. Tôi nhớ rất rõ một việc xảy ra gần nhà tôi ở Montreat, North Carolina vài năm trước đây. Con đường giữa Black Mountain và Asheville được mở rộng từ hai thành bốn đường xe chạy. Nhiều tuần lễ trong thời gian thực hiện công trình, con đường không có các vạch sơn phân định cho nên vào một đêm kia, một tai nạn lưu thông thảm khốc đã xảy ra làm cho năm người thiệt mạng. Hiển nhiên, lý do là vì vạch sơn qui định không được vẽ xuống rõ ràng.")]),n._v(" "),i("p",[n._v("Kinh Thánh dạy rằng cuộc sống Cơ Đốc phải được tăng trưởng liên tục. Khi được tái sinh, bạn được sinh vào trong gia đình Đức Chúa Trời. Chúng tôi hiện có 16 cháu nội ngoại mà đứa nào cũng quí. Mỗi đứa cháu đều là một thành viên được chấp nhận và trân trọng trong đại gia đình. Đây chính là cách nhìn của Đức Chúa Trời về bạn. Ngài muốn bạn lớn lên, tăng tiến và trưởng thành trong Chúa Cứu Thế Giê-xu. Nếu bạn còi cọc, chậm lớn hay cứ mãi mãi là một em bé, là một người lùn thuộc linh thì đây là tình trạng trái tự nhiên, ngược lại với qui luật của Đức Chúa Trời. Trong 2 Phi-e-rơ 3:18, Kinh Thánh dạy rằng chúng ta phải tăng trưởng, bao hàm có sự phát triển vững chắc, lớn lên không ngừng và gia tăng khôn ngoan.")]),n._v(" "),i("h3",{attrs:{id:"doc-kinh-thanh-hang-ngay"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#doc-kinh-thanh-hang-ngay","aria-hidden":"true"}},[n._v("#")]),n._v(" Đọc Kinh Thánh Hàng Ngày")]),n._v(" "),i("p",[n._v("Để tăng trưởng đúng mức và có đời sống thuộc linh mạnh khỏe, Cơ Đốc nhân cần tuân giữ một số qui định sau đây. Trước hết, bạn phải đọc Kinh Thánh hàng ngày.")]),n._v(" "),i("p",[n._v('Đây là một trong những đặc ân lớn nhất của bạn. Đời sống thuộc linh của bạn cần thực phẩm. Loại thực phẩm nào? Thực phẩm thuộc linh. Bạn tìm thực phẩm thuộc linh này ở đâu? Kinh Thánh là lời Đức Chúa Trời bày tỏ Chúa Cứu Thế là Bánh Sống cho linh hồn đói khát và là Nước Sống cho trái tim khô hạn. Nếu không có lương thực dưỡng linh mỗi ngày, tâm linh bạn sẽ bị bỏ đói, bạn sẽ mất hết sinh lực thuộc linh. Kinh Thánh dạy, "Hãy ham thích sữa thiêng liêng của đạo, nhờ đó anh em sẽ được tăng trưởng" (1 Phi-e-rơ 2:2). Hãy đọc Kinh Thánh, học Kinh Thánh, suy gẫm Kinh Thánh và học thuộc lòng Kinh Thánh. Chín mươi lăm phần trăm những khó khăn trong cuộc sống làm Cơ Đốc nhân có thể truy nguyên từ tình trạng thiếu đọc và học lời Chúa.')]),n._v(" "),i("p",[n._v("Giả sử như có một nhà khảo cổ tìm được một tập nhật ký của Thành-Cát Tư-Hãn hay của A-lịch-Sơn Đại Đế, hay những bức thư tình của Cleopatra. Hay giả sử như các phi hành gia Hoa-Kỳ khám phá được những thủ bản bí mật trong chuyến đi bộ trên mặt nguyệt cầu. Lúc đó bạn có thể hình dung cảnh người ta chen chúc tranh nhau vào các tiệm sách khắp nước Mỹ tìm mua những sách in lại các tác phẩm đó! Trong khi ở đây, chúng ta có một quyển sách chính Đức Chúa Trời viết cho nhân loại, thế mà đó lại là quyển sách bị những con người - mệnh danh là văn minh, hoặc chỉ trích hay không thèm để mắt vào.")]),n._v(" "),i("p",[n._v("Ở một số vùng trên thế giới, con người không được tự do tụ họp đọc và học Kinh Thánh với những tín hữu khác. Thật ra, ở hầu hết các nơi, con người thực sự đói khát Lời Đức Chúa Trời! Tôi nhớ lại câu chuyện của một nhạc sĩ người Hoa tại Hoa lục đã tin Chúa và trưởng thành mạnh mẽ nhờ đọc từng trang Kinh Thánh do một người bạn vô danh nào đó xé ra và gởi lén vào cho anh. Có những câu chuyện khác về những tù nhân sống sót suốt hai mươi hay ba mươi năm lao động khổ sai, có lúc bị tra tấn trong các trại tập trung, nhưng đã ra khỏi trại với tâm trí bình thản, hoàn toàn không hề cay đắng đối với những người đã ngược đãi họ.")]),n._v(" "),i("p",[n._v("Thêm một câu chuyện nữa về quyền năng của Kinh Thánh thể hiện trong một bệnh viện tâm thần tại Hoa Kỳ. Một thanh niên bị giữ điều trị trong bệnh viện tâm thần viết cho chúng tôi hỏi xin một quyển Kinh Thánh. Tiến trình phục hồi và lành mạnh hẳn khởi sự từ khi anh nhận được Kinh Thánh rồi bắt đầu đọc. Ngày nay anh đã có gia đình và sống cuộc đời tự lập!")]),n._v(" "),i("p",[n._v('Đừng chỉ đọc lướt qua từng chương để thỏa mãn lương tâm nhưng hãy giấu Lời Chúa trong lòng. Một đoạn ngắn được nghiền ngẫm kỹ lưỡng ích lợi cho linh hồn hơn khối lượng nhiều đọc vội. Đừng nản lòng khi bạn thấy mình không hiểu hết. Có một số người không đọc, viện cớ Kinh Thánh "khó hiểu quá." Nếu không đọc, sách nào bạn cũng sẽ thấy khó hiểu! Trong Kinh Thánh, hãy đọc những phần đơn giản trước. Bạn không thể cho em bé sơ sinh ăn thịt bò ngay trong ngày đầu mà phải cho em bú sữa!')]),n._v(" "),i("p",[n._v("Tôi đề nghị bạn khởi sự với sách Tin Lành Giăng trước hết. Khi đọc, Đức Thánh Linh sẽ làm cho từng đoạn Kinh Thánh sáng lên. Ngài sẽ soi rọi vào những từ khó hiểu và làm rõ những ý nghĩa mập mờ. Dù không thể nhớ hay hiểu hết, bạn cứ tiếp tục đọc, vì chính việc đọc Lời Chúa có tác dụng thanh tẩy đối với tâm trí và tấm lòng bạn. Xin đừng để điều gì chiếm chỗ việc quan trọng thường nhật này.")]),n._v(" "),i("p",[n._v("Những lời Kinh Thánh thuộc lòng có thể gợi lại trong trí những lúc bạn không có Kinh Thánh bên cạnh - những đêm không ngủ, khi lái xe, lúc đi xa, thời điểm phải có những quyết định quan trọng... Lời Kinh Thánh sẽ an ủi, hướng dẫn, sửa dạy, khích lệ - nghĩa là đáp ứng mọi nhu cầu tức thời. Bạn nên cố học thuộc lòng càng nhiều câu Kinh Thánh càng tốt.")]),n._v(" "),i("h3",{attrs:{id:"hoc-cau-nguyen"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hoc-cau-nguyen","aria-hidden":"true"}},[n._v("#")]),n._v(" Học Cầu Nguyện")]),n._v(" "),i("p",[n._v('Qui định thứ hai, bạn cần học bí quyết cầu nguyện. Khởi đầu những lời cầu nguyện của bạn có thể ngập ngừng, vụng về, không rõ ràng, nhưng rồi Đức Thánh Linh là Đấng sống trong bạn sẽ giúp đỡ, dạy dỗ bạn. Mỗi lời cầu nguyện bạn dâng lên đều được đáp ứng. Câu trả lời của Chúa có thể là "có", "không" hay Ngài muốn bạn "chờ đợi". Dù thế nào bạn cũng sẽ được Chúa trả lời.')]),n._v(" "),i("p",[n._v("Cầu nguyện là tương giao, truyền đạt. Đối tượng đáp ứng đầu tiên của trẻ thơ là cha mẹ. Em không hỏi xin điều gì cả, em chỉ nhoẻn miệng cười khi thấy cha mẹ cười, em lên tiếng u ơ khi cha mẹ nói chuyện với em, nhưng những đáp ứng đó đã làm cả nhà vui sướng! Cũng vậy, bạn có thể hiểu được những đáp ứng đầu tiên của chúng ta với Đức Chúa Trời sẽ làm Ngài vui sướng đến đâu!")]),n._v(" "),i("p",[n._v('Những lời cầu xin của bạn luôn luôn phải nằm trong khuôn khổ "Ý Cha được nên." "Hãy vui thỏa nơi Chúa Hằng Hữu thì Ngài sẽ ban cho ngươi điều lòng mình ao ước" (Thi-thiên 37:4). Vui thỏa nơi Chúa cần phải đi trước việc thành đạt điều lòng mình ao ước. Chính lòng thỏa nguyện nơi Chúa sẽ hướng dẫn những ước muốn của chúng ta và từ đó Đức Chúa Trời mới có thể ban cho điều chúng ta khẩn nguyện.')]),n._v(" "),i("p",[n._v("Xin nhớ rằng bạn có thể cầu nguyện bất cứ lúc nào và ở bất cứ nơi nào. Lúc đang rửa chén, làm vườn, trong sở, tiệm buôn hay ở sân chơi, thậm chí cả trong trại giam nữa. Khi cầu nguyện bạn biết rằng Đức Chúa Trời lắng nghe! Chúng tôi có người bạn trong danh sách tử tội. Anh cầu nguyện cho chúng tôi mỗi sáng giữa khoảng 4 đến 6 giờ và điều này thật đã khích lệ chúng tôi rất nhiều.")]),n._v(" "),i("p",[n._v('Bạn nên tập cầu nguyện có hệ thống. Cầu nguyện kết hợp với học Kinh Thánh đem lại một đời sống Cơ Đốc khỏe mạnh. Kinh Thánh dạy chúng ta "cầu nguyện không thôi." Nếu bạn dành riêng những thì giờ cầu nguyện đặc biệt trong ngày thì lãnh vực vô thức trong đời sống bạn sẽ được dằm thắm trong tinh thần cầu nguyện giữa khoảng các giờ cầu nguyện chuyên biệt đó. Nếu mỗi sáng thức dậy bạn quì gối nói qua quít vài ba câu thì không đủ; cần phải có những thì giờ nhất định ở riêng với Chúa. Đối với một số bà nội trợ bận rộn hoặc những người chìm ngập trong cảnh sống đầu tắt mặt tối thì việc dành riêng thì giờ cầu nguyện như trên có thể là việc bất khả. Nhưng đây chính là chỗ mở ra cho việc "cầu nguyện không thôi". Chúng ta có thể cầu nguyện đang khi làm việc và như đã nói, chúng ta có thể cầu nguyện bất cứ lúc nào ở bất cứ nơi nào.')]),n._v(" "),i("p",[n._v("Ma quỉ sẽ đánh phá chúng ta trong từng giờ cầu nguyện. Nó sẽ khiến cho con khóc, điện thoại reo, có người gõ cửa đến thăm... nghĩa là sẽ có đủ thứ trở ngại cho giờ cầu nguyện, nhưng mong bạn cứ cương quyết giữ vững, đừng nản chí. Không bao lâu bạn sẽ thấy giờ cầu nguyện trở thành những giây phút thỏa nguyện nhất trong cuộc sống và bạn sẽ trông đợi cho đến giờ được đến quì nơi chân Chúa hơn bất cứ điều gì khác. Thiếu sự cầu nguyện hàng ngày, liên tục, có hệ thống, đời sống bạn sẽ khô cằn, chán nản, không kết quả. Không cầu nguyện liên lỉ bạn chẳng bao giờ biết được sự bình an sâu kín bên trong Đức Chúa Trời rất muốn ban cho bạn.")]),n._v(" "),i("h3",{attrs:{id:"nho-cay-thanh-linh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#nho-cay-thanh-linh","aria-hidden":"true"}},[n._v("#")]),n._v(" Nhờ Cậy Thánh Linh")]),n._v(" "),i("p",[n._v("Qui định thứ ba: bạn cần thường xuyên, liên tục nhờ cậy Đức Thánh Linh. Cần nhớ rằng Chúa Cứu Thế sống trong bạn là qua Đức Thánh Linh. Thân thể bạn bây giờ là nơi cư trú của Ngôi Ba trong ba ngôi Đức Chúa Trời. Bạn không được phép nhờ Ngài như sai bảo người gíup việc, nhưng phải xin Ngài ngự vào làm mọi sự, xin Ngài cầm quyền tể trị toàn thể đời sống bạn. Hãy thổ lộ với Ngài tất cả những yếu đuối, bất năng, bất ổn, không đáng tin của con người bạn. Hãy đứng tránh sang một bên để Ngài quyết định, chọn lựa mọi sự cho đời sống bạn. Khi biết Đức Thánh Linh cầu nguyện cho chúng ta (Rô-ma 8), thì dù là người yếu đuối nhất chúng ta cũng thấy thật được an ủi.")]),n._v(" "),i("p",[n._v('Bạn không thể nào giữ mình trong nếp sống Cơ Đốc vì chỉ Đức Thánh Linh mới có thể giúp bạn. Nhưng nếu bạn vật lộn, tranh đấu, vùng vẫy thì rất khó cho Ngài. Xin hãy để tâm hồn bạn thư giãn và an nghỉ trong Chúa. Hãy buông bỏ tất cả mọi căng thẳng nội tâm cùng mọi loại mặc cảm. Hãy nhờ cậy Ngài hoàn toàn, đừng băn khoăn lo lắng về những quyết định quan trọng, hãy để Ngài quyết định cho bạn. Đừng lo lắng về ngày mai - Ngài là Đức Chúa Trời của tương lai, Ngài đã thấy kết cuộc ngay từ lúc khởi đầu. Đừng lo lắng về những nhu cầu của đời sống vì Ngài lúc nào cũng có mặt để lo liệu và chu cấp cho bạn. Cơ Đốc nhân đắc thắng thực sự là người thay vì căng thẳng, lo lắng, tranh chiến nội tâm thì hoàn toàn tin cậy rằng Đức Chúa Trời đang cầm quyền tể trị và chắc chắn cuối cùng sẽ thắng. Khi nương nhờ Đức Thánh Linh bạn sẽ thấy nhiều tật bệnh trong thân xác cũng như trong tinh thần sẽ cùng với bao nhiêu âu lo, tranh chiến, căng thẳng tan biến đi. Cho dù khó khăn của chúng ta là gì, hoàn cảnh ra sao, chúng ta cần phải nhớ rằng "Chúa Giê-xu là Đấng chiến thắng", nói theo ngôn từ của Corrie ten Boom, một anh thư đức tin trong thời Đức Quốc Xã.')]),n._v(" "),i("h3",{attrs:{id:"di-nha-tho-deu-dan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#di-nha-tho-deu-dan","aria-hidden":"true"}},[n._v("#")]),n._v(" Đi Nhà Thờ Đều Đặn")]),n._v(" "),i("p",[n._v("Qui định thứ tư: Bạn cần quyết tâm tập thói quen đi nhà thờ đều đặn. Cơ Đốc Giáo là tôn giáo của tình thân hữu. Theo Chúa Giê-xu có nghĩa là yêu thương, công chính, phục vụ, và tất cả những điều này chỉ có thể thành đạt và thể hiện qua những mối quan hệ xã hội. Những mối quan hệ xã hội này được thấy trong hội thánh.")]),n._v(" "),i("p",[n._v('Có hội thánh hữu hình và hội thánh vô hình. Hội thánh vô hình bao gồm tất cả các tín hữu chân chính suốt qua lịch sử hội thánh cho đến tận thế. Hội thánh hữu hình bao gồm các giáo hội Cơ đốc giáo, cả công giáo lẫn Tin Lành, trong đó giáo hội Tin Lành có nhiều hệ phái. Lời Chúa trong Kinh Thánh dạy chúng ta rằng, "Chớ bỏ qua sự nhóm lại như mấy kẻ quen làm..." (Hê-bơ-rơ 10:25). Cơ Đốc nhân cần tương giao - mối tương giao của anh chị em trong Chúa.')]),n._v(" "),i("p",[n._v('Hội thánh hữu hình là Hội Thánh của Chúa Cứu Thế trên đất. Đây là nơi chúng ta họp lại để thờ phượng Đức Chúa Trời, để học hỏi Lời Ngài và để thông công với các tín hữu khác. Kinh Thánh gọi hội thánh là "thánh quốc," "dân thuộc riêng về Đức Chúa Trời," "người nhà của Đức Chúa Trời," "đền thờ của Chúa," "nơi ngự của Thánh Linh Đức Chúa Trời," "thân thể Chúa Cứu Thế." Đây là những ngôn từ hình bóng, những biểu tượng hay hình ảnh dùng để chỉ thị thực tại thuộc linh của hội thánh.')]),n._v(" "),i("p",[n._v("Không gì có thể thay thế cho việc đi nhà thờ. Là môn đồ chân chính của Chúa Giê-xu bạn sẽ thấy những lý do bỏ nhóm như trời nóng quá hay lạnh quá, trời mưa hay trời tuyết... là những lý cớ không xứng đáng đối với người tín đồ thật. Có nhiều người bảo rằng họ có thể ở nhà sáng Chúa Nhật và thờ phượng Chúa trong lòng. Người nào làm như thế là đã không dâng cho Chúa sự thờ phượng Ngài có quyền đòi hỏi, vì Chúa đã tạo dựng chúng ta không chỉ có tâm trí và tấm lòng mà cả thân xác nữa; vì vậy cả thân xác và tâm trí đều cần phải cùng tham dự vào việc dâng lên Chúa hành động thờ phượng toàn diện.")]),n._v(" "),i("p",[n._v("Tuy nhiên ở một số các quốc gia ngày nay việc nhóm họp tại nhà thờ không được khuyến khích. Trong nhiều năm, tín hữu đã buộc phải hội họp riêng tại các tư gia, giới hạn trong vòng các thành viên trong gia đình và có thể thêm một vài người bạn nữa. Thí dụ như ở Trung Hoa, chính quyền đã cho phép mở cửa lại một số nhà thờ trước đây bị đóng cửa và sự kiện tín hữu chen chúc đến nhóm chật ních cho thấy những điều mới nói ở trên là đúng. Cơ Đốc nhân cần lẫn nhau. Chúng ta cần hội họp lại để thờ phượng Đức Chúa Trời, và không gì có thể thay thế được việc đi nhóm họp thờ phượng Chúa.")]),n._v(" "),i("p",[n._v("Trong khi đó tôi nghĩ chúng ta cũng nên biết ơn đối với hội thánh điện tử. Nhiều người phải ở trong các bệnh viện, các nhà dưỡng lão hay ngay cả trong các nhà tù là những nơi chỉ có thể thờ phượng Chúa qua phương tiện truyền hình hay truyền thanh. Nhưng rồi cũng có một số người tuy không ở trong các hoàn cảnh đặc biệt trên nhưng bảo rằng họ có thể ở nhà nghe giảng qua đài truyền thanh hay truyền hình, thay thế cho việc đi thờ phượng Chúa ở nhà thờ. Điều này không đủ. Bạn không đi nhà thờ chỉ để nghe giảng. Bạn đi nhà thờ để thờ phượng Đức Chúa Trời, để phục vụ Ngài cũng như để tương giao với các tín hữu khác. Bạn không thể là một Cơ Đốc nhân hạnh phúc, thành công mà không trung tín, trung thành trong hội thánh. Trong hội thánh, bạn có nơi để phục vụ Chúa, vì chúng ta được Chúa cứu để phục vụ Ngài. Cơ Đốc nhân hạnh phúc là Cơ Đốc nhân phục vụ.")]),n._v(" "),i("h3",{attrs:{id:"tro-nen-chung-nhan-co-doc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#tro-nen-chung-nhan-co-doc","aria-hidden":"true"}},[n._v("#")]),n._v(" Trở Nên Chứng Nhân Cơ Đốc")]),n._v(" "),i("p",[n._v("Qui định thứ năm: bạn cần trở thành một chứng nhân Cơ Đốc. Nếu bạn trung tín thực hành bốn qui tắc trên thì qui tắc thứ năm này sẽ được thực hiện một cách tự nhiên, giống như nếu cứ tiếp tục rót trà, tách nước sẽ tràn ra.")]),n._v(" "),i("p",[n._v('Cách đây ít lâu tôi nghe có người thắc mắc không biết cách làm chứng nào quan trọng hơn, làm chứng bằng đời sống hay làm chứng bằng Lời Chúa? Và câu trả lời là, "Đối với máy bay, cái cánh nào quan trọng hơn? Cánh bên phải hay cánh bên trái?" Nghĩ rằng câu trả lời đó rất hay cho nên một ngày kia khi đang lái xe đưa mấy giáo sĩ đi ăn trưa, tôi đã lặp lại lời trên. Một giáo sĩ lên tiếng bảo rằng, "Câu nói đó hay thật, nhưng không đúng." Tôi ngạc nhiên hỏi bà nói như vậy có nghĩa gì. Bà trả lời, "Trong suốt cả Kinh Thánh Đức Chúa Trời chỉ hứa ban phước cho Lời Ngài, chứ không cho cách sống của chúng ta: ‘Lời ta sẽ không trở về vô ích mà sẽ làm xong công tác ta sai khiến nó... Ai có lời ta, hãy rao giảng cách trung tín.’" Tôi nhận ra bà giáo sĩ này đã nói lên chân lý. Chúng ta chịu trách nhiệm với Đức Chúa Trời về cách sống của chúng ta nhưng chính là Lời Chúa mới được Đức Chúa Trời hứa ban phước và điều này giải thích tại sao một nhạc sĩ trong một nước cộng sản khi nhặt được một trang Kinh Thánh đã nhờ đó mà được hoán cải. Cũng như một viên bộ trưởng y tế Bồ Đào Nha khi trở về nhà trong một ngày mưa lầy lội, thấy một mảnh giấy giắt vào giầy, gỡ ra, đọc được ít lời Kinh Thánh và đã tin nhận Chúa.')]),n._v(" "),i("p",[n._v("Là Cơ Đốc nhân chúng ta được Chúa chỉ định và sai phái làm đại sứ của Vua muôn vua. Hãy để lá cờ nước Trời bay cao trên nóc tòa đại sứ. Giả sử viên đại sứ Hoa Kỳ tại Mạc Tư Khoa ra lệnh hạ cờ Mỹ xuống vì người Nga không thích lá cờ đó. Chắc chắn viên đại sứ sẽ tức khắc bị triệu hồi ngay. Ông ta không còn xứng đáng đại diện cho nước Mỹ nữa.")]),n._v(" "),i("p",[n._v("Nếu chúng ta không sẵn sàng để lá cờ Cơ Đốc nhân bay trên nhà chúng ta, trên sở làm, ngoài cửa tiệm, nơi lớp học thì chúng ta không còn xứng đáng làm đại sứ cho Chúa Cứu Thế! Chúng ta cần chuẩn bị sẵn sàng và cho mọi người chung quanh biết chúng ta là Cơ Đốc nhân. Chúng ta phải làm chứng nhân cho Chúa Cứu Thế.")]),n._v(" "),i("p",[n._v("Chúng ta nói về Chúa bằng hai cách: qua đời sống và bằng lời nói - cả hai cách này phải cùng thực hiện song hành. Mục đích của Đức Chúa Trời cho bạn và tôi sau khi chúng ta được hoán cải là để chúng ta bày tỏ về ân sủng và quyền năng cứu chuộc của Chúa Giê-xu. Chúng ta là những cảm tử quân của Chúa Cứu Thế, Chúng ta phải là chiến sĩ của Ngài.")]),n._v(" "),i("p",[n._v('Chúa Cứu Thế phán, "Ai xưng ta ra trước mặt thiên hạ, ta sẽ xưng người đó ra trước mặt Cha ta trên thiên đàng" (Ma-thi-ơ 10:32). Sách Công Vụ 28:32 mô tả một khung cảnh vô cùng cảm động. Sứ đồ Phao-lô bị giam giữ tại Rô-ma, nhưng từ sáng đến chiều, "khuyên bảo" những người đến thăm về Chúa Giê-xu. Đối với mỗi người chúng ta, đây là việc phải làm hàng ngày, như thí dụ của Chúa Giê-xu, "Có một người đi ra gieo giống..."')]),n._v(" "),i("p",[n._v("Cậu bé phát tin tại công ty Western Union không được phép có một sáng kiến riêng nào. Bổn phận duy nhất của cậu là chuyển các bức điện tín đến tay người nhận. Có lẽ cậu không thích một số các tin tức đó - có những tin buồn, những tin làm người nhận nản chí, mất tinh thần. Nhưng cậu không được phép dừng lại dọc đường bóc thư ra xem rồi sửa đổi nội dung. Bổn phận của cậu chỉ là đưa tin.")]),n._v(" "),i("p",[n._v('Là Cơ Đốc nhân chúng ta có Lời Đức Chúa Trời. Vị Tổng Tư Lệnh bảo chúng ta, "Hãy đi, đem sứ điệp này cho thế giới đang hấp hối." Một số người trong chúng ta bỏ qua, không lưu tâm gì đến mệnh lệnh này. Một số khác bóp méo sứ điệp, thay thế bằng sứ điệp của mình. Có người lại bớt đi, cắt xén một phần sứ điệp. Người khác lại phao lên rằng Chúa nói vậy nhưng không phải vậy đâu! Có người lại bảo những sứ điệp đó không phải của Chúa nhưng do những con người bình thường viết ra mà chính họ cũng không rõ nội dung.')]),n._v(" "),i("p",[n._v("Chúng ta cần nhớ rằng nhiều thế kỷ trước sứ đồ Phao-lô đã khuyên giục các Cơ Đốc nhân chỉ nên dạy Lời Chúa mà thôi. Nên nhớ rằng chúng ta gieo giống. Dù có những hạt giống rơi trên đất cứng, số khác rơi nhằm bụi gai, nhưng công việc của chúng ta vẫn là tiếp tục gieo giống. Chúng ta không ngừng gieo chỉ vì thấy một số thế đất không triển vọng.")]),n._v(" "),i("p",[n._v("Chúng ta đang cầm đèn và phải để ngọn đèn tỏa sáng! Dù có thể đó chỉ là một ngọn nến hắt hiu trong bóng tối mênh mông, chúng ta vẫn có bổn phận phải để đèn chiếu sáng.")]),n._v(" "),i("p",[n._v("Chúng ta đang thổi kèn báo động. Giữa những tiếng ồn ào hỗn loạn tại chiến trường, tiếng kèn nhỏ của chúng ta tưởng như tắt lịm, nhưng chúng ta vẫn phải tiếp tục thổi để cảnh tỉnh những người đang ở chỗ hiểm nguy.")]),n._v(" "),i("p",[n._v("Chúng ta đang nhóm lên ngọn lửa. Giữa một thế giới lạnh cóng đầy hận thù, ích kỷ, ngọn lửa nhỏ của chúng ta dường như không thấm vào đâu, nhưng chúng ta vẫn phải tiếp tục giữ cho mồi lửa không tàn.")]),n._v(" "),i("p",[n._v('Chúng ta đang đập búa. Mỗi nhát búa dường như chỉ làm cánh tay tê dại, nhưng chúng ta vẫn phải tiếp tục quai. Amy Carmichael ở Ấn Độ có lần hỏi một thợ đục đá nhát búa nào làm vỡ tảng đá, anh ta trả lời, "nhát đầu và nhát cuối, cùng với những nhát ở giữa!"')]),n._v(" "),i("p",[n._v("Chúng ta có bánh cho thế giới đang đói khát. Con người dường như đang ăn những thứ khác và không chịu chấp nhận Bánh Hằng Sống, nhưng chúng ta vẫn cứ phải ban ra, vẫn cứ phải phân phát cho mọi linh hồn.")]),n._v(" "),i("p",[n._v('Chúng ta có nước cho những người chết khát. Chúng ta phải đứng lên kêu to, "Hỡi những ai đang khát hãy đến suối nước." Đôi khi họ không thể đến và chúng ta phải đem đến cho họ.')]),n._v(" "),i("p",[n._v("Chúng ta phải kiên trì, không bao giờ bỏ cuộc, tiếp tục sử dụng Lời Hằng Sống!")]),n._v(" "),i("p",[n._v("Chúa Giê-xu bảo rằng nhiều hạt giống chúng ta gieo tìm được đất tốt, mọc lên và kết quả. Chúng ta phải là những chứng nhân trung tín. Kinh nghiệm hứng khởi nhất con người từng biết là đưa dắt được một người đến với Chúa Cứu Thế Giê-xu.")]),n._v(" "),i("p",[n._v("Tôi coi việc giúp người khác hiểu biết ơn cứu rỗi của Chúa Cứu Thế là một đặc ân và lúc nào cũng thấy vui sướng khi biết có người nghe Tin Lành, tiếp nhận Chúa Cứu Thế và được ân sủng Ngài biến cải. Đây là điều có giá trị hơn tất cả tiền bạc trên thế gian. Không có một niềm hạnh phúc nào, một kinh nghiệm nào, một cuộc phiêu lưu lãng mạn nào có thể so sánh với nỗi sung sướng khi đưa dắt được một người đến với Chúa.")]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng, "Người khôn ngoan có tài được linh hồn người ta" (Châm Ngôn 11:30). Sách Đa-ni-ên 12:3 chép rằng, "Những kẻ khôn sáng sẽ được rực rỡ như sự sáng trên vòng khung; và những kẻ dắt đem nhiều người về sự công bình sẽ sáng láng như những ngôi sao đời đời mãi mãi." Phúc Âm Ma-thi-ơ 5:13 chép rằng, "Các ngươi là muối của đất". Muối làm cho người ta khát. Đời sống bạn có làm cho người khác khát khao nước hằng sống không?')]),n._v(" "),i("h3",{attrs:{id:"chung-ta-hay-yeu-thuong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chung-ta-hay-yeu-thuong","aria-hidden":"true"}},[n._v("#")]),n._v(" Chúng Ta Hãy Yêu Thương")]),n._v(" "),i("p",[n._v('Qui định thứ sáu: Hãy để tình thương làm nguyên tắc chỉ đạo trong đời sống bạn. Hãy để cho tình thương điều khiển. Chúa Giê-xu dạy các môn đồ, "Bởi điều này thiên hạ nhận biết các ngươi là môn đồ ta, đó là các ngươi phải yêu thương nhau." Một chỗ khác trong Kinh Thánh cũng dạy cùng điều đó, "Hỡi kẻ rất yêu dấu, chúng ta hãy yêu mến lẫn nhau; vì sự yêu thương đến từ Đức Chúa Trời, kẻ nào yêu thì sanh từ Đức Chúa Trời và nhìn biết Đức Chúa Trời. Ai chẳng yêu thì không biết Đức Chúa Trời; vì Đức Chúa Trời là sự yêu thương. Lòng Đức Chúa Trời yêu chúng ta đã bày tỏ ra trong điều này: Đức Chúa Trời đã sai Con Ngài đến thế gian, đặng chúng ta nhờ Con được sống. Nầy sự yêu thương ở tại đây: Ấy chẳng phải chúng ta đã yêu Đức Chúa Trời, nhưng Ngài đã yêu chúng ta và sai Con Ngài làm sinh tế chuộc tội chúng ta" (I Giăng 4:7-10). Yêu thương không nhất thiết có nghĩa là tán đồng mọi sự với đối tượng yêu. Nếu Đức Chúa Trời ngồi chờ cho đến khi Ngài bằng lòng về chúng ta trước khi sai Con Ngài đến cứu chuộc thì bây giờ chúng ta ở đâu?')]),n._v(" "),i("p",[n._v("Trong tất cả những báu vật Đức Chúa Trời ban cho con cái Ngài, tình thương là điều lớn nhất. Trong tất cả các trái Thánh Linh, tình thương đứng đầu. Kinh Thánh dạy rằng các môn đệ Chúa Cứu Thế phải yêu thương nhau như Đức Chúa Trời đã yêu thương chúng ta đến nỗi đã sai Con Ngài chịu chết trên cây thập tự thay thế chúng ta. Kinh Thánh dạy rằng chính giây phút chúng ta đến với Chúa Cứu Thế, Ngài ban cho chúng ta tình thương siêu nhiên, qua tác động của Đức Thánh Linh tuôn đổ tình thương đó trong lòng chúng ta. Sự kiện chúng ta là Cơ Đốc nhân được thể hiện rõ ràng nhất khi chúng ta yêu thương nhau. Học được bí quyết này của Đức Chúa Trời ngay từ những kinh nghiệm Cơ Đốc đầu tiên là chúng ta đã đi được một quãng đường khá xa, tiến đến cuộc sống Cơ đốc hạnh phúc, trưởng thành.")]),n._v(" "),i("h3",{attrs:{id:"hay-lam-mot-co-doc-nhan-vang-loi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hay-lam-mot-co-doc-nhan-vang-loi","aria-hidden":"true"}},[n._v("#")]),n._v(" Hãy Làm Một Cơ Đốc nhân Vâng Lời")]),n._v(" "),i("p",[n._v("Qui định thứ bảy: bạn hãy làm một Cơ Đốc nhân vâng phục. Hãy để Chúa Cứu Thế đứng đầu trong mọi lựa chọn của đời sống bạn. Hãy tôn Ngài làm Thầy, làm Chúa.")]),n._v(" "),i("h3",{attrs:{id:"hoc-cach-doi-pho-cam-do"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hoc-cach-doi-pho-cam-do","aria-hidden":"true"}},[n._v("#")]),n._v(" Học Cách Đối Phó Cám Dỗ")]),n._v(" "),i("p",[n._v("Qui định thứ tám: học cách đối phó cám dỗ. Như chúng ta đã học, cám dỗ là điều tự nhiên. Bị cám dỗ không phải là tội, chiều theo cám dỗ mới là phạm tội. Đức Chúa Trời không bao giờ đưa cám dỗ đến cho chúng ta, nhưng Ngài cho phép nó thử thách chúng ta. Cám dỗ là việc làm của ma quỉ, chúng ta có thể nhận diện bằng cách này. Một trong những phương thức đối diện cám dỗ là trưng dẫn một câu Kinh Thánh đánh thẳng vào Kẻ Cám Dỗ - nó sẽ bỏ chạy vì nó không thể chịu nổi Lời Đức Chúa Trời.")]),n._v(" "),i("p",[n._v('Khi Chúa Giê-xu bị cám dỗ trong sa mạc Ngài chỉ có nguồn trợ lực duy nhất là Lời Đức Chúa Trời. Ba lần Chúa tuyên bố, "Có Lời chép rằng."')]),n._v(" "),i("p",[n._v('Bạn hãy nói với ma quỉ, "Đức Chúa Trời phán rằng," nó sẽ chạy trốn. Cùng lúc đó, Chúa Cứu Thế Giê-xu qua Đức Thánh Linh sẽ chiến đấu cho bạn. Hãy làm theo cách một bé gái nói, "Mỗi khi nghe tiếng ma quỉ, cháu nhờ Chúa Giê-xu ra mở cửa."')]),n._v(" "),i("p",[n._v("Mọi người đều bị cám dỗ, nhưng có người lại thích. Họ có vẻ sung sướng khi bị cám dỗ. Cầm chổi rượt đánh con chuột bạn sẽ thấy nó không giương mắt nhìn cán chổi mà tìm một lỗ trống. Bạn hãy dời mắt khỏi cám dỗ mà dán mắt vào Chúa Cứu Thế!")]),n._v(" "),i("p",[n._v('Có lần tôi hỏi một viên sĩ quan bộ binh khi ở ngoài mặt trận ông muốn có những người lính can đảm hay những người tuân lệnh. Câu trả lời tức khắc của ông là "Tuân lệnh!" Đức Chúa Trời muốn bạn vâng lời Ngài hơn bất cứ điều gì khác. Để vâng lời Chúa bạn phải biết mệnh lệnh của Ngài, và đây là một lý do nữa cho thấy tính chất cần thiết của việc đọc và học Kinh Thánh. Kinh Thánh là la bàn và là sách luật. Xin hãy tuân giữ những điều Đức Chúa Trời dạy bạn.')]),n._v(" "),i("h3",{attrs:{id:"tro-nen-mot-co-doc-nhan-quan-bang"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#tro-nen-mot-co-doc-nhan-quan-bang","aria-hidden":"true"}},[n._v("#")]),n._v(" Trở Nên Một Cơ Đốc nhân Quân Bằng")]),n._v(" "),i("p",[n._v('Qui định thứ chín: bạn cần trở nên một Cơ Đốc nhân quân bằng. Có một câu nói rất hay bảo rằng, "Một số Cơ Đốc nhân quá lo nghĩ đến thiên đàng đến nỗi trở thành vô dụng trong trần gian."')]),n._v(" "),i("p",[n._v('Rõ ràng, Kinh Thánh dạy chúng ta phân rẽ với tội lỗi, nhưng không có chỗ nào trong Kinh Thánh bảo chúng ta phải trở thành lập dị hay bất tự nhiên. Chúng ta cần có cuộc sống tươi sáng, cao thượng, bặt thiệp, thân thể sạch sẽ, tâm trí trong sạch, tư cách điềm đạm và khả ái. Đối với những lời tán tụng rẻ tiền, những chuyện ngồi lê đôi mách, những đàm luận nhảm nhí, những lối cười đùa nham nhở cần phải tránh như tránh rắn độc. Ngoại diện của chúng ta cần gọn gàng, sạch sẽ, dễ mến, thời trang càng thích hợp càng tốt nhưng không diêm dúa. Tất cả những gì thái quá đều phải tránh. Đời sống và phong cách của chúng ta phải tôn cao Phúc Âm làm cho đạo Chúa trở nên hấp dẫn đối với người ngoài. Như cố tiến sĩ Barnhouse từng nói, "Con người có thể không được đọc phúc âm trong những cuốn Kinh Thánh bìa da, nhưng họ không thể tránh đọc phúc âm trong đôi giày da- nghĩa là trong đời sống những người tin Chúa."')]),n._v(" "),i("h3",{attrs:{id:"song-vuot-len-tren-hoan-canh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#song-vuot-len-tren-hoan-canh","aria-hidden":"true"}},[n._v("#")]),n._v(" Sống Vượt Lên Trên Hoàn Cảnh")]),n._v(" "),i("p",[n._v("Qui định thứ mười: bạn hãy sống vượt lên trên hoàn cảnh. Bạn như thế nào là do Đức Chúa Trời tạo dựng! Bạn ở đâu là do Ngài sắp xếp! Vì vậy bạn có thể phục vụ Chúa và tôn vinh Ngài tốt nhất là bằng chính con người bạn ở ngay tại nơi đang sống. Một số người luôn luôn nhìn sang bên kia hàng rào nghĩ rằng cỏ bên đó xanh hơn. Họ tốn biết bao nhiêu thì giờ mong cho tình hình đổi khác, cố tìm những lý do tại sao không có những thay đổi đó đến nỗi đã bỏ lỡ bao nhiêu lợi điểm, bao nhiêu cơ hội mở ra ngay trước mặt.")]),n._v(" "),i("p",[n._v('Xin hãy như Phao-lô khi bảo rằng "Tôi không để những việc này ảnh hưởng đến tôi, tôi không kể sự sống tôi là quí" (Công vụ 20:24). Ông cũng bảo đã tập sống trong lúc dư dật cũng như khi thiếu thốn. Ông đã tập làm một Cơ Đốc nhân chân chính ngay cả trong những lúc lao tù. Đừng để cho hoàn cảnh vùi dập nhưng hãy tập sống bình thản trong mọi cảnh ngộ, ý thức rằng chính Chúa đang ở với bạn.')]),n._v(" "),i("p",[n._v("Những nguyên tắc và những gợi ý này tưởng chừng như quá đơn sơ, nhưng nếu tuân giữ, bạn sẽ thấy kết quả. Tôi đã từng thấy những điều này được thử nghiệm trong cuộc sống hàng nghìn người, tôi cũng đã trắc nghiệm trong chính đời sống tôi. Nếu được tuân giữ chu đáo, trung tín, những qui định này sẽ đem bình an, hạnh phúc, vui tươi đến cho linh hồn và tâm trí bạn, và rồi bạn sẽ học được bí quyết sống cuộc đời thỏa nguyện.")]),n._v(" "),i("h2",{attrs:{id:"chuong-16-co-doc-nhan-va-giao-hoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-16-co-doc-nhan-va-giao-hoi","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 16: Cơ Đốc nhân và giáo hội")]),n._v(" "),i("p",[i("strong",[n._v('"Anh em cũng nhờ Ngài mà được dự phần vào nhà đó, đặng trở nên nhà ở của Đức Chúa Trời trong Đức Thánh Linh" (Ê-phê-sô 2:22)')])]),n._v(" "),i("p",[n._v("Người là một sinh vật xã hội, có bản chất thích sống tập đoàn, thấy an toàn và thỏa mãn nhất khi ở gần những người đồng hội đồng thuyền. Trong số tất cả những tập thể của con người, trong số tất cả những bộ lạc, tộc họ, tổ chức và hội đoàn của con người suốt qua lịch sử, không có một tập thể nào mạnh mẽ, triệt để và phổ quát hơn hội thánh.")]),n._v(" "),i("p",[n._v('Trong những thời kỳ sơ khai, con người sống quần tụ để bảo vệ lẫn nhau, và rất lâu về sau, họ mới biết kết hợp vì lạc thú và lợi ích hỗ tương. Trong nền văn minh tiến bộ hơn, những hội đoàn tư xuất hiện để đem lại cho hội viên một ý thức liên đới, một cảm nhận được "biệt riêng", nghĩa là được phân biệt hẳn với những người không phải là hội viên. Những lời tuyên thệ, những nghi thức nhập hội, và nội qui được thiết định, đem lại tính cách quan trọng cho hội đoàn.')]),n._v(" "),i("p",[n._v('Những tổ chức theo chủng tộc và quốc gia được thành lập mà quyền gia nhập giới hạn cho những người sinh trưởng ở cùng một vùng địa dư hay cùng cam kết đứng chung dưới một ngọn cờ. Các câu lạc bộ, các hội đoàn trong đại học, các hội quán, các hội văn bút, các đảng chính trị, các tổ chức quân sự, tất cả những hội đoàn này, từ các "hội phụ lão" đến "các băng đảng" trong giới học sinh thể hiện nhu cầu con người cần được an ủi và khích lệ giữa vòng những người chấp nhận họ vì có cùng lối sống.')]),n._v(" "),i("p",[n._v("Tuy nhiên không ở nơi nào con người có thể tìm được sự an ủi, sự khích lệ, sự bình an giống như có ở trong hội thánh, vì hiển nhiên trong tất cả những cộng đoàn khác, những sự khích lệ đó chỉ đến từ con người. Những cộng đoàn đó vạch ra những biên giới giả tạo và chỉ dựng lên được một ảo tưởng về sự bảo vệ; trong khi đó hội thánh là một cơ thể sống động nhận năng lực từ chính Chúa để có ý nghĩa và sức sống, thay vì dựa vào những nguồn mạch bên ngoài.")]),n._v(" "),i("h3",{attrs:{id:"khoi-nguyen-hoi-thanh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#khoi-nguyen-hoi-thanh","aria-hidden":"true"}},[n._v("#")]),n._v(" Khởi Nguyên Hội Thánh")]),n._v(" "),i("p",[n._v('Từ ngữ hội thánh dịch từ nguyên ngữ Hi lạp ecclesia có nghĩa là "những người được gọi ra," hay là một hội chúng. Dù từ ngữ hội thánh đã nhanh chóng trở thành một thuật ngữ trong Cơ Đốc Giáo, nhưng nó đã có một lịch sử tiền Cơ Đốc. Trong khắp thế giới Hi lạp, từ ngữ hội chúng được dùng chỉ buổi hội họp thường xuyên của các công dân trong một đô thị quốc gia. Một nhóm công dân có thể được một sứ giả kêu gọi ra để bàn luận và quyết định việc công. Trong nguyên ngữ Hi-bá của Cựu Ước cũng có một từ tương đương được dịch là cộng đoàn hay hội chúng Israel có các thành viên được định là những người được Đức Chúa Trời kêu gọi. Vì vậy chúng ta thấy Ê-tiên trong sách Công Vụ Các Sứ Đồ đã dùng từ ngữ này để mô tả Môi-se "là một người trong hội chúng (dân hội) tại trong sa mạc" (Công-Vụ 7:38). Vì vậy, đối với người Hi lạp trong thế kỷ đầu, từ ngữ hội chúng chỉ thị một hội đoàn dân chủ, tự trị; còn đối với người Do thái, đó là một hội đoàn thần chủ (theocratic society) mà thành viên là thần dân của Đức Chúa Trời.')]),n._v(" "),i("p",[n._v('Từ ngữ hội thánh áp dụng cho cộng đoàn Cơ Đốc được chính Chúa Giê-xu dùng đầu tiên khi Ngài bảo Phi-e-rơ, "Ta sẽ lập hội thánh ta trên đá này, các cửa âm phủ không thắng được hội đó" (Ma-thi-ơ 16:18). Như vậy chính Chúa Giê-xu đã thành lập hội thánh. Ngài là tảng đá góc vĩ đại trên đó hội thánh được xây dựng. Ngài là nền móng của mọi kinh nghiệm Cơ Đốc và hội thánh được thành lập dựa trên Ngài. "Vì chẳng ai có thể lập một nền khác ngoài nền đã lập là Chúa Cứu Thế Giê-xu" (I Cô-rinh-tô 3:11). Chúa Giê-xu tuyên bố Ngài là Đấng sáng lập hội thánh, là Đấng xây dựng hội thánh và hội thánh chỉ thuộc riêng về Ngài. Chúa cũng đã hứa Ngài sẽ ở với và ở trong tất cả mọi thành viên hội thánh Ngài. Hội thánh của Chúa không chỉ là một tổ chức, nhưng là một cơ thể sống hoàn toàn khác hẳn với bất cứ điều gì thế gian từng biết, đó là chính Đức Chúa Trời sống với và sống trong những con người bình thường là thuộc viên hội thánh Ngài.')]),n._v(" "),i("h3",{attrs:{id:"chua-cuu-the-gie-xu-la-dau-hoi-thanh"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chua-cuu-the-gie-xu-la-dau-hoi-thanh","aria-hidden":"true"}},[n._v("#")]),n._v(" Chúa Cứu Thế Giê-xu Là Đầu Hội Thánh")]),n._v(" "),i("p",[n._v('Kinh Thánh Tân Ước dạy rằng trong khi thật sự chỉ có một hội thánh phổ thông duy nhất thì cũng còn có rất nhiều các hội thánh địa phương kết hợp thành nhiều giáo hội, hội đồng khác nhau. Những hội thánh địa phương và các giáo phái này có thể chia ra theo quốc gia hay theo trường phái thần học hay tùy khuynh hướng của các thuộc viên. Tuy nhiên Tân Ước dạy rằng dù có nhiều giáo phái và các tổ chức phân chia khác nhau trong cấu trúc hội thánh, chúng ta chỉ có "một Chúa" như một lời một bài thánh ca, "Nền hội thánh là Chúa Cứu Thế Giê-xu, Chúa hội thánh."')]),n._v(" "),i("p",[n._v("Chúa Cứu Thế Giê-xu là đầu hội thánh hổ thông vĩ đại này. Từ Ngài lưu xuất ra mọi hoạt động và giáo huấn của hội thánh, vì Ngài là nguồn mạch của mọi kinh nghiệm Cơ đốc.")]),n._v(" "),i("p",[n._v("Trong thời đại điện tử hôm nay chúng ta có thể đem so sánh hội thánh với hệ thống thông tin rộng lớn với một tổng đài trung ương từ đó phát ra toàn bộ quang ba hay âm ba thực hiện mọi chức năng liên lạc. Trong hệ thống hỏa xa cũng luôn luôn có một văn phòng trung ương đưa ra mọi chỉ thị cho việc điều hành tất cả các chuyến tầu. Trong quân đội, vị tướng tư lệnh ban chỉ thị cho nhiều đơn vị dưới quyền. Các sĩ quan thừa hành có thể triển khai những chỉ thị đó khác nhau, nhưng chỉ thị của vị tướng tư lệnh luôn luôn là căn bản cho mọi ứng xử.")]),n._v(" "),i("p",[n._v("Đối với hội thánh, Chúa Cứu Thế Giê-xu đứng ở vị trí tướng tư lệnh. Hội thánh tồn tại là dựa trên mọi mệnh lệnh của Ngài. Năng quyền của hội thánh trực tiếp đến từ Chúa; các hội thánh có bổn phận tuân giữ chặt chẽ mọi lệnh truyền của Chúa. Như vị tướng tư lệnh muốn các chỉ thị của mình được thi hành đến nơi đến chốn thì Chúa Giê-xu cũng trông mong từng nhánh nhỏ của hội thánh tuân giữ đầy đủ mọi lời dạy của Ngài.")]),n._v(" "),i("p",[n._v("Hội thánh đã từng bị phê phán rất nhiều vì những tranh luận nội bộ, những gây gổ, bất hòa, thiếu hiệp nhất. Tuy nhiên đây không phải là điều quan trọng, vì những bất đồng này xuất phát từ việc diễn giải khác nhau mệnh lệnh của Nguyên Soái mà không phản ánh sự khôn ngoan hay thẩm quyền tuyệt đối trong việc ban mệnh lệnh của Ngài.")]),n._v(" "),i("p",[n._v("Nghiên cứu tất cả những niềm tin căn bản của các giáo phái khác nhau bạn sẽ thấy về căn bản và lịch sử hầu như tất cả đều giống nhau. Các giáo phái có thể khác nhau khá nhiều về nghi lễ, có những xung đột về thuật ngữ thần học nhưng về căn bản, tất cả đều công nhận Chúa Cứu Thế Giê-xu là Thượng Đế nhập thể, là Đấng đã chịu chết trên thập tự giá và đã phục sinh để ban sự cứu rỗi cho con người, và đây chính là sự kiện quan trọng hơn cả đối với nhân loại.")]),n._v(" "),i("h3",{attrs:{id:"mot-giao-hoi-hay-nhieu-giao-hoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-giao-hoi-hay-nhieu-giao-hoi","aria-hidden":"true"}},[n._v("#")]),n._v(" Một Giáo Hội Hay Nhiều Giáo Hội?")]),n._v(" "),i("p",[n._v('Bạn đã tiếp nhận Chúa Cứu Thế Giê-xu làm Đấng Cứu Chuộc và đã đặt trọn lòng nhờ cậy, tín thác nơi Ngài, bạn đã trở nên thành viên của hội thánh phổ quát, vô hình. Bạn là thành viên của đại gia đình đức tin, bạn là chi thể trong thân Chúa Cứu Thế. Bạn đã được gọi để vâng phục Chúa Cứu Thế, nghĩa là theo gương Ngài kết hợp với các tín hữu khác thờ phượng Đức Chúa Trời. "Chớ bỏ qua sự nhóm lại như mấy kẻ quen làm" (Hy-bá 10:25).')]),n._v(" "),i("p",[n._v("Chúng ta đang bàn về hội thánh địa phương trong cộng đồng chúng ta đang sống, một hội thánh hiển nhiên có nhiều khiếm khuyết, nhiều thiếu sót. Nhưng chúng ta cần nhớ rằng con người không thể nào toàn hảo cho nên những tổ chức con người thiết lập dù để tôn vinh Đức Chúa Trời thì cũng vẫn không thể nào tránh được tính chất khiếm khuyết đó. Chỉ có một mình Chúa Giê-xu là con người toàn hảo duy nhất từng sống trên trần gian. Chúng ta và tất cả những con người còn lại nếu tốt nhất thì cũng chỉ là những tội nhân đã hối cải đang cố gắng đi theo gương mẫu tuyệt hảo của Ngài; còn hội thánh nếu tự coi mình hay một thành viên nào của mình là không sai lầm (vô ngộ) thì không khác gì một người mù ngắm bóng mình trong gương!")]),n._v(" "),i("p",[n._v("Samuel Rutherford có lần nhận được bức thư của một số thuộc viên phiền trách vị mục sư của họ về tình trạng hội thánh. Ông đã viết một bức thư nghiêm khắc trả lời bảo rằng họ không phải là người chịu trách nhiệm về đời sống mục sư, nhưng họ phải có trách nhiệm cầu nguyện cho mục sư, tiếp tục sinh hoạt trong hội thánh và phục vụ Chúa. Chúa sẽ tôn trọng và ban phước cho những người như thế.")]),n._v(" "),i("p",[n._v('Tình trạng thờ phượng Chúa trong đền tạm dưới thời thầy tế lễ Hê-li đã suy thoái trầm trọng đến nỗi dân chúng khinh bỉ sinh tế dâng cho Đức Chúa Trời vì Hê-li "biết tính nết quái gở của các con trai mình, mà không cấm" (I Sa-mu-ên 3:13). Cậu bé Sa-mu-ên dù phải ở trong khung cảnh đó nhưng đã lớn lên, trở thành một tiên tri vĩ đại của Đức Chúa Trời.')]),n._v(" "),i("p",[n._v('Trong Tân Ước chúng ta thấy chính những nhà lãnh đạo trong đền thờ đã là những người đóng đinh Chúa Cứu Thế, tuy nhiên sau khi Chúa phục sinh và thăng thiên, "các môn đồ cứ ở trong đền thờ luôn, ngợi khen Đức Chúa Trời" (Lu-ca 24:53).')]),n._v(" "),i("p",[n._v("Ý định của Chúa khi thành lập hội thánh là để các môn đệ gia nhập và trung tín sinh hoạt trong đó. Ngày nay nếu bạn ở trong số hơn năm mươi phần trăm dân Mỹ chưa có một gắn bó tích cực nào với hội thánh, bạn sẽ vô cùng bối rối khi thấy số lượng hội thánh bạn có thể gia nhập. Để chọn một hội thánh, khuynh hướng tự nhiên là trở về với giáo hội bạn theo từ thơ ấu. Tuy nhiên, có lẽ bạn cũng thấy muốn tìm một hội thánh dựa trên phán đoán thuộc linh trưởng thành hơn. Gia nhập một hội thánh là một quyết định quan trọng, vì không những hội thánh đáp ứng những nhu cầu thuộc linh lớn lao của bạn mà quan trọng hơn là còn tạo cho bạn cơ hội phục vụ và đáp ứng nhu cầu người khác. Vì vậy, khi chọn hội thánh bạn cần cầu nguyện cẩn thận để bạn có cơ hội được Chúa sử dụng hữu hiệu nhất.")]),n._v(" "),i("h3",{attrs:{id:"mot-hoi-thanh-cho-moi-nguoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-hoi-thanh-cho-moi-nguoi","aria-hidden":"true"}},[n._v("#")]),n._v(" Một Hội Thánh Cho Mọi Người")]),n._v(" "),i("p",[n._v("Có người thấy dễ đến gần Chúa trong những ngôi giáo đường uy nghi và với một hình thức lễ nghi nào đó. Người khác lại muốn đến với Chúa với tấm lòng đơn sơ. Có người thích nghi thức trang nghiêm, có người lại thích những gì thân mật. Tuy nhiên điều quan trọng không phải là chúng ta cảm thấy thế nào nhưng là chiều sâu và sự chân thành trong khi chúng ta thờ phượng. Mỗi người chúng ta phải tìm và gia nhập một nhà thờ nào chúng ta có thể đạt được tốt nhất mục tiêu này.")]),n._v(" "),i("p",[n._v("Không nên gia nhập và phục vụ trong hội thánh vì một ông mục sư nào đó, nhưng đúng ra là phải vì tập thể hội thánh. Mục sư có thể thay đổi - một hội thánh mạnh khỏe, hào hứng cần được thay đổi mục sư, nhưng tất cả mọi tín lý, giáo điều của hội thánh vẫn y nguyên. Đối tượng lòng trung thành phải là hội thánh và Chúa Cứu Thế của hội thánh. Một hội thánh ổn định, bền chắc khi các thuộc viên nhận thức rằng chính tình yêu chung của họ với Chúa Cứu Thế Giê-xu và mong ước chân thành muốn theo chân Ngài kết chặt họ với nhau.")]),n._v(" "),i("p",[n._v("Một Cơ Đốc nhân chân chính không những đi nhà thờ để nhận lãnh ơn ích, nhưng cũng để đóng góp vào hội thánh. Tín hữu đi nhà thờ để thêm lời cầu nguyện vào lời cầu nguyện của các anh chị em khác, để thêm lời tạ ơn ca ngợi Chúa, để góp sức trong nỗ lực tìm kiếm ơn phước Chúa cho hội thánh, để làm mạnh mẽ thêm lời chứng về sự cứu rỗi qua Chúa Cứu Thế Giê-xu. Cơ Đốc nhân đi nhà thờ để tham gia với các con cái Chúa trong sự thờ phượng Đức Chúa Trời, trong sự chiêm ngắm tình yêu và lòng thương xót vô biên của Ngài cũng như để gần gũi, kết thân với các tín hữu khác.")]),n._v(" "),i("p",[n._v("Cơ Đốc nhân không tích cực tham gia vào sinh hoạt trong hội thánh địa phương làm tôi nhớ đến hình ảnh một cục than hồng bị gắp ra khỏi lò. Cục than nguội dần, không còn ngọn lửa bốc lên nữa và không bao lâu thì tắt ngấm. Ở miền Viễn Tây, khi chó sói đến tấn công một bầy cừu, trước hết chúng xông vào làm tan tác, sau đó mới quay ra tấn công những con lẻ loi, lạc bầy.")]),n._v(" "),i("h3",{attrs:{id:"hoi-thanh-la-mot-ong-dan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hoi-thanh-la-mot-ong-dan","aria-hidden":"true"}},[n._v("#")]),n._v(" Hội Thánh Là Một Ống Dẫn")]),n._v(" "),i("p",[n._v("Hội thánh phải là phương tiện dẫn truyền ngân khoản cho công tác Cơ Đốc và nhu cầu của các tín hữu khác. Kinh Thánh dạy chúng ta dâng một phần mười lợi tức cho Chúa. Đây là phần thuộc về Chúa. Thêm vào một phần muời đó, bạn cần dâng cho Chúa tùy theo sự sung túc Chúa ban. Dâng hiến là một ân sủng Cơ Đốc cần được đan dệt cho đến khi hòa lẫn vào trong tấm vải của cuộc sống hàng ngày. Lòng rộng rãi phải là động cơ thôi thúc chúng ta trong mọi sự.")]),n._v(" "),i("p",[n._v('Chúa Cứu Thế dạy rằng, "Ban cho có phước hơn nhận lãnh" (Công Vụ 20:35). Chúa biết rõ sự ban cho sưởi ấm tấm lòng và thỏa mãn linh hồn như thế nào cho nên Ngài rất mong chúng ta được hưởng ân phúc đặc biệt này. Người ta sống ích kỷ là vì sợ hãi, trong khi đó Cơ Đốc nhân cần phải đứng thẳng lên không nao núng. Chúa Giê-xu luôn luôn đứng với hai bàn tay rộng mở chứ không nắm chặt đầy tham lam ích kỷ. Nếu có thể được, chúng ta nên cố gắng ban cho cách yên lặng, âm thầm. Chúa Giê-xu cũng dạy rằng, "Khi bố thí đừng cho tay trái biết việc làm của tay phải"(Ma-thi-ơ 6:3).')]),n._v(" "),i("p",[n._v("Việc ban hiến không thể tính bằng tiền, cũng không tính bằng những thùng quần áo cũ. Đôi khi tặng phẩm lớn nhất lại là tình bạn và tình xóm giềng. Một lời nói ân cần, một tiếng chào thân mật, một buổi tối dành cho một người đang cô đơn có thể đem đến một mùa gặt lớn cho Nước Trời. Bạn không thể nào trở thành người chiến thắng linh hồn tội nhân nếu bạn không được chuẩn bị để ban hiến chính mình. Không chỉ là tiền bạc của bạn mà là thì giờ, khả năng và chính bạn - tất cả đều phải được dâng lên phục vụ Chúa Cứu Thế.")]),n._v(" "),i("p",[n._v("Việc bạn dâng hiến hơn số phần mười không nên giới hạn theo những qui tắc hay phương pháp cố định nào nhưng phải dựa trên nhu cầu bạn thấy theo những qui định của chính Chúa Cứu Thế trong Ma-thi-ơ 6:1-4, nghĩa là làm một cách kín đáo, âm thầm. Người cần sự giúp đỡ của bạn có thể là người hàng xóm, cậu bé đưa báo hay một người nào đó xa xôi tận Phi Châu hay Nam Mỹ. Việc ban hiến của chúng ta là một thể hiện tình yêu đối với Đức Chúa Trời. Chúng ta dâng cho Chúa để đền đáp trong muôn một tình thương lớn lao Ngài ban. Đây chính là cách chúng ta trải rộng tình thương của Ngài ra tận những vùng xa xôi.")]),n._v(" "),i("p",[n._v('Cơ Đốc nhân cũng phải chia xẻ những trách nhiệm trong cộng đồng theo thì giờ và tài chánh cho phép. Người nhận được sự giúp đỡ tiền bạc cần biết bạn chia xẻ trong danh Chúa Cứu Thế, vì vậy kèm theo món tiền tặng dữ, bạn nên có một bức thư cho tổ chức từ thiện đó với nội dung như sau: "Là một Cơ Đốc nhân, tôi tin rằng Chúa Giê-xu muốn tôi ủng hộ cộng đồng theo khả năng hiện có. Tôi xin gửi đến quí vị món tiền này. Cầu xin Đức Chúa Trời ban phước cho công việc của quí vị."')]),n._v(" "),i("p",[n._v('Bạn cẩn thận không nên để bị mắc tội ăn trộm Đức Chúa Trời. Kinh Thánh dạy rằng, "Các ngươi bị rủa sả, vì các ngươi hết thảy cả nước đều ăn trộm ta. Các ngươi hãy đem hết thảy phần mười vào kho, hầu cho có lương thực trong nhà ta; và từ nay các ngươi khá lấy điều này mà thử ta, Đức Giê-hô-va vạn quân phán, xem ta có mở các cửa sổ trên trời cho các ngươi, đổ phước xuống cho các ngươi đến nỗi không chỗ chứa chăng" (Ma-la-chi 3:9,10).')]),n._v(" "),i("p",[n._v('Tiến sĩ Louis Evans từng nói, "Tin mừng miễn phí, nhưng cần phải có tiền mua xô đựng nước cứu rỗi." Dâng hiến là một hành động có tính cách thờ phượng y như cầu nguyện hay ca ngợi. Hiện nay chính phủ Mỹ đưa ra định mức cho phép cá nhân được dâng hiến đến 50 phần trăm lợi tức cho các cơ quan từ thiện và 25 phần trăm cho các tổ chức tôn giáo, và số dâng hiến đó được miễn thuế lợi tức, nhưng theo ước lượng, chưa đến mười phần trăm dân Mỹ tận dụng đặc ân này. Các công ty được cho phép dâng hiến đến 10 phần trăm nhưng chỉ có khoảng 15 phần trăm các công ty sử dụng quyền lợi trên. Cho dù chính phủ không đưa ra địmh mức miễn thuế trên thì mười phần trăm lợi tức vẫn thuộc về Đức Chúa Trời.')]),n._v(" "),i("h3",{attrs:{id:"hoi-thanh-pho-bien-tin-mung"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hoi-thanh-pho-bien-tin-mung","aria-hidden":"true"}},[n._v("#")]),n._v(" Hội Thánh Phổ Biến Tin Mừng")]),n._v(" "),i("p",[n._v('Mục đích của Hội Thánh là rao giảng phúc âm theo lệnh truyền, "Hãy đi khắp thế gian giảng Tin Lành cho mọi người" và làm báp tem cho những người tin. Sứ mạng căn bản và chính yếu của hội thánh là công bố Chúa Cứu Thế cho những người hư vong. Nhu cầu của thế giới hôm nay là phổ biến tiếng kêu cứu cho hội thánh đến giúp. Thế giới đang ngụp lặn trong trong những nan đề luân lý, xã hội và kinh tế. Con người đang đắm chìm trong các lớp sóng tội ác và hổ nhục. Thế giới cần Chúa Cứu Thế. Sứ mạng của Hội Thánh là quăng phao cứu nạn xuống cho tội nhân đang hư vong, chìm đắm khắp nơi.')]),n._v(" "),i("p",[n._v('Chúa Giê-xu phán, "Khi Đức Thánh Linh giáng trên các ngươi, các ngươi sẽ nhận lấy quyền phép làm chứng về ta..." (Công Vụ 1:8). Với quyền năng Đức Thánh Linh chúng ta có thể nắm tay các Cơ Đốc nhân khác để đưa con người về với Chúa Cứu Thế. Sáu mươi lăm phần trăm dân số thế giới chưa được nghe Tin Lành của Chúa Cứu Thế Giê-xu. Thế hệ chúng ta đã thất bại thê thảm trong nỗ lực rao giảng Tin Lành cho thế giới. Theo hội dịch Kinh Thánh Wycliffe thì còn đến hơn ba ngàn ngôn ngữ và thổ ngữ chưa có Kinh Thánh.')]),n._v(" "),i("p",[n._v("Hội thánh ban đầu không có Kinh Thánh, không có chủng viện, không nhà in, không sách báo, không có các cơ sở giáo dục, không có truyền thanh, truyền hình, không xe hơi, không máy bay, nhưng chỉ trong vòng một thế hệ, phúc âm đã được rao giảng cho hầu hết thế giới đương thời. Bí quyết của công cuộc rao giảng tin mừng này chính là quyền năng Đức Thánh Linh.")]),n._v(" "),i("p",[n._v("Ngày nay, trước những tiến bộ vược bực của các phương tiện truyền thông, quyền năng Đức Thánh Linh vẫn y nguyên. Vì vậy chúng ta không cần phải vận dụng sức riêng, vì làm như thế chúng ta sẽ thất bại.")]),n._v(" "),i("p",[n._v("Ngày nay, những bàn chân duy nhất Chúa có là bàn chân chúng ta. Những bàn tay duy nhất Chúa có là bàn tay chúng ta. Lưỡi duy nhất Chúa có là lưỡi chúng ta. Chúng ta phải tận dụng mọi khả năng, phương tiện, phương pháp có được để đưa dắt con người đến với Ngài. Đây là đại sứ mạng của hội thánh. Phương pháp của chúng ta có thể thay đổi, như truyền giáo bằng sự thăm viếng, bằng giáo dục, giảng dạy, bằng các phương tiện kỹ nghệ, bằng các tổ chứng đạo, bằng truyền thanh, truyền hình, bằng phim ảnh, và bằng phương tiện chúng ta vẫn thường gọi là truyền thông đại chúng.")]),n._v(" "),i("p",[n._v('Tôi được biết ngày nay ở nhiều nơi trên thế giới hội thánh bị cấm đoán, bị giới hạn và đôi khi bị tiêu diệt, tuy nhiên đây là điều đã được minh chứng nhiều lần đó là "Huyết những người tử đạo chính là hạt giống của hội thánh." Hội thánh của Đức Chúa Trời là hội thánh lấy Kinh Thánh làm trung tâm, hội thánh đó sẽ tăng trưởng mạnh mẽ khi bị bách hại dữ dội. Lời hứa của Chúa "nơi nào có đôi ba người nhân danh ta nhóm lại thì có ta ở giữa" đã đúng theo tự nghĩa ở nhiều nơi trên thế giới. Những nơi đó, dù Cơ Đốc nhân phải sống trong cảnh bần hàn nhưng họ vẫn trung tín dâng phần mười. Khi có tín hữu nào gặp khó khăn hoạn nạn, các tín hữu khác đến trợ giúp. Không được phép nói về Chúa công khai, họ tìm mọi cơ hội làm chứng bằng đời sống. Cứ như vậy, khi một người bị bách hại tàn nhẫn, vô cớ, nhưng vẫn bình thản, thậm chí vui vẻ chịu đựng, thế nào cũng sẽ có người thấy. Họ sẽ tìm đến bảo rằng, "Tôi thấy anh bị xử tệ, nhưng anh vẫn vui vẻ." Đây chính là lúc người tín đồ nắm lấy cơ hội để chia xẻ về đức tin nơi Chúa Cứu Thế." Vì vậy, nơi nào hội thánh Chúa bị bách hại lại càng tăng trưởng, và đây cũng chính là thách thức cho chúng ta hành xử như họ.')]),n._v(" "),i("h2",{attrs:{id:"chuong-17-toi-co-phai-la-nguoi-giu-em-toi-dau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-17-toi-co-phai-la-nguoi-giu-em-toi-dau","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 17: Tôi có phải là người giữ em tôi đâu")]),n._v(" "),i("p",[i("strong",[n._v('"Xin tha nợ chúng con, cũng như chúng con tha người mắc nợ chúng con". (Ma-thi-ơ 6:31)')])]),n._v(" "),i("p",[n._v('Vì bạn đã quyết định theo Chúa Cứu Thế và đã khởi sự học Kinh Thánh, bạn thấy mình phải đương đầu với nhiều nan đề và bổn phận xã hội. Bạn đã giải hòa với Đức Chúa Trời, không còn ở trong thế chống đối và thù nghịch với Ngài nữa. Tội lỗi bạn đã được tha thứ. Những tư duy của bạn hướng về những chân trời mới và đó là những viễn cảnh mới của cuộc đời bạn. Thế giới quan của bạn hoàn toàn thay đổi. Bạn khởi sự nhìn người khác qua cặp mắt của Chúa Giê-xu. Những ý tưởng và những lý tưởng cũ cũng đổi thay. Những thành kiến bạn từng nắm chắc từ đầu nay khởi sự trôi tuột đi. Lòng ích kỷ trước đây từng là đặc điểm cá tính bạn ở nhiều lãnh vực của đời sống nay biến mất. Bỗng nhiên bạn hiểu ngay câu chuyện Ca-in, A-bên trong sách Sáng Thế Ký có ý nghĩa gì. Khi có ai tự hỏi, "Tôi có phải là người giữ em tôi không?" Câu trả lời sẽ là, "Không, tôi không phải là người giữ em mà là anh ruột."')]),n._v(" "),i("p",[n._v("Nhiều người từ chối không muốn trở thành Cơ Đốc nhân vì cuộc sống Cơ Đốc nhân đã bị trình bày một cách méo mó, tiêu cực thay vì tích cực. Họ bảo rằng tác phong Cơ Đốc chống lại mọi điều thoải mái, ích lợi. Họ bảo rằng Cơ Đốc nhân giống như một người đàn bà luôn miệng than rằng tất cả những cái đáng làm trong đời sống nếu không vô luân, bất hợp pháp thì lại hại cho sức khỏe!")]),n._v(" "),i("p",[n._v("Trái với chuyện người đời tin, làm một Cơ Đốc nhân thật không có nghĩa là phải từ bỏ tất cả mọi lạc thú chân chính. Chỉ những lạc thú tội lỗi mới bị Đức Chúa Trời cấm và buộc chúng ta phải từ bỏ. Sự kiện chúng ta hoàn toàn chấp nhận Chúa Cứu Thế và quyết tâm để Ngài hướng dẫn theo ý muốn Đức Chúa Trời hầu như tức khắc kéo bạn đến nguồn mạch của lạc thú chân thật, là mối tương giao với Chúa Cứu Thế. Đối với bạn là một người chưa được tái sinh thì hiển nhiên điều này chẳng có gì vui thú, nhưng với người thực sự kinh nghiệm tương giao với Chúa Cứu Thế hàng ngày biết rằng niềm vui đó trổi vượt mọi sinh hoạt trần gian.")]),n._v(" "),i("p",[n._v('Trong lời giới thiệu tuyển tập về George MacDonald, C.S. Lewis viết, "Ông dường như là một người ... ham vui, biết tận hưởng tất cả những điều tuyệt đẹp và tuyệt hảo có thể mua bằng tiền, nhưng cũng thỏa nguyện một cách sâu sắc khi không có những điều đó."')]),n._v(" "),i("p",[n._v('Còn chính George MacDonald thì viết: "Nếu có thể được, tôi muốn ngồi gần bên lò sưởi ấm áp trong mùa đông hay bên một bình hoa tươi trong mùa hè. Nhưng nếu không có, tôi vẫn nghĩ được như thế là quí, rồi quay qua vùi đầu vào công việc. Tôi không tin rằng thỏa lòng có nghĩa là khinh bỉ những gì mình không có."')]),n._v(" "),i("p",[n._v('Chính tác giả Thi-thiên cũng nói rằng, "Họ nhờ sự dư dật của nhà Chúa mà được thỏa nguyện; Chúa sẽ cho họ uống nước sông phước lạc của Chúa" (Thi-thiên 36:8). Đức Chúa Trời cũng phán rằng, "Đức Chúa Trời sẽ chẳng từ chối điều tốt lành gì cho những ai ăn ở ngay thẳng" (Thi-thiên 84:11b). Thánh Phao-lô bảo rằng, "Đức Chúa Trời là Đấng mỗi ngày ban mọi vật dư dật cho chúng ta được hưởng" (I Ti-mô-thê 6:17).')]),n._v(" "),i("p",[n._v("Chính mối tương giao với Chúa Cứu Thế sẽ giúp chúng ta có thể sống hiện thực. Lối sống theo Chúa Cứu Thế không đòi chúng ta phải từ bỏ những mối quan tâm và những ước vọng chính đáng. Kinh Thánh dạy rằng Chúa Cứu Thế có thể trở lại bất cứ lúc nào, nhưng Kinh Thánh cũng khuyên chúng ta phải tiếp tục nỗ lực làm việc bình thường cho đến ngày Ngài trở lại.")]),n._v(" "),i("p",[n._v("Lấy thí dụ, không có gì sai đối với việc ăn, uống, cưới, gả trong thời Nô-ê. Tuy nhiên khi con người chỉ biết say sưa, chìm đắm trong những sinh hoạt này mà xao lãng hẳn khía cạnh thuộc linh thì đó là lối sống nguy hiểm, sai lầm (Lu-ca 17:26).")]),n._v(" "),i("p",[n._v("Cũng không có gì sai trong thời Nô-ê khi người ta buôn bán, hoạch định, xây cất trừ phi họ sử dụng những phương cách tội lỗi gian tham (Lu-ca 17:28). Sai lầm cơ bản trong thời Nô-ê là con người đã coi tất cả những sinh hoạt nêu trên là mối quan tâm duy nhất trong đời. Họ không nghĩ đến điều gì khác ngoại trừ những vui thú riêng, tài sản riêng và việc thu gom, tích lũy lợi lộc vật chất. Họ đắm chìm trong vật chất cuộc đời đến nỗi không còn thì giờ nào dành cho Đức Chúa Trời. Thái độ này làm Chúa rất buồn lòng và Ngài chắc sẽ phải đoán xét những kẻ vi phạm.")]),n._v(" "),i("p",[n._v('Như có người từng nói, "Kinh Thánh không được viết để khích lệ con người quan tâm nhiều hơn đến việc đời vì Kinh Thánh mặc nhiên cho rằng con người vốn đã có quá nhiều quan tâm đến thế gian rồi! Kinh Thánh nhắm đến việc khích lệ con người nhìn việc đời trong ánh sáng của sự việc thuộc linh có tầm quan trọng và giá trị lớn lao hơn."')]),n._v(" "),i("p",[n._v("Kinh Thánh dạy chúng ta phải chăm lo công việc hàng ngày và phải làm cho chu đáo. Chúa đặt để chúng ta trong trần gian và giao cho chúng ta công tác. Những người xưng mình là Cơ Đốc nhân được Chúa dạy không những phải làm việc, mà còn phải làm việc hết khả năng.")]),n._v(" "),i("p",[n._v('Trong Kinh Thánh, Bết-sa-lê-ên được khen ngợi trong ngành thủ công làm đồ kim khí, đá và gỗ. Người thợ cả này được ban đầy dẫy Thánh Linh và tài năng: "Ta đã ban cho người đầy dẫy Thần của Đức Chúa Trời, sự khéo léo, tài năng và sự khôn ngoan để tạo ra những kiểu mẫu nghệ thuật trong các nghề vàng, bạc và đồng, để khắc và khảm ngọc, đẽo gỗ và làm được mọi thứ nghề thợ" (Xuất Ai-cập 31:3-5). Gia-cốp và các con chăn nuôi gia súc. Giô-sép làm thủ tướng. Đa-ni-ên là chính khách. Cả Giô-sép và Chúa Giê-xu là thợ mộc và một số các môn đệ khác là ngư phủ. Chúng ta cũng được biết hoạn quan Ê-thi-ô-pi phụ trách tài chánh cho nữ hoàng Can-đác; Ly-đi buôn gấm vóc; Phao-lô, Bê-rít-sin và A-qui-la làm nghề may trại và Lu-ca làm thầy thuốc.')]),n._v(" "),i("p",[n._v("Hiển nhiên, lý tưởng Cơ Đốc không đòi một người phải từ bỏ mọi mối quan tâm đối với nghề nghiệp trong trần gian; trái lại, chúng ta tìm kiếm sự hướng dẫn của Đức Chúa Trời để có thể hết lòng hết sức làm việc mỗi ngày và để giữ cả công việc lẫn những ước vọng của chúng ta tùng phục Chúa luôn luôn là Đấng hỗ trợ chúng ta tích cực trong cuộc sống hàng ngày trên đất . Ngài ban tài năng, giúp đỡ chúng ta trong công việc và ban phước lạc cho cuộc sống.")]),n._v(" "),i("p",[n._v("Trong một bài luận thuyết đầy hứng khởi, F.W. Boreham trích dẫn tiên tri Ê-sai rồi kể lại cách người thợ mộc Na-xa-rét đã khích lệ đám thợ bạc ở nhiều thời đại như thế nào. Nhiều tác giả nổi tiếng thế giới đã tìm được nguồn cảm hứng từ Giê-xu Na-xa-rét; những nghệ sĩ danh tiếng nhất, những nhạc sĩ, điều khắc gia cũng từng được soi sáng qua cuộc đời Chúa Cứu Thế.")]),n._v(" "),i("p",[n._v("Chúa cũng giúp chúng ta đương đầu với những vấn đề xã hội và đây chính là chỗ làm chúng ta bối rối. Chính trong những công việc hàng ngày, cách chúng ta đương đầu với những nan đề xã hội chung quanh mà thế giới có thể thấy được Chúa Cứu Thế trong chúng ta.")]),n._v(" "),i("p",[n._v('Như nhạc gia tôi là cố tiến sĩ L. Nelson Bell từng viết trong tạp san the Southern Presbyterian Journal như sau, "Nếu bạn ở trong nhà thờ sáng Chúa nhật, những người nhìn thấy biết ngay bạn là tín đồ. Nhưng còn những người bạn gặp trong tuần, ở nơi làm, ngoài đường phố, trong cửa tiệm và bao nhiêu nơi khác nữa thì sao? Việc chúng ta xưng nhận là Cơ Đốc nhân chính thống có chỗ riêng của nó. Chúng ta dự nhóm, tham gia vào các chương trình, các hoạt động trong nhà thờ là điều đương nhiên trong cuộc sống Cơ Đốc nhân. Nhưng như tất cả chúng ta đều biết, công việc làm ăn, những trách nhiệm với gia đình, những sinh hoạt hàng ngày, tất cả đều hợp lại trở thành một bài trắc nghiệm tính thực tại của kinh nghiệm và đức tin Cơ Đốc. Trong những tiếp xúc hàng ngày kia của chúng ta, người khác thấy gì? Liệu những người gặp chúng ta trong ngày thường có thể biết chúng ta là Cơ Đốc nhân không? Liệu những người mới gặp có nhận ra được điều gì trong chúng ta khiến họ thấy khác với những người không biết Chúa? Hiển nhiên, một trong những trắc nghiệm đúng nhất về tính nết Cơ Đốc nhân là qua đời sống thường nhật.')]),n._v(" "),i("p",[n._v('Thực tại của việc xưng nhận mình là Cơ Đốc nhân được thể hiện bằng nhiều cách: qua những điều chúng ta nói hay không nói và qua những gì chúng ta làm hoặc không làm. Dù điều chính yếu trong Cơ Đốc Giáo không phải là những cái bề ngoài, nhưng đạo Chúa thực sự được diễn đạt qua những câu đàm thoại, qua thói quen, trong cách giải trí, trong các mối quan tâm hay mong ước ghi nhận trong đời sống hàng ngày. Những câu chuyện chúng ta trao đổi có tôn cao Chúa Cứu Thế không? Những thói quen của chúng ta có phải là những thói quen được Ngài chấp nhận? Những thú giải trí, tiêu khiển của chúng ta có phải là những sinh hoạt Ngài có thể chứng kiến? Chúng ta có cúi đầu tạ ơn Chúa trước bữa ăn ở những nơi công cộng? Dựa trên cách chúng ta quan tâm đến của cải vật chất, người ta có thể biết lòng yêu mến gắn bó của chúng ta đặt vào những điều trên cao hay chủ yếu là vào trần thế? Người ta có thấy trong chúng ta tham vọng đối với một địa vị hay chức vụ nào không phù hợp với Cơ Đốc nhân? Chúng ta cần tự vấn bằng những câu hỏi này và nhiều câu khác, vì qua chính những điều đó người ta sẽ nhận định chúng ta có phải là Cơ Đốc nhân hay không."')]),n._v(" "),i("p",[n._v('Ít lâu sau khi nhạc gia tôi qua đời, nhà tôi thuê một người sửa sang lối đi trong gia trang trên núi. Một ngày kia trên đường ra phố, nhà tôi ghé thăm. Trong lúc nói chuyện, bất chợt ông ta hỏi, "Bà có phải là con gái Tiến sĩ Bell không?" Nhà tôi xác nhận, ông ta kêu lên đầy quí mến, "Ôi chao! Tôi chưa bao giờ gặp một người nào tin kính Chúa tốt như ông cụ!"')]),n._v(" "),i("p",[n._v("Chúng ta có thái độ nào đối với những người khác chủng tộc? Thái độ đối với tính dục? Đối với những vấn đề quản lý lao động? Vấn đề phá thai? Thái độ coi những lệch lạc trái tự nhiên về tình dục là một lối sống phải được chấp nhận? Chúng ta phải có thái độ nào đối với cuộc sống lạm dụng rượu chè, cờ bạc cùng những nan đề tiềm sinh trong lối sống đó? Đây là những câu hỏi rất thực tiễn và rất thật cần phải được trả lời, thể hiện và sống trước những con người đồng thời của chúng ta.")]),n._v(" "),i("p",[n._v('Nguyên tắc hướng dẫn mối liên hệ của chúng ta với thế giới xung quanh phải là điều Chúa Giê-xu dạy, "Các ngươi muốn người ta làm cho mình thể nào hãy làm cho người ta thể ấy" (Lu-ca 6:31).')]),n._v(" "),i("p",[n._v('Nhiều người phê phán cái gọi là "Tin Lành xã hội," nhưng Chúa Giê-xu lại dạy chúng ta phải đem Tin Lành cho thế giới. Thật ra không có cái gọi là "Tin Lành xã hội" đâu mà chỉ là gọi sai vì chỉ có một Tin Lành duy nhất như thánh Phao-lô khẳng định, "Nếu ai truyền cho anh em một Tin Lành nào khác với Tin Lành anh em đã nhận thì người ấy đáng bị Anathem!" Trong khi đó Thánh Phao-lô cũng viết trong I Ti-mô-thê 5:8 như sau, "Ví bằng có ai không săn sóc đến bà con mình, nhất là không săn sóc đến người nhà mình, ấy là người chối bỏ đức tin, còn xấu hơn người không tin nữa" (I Ti-mô-thê 5:8). Chính phủ liên bang Mỹ sẽ nhẹ gánh biết bao nhiêu nếu tất cả các Cơ Đốc nhân nhận trách nhiệm lo cho các nhu cầu của thân nhân!')]),n._v(" "),i("p",[n._v('Franklin Graham - con của tiến sĩ Billy Graham, đã tích cực dấn thân trong công tác xã hội và hiện đứng đầu một cơ quan cứu trợ Cơ Đốc. Trong một cuộc phỏng vấn gần đây, người ta đã trích dẫn ông nói rằng, "rao giảng tin mừng luôn luôn phải là vấn đề ưu tiên." Ông nhắc nhở các cơ quan phát triển và cứu trợ Tin Lành không được quên lãng nhu cầu đưa con người đến với Chúa Cứu Thế. Ông nói, "Mệnh lệnh cho Cơ Đốc nhân là rao giảng Tin Lành chứ không phải là hoạt động phát triển." Ly nước lạnh phải đến sau, hoặc có khi đến trước, nhưng dù thế nào không nên dùng thay thế cho phúc âm (nghĩa là không nên dùng sự giúp đỡ vật chất thay thế cho việc rao giảng Tin Lành). Hơn tất cả mọi người khác, Cơ Đốc nhân cần quan tâm đến những nan đề và những bất công xã hội. Suốt qua bao nhiêu thế kỷ, hội thánh đã đóng góp nhiều hơn bất cứ tổ chức hay cơ quan nào trong việc nâng những tiêu chuẩn xã hội lên những độ cao mới. Việc thuê mướn trẻ con làm việc đã bị cấm. Chế độ nô lệ đã bị bãi bỏ ở Anh và Mỹ và một số nơi khác trên thế giới. Địa vị của phụ nữ đã được nâng lên đến mức độ chưa từng có trong lịch sử cũng như nhiều cải cách khác được thực hiện hầu hết là kết quả do ảnh hưởng những giáo huấn của Chúa Cứu thế Giê-xu. Cơ Đốc nhân cần thế chỗ Chúa Giê-xu trong xã hội, với lòng quả cảm về phương diện luân lý, đứng lên bênh vực cho điều phải, công lý và danh dự.')]),n._v(" "),i("h3",{attrs:{id:"lam-cong-dan-tot"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#lam-cong-dan-tot","aria-hidden":"true"}},[n._v("#")]),n._v(" Làm công dân tốt")]),n._v(" "),i("p",[n._v("Trước hết: Cơ Đốc nhân phải là công dân tốt. Kinh Thánh dạy rằng Cơ Đốc nhân phải là người tuân giữ luật pháp, đồng thời là người trung thành với tổ quốc. Lòng trung thành và yêu mến quê hương không có nghĩa là chúng ta không được phê phán những điều luật bất công. Kinh Thánh dạy rằng Đức Chúa Trời không vị nể bất cứ ai. Mọi người đều phải có những cơ hội bằng nhau. Chính quyền của Đức Chúa Trời phải là mẫu mực của chúng ta.")]),n._v(" "),i("p",[n._v('Kinh Thánh cũng dạy rằng chúng ta phải cộng tác với nhà cầm quyền. Thánh Phao-lô khuyên Ti-mô-thê phải "khẩn nguyện, cầu xin, kêu van ... cho mọi người, cho các vua, cho hết thảy các bậc cầm quyền..." (I Ti-mô-thê 2:1,2). Chúa Giê-xu từng hỏi, "Có nên nộp thuế không?" Rồi Ngài đã nêu một gương mẫu muôn đời bằng cách đóng thuế đầy đủ. Điều hành chính quyền, duy trì luật pháp và trật tự cần phải có tiền. Người trốn thuế là kẻ ăn bám xã hội và là một kẻ trộm đạo. Không một Cơ Đốc nhân chân chính nào lại là người trốn thuế. Chúa Giê-xu dạy chúng ta phải "trả lại cho Sê-sa những gì của Sê-sa" (Mác 12:17). Chúng ta cũng còn phải phải làm hơn là chỉ đóng thuế mà thôi. Chỉ là người tuân giữ luật pháp không chưa đủ, chúng ta cần tích cực làm việc và thực hiện những điều tốt cho đất nước. Đôi khi chúng ta được gọi để chết cho quê hương. Chúng ta phải tận tâm trong công việc, sống như những công dân gương mẫu.')]),n._v(" "),i("p",[n._v("Chúng ta cần sống rộng rãi, giúp đỡ những người cùng túng, đóng góp cho những tổ chức trung tín, chân thật, trong việc phục vụ những người ở cảnh cơ hàn. Chúng ta cần tham gia những hoạt động đa dạng của các tổ chức từ thiện như Hội Hồng Thập Tự, Cứu Thế Quân, Hoàn Cầu Khải Tượng, Hầu Bao Sa-ma-ri (Samaritan’s Purse), Quĩ Nước Mắt, (The Tear Fund) và những tổ chức chân chính, đứng đắn, hữu dụng khác. Đồng thời, là những người phục vụ có trách nhiệm, chúng ta cần kiểm tra tính cách đáng tin, chân thành của những tổ chức từ thiện này để biết chắc rằng công tác cứu trợ được thực hiện đến nơi đến chốn.")]),n._v(" "),i("p",[n._v('Cơ Đốc nhân cần quan tâm đến các cô nhi viện, nhà thương, trại tỵ nạn, nhà tù và những cơ quan xã hội. Chúa Giê-xu phán, "Hãy yêu người lân cận như mình" (Ma-thi-ơ 22:39). Bạn thử nghĩ đến một nước không có một tổ chức từ thiện nào! Chắc chắn không ai muốn sống ở đó.')]),n._v(" "),i("p",[n._v('Tôi nhớ có lần đi thăm một nước trong đó có cả viện dưỡng lão của chính phủ và của giáo hội. Trong viện dưỡng lão Cơ Đốc chúng ta thấy tình thương được thể hiện, nhưng trong viện dưỡng lão công, việc săn sóc mang tính cách lạnh lùng, chiếu lệ. Vị bác sĩ làm việc ở cả hai nơi bảo rằng những cụ già sống ở nhà dưỡng lão Cơ Đốc không những vui vẻ, hạnh phúc hơn mà còn sống lâu hơn. Chúng ta thích sống nơi nào đầy ắp tình xóm giềng, chúng ta phải sống dấn thân trong cộng đồng và những người có trách nhiệm cần phải được tôn trọng, hỗ trợ và hợp tác. Kinh Thánh dạy rằng, "Mọi người phải vâng phục các Đấng cầm quyền trên mình; vì chẳng có quyền nào mà không đến bởi Đức Chúa Trời : Các quyền đều là bởi Đức Chúa Trời chỉ định" (Rô-ma 12:1).')]),n._v(" "),i("h3",{attrs:{id:"hieu-khach"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#hieu-khach","aria-hidden":"true"}},[n._v("#")]),n._v(" Hiếu khách")]),n._v(" "),i("p",[n._v('Thứ hai: Cơ Đốc nhân phải "ân cần tiếp khách" (I Ti-mô-thê 3:2). Kinh Thánh dạy rằng nhà chúng ta phải là nơi tiếp đãi khách và những người ra vào nhà chúng ta có thể cảm nhận được sự hiện diện của Chúa Cứu Thế. Những gia đình Cơ Đốc hạnh phúc nhất tôi biết là những gia đình hiếu khách, là nơi xóm giềng thấy thoải mái, tự nhiên, nơi thanh niên được tiếp đãi, người lớn tuổi được kính trọng, và trẻ thơ được yêu mến. Điều đó có nghĩa là những gì Đức Chúa Trời ban cho chúng ta đều được chia xẻ cho người khác và làm như vậy, Đức Chúa Trời sẽ ban phước tràn trề trên gia đình chúng ta.')]),n._v(" "),i("h3",{attrs:{id:"mot-quan-diem-dung-dan-ve-tinh-duc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-quan-diem-dung-dan-ve-tinh-duc","aria-hidden":"true"}},[n._v("#")]),n._v(" Một quan điểm đúng đắn về tình dục")]),n._v(" "),i("p",[n._v("Thứ ba: Chúng ta phải có quan điểm Cơ đốc đối với tình dục. Không một chỗ nào trong Kinh Thánh dạy rằng tình dục tự nó là tội lỗi, dù nhiều nhà giải kinh đã cố gắng tô vẽ như thế. Nhưng Kinh Thánh dạy rằng lạm dụng tình dục mới là tội lỗi. Quan hệ tình dục, là hành vi qua đó mọi đời sống trên thế giới được tạo thành, phải là một kinh nghiệm lạ lùng, đầy ý nghĩa và thỏa nguyện đối với con người.")]),n._v(" "),i("p",[n._v("Tuy nhiên, con người với bản chất gian ác, tự hủy hoại, đã đem một sinh hoạt vinh hiển và toàn vẹn nhất nguyên thủy dành thể hiện tình yêu giữa hai người thành một điều thấp thỏi, tầm thường, nhớp nhúa. Tình dục nếu loại bỏ tất cả mọi cam kết, mọi yêu thương, tương kính và ước vọng sâu xa chân thành muốn đem vui sướng, thỏa nguyện cho người kia, sẽ chỉ còn là một hành động của thú vật, và đây là điều Kinh Thánh cảnh cáo bằng những lời khẳng định!")]),n._v(" "),i("p",[n._v('Một điều rất hay đó là Kinh Thánh là một trong những cuốn sách nói thẳng hơn hết về đề tài tình dục. Kinh Thánh không cố ý mặc cho tình dục những khía cạnh đúng hoặc sai. Thái độ quỉ quyệt, úp mở, hổ thẹn, "làm bộ như tình dục không hiện hữu" hoàn toàn là thái độ do con người tạo ra.')]),n._v(" "),i("p",[n._v('Để tránh biện pháp khó hiểu "tảng lờ không bàn đến tình dục", nền văn minh của chúng ta ngày nay đã dùng phương cách nhấn mạnh khía cạnh máy móc của quan hệ tình dục, mà đề cập rất ít đến môi trường thiêng liêng là nơi phát sinh cách diễn đạt tình yêu nồng thắm, chan hòa của con người.')]),n._v(" "),i("p",[n._v("Các phiên tòa xử ly hôn của chúng ta là bằng chứng bi thảm về sự bất năng của con người trong cố gắng đạt mối tương giao tươi đẹp, lâu bền mà không cần có nền tảng vững chắc là những giá trị thuộc linh.")]),n._v(" "),i("p",[n._v("Tình dục là một phần của đời sống mà chúng ta dù muốn cũng không thể bãi bỏ, vì không có tình dục tất cả sự sống sẽ chấm dứt. Quan hệ tình dục, qua đó mọi sự sống trên địa cầu được duy trì, phải là kinh nghiệm kỳ thú đầy ý nghĩa của con người. Sử dụng đúng quan hệ tình dục sẽ đem vui thỏa cho gia đình. Sử dụng sai, nó sẽ biến gia đình thành địa ngục. Tính dục được sử dụng một cách khôn ngoan sẽ trở thành đầy tớ giỏi. Sử dụng sai lầm, nó sẽ thành một ông chủ tàn ác.")]),n._v(" "),i("p",[n._v("Cơ Đốc nhân sẽ có một cảm giác buồn giận, một cảm giác vi phạm khi thấy tình dục được phơi bầy sỗ sàng trên các hàng tít lớn trong báo chí, được lợi dụng đưa vào quảng cáo và được dùng như một thứ mồi rẻ tiền bên ngoài các rạp hát. Cơ Đốc nhân cảm thấy xấu hổ cho đồng loại của mình khi thấy họ quá ngu dại, thô thiển, tục tằn khi làm dơ nhớp, làm méo mó một hành vi được phú ban cho mọi loài sống.")]),n._v(" "),i("h3",{attrs:{id:"mot-quan-diem-co-doc-ve-hon-nhan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#mot-quan-diem-co-doc-ve-hon-nhan","aria-hidden":"true"}},[n._v("#")]),n._v(" Một quan điểm cơ đốc về hôn nhân")]),n._v(" "),i("p",[n._v("Thứ tư: điều tự nhiên là những ai chấp nhận quan điểm Cơ Đốc về tình dục cũng sẽ chấp nhận quan điểm Cơ Đốc về hôn nhân. Trước khi bạn bước vào hôn nhân hãy xem xét những mối liên hệ thuộc linh làm cho cuộc hôn nhân trên đất được kết chặt trên thiên đàng. Dần hồi trong tiến trình trưởng thành, chúng ta học biết yêu thương, trước hết là cha mẹ, bạn bè và sau này là một người chia xẻ cuộc đời với chúng ta. Chúng ta đã biết tiến trình này khó khăn như thế nào, vì điều đến với một tội nhân chưa tái sinh một cách tự nhiên chính là dục vọng chứ không phải tình yêu chân thật.")]),n._v(" "),i("p",[n._v("Nhiều người đã ôm nỗi bất hạnh kinh khủng khi chọn bạn đời giữa lúc đang lăn lộn trong những lao nhọc của trần gian, của xác thịt và ma quỉ, trong khi đối tượng họ chọn lại cũng ở trong tình trạng tương tự. Cho nên đối với những cuộc hôn nhân kết hợp giữa hai linh hồn không có ý thức thuộc linh, là những người hầu như không thể có được tình yêu chân chính, lâu dài, chúng ta không ngạc nhiên khi thấy quá nhiều trường hợp kết thúc ở những phiên tòa ly hôn.")]),n._v(" "),i("p",[n._v("Hôn nhân là một kết hợp thánh vì nó cho phép hai người giúp nhau thực hiện số phận thuộc linh tương lai. Đức Chúa Trời tuyên bố hôn nhân là tốt lành vì Ngài biềt người nam cần người giúp đỡ và người nữ cần người bảo vệ. Ngài muốn tất cả mọi người chồng và vợ không bao giờ được phép quên lãng mục tiêu nguyên thủy này của hôn nhân. Vai trò người vợ là yêu thương, giúp đỡ, hỗ trợ chồng trong mọi cách khả thể cũng như vai trò người chồng là yêu thương, bảo vệ và cấp dưỡng vợ, con để gia đình được hòa hợp, đầy ắp bình an của Đức Chúa Trời. Cả vợ lẫn chồng đều phải cùng tùng phục nhau, cùng yêu thương nhau.")]),n._v(" "),i("p",[n._v("Những người bước vào hôn nhân với hiểu biết thật rõ ràng mục đích và những qui luật của Đức Chúa Trời cho hôn nhân không bao giờ cần phải ra tòa ly hôn. Những cuộc hôn nhân nào thiếu những điều kiện lý tưởng này cần trước hết học biết Đức Chúa Trời mong đợi những gì nơi vợ, nơi chồng, cùng cầu nguyện xin Chúa giúp đỡ và hướng dẫn trong việc thực hiện mạng lệnh của Ngài.")]),n._v(" "),i("h3",{attrs:{id:"phuong-thuc-co-doc-cho-nhung-nan-de-quan-ly-lao-dong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#phuong-thuc-co-doc-cho-nhung-nan-de-quan-ly-lao-dong","aria-hidden":"true"}},[n._v("#")]),n._v(" Phương thức cơ đốc cho những nan đề quản lý lao động")]),n._v(" "),i("p",[n._v('Thứ năm: chúng ta phải giữ thái độ Cơ đốc trong các mối quan hệ quản lý lao động. Kinh Thánh dạy rằng, "Hễ làm việc gì hãy hết lòng mà làm như làm cho Chúa, chứ không phải làm cho người ta, vì biết rằng anh em sẽ bởi Chúa mà được cơ nghiệp làm phần thưởng. hãy hầu việc Đấng Christ tức là Chúa. Vì ai ăn ở bất nghĩa, sẽ lại chịu lấy sự bất nghĩa của mình: không tây vị ai hết" (Cô-lô-se 3:23-25).')]),n._v(" "),i("p",[n._v("Nếu nguyên tắc của Chúa Cứu Thế nắm ưu thắng trong các mối quan hệ quản lý lao động, chúng ta sẽ hiếm khi có đình công. Sẽ không có những cuộc tranh cãi, thương thảo lâu dài trong đó không bên nào chịu tương nhượng quyền lợi bên kia. Chủ sẽ đối xử rộng rãi, hào phóng với công nhân, trong khi công nhân sẽ tận tâm làm việc suốt ngày theo đúng khế ước, vì họ không làm chỉ vì lương hướng. Chúng ta có thể học điều này nơi thái độ của chủ và thợ Nhật Bản đối xử với nhau.")]),n._v(" "),i("p",[n._v("Kinh Thánh dạy rằng tất cả những việc làm chân thật đều đáng quí trọng và Cơ Đốc nhân phải là những công nhân trung tín, hữu hiệu, và sẵn lòng hơn hết. Cơ Đốc nhân phải là người đứng lên giữa cơ xưởng hoặc cửa hàng đòi hỏi công lý, đồng thời cũng phải là người không chịu cúi xuống nhận một điều lợi không chính đáng.")]),n._v(" "),i("p",[n._v('Cũng vậy, Cơ Đốc nhân làm chủ phải đối xử rộng rãi và tôn trọng công nhân, nêu gương cho giới chủ nhân. Một chủ nhân có những quan điểm Cơ Đốc chân thật không thể không quan tâm đến những biện pháp an toàn lao động, điều kiện làm việc tốt và phúc lợi của công nhân. Ông sẽ không coi công nhân như là "nhân lực" nhưng là những con người.')]),n._v(" "),i("p",[n._v("Cả chủ và thợ cần phải nhớ rằng những điều kiện được cải thiện hiện tại, cùng với những cảm thông chúng ta có ngày nay giữa hai giai tầng đã có khởi nguyên từ kết quả của một cuộc phục hưng tâm linh vĩ đại. Di sản về các nghiệp đoàn lao động đến từ hội thánh và từ những cuộc phục hưng mạnh mẽ thời Wesley ở thế kỷ 18. Tự do xã hội cho các tầng lớp lao động khởi sự có là khi một lãnh tụ Cơ Đốc Lord Shaftesbury, dù bị cả gia tộc chống đối dữ dội, vẫn suốt đời theo đuổi cuộc tranh đấu cho việc cải thiện điều kiện làm việc của công nhân - làm ít giờ hơn, lương cao hơn và được đối xử tử tế hơn.")]),n._v(" "),i("p",[n._v("Nếu không nhờ cuộc phục hưng tâm linh của thế kỷ 18, những thành quả lao động đã thâu đạt chắc hẳn chưa thể có hoặc đã phải đợi đến một thời điểm trễ hơn nhiều trong lịch sử của chúng ta. Khi một số các lãnh tụ lao động nói đến việc loại trừ tôn giáo ra ngoài vòng pháp luật, loại bỏ Đức Chúa Trời, bất chấp Kinh Thánh, coi thường hội thánh... họ cần phải nhớ rằng rất nhiều điều họ có hôm nay là thành quả quyền năng tin mừng của Chúa Cứu Thế.")]),n._v(" "),i("p",[n._v("Một số các lãnh tụ lao động cũng như các nhà kỹ nghệ đã trở nên kênh kiệu, kiêu ngạo, giàu có, tự mãn, và say mê, đeo đuổi quyền lực. Tất cả những người này cần phải hạ mình xuống trước Đức Chúa Trời, nhận thức nhu cầu của nhau, cố gắng áp dụng Luật Vàng (làm cho người điều mình muốn người ta làm cho mình) trong nghĩa thực tiễn hơn hết.")]),n._v(" "),i("h3",{attrs:{id:"quan-diem-co-doc-ve-chung-toc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#quan-diem-co-doc-ve-chung-toc","aria-hidden":"true"}},[n._v("#")]),n._v(" Quan điểm cơ đốc về chủng tộc")]),n._v(" "),i("p",[n._v("Thứ sáu: Cơ đốc nhân nhìn vấn đề chủng tộc theo nhãn quan của Chúa Cứu Thế và nhận rằng hội thánh chỉ mới giải quyết được một phần nan đề lớn này. Chúng ta đã để cho giới thể thao, giải trí, chính trị, quân lực, giáo dục và kỹ nghệ qua mặt. Hội thánh đúng ra phải chủ động hơn trong vai trò lãnh đạo. Hội thánh đúng ra phải tự nguyện làm điều các tòa án liên bang Mỹ làm là áp lực và bắt buộc. Nhưng phân tích cho đến cùng, chỉ có thể có giải pháp chung cuộc, đích thực ở chân thập tự giá, là nơi tất cả chúng ta có thể gặp nhau trong tình huynh đệ. Càng đến gần Chúa Cứu Thế bao nhiêu thì những người thuộc nhiều chủng tộc có thể đến gần nhau bấy nhiêu.")]),n._v(" "),i("p",[n._v("Kinh Thánh dạy rằng trong Chúa Cứu Thế không còn phân biệt Do thái hay ngoại bang, nam hay nữ, Hy lạp hay dã man, giàu hay nghèo. Kinh Thánh cho biết tất cả chúng ta là một trong Chúa Cứu Thế. Mặt đất tại chân thập tự giá rất bằng phẳng. Khi Chúa Cứu Thế mở mắt thuộc linh cho chúng ta, chúng ta sẽ không thấy màu da, giai cấp hay điều kiện, nhưng chỉ thấy những con người với cùng những khát khao, âu lo, thiếu thốn với những ước vọng cũng y như chúng ta. Chúng ta sẽ khởi sự nhìn người khác bằng nhãn quan của Chúa là Thầy chúng ta. Chúng ta sẽ trở thành bạn hữu của những người đó, mời họ vào nhà.")]),n._v(" "),i("h3",{attrs:{id:"quan-diem-co-doc-ve-chu-nghia-duy-vat"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#quan-diem-co-doc-ve-chu-nghia-duy-vat","aria-hidden":"true"}},[n._v("#")]),n._v(" Quan điểm cơ đốc về chủ nghĩa duy vật")]),n._v(" "),i("p",[n._v("Thứ bảy: thái độ Cơ Đốc phải là thái độ ưu thắng trong lãnh vực kinh tế. Chúa Giê-xu dạy rằng sự sống con người không cốt tại của cải dư dật. Đồng tiền la một tên nô lệ tốt nhưng là một ông chủ xấu. Của cải, tài sản là để sử dụng, để vui hưởng, để chia xẻ, để ban cho chứ không phải để tích lũy. Thánh Phao lô bảo rằng lòng mê tham tiền bạc là gốc rễ mọi điều ác (I. Timôthê 6:10). Của cải có chỗ riêng và quyền hạn của nó nhưng nó không được chiếm địa vị kiểm soát, chủ trị đời sống chúng ta. Lòng tham sẽ đặt tiền bạc lên trên cuộc sống. Nó xiềng xích và nô lệ hóa kẻ tôn thờ nó. Nó làm chai cứng tấm lòng, làm chết lịm mọi thôi thúc thanh cao và hủy diệt những phẩm tính linh hoạt của sự sống.")]),n._v(" "),i("p",[n._v("Chúng ta cần cảnh giác đối với lòng tham ở mọi mức độ và ở mọi hình thức! Tất cả mỗi chúng ta phải giữ mình khỏi tham dục qua tinh thần cảnh giác, qua sự cầu nguyện, tự chế và kỷ luật. Đời sống không phải chỉ có tiền bạc, nhà cửa, đất ruộng, khả năng kiếm tiền và những thành đạt về phương diện tài chánh. Lòng tham lam không được phép biến con người thành nô lệ cho tiền của.")]),n._v(" "),i("p",[n._v("Khi có người xin Chúa Giê-xu giải quyết tranh chấp trong việc chia gia tài giữa hai anh em, Chúa từ chối bằng một lời cảnh cáo và dùng một ẩn dụ qua đó Chúa thường đưa ra những áp dụng thực tiễn trong cuộc sống trần gian từ những sứ điệp thiên đàng. Chúa kể câu chuyện về một điền chủ giàu có giữa lúc thịnh đạt lại nghĩ đến một tài sản vĩ đại hơn với một kế hoạch lâu dài đầy ắp những lạc thú vật chất và danh vọng cá nhân. Hiển nhiên ông ta là một người có tài năng, biết tiết kiệm, cần mẫn, khôn ngoan, thành thật và ngay thẳng trong mọi giao dịch, nhưng ông ta là nạn nhân của tham vọng và lòng ích kỷ, như bao nhiêu người khác.")]),n._v(" "),i("p",[n._v("Ông ta đo lường thành công của mình bằng những thửa ruộng thẳng cánh cò bay với những kho lẫm đầy lúa gạo và nuôi linh hồn mình bằng những cái hư không của loài người. Cuộc đời ông ta được tóm thâu trong tiền của và tập trung vào bản thân khi ông ta lập kế hoạch không hề nghĩ gì đến Đức Chúa Trời hay những cái bất trắc trong đời sống.")]),n._v(" "),i("p",[n._v("Nhưng Đức Chúa Trời là Đấng đưa ra quyết định tối hậu cho nên chương trình lâu dài của ông điền chủ bỗng dưng đứt đoạn do cái chết bất ngờ. Tất cả tài sản ông ta nhọc nhằn thâu góp bỗng dưng tuột khỏi những ngón tay lạnh cứng để bị phân chia tứ tán cho người khác, trong khi ông ta phải một mình đứng trước Đức Chúa Trời với hai bàn tay trắng, không có gì biện bạch cho cuộc đời đã sống trên trần gian.")]),n._v(" "),i("p",[n._v("Hơn bất cứ ai, Cơ Đốc nhân phải ý thức rằng chúng ta vào đời với hai bàn tay không và cũng sẽ lìa đời với hai bàn tay trắng. Thật ra trong suốt hành trình chúng ta không thể sở hữu điều gì- đất ruộng hoặc con người. Chỉ duy Đức Chúa Trời là Đấng sở hữu tất cả và chúng ta là những người quản lý tài sản của Ngài trong khoảng thời gian ngắn ngủi của cuộc đời trên đất. Tất cả những gì chúng ta thấy mình có thật ra chỉ là những món Đức Chúa Trời cho mượn. Khi không thấy chân lý muôn đời quan trọng này chúng ta sẽ trở thành những kẻ ham hố, tham lam.")]),n._v(" "),i("p",[n._v('Khi chúng ta giựt lấy một điều gì hay một người nào và bảo rằng "Cái này của tôi," khi chúng ta nhìn vật gì của ai với con mắt ghen tuông rồi toan tính sang đoạt bằng cách này hay hay cách khác, chúng ta quên rằng dù điều thâu đoạt có là gì đi nữa, chúng ta cũng không thể đem theo khi phải tường trình trước tòa phán xét.')]),n._v(" "),i("p",[n._v('Điều này không có nghĩa của cải trần gian tự nó là xấu. Kinh Thánh không hề dạy như vậy nhưng khẳng định rằng Đức Chúa Trời muốn chúng ta tận dụng mọi tài nghệ, khả năng, và mọi cơ hội cuộc sống đem lại. Tuy nhiên cần nhớ rằng có những cách thủ đắc tiền tài đúng và những cách sai; có những cách thâu đoạt quyền lực đúng và cũng có những phương cách sai lầm. Nhiều Cơ Đốc nhân hiểu lầm điều này nên đã rơi vào một tình trạng tội lỗi vô cùng nguy hại khi kiêu hãnh về sự nghèo khổ đến mức đau thương, đứng đó trong tình trạng vô vọng bảo rằng "ý Chúa được nên", trong khi con cái nheo nhóc, thiếu thốn không ai chăm sóc. Thánh Phao-lô nói, "Ví bằng có ai không săn sóc đến bà con mình, nhất là không săn sóc đến người nhà mình, ấy là người chối bỏ đức tin, lại xấu hơn người không tin nữa."')]),n._v(" "),i("p",[n._v("Chúa Giê-xu kể một trong những thí dụ đặc sắc hơn cả minh giải điểm này khi Ngài kể lại câu chuyện về một ông nhà giàu giao cho các tôi tớ mỗi người một số tiền để đầu tư sinh lợi trong khi ông đi xa. Trở về, ông thấy có những đầy tớ đã đầu tư rất khôn ngoan, gia tăng gấp bội số vốn ông đã giao. Chủ khen ngợi họ biết phán đoán và khéo quản lý. Trong khi đó, tên đầy tớ hèn nhát, không biết làm gì với số tiền được giao, ngoài việc đem chôn giấu, đã bị chủ lên án nặng nề.")]),n._v(" "),i("p",[n._v('Hãy cố gắng kiếm tiền càng nhiều càng tốt bằng phương cách phù hợp với luật pháp Đức Chúa Trời và sử dụng tiền bạc để thực hiện mạng lịnh của Ngài. Dâng một phần mười cho Chúa, dâng hoa quả đầu mùa, "Hãy lấy tài vật và hoa lợi đầu mùa của con mà tôn vinh Chúa Hằng Hữu" (Châm Ngôn 3:9). Hãy dâng hiến trung tín vì Kinh Thánh dạy rằng đó là việc tốt và phải lẽ. Sau khi đã dâng một phần mười, tìn hữu cũng cần có những của dâng lạc hiến khác. Khi nào có bất cứ một nghi ngại nào về các giá trị của vật chất, bạn hãy lấy Kinh Thánh ra đọc xem Chúa dạy gì về tiền bạc, về việc kiếm tiền, về việc sử dụng cũng như phân chia tài sản. Bạn chỉ cần tự hỏi, "Chúa Giê-xu sẽ hành xử như thế nào trong tình huống này?" và qua đó, chỉ qua đó bạn sẽ được hướng dẫn.')]),n._v(" "),i("p",[n._v('Có một thời gian, trong số những bạn thân của tôi là một kỹ nghệ gia rất giàu có. Một ngày kia chúng tôi đi ăn trưa với nhau, người bạn này bình thản bảo rằng ông vừa bán được một sản phẩm và gia đình ông đã thâu được đến 13 triệu đô la trong lần làm ăn này. Nhưng rồi ông bất ngờ đổi đề tài, nói lên mối quan tâm sâu sắc nhất, ông bảo, "Để tôi nói cho ông nghe tôi đã khám phá được gì trong lời Chúa sáng nay!" Người bạn kỹ nghệ gia giàu có này đã có thứ tự ưu tiên đúng.')]),n._v(" "),i("h3",{attrs:{id:"quan-diem-co-doc-ve-nhung-nguoi-chiu-kho"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#quan-diem-co-doc-ve-nhung-nguoi-chiu-kho","aria-hidden":"true"}},[n._v("#")]),n._v(" Quan điểm cơ đốc về những người chịu khổ")]),n._v(" "),i("p",[n._v("Thứ tám: Cơ Đốc nhân là người quan tâm đến những nỗi khổ đau của đồng loại xung quanh. Những khu nhà ổ chuột trên đất nước bạn sẽ làm lòng bạn nặng trĩu. Cảnh nghèo đói và khổ đau của hàng ngàn người gần nơi bạn ở sẽ trở thành mối quan tâm của bạn. Bạn sẽ cộng tác với các tổ chức và hội đoàn để giúp giảm bớt những nỗi đau khổ của con người quanh bạn. Nhiều người đổ quá nhiều thì giờ cho các chương trình vĩ đại, xa vời mà không đóng góp gì cho việc cứu khổ trong tầm tay. Ai là người lân cận chúng ta? Bất cứ người nào gần chúng ta nhất, có thể là vợ, là chồng, là con cái, hay những người hàng xóm, trong thành phố, rồi trong đất nước chúng ta.")]),n._v(" "),i("p",[n._v("Kinh Thánh bảo rằng giới bình dân vui lòng nghe Chúa Giê-xu. Chúa đi đến đâu Ngài chữa lành người đau đến đó. Ngài an ủi người buồn thảm, khích lệ họ một cách thực tiễn. Nhiều năm trước, một giám mục người Anh nói với tôi rằng ông không biết có một tổ chức xã hội nào ở nước Anh (kể cả hội bảo vệ súc vật) lại không khởi sự từ một cuộc thức tỉnh đối với tin mừng. Cơ Đốc nhân quan tâm và giúp đỡ việc xây dựng và phát triển nhà thương, cô nhi viện, nhà dưỡng lão và các cơ sở từ thiện khác nhắm vào nỗ lực giúp đỡ những thành phần thiếu may mắn trong xã hội. Cơ Đốc nhân mong muốn góp phần trong việc chia xẻ sự giàu có lớn lao của đất nước này với những người thiếu thốn trong các miền khác trên thế giới. Cơ Đốc nhân sẽ là người hỗ trợ những tổ chức xã hội quốc gia hay quốc tế có tiếng tốt trong việc giúp đỡ những người kém may mắn trên thế giới. Tuy nhiên có một điểm chúng ta cần thận trọng đó là khi dâng tặng tiền bạc của Chúa cho một tổ chức từ thiện nào, chúng ta cần kiểm soát xem nó được chi tiêu ra sao. Có nhiều tổ chức làm việc đầy tinh thần trách nhiệm đáng cho chúng ta hỗ trợ và cầu nguyện, nhưng cũng có những tổ chức khác chúng ta không nên đóng góp.")]),n._v(" "),i("p",[n._v("Không có một chỗ nào trong Kinh Thánh dạy chúng ta trốn đời mà trái lại, chúng ta cần tham gia, cùng với những người có thiện chí tích cực hoạt động cho mục đích nâng đỡ những người kém may mắn. Đức Chúa Trời cần có những công tác viên xã hội, lính gác tù, cảnh sát, bác sĩ, trợ tá bệnh viện, y tá, những người làm công tác từ thiện và nhiều thành phần khác có thể giúp giảm bớt những đau khổ của con người.")]),n._v(" "),i("p",[n._v('Khẩu hiệu của Hội Rotary là "Để sự phục vụ lên trên bản thân." Khẩu hiệu của Hội Kiwanis là "Chúng tôi xây dựng". Khẩu hiệu của Hội Lions là "Tự do, sáng suốt và an ninh quốc gia". Tất cả những ý tưởng này bắt nguồn từ Cơ Đốc Giáo. Nhiều tôn giáo ngoại bang chưa từng có một hội đoàn chủ trương phục vụ như những hội đoàn trên. Tất cả những hội đoàn này thật sự là những phó sản của Cơ Đốc Giáo cho dù một số hội viên không phải là Cơ Đốc nhân. Mùi thơm của Chúa Cứu Thế là mùi thơm của những tổ chức xã hội này.')]),n._v(" "),i("h3",{attrs:{id:"tinh-yeu-thuong-anh-em"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#tinh-yeu-thuong-anh-em","aria-hidden":"true"}},[n._v("#")]),n._v(" Tình yêu thương anh em")]),n._v(" "),i("p",[n._v('Thứ chín: Cơ Đốc nhân có một bổn phận đặc biệt đối với các anh em tín hữu khác. Họ thuộc một tầng lớp đặc biệt và chúng ta phải dành một tình yêu siêu đẳng cho họ, "Chúng ta biết rằng mình đã vượt khỏi sự chết qua sự sống, vì chúng ta yêu anh em mình. Còn ai chẳng yêu thì ở trong sự chết" (I Giăng 3:14).')]),n._v(" "),i("p",[n._v('Chúng ta phải yêu kẻ thù, yêu cả những người bách hại chúng ta, "những người lấy mọi điều dữ nói vu cho chúng ta" (Ma-thi-ơ 5:11).')]),n._v(" "),i("p",[n._v('Nhưng tình yêu thương lớn nhất trong tình thương nhân loại là tình thương dành cho các tín hữu khác. Chúa Giê-xu dạy rằng, "Điều răn của ta là các ngươi hãy yêu nhau như ta đã yêu các ngươi" (Giăng 15:12). Chúng ta cũng được dạy phải phục vụ lẫn nhau, "Hỡi anh em, hãy vì tình thương làm đầy tớ lẫn nhau."')]),n._v(" "),i("p",[n._v("Lạy Chúa xin giúp con sống từng ngày\nQuên mình\nĐến nỗi ngay cả khi quì gối cầu nguyện,\nCon cũng chỉ cầu thay cho người khác.\nXin giúp con trong mọi công việc,\nCon làm ngay thật, chân thành\nBiết rằng mọi việc con làm cho Chúa\nĐều phải là làm cho tha nhân.\nTha nhân, Lạy Chúa, phải, tha nhân,\nXin hãy lấy điều này làm khẩu hiệu\nXin để con sống cho người khác\nĐể con có thể sống như Ngài.\nC.D. Meigs")]),n._v(" "),i("p",[n._v('Với tư cách là Cơ Đốc nhân, Kinh Thánh dạy rằng bổn phận chúng ta đối với nhau là phải nêu gương tốt cho nhau. Thánh Phao-lô bảo rằng, "phải lấy lời nói, nết làm, sự yêu thương, đức tin và sự tinh sạch mà làm gương cho các tín đồ" (I Tim. 4:12). Đây không phải là một gợi ý của Phao-lô, nhưng là một mệnh lệnh! Không phải là một đề nghị mà là một bổn phận. Chúng ta phải là những Cơ Đốc nhân gương mẫu.')]),n._v(" "),i("p",[n._v('Kinh Thánh cũng dạy chúng ta phải tha thứ cho nhau, "Hãy ở với nhau cách nhân từ, đầy dẫy lòng thương xót, tha thứ nhau như Đức Chúa Trời đã tha thứ anh em trong Đấng Christ vậy" (Ê-phê-sô 4:32). Chúa Giê-xu dạy rằng "nếu không tha thứ cho người ta thì Cha các ngươi cũng sẽ không tha thứ cho các ngươi" Chúa cũng dạy, "Khi các ngươi đứng cầu nguyện, nếu có sự gì bất bình cùng ai thì hãy tha thứ, để Cha các ngươi ở trên trời cũng tha lỗi cho các ngươi" (Mác 11:25).')]),n._v(" "),i("p",[n._v("Chúng ta cũng được dạy rằng Cơ Đốc nhân không được đoán xét lẫn nhau nhưng phải quyết định không bao giờ để đá vấp chân hay một trở ngại nào trên lối đi của anh em chúng ta.")]),n._v(" "),i("p",[n._v("Kinh Thánh dạy chúng ta phải tùng phục nhau, chúng ta phải mặc lấy sự khiêm nhường khi đối xử với nhau. Chúng ta phải tôn trọng, quí mến nhau, phải đặt người khác lên trước và chúng ta sau cùng.")]),n._v(" "),i("p",[n._v('Sau khi bị tai biến mạch máu não, trong sáu năm cuối đời, nhạc mẫu tôi phải ngồi trên xe lăn, nhạc gia tôi là bác sĩ L. Nelson Bell, một nhà chơi thể thao, bác sĩ, nhà truyền giáo và nhà văn (trong năm cuối đời, ông còn là người điều hành hội thánh Giám Lý Miền nam trước khi liên kết), đã tận tụy săn sóc bà với tất cả tâm tình thương mến. Một ngày kia ông bảo nhà tôi, "Con biết không, đây là những ngày hạnh phúc nhất của bố mẹ! Săn sóc mẹ con là một đặc ân lớn nhất trong đời ba." Những ai thấy lúc ông săn sóc cho bà, biết những điều ông nói là chân thật.')]),n._v(" "),i("p",[n._v("Là Cơ Đốc nhân, chúng ta phải mang gánh nặng cho nhau. Có những gánh nặng mỗi người phải tự mang lấy, không ai mang thay cho ai được. Nếu xao nhãng, gánh nặng cá nhân kia sẽ mãi còn đó. Tuy nhiên, có những gánh nặng khác bạn bè có thể giúp như những lúc buồn đau, tai họa, thử thách, cô đơn, những gánh nặng gia đình, những khó khăn thuộc linh, con cái mắc vòng nghiện ngập ma túy hay lao lý hay mất tích. Nhưng chúng ta không nên lo lắng về gánh nặng, chúng ta phải gom hết và chất lên vai Đức Chúa Trời, hướng về Chúa xin Ngài ban quyền năng, nâng đỡ và bổ sức cho chúng ta. Tuy nhiên bổn phận chúng ta là phải giúp đỡ anh em mình mang gánh nặng riêng.")]),n._v(" "),i("p",[n._v('Kinh Thánh dạy rằng chúng ta phải sống rộng rãi với nhau. Chúa dạy bổn phận Cơ Đốc nhân là săn sóc cô nhi quả phụ và giúp đỡ những người nghèo khó trong cộng đồng Cơ Đốc. Kinh Thánh dạy chúng ta phải đóng góp cho các nhu cầu của các tôi tớ Chúa... tiếp đãi khách lạ... rửa chân các thánh đồ, cứu giúp những người khốn khổ... Chúa Giê-xu cũng dạy "Hễ các ngươi đã làm việc đó cho một người trong những người rất hèn mọn này của anh em ta, ấy là đã làm cho chính mình ta vậy"... và " ban cho có phước hơn nhận lãnh." Tất cả những điều này là các bổn phận xã hội của tín hữu với nhau.')]),n._v(" "),i("h3",{attrs:{id:"an-sung-the-hien-trong-hanh-dong"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#an-sung-the-hien-trong-hanh-dong","aria-hidden":"true"}},[n._v("#")]),n._v(" Ân sủng thể hiện trong hành động")]),n._v(" "),i("p",[n._v('Điểm cuối cùng, Cơ Đốc nhân phải là những người thể hiện lòng nhân hậu, và đây là một trong những mỹ đức quan trọng và lớn nhất của người tin Chúa. Chính sức mạnh niềm tin của chúng ta đôi khi khiến chúng ta có khuynh hướng nghĩ rằng mình đúng và tất cả những người khác sai. Đây là điều tốt nếu những tín niệm của chúng ta dựa trên những mạng lịnh minh thị của Chúa "Ngươi phải" hoặc "Ngươi chớ" thay vì dựa trên ý kiến riêng của chúng ta. Nhiều phe nhóm khác nhau, thường khi chống đối nhau trong hội thánh đều nhấn mạnh khuynh hướng ghê sợ của con người là qui tụ lại thành từng nhóm riêng, xây dựng trên những tín niệm sâu sắc về những vấn đề vụn vặt mà mỗi nhóm tin rằng chỉ một mình họ có lời giải đáp. Cố tiến sĩ Harry Ironside từng nói: Chúng ta phải cẩn thận đừng nhầm thành kiến với tín niệm."')]),n._v(" "),i("p",[n._v('Tất nhiên chúng ta phải ghê tởm sự gian ác, những việc xấu xa, sai trái, nhưng thái độ không dung nạp tội lỗi thường biến thành thái độ không chấp nhận tội nhân. Chúa Giê-xu ghét tội lỗi, nhưng thương yêu tội nhân. Tôi thấy vừa sửng sốt, vừa khôi hài khi nghe một người có một nền tảng tôn giáo khá căn bản tuyên bố trong một chương trình truyền hình rằng "bạn không hề thấy Chúa Giê-xu gần gũi những người có tư cách đáng ngờ hay những người có quan niệm và thái độ không phù hợp với những điều Chúa Giê-xu biết là cao quí, đúng đắn."')]),n._v(" "),i("p",[n._v("Người nói như thế cần phải biết rằng Chúa Giê-xu không ngại tiếp xúc với bất cứ ai! Một trong những điều các thấy thông giáo và phe Pha-ri-si chỉ trích Chúa Giê-xu là thái độ sẵn sàng giúp đỡ cũng như trò chuyện, trao đổi ý kiến với mọi người, dù đó là thành phần thâu thuế, trộm cắp, các giáo sư, gái buôn hương, kẻ giàu hay người nghèo! Ngay cả các môn đệ cũng trách Chúa đã tiếp xúc với những người bất xứng ở nơi công cộng, nhưng tất cả những điều này không làm giảm bớt lòng thương cảm của Chúa đối với những thành phần bần cùng, đui mù, bẩn chật trong xã hội.")]),n._v(" "),i("p",[n._v('Chúa Giê-xu có một tinh thần cởi mở, bao quát hơn hết thế giới từng chứng kiến. Ngài có một tín niệm vô cùng mạnh mẽ trong lòng rằng Ngài có thể hòa nhập với mọi hạng người mà không sợ bị ô nhiễm. Chính sự sợ hãi làm cho chúng ta không muốn nghe quan điểm của người khác, ngại rằng quan điểm của chúng ta bị phê phán. Chúa Giê-xu không hề có nỗi lo sợ đó, không hề có những quan niệm hẹp hòi, không cần gì phải giữ kẽ hay thủ thế để tự bảo vệ. Chúa biết rõ khác biệt giữa lòng bao dung với tinh thần thoả hiệp và đây chính là những điều chúng ta cần học nơi Ngài. Chúa đã nêu cho chúng ta một gương mẫu trong sáng, vĩ đại, hơn hết về chân lý kết hợp vơí tình thương và khi từ giã Ngài phán rằng, "Hãy đi, làm theo như vậy" (Lu-ca 10:37).')]),n._v(" "),i("p",[n._v("Trên đây chỉ là một vài trong số rất nhiều những bổn phận xã hội của Cơ Đốc nhân. Cơ Đốc nhân không thể ẩn dật trong cuộc sống cô độc nhưng anh là một thành viên trong xã hội. Vì thế, những lời dạy của Chúa Giê-xu thường có liên quan đến thái độ của chúng ta đối với đồng bào, đồng loại.")]),n._v(" "),i("p",[n._v("Bạn cần học Kinh Thánh, đọc và sống theo lời dạy của Kinh Thánh. Chỉ lúc đó bạn mới có thể bày tỏ về quyền năng biến đổi của Chúa Cứu Thế đang ngự trị trong lòng mình cho một thế giới đầy bối rối.")]),n._v(" "),i("h2",{attrs:{id:"chuong-18-hy-vong-cho-tuong-lai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-18-hy-vong-cho-tuong-lai","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 18: Hy vọng cho tương lai")]),n._v(" "),i("p",[i("strong",[n._v('"Vì sẽ có tiếng kêu lớn và tiếng của thiên sứ lớn cùng tiếng kèn của Đức Chúa Trời, thì chính mình Chúa ở trên trời giáng xuống; bấy giờ những kẻ chết trong Đấng Christ sẽ sống lại trước hết. Kế đến chúng ta là kẻ sống mà còn ở lại, sẽ cùng nhau đều được cất lên với những người ấy giữa đám mây, ở nơi không trung mà gặp Chúa, như vậy chúng ta sẽ ở cùng Chúa luôn luôn. Thế thì anh em hãy dùng lời đó mà yên ủi nhau." (I Tê-sa-lô-ni-ca 4:16-18)')])]),n._v(" "),i("p",[n._v('Cách đây ít lâu, trong mục điểm sách của một trong những tờ báo lớn tại Hoa kỳ có một bài với tựa đề, "Văn Chương Của Ngày Tận Thế" trong đó tác giả liệt kê tất cả những tên sách ngày nay viết về ngày chung kết thế giới. Trong lĩnh vực phim ảnh cũng vậy, ngày nay có một số phim gọi là những phim "Hạt-ma -ghê-đôn" nói về cuộc chiến cuối cùng trong ngày tận thế. Mối quan tâm về ngày tận thế quả đã xâm lăng cả đến thế giới giải trí. Người ta tự hỏi không biết có còn thế giới ngày mai nữa không. Ngươì ta lo lắng không biết điều gì sẽ xảy ra vào năm 2000, thậm chí không biết thế giới có tồn tại đến đó hay không. Vì cuộc chạy đua vũ trang và những căng thẳng trên thế giới với những vụ xáo trộn xảy ra trên khắp các lục địa khiến cho một số lãnh tụ thế giới nghi ngờ rằng con người có thể không qua khỏi năm 2000 - tình hình dường như càng ngày càng tệ hại hơn.')]),n._v(" "),i("p",[n._v('Một số người hỏi, "Khi nào Chúa Cứu Thế Giê-xu trở lại?" Chúa bảo chúng ta không nên định ngày, giờ, nhưng Ngài có chỉ cho chúng ta một số các dấu hiệu, chúng ta sẽ thảo luận thêm vấn đề này sau. Sự trở lại của Chúa Cứu Thế Giê-xu chỉ riêng trong Tân Ước đã được đề cập đến hơn 300 lần. Điều này cho thấy Đức Thánh Linh là Đấng chỉ huy việc trứ tác Kinh Thánh đã nhấn mạnh sự kiện Chúa Cứu Thế Giê-xu sẽ trở lại địa cầu.')]),n._v(" "),i("p",[n._v('C. S. Lewis, giáo sư danh tiếng của đại học Oxford và Cambridge từng nói rằng có ba điều làm con người không tin vào sự trở lại của Chúa Cứu Thế. Trước hết là sự kiện Chúa không trở lại trong thế kỷ thứ nhất như người ta trông đợi. Vì vậy, người ta bảo rằng, "Mọi sự diễn tiến bình thường, trước sao, sau vậy và Chúa không trở lại. Tại sao Ngài không trở lại?" Lý do thứ hai, theo Lewis đó là vì thuyết tiến hóa, là lý thuyết cho rằng con người có thể tự đem lại tiến bộ, không cần đến Chúa. Con người cho rằng họ có thể tự thăng tiến. Lý do thứ ba là sự trở lại của Chúa Cứu Thế nằm ngoài mọi mối quan tâm của những người sống trong ảnh hưởng của chủ nghĩa duy vật chất. Chúng ta chỉ biết hưởng thụ những gì mình thích nhất trên trần gian. Dù C.S. Lewis đã qua đời từ nhiều năm nhưng với những nhận xét trên, chúng ta thấy ông quả đã có cái nhìn đúng thời đại chúng ta hôm nay.')]),n._v(" "),i("p",[n._v('Chỉ vài hôm trước ngày tổng thống John F. Kennedy nhậm chức, tôi và thượng nghị sĩ George Smathers ở Florida được mời chơi gôn và thăm khu nhà của dong họ Kenedy ở Palm Beach. Trên đường chúng tôi từ sân gôn trở về, tổng thống Kennedy dừng xe, quay lại hỏi tôi, "Mục sư Billy Graham, ông có tin rằng Chúa Giê-xu sẽ sắp trở lại trái đất không?" Tôi sửng sốt trước câu hỏi này. Thứ nhất là vì tôi không bao giờ nghĩ một người như tổng thống Kennedy lại hỏi tôi câu đó. Thứ hai, tôi không chắc là ông có biết sự kiện Chúa Giê-xu sẽ trở lại hay không! Trước sự việc này, tôi chỉ gặp ông có vài lần cho nên không biết căn bản tôn giáo của ông như thế nào. Tôi đã trả lời, "Thưa tổng thống, tôi tin Chúa Giê-xu sẽ trở lại chứ!" Tổng Thống Kennedy bảo, "Vậy thì nhờ ông giải thích cho tôi."')]),n._v(" "),i("p",[n._v("Trong ít phút sau đó tôi có cơ hội nói với ông về sự kiện Chúa Giê xu sẽ trở lại. Tôi thường tự hỏi tại sao tổng thống Kennedy đã hỏi tôi câu đó và tôi nghĩ một phần câu trả lời đã đến khoảng ba năm sau, khi ông bị ám sát. Hồng y Cushing đã đọc những câu Kinh Thánh trong I Tê-sa-lô-ni-ca 4:16-18 tôi trích dẫn ở đầu chương này trong tang lễ tổng thống Kennedy, và hàng triệu người trên khắp thế giới đã theo dõi buổi lễ trên truyền hình.")]),n._v(" "),i("p",[n._v('Câu 17 trong phân đoạn Kinh Thánh trên nổi bật hẳn lên, "kế đến chúng ta là kẻ sống mà còn ở lại, sẽ cùng nhau đều được cất lên với những người ấy giữa đám mây, tại nơi không trung mà gặp Chúa." từ ngữ "cất lên" được dịch từ nguyên ngữ Hi lạp có nghĩa là đem đi bất ngờ nhanh chóng. Ngày đó sẽ đến rất mau khi Chúa Giê-xu trở lại để "bất ngờ đem đi" những người theo Ngài từ tất cả các nghĩa trang trên thế giới, và tất cả chúng ta là những người đang sống sẽ cùng nhập bọn với họ ra đi trong một cuộc xuất hành vĩ đại! Đây chính là hy vọng tương lai của Cơ Đốc nhân.')]),n._v(" "),i("h3",{attrs:{id:"vi-nhung-loi-hua-trong-cuu-uoc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#vi-nhung-loi-hua-trong-cuu-uoc","aria-hidden":"true"}},[n._v("#")]),n._v(" Vì Những Lời Hứa Trong Cựu Ước")]),n._v(" "),i("p",[n._v('Tại sao Chúa Cứu Thế sắp trở lại? Có năm lý do sau đây. Trước hết, Chúa Giê-xu phải trở lại trần gian vì có những lời hứa trong Cựu Ước chưa thành nghiệm. Hàng chục lời tiên tri về lần đến đầu tiên đã ứng nghiệm, nhưng còn một số khác chưa xảy ra. Thí dụ như Kinh Thánh chép rằng, "Vì có một con trẻ sinh cho chúng ta, tức là một con trai ban cho chúng ta; quyền cai trị sẽ nấy trên vai Ngài. Ngài sẽ được xưng là Đấng lạ lùng, là Đấng Mưu luận, là Đức Chúa Trời Quyền năng, là Cha đời đời, là Chúa bình an. Quyền cai trị và sự bình an của Ngài cứ thêm mãi không thôi, ở trên ngôi Đa-vít và trên nước Ngài, đặng làm cho nước bền vững, và lập lên trong sự chánh trực công bình, từ nay cho đến đời đời" (Ê-sai 9:5,6). Có ngườii đã bảo rằng, "Khi chính quyền của chúng ta đặt trên vai Ngài thì sự thịnh vượng và bình an của chúng ta sẽ không bao giờ chấm dứt."')]),n._v(" "),i("p",[n._v('Đây là phần tiên tri chưa hoàn tất. Một hài nhi đã giáng sinh. Một Con trai đã được Đức Chúa Trời cho nhân loại. Nhưng quyền chính chưa được đặt trên vai Ngài. Ngài chưa ban hòa bình cho thế giới, cũng như chưa đem công lý trong lần đến đầu tiên. Nhưng đó là những điều Ngài sẽ ban trong lần đến thứ nhì, vì Kinh Thánh dạy rằng tất cả những lời tiên tri này sẽ phải được ứng nghiệm. Nhà tiên tri Mi-chê nói rằng, "Ngài sẽ làm ra sự phán xét giữa nhiều dân, đoán định các nước mạnh nơi phương xa; và họ sẽ lấy gươm rèn lưỡi cày, lấy giáo rèn lưỡi liềm; nước này chẳng còn giá gươm lên nghịch cùng nước khác, và cũng không tập sự chiến tranh nữa" (4:3). Nhưng chúng ta thử xem những cuộc chiến tranh đang diễn ra ngày nay, cuộc chạy đua vũ trang đang gia tăng. Nhưng rồi một ngày kia người ta sẽ lấy mũi giáo rèn thành dụng cụ tỉa cây và rồi các nước không còn giá gươm lên chinh chiến lẫn nhau nữa. Tại sao? Vì Chúa Hoà Bình sẽ trở lại - Ngài sẽ là thủ lĩnh thế giới.')]),n._v(" "),i("h3",{attrs:{id:"vi-chinh-loi-ngai-da-phan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#vi-chinh-loi-ngai-da-phan","aria-hidden":"true"}},[n._v("#")]),n._v(" Vì Chính Lời Ngài Đã Phán")]),n._v(" "),i("p",[n._v('Thứ hai, Chúa Cứu Thế phải trở lại địa cầu vì chính Ngài đoan chắc như thế. Ngài là chân lý tuyệt đối. Cả hai chương 24 và 25 của phúc âm Ma-thi-ơ đều chỉ nói về việc Ngài trở lại. Thí dụ như trong Ma-thi-ơ 24:27 ghi, "Vì như chớp phát ra từ phương đông, nháng đến phương tây, thì sự Con Người đến cũng thể ấy." Trong Ma-thi-ơ 25:31-32 Chúa cũng nói, "Khi Con Người ngự trong vinh hiển mình mà đến với các thiên sứ thánh, thì Ngài sẽ ngồi trên ngôi vinh hiển của Ngài. Muôn dân nhóm lại trước mặt Ngài..." Lời tiên tri này chưa thành nghiệm, nhưng đây là lời Chúa phán và tôi tin sẽ xảy ra.')]),n._v(" "),i("p",[n._v('Chúa Giê-xu không bao giờ nói dối. Ngài bảo rằng, "Ta đi sắm sẵn cho các ngươi một chỗ. Khi ta đã đi và đã sắm sẵn cho các ngươi một chỗ rồi, Ta sẽ trở lại đem các ngươi đi với ta, hầu cho ta ở đâu thì các ngươi cũng ở đó" (Giăng 14:2,3). Ngài sẽ đích thân trở lại, chính Chúa Giê-xu sẽ trở lại, cho thấy Ngài yêu thương chúng ta đến chừng nào! Chương trình cứu rỗi không chỉ thỏa mãn chúng ta trong thế giới này, ban cho chúng ta sự sống mới tại đây, nhưng Ngài còn có một chương trình vĩ đại cho chúng ta trong tương lai. Cho cả cõi đời đời!')]),n._v(" "),i("p",[n._v("Kinh Thánh cho biết chúng ta sẽ cùng cai trị với Ngài. Chúng ta là kẻ đồng kế tự với Chúa Cứu Thế Giê-xu và chúng ta sẽ cùng sống với Ngài trong cõi đời đời! Hiện nay Chúa Giê-xu đang làm gì? Ngài đang chuẩn bị nơi ở cho chúng ta! Đã gần hai ngàn năm qua, nơi ở đó chắc chắn phải rất đặc biệt. Đó là sự mắt chưa thấy, tai chưa nghe và lòng chưa hề nghĩ đến, nhưng đó là điều Đức Chúa Trời chuẩn bị cho những kẻ yêu mến Ngài.")]),n._v(" "),i("h3",{attrs:{id:"vi-vi-tri-hien-tai-cua-sa-tan"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#vi-vi-tri-hien-tai-cua-sa-tan","aria-hidden":"true"}},[n._v("#")]),n._v(" Vì Vị Trí Hiện Tại của Sa-tan")]),n._v(" "),i("p",[n._v('Lý do thứ ba tại sao Chúa Giê-xu phải đến đó là vì vị trí hiện tại của Sa-tan. Ngoài Đức Chúa Trời, ma quỉ là hữu thể quyền lực nhất trên thế giới ngày nay. Có điều kỳ lạ là Kinh Thánh cho chúng ta biết Sa-tan có thể tiếp cận được với thiên đàng, tới ngôi Đức Chúa Trời. Kinh Thánh cho biết nó là kẻ kiện cáo anh em cả ngày lẫn đêm. Sa-tan được gọi là "chê-ru-bin đang che phủ," nhưng cũng là sư tử gầm thét- là vua các loài ác thú. Nó đi rình mò xung quanh tìm kiếm người nào nó có thể nuốt được. Lúc cám dỗ Chúa Giê-xu, nó đã chỉ cho Chúa thấy các nước trên thế gian, bảo rằng nó sẽ cho Chúa tất cả nếu Chúa bằng lòng sấp mình xuống thờ lạy nó (Ma-thi-ơ 4:9). Chúa Giê-xu không tranh luận với nó về điều đó. Sa-tan có quyền cho Chúa cả thế giới, cả hệ thống gian ác của trần gian. Nhưng tạ ơn Đức Chúa Trời, để đối phó, Chúa Giê-xu đã trưng dẫn lời Kinh Thánh và đó là chỗ Sa-tan không thể chịu nổi! Kinh Thánh luôn luôn là vũ khí đánh bại Sa-tan.')]),n._v(" "),i("p",[n._v('Trong II Cô-rinh-tô 4:4, Sa-tan được gọi là "chúa đời này", nghĩa là nó điều khiển tất cả những tôn giáo, những triết thuyết hư giả, sai lạc trên thế giới. Kinh Thánh cho biết thế giới ở dưới sự kiểm soát của chúng. Những gì sẽ xuất hiện trên thế giới này nếu Sa-tan bình yên vô sự và không có gì xảy ra cho nó? Ai sẽ loại bỏ được sự ác? Ai sẽ loại trừ được Sa-tan? Đứng trước Sa-tan, loài người thúc thủ. Con người không thể cột trói Sa-tan. Hội thánh không thể lật đổ nó. Những nhà lập pháp bất lực. Liên Hiệp Quốc không biết phải đối phó với nó như thế nào, thậm chí, không biết là mình đang phải đương đầu với một quyền lực của thần linh- một quyền lực gian ác lớn lao trong thế giới ngày nay.')]),n._v(" "),i("p",[n._v('Tuy nhiên, xin chúng ta đừng quên sự kiện này, đó là có một Đấng quyền uy hơn Sa-tan! Đấng đó đã đánh bại Sa-tan gần hai nghìn năm trước trên thập tự giá. Ma quỉ không muốn Chúa lên cây thập tự vì nó biết điều Ngài sẽ làm trên đó. Nó biết khi chịu chết trên cây thập tự, Chúa sẽ gánh vác hết tội lỗi của mọi người trần gian. Cũng từ thập tự giá của Chúa Giê-xu, Đức Chúa Trời đã nói với loài người, "Ta yêu con, ta muốn thứ tha mọi tội lỗi của con. Ta muốn con trở thành con cái Ta để một ngày kia con có thể cùng ở với ta trên thiên đàng." Nếu Chúa Giê-xu không chịu chết mà xuống khỏi cây thập tự thì chúng ta không thể được cứu rỗi, không thể lên thiên đàng. Đó là lý do tại sao ma quỉ không muốn Ngài chịu chết trên thập tự. Đó là lý do chúng chế nhạo, thách thức Chúa, "Nếu ngươi là Con Đức Chúa Trời hãy xuống khỏi cây thập tự đi!" Sa-tan thảm bại nặng nề hơn hết là tại thập tự giá và khi Chúa Cứu Thế Giê-xu phục sinh.')]),n._v(" "),i("p",[n._v("Hiện nay Sa-tan chưa bị cột trói. Nó vẫn còn cầm quyền trên thế gian. Nó là ma quỉ hành động trong trần gian. Tất cả những chuyện bất công, tất cả các cuộc chiến tranh trên thế giới đều do ma quỉ xúi giục. Tất cả những tội phạm, mọi chuyện gian ác, tất cả những gì kinh khủng nhất đang diễn ra, cũng đều xuất phát từ ma quỉ. Với quyền lực siêu nhiên, ma quỉ có những kế hoạch phi thường, kể cả nỗ lực chống nghịch Đức Chúa Trời.")]),n._v(" "),i("p",[n._v("Trên khắp cả đất trời chỉ có một người có thể đối phó với ma quỉ, và Ngài sẽ trở lại để trừng trị nó. Ngài sẽ ném Sa-tan vào vực sâu không đáy, và cuối cùng vào hồ lửa. Lúc đó sẽ không còn ma quỉ nữa. Chúng ta sẽ được giải thoát khỏi sức lôi kéo kinh khủng của điều ác cũng như quyền lực thống trị của Sa-tan trên tấm lòng con người qua hàng bao nhiêu thế kỷ. Đó là lý do tại sao Chúa Cứu Thế đã phải đến. Ngài là Đấng duy nhất thực hiện được những điều đó. Có lần tôi đã đọc được trên một tạp chí, bảo rằng thế giới hiện đang tìm kiếm một nhà giải phóng. Chúng ta có thể khẳng định rằng thế giới đã có nhà giải phóng đó!")]),n._v(" "),i("h3",{attrs:{id:"vi-nhung-roi-loan-tren-the-gioi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#vi-nhung-roi-loan-tren-the-gioi","aria-hidden":"true"}},[n._v("#")]),n._v(" Vì Những Rối Loạn Trên Thế Giới")]),n._v(" "),i("p",[n._v('Lý do thứ tư tại sao Chúa Giê-xu phải trở lại là vì những rối loạn hiện tại trên thế giới- một thế giới đầy những đau thương, thống khổ, đói kém, chiến tranh, giết tróc, tham lam, ghen ghét, lừa đảo và tham nhũng. Hầu như mọi hình thức chính quyền của con người đều thất bại. Chính quyền nào lên dường như cũng đều thất bại trong việc đối phó với tất cả những nan đề đó. Thế giới đang thúc thủ và trở nên vô vọng hơn khi bước vào thời đại kỹ thuật tin học. Kinh Thánh chép, "Hãy bảo những kẻ có lòng sợ hãi rằng: Khá vững lòng, đừng sợ nữa! Đây này, Đức Chúa Trời các ngươi sẽ lấy sự báo thù mà đến, tức là sự báo trả của Đức Chúa Trời. Chính Ngài sẽ đến và cứu các ngươi!" (Ê-sai 35:4). Chúa Giê-xu sẽ cứu chúng ta khỏi chính mình. Có nhiều nhà xã hội và các khoa học gia ngày nay tin rằng con người có khả năng hủy hoại chính mình. Thật vậy, nhân loại hôm nay đang có khả năng đó. Nhưng Chúa Cứu Thế sẽ trở lại. Ngay khi con người đang vung gươm lên chém giết lẫn nhau thì Ngài sẽ đến, thành lập vương quốc công chính và vinh quang, hòa bình và công lý. Lúc đó thế giới chúng ta sẽ là một thế giới tốt đẹp biết chừng nào! Bạn đã sẵn sàng chưa?')]),n._v(" "),i("h3",{attrs:{id:"vi-nhung-nguoi-da-chet-trong-chua"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#vi-nhung-nguoi-da-chet-trong-chua","aria-hidden":"true"}},[n._v("#")]),n._v(" Vì Những Người Đã Chết Trong Chúa")]),n._v(" "),i("p",[n._v("Lý do thứ năm Chúa Cứu Thế Giê-xu phải trở lại là vì những người đã chết trong Chúa - những người tin cậy Đức Chúa Trời nhưng đã qua đời. Khi xem phim The Holocaust (Cuộc Tàn Sát) tôi nghĩ rằng trong số những người bị Hitler giết, có nhiều người là Cơ Đốc nhân. Hàng ngàn người chịu cảnh bất công, bị giết tróc, và bao nhiêu người đã đi qua lịch sử, là những Cơ Đốc nhân, tự hỏi tại sao họ phải chết. Nhiều người chắc hẳn cũng đã tự vấn tại sao họ phải chịu khổ vì cớ đức tin. Nhưng Chúa Giê-xu sẽ trở lại và sẽ có một cuộc phục sinh vĩ đại. Cơ Đốc nhân sẽ sống lại từ cõi chết! Rồi những kẻ đang sống vào thời điểm đó sẽ được cất lên không trung gặp Chúa, như vậy, chúng ta sẽ ở cùng Chúa luôn luôn. Khi nào những biến cố này xảy ra? Chúng ta không biết đích xác ngày giờ, nhưng chúng ta tin rằng ngày đó đang tiến lại gần. Chắc chắn ngày đó đã gần hơn hai ngàn năm kể từ ngày Chúa tuyên bố những lời trên.")]),n._v(" "),i("p",[n._v('Chúa đã để lại một số dấu hiệu gíup chúng ta theo dõi. Ngài dạy rằng, trước hết sẽ có những xáo trộn về trí thức và tinh thần trên khắp thế giới: "dân các nước sầu não, rối loạn..." (Lu-ca 21:25). Từ ngữ "sầu não" có nghĩa là bị hất tung tứ phía, còn "rối loạn" là không lối thoát. Nhân loại chúng ta rồi ra sẽ đi đến chỗ này - tình trạng không lối thoát.')]),n._v(" "),i("h3",{attrs:{id:"loi-thoat-2"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#loi-thoat-2","aria-hidden":"true"}},[n._v("#")]),n._v(" Lối Thoát")]),n._v(" "),i("p",[n._v("Jean Paul Sartre viết một quyển sách nhan đề Không Lối Thoát, và ông cho biết không có giải pháp nào cho tình trạng tiến thoái lưỡng nan của nhân loại. Sartre bảo rằng không có lối thoát, nhưng tôi xin khẳng định với bạn rằng có lối thoát và Chúa Cứu Thế Giê-xu chính là lối thoát của chúng ta.")]),n._v(" "),i("p",[n._v('Chúa Giê-xu cũng bảo rằng ngay trước khi Chúa trở lại, tình trạng luân lý của con người sẽ suy đồi. Chúa phán, "Việc đã xảy đến trong đời Nô-ê thì cũng sẽ xảy đến trong ngày Con Người: người ta ăn uống, cưới gả cho đến ngày Nô-ê vào tàu, và nước lụt đến hủy diệt thiên hạ hết" (Lu-ca 17: 28,29). Rồi Chúa cũng nói, "Ngày Con Người hiện ra cũng một thể ấy" (c.30). "Việc đã xảy đến trong ngày Nô-ê thì cũng xảy đến trong ngày Con Người" (Lu-ca 17: 26). Đặc điểm trong cả hai trường hợp - thời Nô-ê và thời Lót, là tình trạng đạo đức suy đồi. Nền tảng đạo đức cũng đang xụp đổ xung quanh chúng ta. Thế giới đang đắm chìm trong tình trạng vô đạo đức chưa từng có trong lịch sử và Chúa Giê-xu bảo rằng đó chính là tình trạng nhân loại trong thời kỳ ngay trước khi Ngài trở lại. Thứ ba, Chúa Giê-xu cũng bảo rằng sẽ có sự sa ngã xảy ra, nghĩa là những người đã từng tin Chúa sẽ sa ngã lẻ tẻ từng cá nhân hay có khi nguyên cả một tập thể. "Nhiều tiên tri giả sẽ dấy lên, lừa dối nhiều người." Chúng ta hãy nhìn vào các tiên tri giả ngày nay: "Đức Thánh Linh phán tỏ tường rằng trong đời sau rốt, có mấy kẻ sẽ bội đạo mà theo các thần lừa dối, và đạo lý của quỉ dữ..." (I Ti-mô-thê 4:1). Người ta sẽ lạc xa đạo thật. Nhiều người sẽ có những ý tưởng sai lạc về Đức Chúa Trời và có cái nhìn lệch lạc về Cơ Đốc Giáo. Họ không phải là những môn đồ thật của Chúa Cứu Thế Giê-xu. Họ đã sa ngã một cách thiếu khôn ngoan, có lẽ không phải vì lý do tri thức. Có thể họ vẫn tin, nhưng đã sa ngã trong cách sống. Chính lối sống của bạn biện minh cho niềm tin. Chúa Giê-xu bảo rằng, "nhờ trái mà biết cây". Ma quỉ vẫn luôn luôn đi dạo, dụ hoặc con người bảo rằng "Có phải Đức Chúa Trời phán dặn thế không?" để cố tìm cách gieo nghi ngờ đối với Lời Kinh Thánh. Rất nhiều tiên tri giả ngày nay xuất hiện khắp nơi, đặt vấn đề với Kinh Thánh, bảo rằng điều này điều kia không đáng tin. Tôi xin nói với bạn rằng toàn thể Kinh Thánh là lời được linh cảm của Đức Chúa Trời. Kinh Thánh chính là lời Thánh của Đức Chúa Trời. Rồi cũng có kẻ sẽ nói với chúng ta rằng, "Chúa Cứu Thế Giê-xu chỉ là một người thường, không phải là Đức Chúa Trời". Tôi xin khẳng định với bạn rằng Chúa Giê-xu là Thần Nhân. Ngài là Đức Chúa Trời và Ngài cũng là người. Ngài thương yêu chúng ta đến nỗi đã chịu chết cho chúng ta.')]),n._v(" "),i("p",[n._v('Điểm thứ tư: Chúa Giê-xu cho biết trước ngày Ngài trở lại, "tội ác sẽ thêm nhiều, lòng yêu mến của nhiều người sẽ nguội lần" (Ma-thi-ơ 24:12). Hiện nay chúng ta thấy những điều đó đầy dẫy trên báo chí. Chắc bạn cũng đã từng nghe về những vụ khủng bố trên khắp thế giới? Các hoạt động khủng bố, phá hoại gia tăng trên mọi quốc gia, nhưng vì có những kỹ thuật mới cho nên con người không thể nào ứng phó được. Chúa Giê-xu nói trong Lu-ca 21:9, "Lại khi các ngươi nghe nói về giặc giã, loạn lạc, thì đừng kinh khiếp, vì các điều đó phải đến trước; nhưng chưa phải là cuối cùng liền đâu." Từ ngữ "giặc giã" chỉ thị tình trạng nổi loạn chống lại thẩm quyền, và đó là điều chúng ta đã thấy ở nhiều nơi trên thế giới và Chúa Giê-xu bảo đó là một trong những dấu hiệu của ngày tận thế. Rồi Chúa cũng nói đến các hội nghị hòa bình là chi tiết được thánh Phao-lô đề cập trong I Tê-sa-lô-ni-ca 5:3, "Khi người ta sẽ nói rằng: Bình hòa và an ổn thì tai họa thình lình vụt đến, như sự đau đớn xảy đến cho người đàn bà có nghén, và người ta chắc không tránh khỏi đâu." Tiên tri Ê-sai cũng viết, "Những kẻ ác chẳng hưởng sự bình an bao giờ, Chúa Hằng Hữu phán vậy"(Ê-sai 48:22). Chưa bao giờ chúng ta thấy có nhiều người đi tìm sự bình an như bây giờ. Liên Hiệp Quốc họp hành liên miên tìm các giải pháp hòa bình. Quốc hội nhiều quốc gia cũng hội họp để tìm hòa bình. Các lãnh tụ thế giới - như bộ trưởng ngoại giao của Hoa Kỳ chẳng hạn, bay đi khắp thế giới để tìm hòa bình, để xoa dịu chỗ này một chút, trấn an chỗ kia một chút, nhưng khi vừa về đến Hoa Thịnh Đốn, ông lại đã nghe tin chiến sự bùng nổ ở một nơi nào đó. Chúa Giê-xu bảo rằng thời kỳ cuối cùng sẽ như vậy: "Nếu những ngày ấy không giảm bớt thì chẳng có một người nào được cứu; song vì cớ các người được chọn, thì những ngày ấy sẽ giảm bớt" (Ma-thi-ơ 24:22).')]),n._v(" "),i("p",[n._v('Chúa Giê-xu cũng đề cập đến một điều khác: công cuộc truyền giảng Tin Lành toàn cầu được chú trọng để rao giảng phúc âm trước ngày Ngài trở lại. "Tin-lành này về Nước Đức Chúa Trời sẽ được giảng ra khắp đất, để làm chứng cho muôn dân. Bấy gì sự cuối cùng sẽ đến." Lần đầu tiên trong lịch sử nhân loại, Tin Lành đã được nghe khắp thế giới. Vô tuyến truyền thanh, truyền hình, vệ tinh truyền thông, sách báo đã có mặt khắp mọi miền trên thế giới. Không có một chỗ nào không thể nghe được Tin Lành Đây là lần đầu có sự kiện này.')]),n._v(" "),i("p",[n._v('Bây giờ chúng ta phải làm gì? Chúng ta phải đối phó với tình hình này như thế nào? Trước hết, chúng ta phải thanh tẩy đời sống. Kinh Thánh dạy, "Ai có hy vọng này thì tự làm nên thanh sạch." Bạn có hy vọng về sự tái lâm của Chúa Cứu Thế Giê-xu không? Nếu có, bạn phải thể hiện hy vọng đó bằng một cuộc sống trong sạch, thánh thiện, đầu phục, hiến tế. Khi tiếp nhận Chúa Cứu Thế, chúng ta kinh nghiệm ý nghĩa đầu tiên của sự thánh hóa. Nhưng rồi qua tiến trình thánh hóa, chúng ta sẽ tăng trưởng trong ân sủng và trong sự thông biết Chúa. Rồi một ngày kia, khi chúng ta thấy Chúa Giê xu mặt đối mặt chúng ta sẽ bước vào sự nên thánh toàn diện, chúng ta sẽ trở nên toàn hảo như Chúa là Đấng toàn hảo. Chúng ta sẽ thấy Ngài thật viên mãn như vốn có thật vậy.')]),n._v(" "),i("p",[n._v('Thứ hai, chúng ta cần chờ đợi với lòng kiên trì. Tôi biết đôi khi chúng ta lo lắng tự hỏi không biết Chúa Giê- xu có sẽ thực sự trở lại hay không. Kinh Thánh khẳng định, "Còn ít lâu, thật ít lâu nữa, thì Đấng đến sẽ đến; Ngài không chậm trễ đâu" (Hy-bá 10:37). Ngài đã định ngày trở lại và chỉ một mình Đức Chúa Trời biết ngày đó. Thời điểm đã có và Ngài sẽ trở lại đúng hẹn, không trễ cũng không sớm, dù chỉ một giờ.')]),n._v(" "),i("p",[n._v('Điều thứ ba chúng ta phải làm là thức canh. Từ ngữ "thức canh" có nghĩa là chúng ta không chỉ mong muốn xuông mà tâm trí phải luôn luôn nghĩ đến việc Ngài trở lại. Ở đầu chương này chúng ta đã trích dẫn câu Kinh Thánh trong Tít 2:13, "đang chờ đợi hy vọng hạnh phước của chúng ta, và sự hiện ra của sự vinh hiển Đức Chúa Trời lớn và Cứu Chúa chúng ta là Chúa Cứu Thế Giê-xu."')]),n._v(" "),i("p",[n._v('Điều thứ tư là chúng ta phải làm việc. Có người bảo rằng, "Chúa sắp trở lại rồi - chúng ta nên tạm ngưng, nghỉ hết mọi công tác." Không! chúng ta phải tiếp tục phục vụ. Có thể Ngài chưa trở lại trong vòng một trăm năm hay một ngàn năm nữa. Có thể Ngài chưa trở lại trong đời bạn, nhưng chúng ta hãy làm hết sức mình đưa người khác đến với Chúa Cứu Thế. Sự trở lại của Ngài phải là thôi thúc mạnh mẽ cho chúng ta phục vụ.')]),n._v(" "),i("p",[n._v('Điều cuối cùng đó là chúng ta phải chuẩn bị. Bạn đã chuẩn bị cho ngày Chúa trở lại chưa? Chính Chúa đã dạy, "Các ngươi hãy chực cho sẵn sàng, vì Con Người sẽ đến trong giờ các ngươi không ngờ" (Lu-ca 12:40). Kinh Thánh ghi, "Bởi đức tin, Nô-ê được Chúa mách bảo cho về những việc chưa thấy, và người thành tâm kính sợ đóng một chiếc tàu để cứu người nhà mình..." (Hy-bá 11:7). Từ ngữ "kính sợ" có nghĩa là "kinh khủng". Đúng vậy! Có người được kéo vào nước Đức Chúa Trời bằng tình yêu, nhưng cũng có người đến trong sự run rẩy, sợ hãi. Chúng ta cần phải biết sợ hãi, vì những người không biết Chúa Giê-xu là Đức Chúa Trời và Đấng Cứu Thế, có nghĩa là họ sẽ bị phán xét, bị quăng vào địa ngục. Chúng ta phải đến với Chúa Cứu Thế khi còn cơ hội.')]),n._v(" "),i("h2",{attrs:{id:"chuong-19-binh-an-voi-chua"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-19-binh-an-voi-chua","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 19: Bình an với Chúa")]),n._v(" "),i("p",[i("strong",[n._v('"Ngài sẽ lau ráo hết nước mắt khỏi mắt chúng , sẽ không có sự chết cũng không có than khóc, kêu ca hay là đau đớn nữa..." (Khải Huyền 21:4)')])]),n._v(" "),i("p",[n._v("Trong quyển sách này chúng ta đã xem xét ý nghĩa của sự bình an với Chúa. Chúng ta đã biết là Cơ Đốc nhân có nghĩa gì. Chúng ta đã biết giá phải trả để được cái gọi là bình an và hạnh phúc. Tôi biết có người đã sẵn sàng ký ngân phiếu một triệu đô la nếu có được bình an. Hàng triệu người ngày nay đang tìm kiếm bình an. Mỗi lần họ đến gần chỗ có thể tìm được bình an nơi Chúa Giê-xu thì Sa-tan lại tìm cách xô họ ra xa, làm mù mắt họ, phun ra một màn khói mờ. Sa-tan dọa dẫm họ, khiến họ lạc đường! Nhưng là Cơ Đốc nhân, chúng ta đã tìm thấy sự bình an đó. Sự bình an thuộc về chúng ta cho đến đời đời. Chúng ta đã tìm được bí quyết của sự sống.")]),n._v(" "),i("p",[n._v('Từ ngữ "bình an" thường được dùng trong vòng bốn mươi, năm mươi năm qua. Chúng ta nói về bình an, các lãnh tụ tham dự nhiều hội nghị hòa bình; tuy nhiên ngày nay chúng ta thấy thế giới đang tiến nhanh về bất cứ chỗ nào trừ đến chỗ hòa bình.')]),n._v(" "),i("p",[n._v('Thánh Phao-lô đã nói về nhân loại rằng, "Chúng không biết con đường bình an" (Rô-ma 3:17). Thử nhìn quanh, chúng ta sẽ thấy có rất ít bình an trong đời sống cá nhân, gia đình, xã hội, kinh tế hay chính trị ở bất cứ đâu. Tại sao? Bởi vì trong tất cả chúng ta đều có những mầm mống của sự nghi ngờ, bạo động, hận thù, hủy diệt.')]),n._v(" "),i("p",[n._v('Chúa Giê-xu dạy rằng, "Phước cho kẻ làm cho người hoà thuận" (Ma-thi-ơ 5:9). Chúng ta cần phải nỗ lực kiến tạo hoà bình, nhưng không theo đường lối của chủ nghĩa hoà bình, nghĩa là hòa bình bằng mọi giá. Chúng ta phải hoạt động cho hoà bình, nhưng Chúa Giê-xu cũng báo trước rằng, "Các ngươi sẽ nghe nói về giặc và tiếng đồn về giặc , hãy giữ mình, đừng bối rối, vì những sự ấy phải đến; song chưa là cuối cùng đâu. Dân này sẽ dấy lên nghịch cùng dân khác, nước nọ nghịch cùng nước kia..." (Ma-thi-ơ 24:6,7).')]),n._v(" "),i("p",[n._v('Chúng ta chỉ có thể kinh nghiệm sự bình an khi chúng ta đã nhận được sự tha thứ của Đức Chúa Trời được giải hoà với Chúa, có sự hoà hợp bên trong, hoà hợp với anh em chúng ta và nhất là với Đức Chúa Trời. Sách tiên tri Ê-sai 57: 21 ghi như sau, "Đức Chúa Trời ta đã phán, Những kẻ gian ác chẳng hưởng sự bình an." Nhưng qua huyết đổ ra trên cây thập tự, Chúa Cứu Thế đã giải hoà chúng ta với Đức Chúa Trời, và chính Ngài là sự bình an của chúng ta. Nếu tiếp nhận Chúa Cứu Thế Giê-xu bằng đức tin, chúng ta sẽ được Đức Chúa Trời coi là công chính, chúng ta sẽ nhận được sự bình an sâu kín bên trong mà con người không thể có bằng phương cách nào khác. Khi Chúa Cứu Thế ngự vào tâm hồn, chúng ta sẽ được giải phóng khỏi mặc cảm tội lỗi. Được tẩy sạch mọi vết tích của xúc cảm bất xứng chúng ta có thể ngước đầu cao lên, nhìn thẳng vào anh em mình biết rằng, "khi tánh hạnh người nào đẹp lòng Chúa, thì Ngài cũng khiến các thù nghịch người ở hoà bình với người" (Châm ngôn 16:7). Quan trọng hơn, chúng ta biết mình có thể đứng trước mặt Chúa trong giờ lâm tử với cùng một cảm nhận về sự yên ổn và bình an đó.')]),n._v(" "),i("p",[n._v('Trong Kinh Thánh Chúa Giê-xu cho biết cuộc chiến tranh sẽ sẽ kéo dài cho đến chung cuộc. Ngài biết rằng bản chất con người sẽ không thay đổi nếu không có sự tái sinh tâm linh. Ngài biết đại đa số nhân loại sẽ không cải đổi để đến với Ngài vì đa số con người trên thế giới hôm nay chưa được "tái sinh." Chính vì thế mà cái tiềm ẩn trong chúng ta là khuynh hướng bạo động có thể bùng nổ ở gia đình, trong cộng đồng, trên thế giới.')]),n._v(" "),i("p",[i("em",[n._v("Kinh Thánh mô tả ba loại bình an.")])]),n._v(" "),i("h3",{attrs:{id:"binh-an-voi-chua-2"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#binh-an-voi-chua-2","aria-hidden":"true"}},[n._v("#")]),n._v(" Bình An Với Chúa")]),n._v(" "),i("p",[n._v('Trước hết là bình an hay hòa bình với Đức Chúa Trời. Thánh Phao-lô viết như sau, "Chúng ta đã được xưng công bình bởi đức tin, thì được hoà thuận với Đức Chúa Trời bởi Chúa Cứu Thế Giê-xu, Chúa chúng ta" (Rô-ma 5:1); "bởi huyết Ngài trên thập tự giá thì đã làm nên hoà bình..." (Cô-lô-se 1:20). Đây là bình an bạn có thể có tức khắc - bình an với Đức Chúa Trời.')]),n._v(" "),i("p",[n._v('Cuộc chiến rộng lớn nhất đang diễn ra trên thế giới hôm nay là giữa con người với Đức Chúa Trời. Có lẽ người ta không ý thức mình đang gây chiến với Đức Chúa Trời, nhưng nếu họ không biết Chúa Giê-xu là Đấng Cứu Thế, và không nhận Ngài là Chúa thì Đức Chúa Trời coi họ là những người đang gây chiến với Ngài. Khoảng cách biệt này do tội lỗi gây ra. Kinh Thánh tuyên bố rằng "mọi người đều đã phạm tội, không còn phản chiếu vinh quang Thượng Đế" (Rô-ma 3:23). Có người bảo rằng "Tôi đã gia nhập hội thánh, đã nhận thánh lễ báp tem". Những người này cần nhớ rằng Chúa Giê-xu đến để ngự trị lòng họ, không phải chỉ là Đấng Cứu rỗi nhưng còn là Chủ, là Chúa cuộc đời họ nữa!')]),n._v(" "),i("p",[n._v('Sẽ là một thảm họa lớn lao nhất nếu tôi không nói với bạn rằng, không ăn năn tội, không nhận Chúa Giê-xu làm Đấng Cứu Chuộc, bạn sẽ bị hư vong. Phúc Âm Giăng 3:16 khẳng định như sau, "Vì Đức Chúa Trời yêu thương thế gian đến nỗi đã ban Con Một của Ngài, hầu cho hễ ai tin Con ấy, không bị hư vong mà được sự sống đời đời." Đây không chỉ là niềm tin trong lý trí, nhưng cũng phải là niềm tin trong tấm lòng, trong tình cảm, trong tâm hồn của bạn nữa. Nó đòi bạn hoàn toàn tin cậy, hoàn toàn ký thác và cam kết với Chúa. Chúng ta đem tất cả mọi sự đặt nơi chân thập tự giá là nơi Chúa Cứu Thế Giê-xu chịu chết cho tội lỗi chúng ta. Ngài đã giải hoà với Đức Chúa Trời bằng cái chết trên thập tự giá. Nếu chúng ta quay lưng lại với Ngài, không phó thác cuộc đời chúng ta cho Ngài, chúng ta không có hy vọng gì trong tương lai.')]),n._v(" "),i("p",[n._v('Đối với người đã có bình an với Chúa, bình an đó đã được trả giá bằng huyết của Con Đức Chúa Trời. Thánh Phi-e-rơ bảo rằng đó là nhờ "huyết báu Đấng Christ, như huyết của chiên con không tì vết" (I Phi-e-rơ 1: 19). Nếu tôi là một con người duy nhất trên trần gian thì Chúa Giê-xu cũng sẽ chịu chết thay cho tôi vì Ngài yêu thương tôi. Ngài cũng yêu thương bạn và tình yêu của Ngài đã tuôn đổ chan hoà trên thập giá.')]),n._v(" "),i("h3",{attrs:{id:"xin-gap-lai-toi-tren-thien-dang"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#xin-gap-lai-toi-tren-thien-dang","aria-hidden":"true"}},[n._v("#")]),n._v(' "Xin Gặp Lại Tôi Trên Thiên Đàng"')]),n._v(" "),i("p",[n._v('Đọc tiểu sử nữ hoàng Victoria của nước Anh, tôi được biết bà thỉnh thoảng đến thăm các khu nghèo tại Luân Đôn. Một ngày kia, nữ hoàng vào uống trà với một bà cụ già, lúc đứng dậy ra về bà nói, "Bà cụ có cần tôi giúp gì không?" Bà cụ trả lời, "Muôn tâu, thưa có. Mong nữ hoàng sẽ gặp lại tôi trên thiên đàng." Nữ hoàng Anh quay lại với bà, nhỏ nhẹ, "Phải, tôi sẽ có trên đó, nhưng tất cả là nhờ huyết đã đổ trên cây thập tự cho bà và cho cả tôi." Nữ hoàng Victoria trong thời của bà, là một phụ nữ quyền lực nhất thế giới mà vẫn phải nhờ cậy vào huyết Chúa Cứu Thế để được cứu rỗi thì chúng ta cũng vậy. Kinh Thánh khẳng định rằng Đức Chúa Trời là Chúa của sự bình an (I Cô-rinh-tô 14:33). Đức Chúa Trời ban sự cứu rỗi thông qua thập tự giá. Nhờ huyết Con Ngài đổ ra đã đem lại hoà bình và cuộc chiến giữa bạn với Đức Chúa Trời kết thúc mau chóng. Hòa ước được ký kết bằng huyết của Con Ngài là Chúa Cứu Thế Giê-xu.')]),n._v(" "),i("p",[i("em",[n._v("Bạn hiện đang có bình an với Đức Chúa Trời, hay tội lỗi vẫn tiếp tục ngăn cách bạn với Ngài?")])]),n._v(" "),i("h3",{attrs:{id:"binh-an-cua-duc-chua-troi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#binh-an-cua-duc-chua-troi","aria-hidden":"true"}},[n._v("#")]),n._v(" Bình An Của Đức Chúa Trời")]),n._v(" "),i("p",[n._v("Sự bình an thứ hai nói trong Kinh Thánh là bình an của Đức Chúa Trời. Những người biết Chúa Cứu Thế Giê-xu dù phải sống trong bất cứ nan đề nào, ngay cả việc đối diện với cái chết, vẫn có sự bình an của Đức Chúa Trời trong lòng. Vợ chồng trong cảnh tử biệt sinh ly, con cái đau ốm, mất công ăn việc làm...bạn vẫn có thể có sự bình an mà chính mình không hiểu. Sự bình an vẫn có thể âm thầm ngự trị tâm hồn bạn ngay vào những lúc bạn đầm đìa nước mắt bên phần mộ.")]),n._v(" "),i("p",[n._v('Một tờ báo trích dẫn lời một nhà phân tâm học bảo rằng ông không thể thêm gì vào lời khuyên của Thánh Phao-lô trong thư Phi-líp 4:6-7, "Chớ lo phiền chi hết, nhưng trong mọi sự hãy dùng lời cầu nguyện, nài xin, và sự tạ ơn mà trình các sự cầu xin của mình cho Đức Chúa Trời. Sự bình an của Đức Chúa Trời vượt quá mọi sự hiểu biết sẽ giữ gìn lòng và ý tưởng anh em trong Chúa Cứu Thế Giê-xu." Xin đừng lo lắng vô ích. Đã bao nhiêu lần bạn và tôi lo lắng quay quắt, đi tìm một chút bình an cho tâm hồn? Sự bình an của Đức Chúa Trời có thể có ngay trong lòng chúng ta - chính giờ này.')]),n._v(" "),i("p",[n._v('Thánh Phao-lô trong thư Cô-lô-se 3:15 viết, "xin sự bình an của Chúa cứu Thế cai trị trong lòng anh em." Một số người trong các bạn đã tin Chúa Giê-xu làm Đấng Cứu Chuộc, nhưng chưa thực sự tôn Ngài làm Chúa, cho nên bạn đang đánh mất sự bình an của Đức Chúa Trời trong những rối ren, vật lộn, trong những thử thách và mọi áp lực của đời sống. Sự bình an của Đức Chúa Trời có đang ở trong lòng bạn không?')]),n._v(" "),i("p",[n._v("Chúng ta đều quen với sự biến cải xảy ra bên trong Sau-lơ trên đường đến thành Đa-mách khi Chúa Cứu Thế ngự vào lòng ông, biến ông từ một trong những kẻ thù tàn hại nhất trở thành một trong những người bênh vực Ngài mạnh mẽ nhất. Nhiều thay đổi lạ lùng như thế trong tâm tính con người đang xảy ra hôm nay, cũng do cùng một phương cách như đã biến đổi Sau-lơ thành Phao-lô - đó là sự tái sinh qua Chúa Cứu Thế Giê-xu!")]),n._v(" "),i("p",[n._v('Không một triết lý trần gian nào có thể thành đạt được những thay đổi như thế hay đem lại sức mạnh như vậy. Trong khi đó, chính năng lực mạnh mẽ này lại sẵn dành cho bạn bất cứ lúc nào. Đức Chúa Trời phán rằng, "Đừng sợ vì Ta ở với ngươi; chớ kinh khiếp vì ta là Đức Chúa Trời ngươi! Ta sẽ bổ sức cho ngươi; phải Ta sẽ giúp đỡ ngươi, lấy tay hữu công bình ta mà nâng đỡ ngươi" (Ê-sai 41:10).')]),n._v(" "),i("p",[n._v("Dù hoàn cảnh nào, thôi thúc nào, bổn phận nào, dù giá phải trả là gì và dù phải hy sinh đến đâu, sức mạnh của Ngài sẽ là sức mạnh của bạn trong lúc bạn cần.")]),n._v(" "),i("p",[n._v("Có những lợi ích thuộc thể phát sinh từ nếp sống Cơ Đốc, trong khi đó tội lỗi và ý thức về sự bất xứng sâu xa bên trong làm tổn hại cả thể xác lẫn tinh thần. Ý thức về tình trạng nhơ bẩn, vô luân, về những cay đắng hận thù với người khác, ý thức về những trục trặc, chán nản và tình trạng bất năng trong việc thành đạt những mục tiêu mong muốn - tất cả những điều này là những nguyên nhân chính gây nên bệnh tật cho cả thể xác lẫn tinh thần. Ý thức và mặc cảm tội lỗi con người thiên nhiên mang trong mình khiến anh ta không hoàn tất được những bổn phận phải làm, khiến anh ta lâm bệnh cả thể xác lẫn tinh thần. Việc Chúa Giê-xu kết hợp sự giảng dạy và chữa bệnh trong những năm tại thế không phải là việc ngẫu nhiên. Giữa sự sống tâm linh với sức khỏe thể xác và tinh thần có một mối liên hệ rất thật.")]),n._v(" "),i("p",[n._v("Bình an với Chúa và bình an của Chúa trong lòng con người và niềm vui tương giao với Chúa Cứu Thế tự nó đã có hiệu quả hữu hiệu trên thân xác và tinh thần, đưa đến chỗ phát triển và bảo tồn năng lực thể xác và tinh thần. Vì vậy, Chúa Cứu Thế đã khích lệ quan tâm đến thân xác, tinh thần cũng như tâm linh, thêm vào với sự bình an sâu kín bên trong, là sự phát triển đời sống thuộc linh, niềm vui và mối tương giao với Chúa Cứu Thế, cũng như sức mạnh mới đến từ sự tái sinh.")]),n._v(" "),i("p",[n._v('Có một số đặc ân mà chỉ Cơ Đốc nhân chân chính mới biết vui hưởng, thí dụ như đặc quyền được Chúa ban sự khôn ngoan và thường xuyên hướng dẫn. Kinh Thánh dạy, "Ví bằng trong anh em có kẻ kém khôn ngoan, hãy cầu xin Đức Chúa Trời là Đấng ban cho mọi người cách rộng rãi, không trách móc ai thì kẻ ấy sẽ được ban cho" (Gia-cơ 1: 5).')]),n._v(" "),i("p",[n._v("Cơ Đốc nhân cũng được ban cho một thái độ lạc quan chân chính, được bảo đảm rằng theo mặc khải thiên thượng, mọi sự đến cuối cùng rồi sẽ tốt đẹp.")]),n._v(" "),i("p",[n._v("Cơ Đốc nhân cũng có một quan niệm xác định về thế giới. Thế giới quan này cho thấy mục tiêu của Đức Chúa Trời và chung cuộc của mọi sự. Nó bảo đảm với chúng ta rằng cho dù trong cảnh chiến tranh giặc giã, hay khi chúng ta ở giữa những sức mạnh tàn phá của thiên nhiên, Đức Chúa Trời vẫn đang ngự trên ngôi, cầm quyền tể trị mọi sự. Chính Sa-tan cũng bị quyền lực của Đức Chúa Trời giới hạn. Nó chỉ được ban cơ hội hành sử ảnh hưởng gian ác của nó khi Đức Chúa Trời cho phép trong thời gian và trong hạn định của Ngài. Kinh Thánh dạy chúng ta rằng Đức Chúa Trời có một chương trình nhất định cho mỗi một thời kỳ lịch sử, cho từng quốc gia và cho từng cá nhân. Kinh Thánh tiết lộ chương trình của Đức Chúa Trời về sự tái lâm của Chúa Cứu Thế khi nước Ngài được thiết lập như đã trình bày. Vì vậy, đối với Cơ Đốc nhân đời sống có một kế hoạch và một sự bảo đảm rằng chung cuộc Đức Chúa Trời sẽ toàn thắng tất cả những gì không công chính.")]),n._v(" "),i("p",[n._v('Để tóm tắt tính ưu việt của cuộc sống Cơ Đốc trên tất cả những lối sống khác, chúng ta không thể bỏ qua lợi điểm Cơ Đốc nhân có trong cả cõi vĩnh hằng. Gióp bảo rằng, "Nếu loài người chết, có được sống lại chăng?" (Gióp 14:14). Ông trả lời chính câu hỏi của mình rằng, "Tôi biết rằng Đấng Cứu Chuộc tôi vẫn sống, đến lúc cuối cùng Ngài sẽ đứng trên đất" (Gióp 19:25). Còn có viễn ảnh nào, tương lai nào, hy vọng nào, cuộc sống nào tươi đẹp hơn! Tôi sẽ không đánh đổi địa vị này với con người giàu có nhất, ảnh hưởng nhất trần gian. Tôi thà làm con Vua Trời, làm người đồng kế thừa với Chúa Cứu Thế, làm thành viên Hoàng Gia thiên quốc!')]),n._v(" "),i("p",[n._v("Tôi biết tôi đến từ đâu, tại sao tôi ở đây và sẽ đi đâu. Tôi có sự bình an đầy ắp tâm hồn. Sự bình an của Ngài tuôn tràn, phủ ngập linh hồn tôi!")]),n._v(" "),i("p",[n._v("Cuồng phong gào thét. Những lượn sóng cao ngất của biển cả ầm ầm đánh vào bờ đá. Chớp nhoáng lòe ra, sấm động vang trời, gió ào ào thổi, nhưng con chim nhỏ cứ bình yên đứng trong hốc đá, dấu đầu dưới cánh trong giấc ngủ say. Đó là sự bình an - hoàn toàn an nghỉ trong bão tố!")]),n._v(" "),i("p",[n._v("Trong Chúa Cứu Thế chúng ta được nghỉ ngơi, được bình an giữa những rối ren, biến loạn, bối rối của cuộc đời. Cơn bão dù cuồng nộ, nhưng lòng chúng ta an nghỉ. Đến cuối cùng, chúng ta đã tìm được sự an bình!")]),n._v(" "),i("h3",{attrs:{id:"binh-an-trong-tuong-lai"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#binh-an-trong-tuong-lai","aria-hidden":"true"}},[n._v("#")]),n._v(" Bình An Trong Tương Lai")]),n._v(" "),i("p",[n._v('Loại bình an thứ ba trong Kinh Thánh là bình an trong tương lai. Kinh Thánh hứa rằng sẽ có một thời kỳ cả thế giới sẽ hưởng bình an. Trong hiện tại thì thế giới dường như đang tiến dần đến cuộc chiến cuối cùng là trận Hạt-ma-ghê-đôn. Trong sách Khải Huyền 6:4, sừ đồ Giăng yêu dấu viết về một con ngựa sắc hồng, "kẻ ngồi trên ngựa được quyền cất lấy cuộc hòa bình khỏi thế gian, hầu cho người ta giết lẫn nhau." Chúng ta sẽ không thể có hòa bình trường cửu cho đến khi Chúa Hòa Bình đến.')]),n._v(" "),i("p",[n._v("Thật vậy, Ngài sẽ đến. Sẽ có một ngày trời sẽ mở ra và Chúa Cứu Thế Giê-xu sẽ trở lại. Ngài sẽ thiết lập vương quyền trên đất, và lúc đó chúng ta sẽ có bình an, sẽ có công bình xã hội. Đó sẽ thực sự là thời đại huy hoàng!")]),n._v(" "),i("p",[n._v('Tiên tri Ê-sai tiên báo rằng, "Quyền cai trị sẽ đặt trên vai Ngài: Ngài sẽ được xưng là Đấng Lạ Lùng, Đấng Mưu Luận, Đức Chúa Trời quyền năng, Cha Đời Đời và Chúa Bình An. Quyền cai trị và sự bình an của Ngài cứ thêm mãi không thôi..." (Ê-sai 9:6,7). Xin bạn nghĩ đến điều này: không còn chiến tranh, giặc giã, không còn hận thù, không còn bạo động. Tất cả đều bình an, yên ổn.')]),n._v(" "),i("h3",{attrs:{id:"o-voi-chua-cuu-the"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#o-voi-chua-cuu-the","aria-hidden":"true"}},[n._v("#")]),n._v(" Ở Với Chúa Cứu Thế")]),n._v(" "),i("p",[n._v('Bạn có biết Chúa Cứu Thế không? Bạn có chắc Ngài đang ở trong lòng bạn không? Có lẽ từ lâu bạn vẫn từng nghĩ, "Tôi muốn chắc mình có sự bình an với Chúa. Tôi muốn được chuẩn bị sẵn sàng đối diện với cái chết. Tôi muốn tất cả mọi tội lỗi được tha. Tôi muốn tình trạng mắc tội được gỡ bỏ. Tôi muốn được ở với Chúa Cứu Thế khi Ngài trở lại thiết lập Nước Trời."')]),n._v(" "),i("p",[n._v('Tất cả những điều trên đều thuộc về bạn như một quà tặng. Bạn không cần phải trả một giá nào, "Ấy là nhờ ân điển, bởi đức tin anh em được cứu; điều đó không phải do anh em nhưng là tặng phẩm của Đức Chúa Trời" (Ê-phê-sô 2:7,8).\n')]),i("p",{attrs:{align:"center"}},[n._v("\nXin bạn hãy trao cả tấm lòng và cuộc sống bạn cho Chúa Cứu Thế. Xin đừng trì hoãn.\n")]),i("p"),n._v(" "),i("h2",{attrs:{id:"chuong-20-doi-sau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-20-doi-sau","aria-hidden":"true"}},[n._v("#")]),n._v(" Chương 20: Đời sau")]),n._v(" "),i("p",[i("strong",[n._v('"Theo như đã định cho loài người phải chết một mình rồi chịu phán xét." (Hê-bơ-rơ 9:27)')])]),n._v(" "),i("p",[n._v("Năm trước trên hệ thống truyền hình có một chương trình tựa đề "),i("em",[n._v('"Ngày Sau" (The Day After)')]),n._v(". Trong phần cuối cuốn phim, toàn thế giới trông như một bãi tha ma rải rác đầy xác chết. Đối với hàng triệu người xem, cuốn phim này là một kinh nghiệm hoảng loạn.")]),n._v(" "),i("p",[n._v('Nếu chỉ với trí tưởng tượng rồi dàn dựng cuốn phim về một mùa đông nguyên tử như trên, đã có thể khiến hàng triệu người suy nghĩ đến tương lai thì sự kiện về "ngày sau" đối với những người loại bỏ Chúa Cứu Thế, sẽ khiến con người phải quan tâm đến đâu! So với sự phán xét cuối cùng và số phận đời đời trong hỏa ngục thì cảnh chết chóc trong truyền hình kể trên bất quá chỉ như một buổi sinh hoạt ngoài trời!')]),n._v(" "),i("p",[n._v("Nhưng tương lai bạn không xoay quanh tình hình thế giới, dù tình hình đó có ảm đạm đến đâu. Tương lai bạn tùy thuộc vào biến cố đã xảy ra cách đây hai nghìn năm trên thập tự giá, và vào việc bạn tiếp nhận hay loại bỏ Chúa Bình An.")]),n._v(" "),i("p",[n._v("Trong khi cập nhật hóa cuốn sách này, lời cầu nguyện của tôi là bạn không trì hoãn tìm kiếm giải đáp cho vấn nạn của bạn thêm một giây phút nào nữa. So với ấn bản đầu tiên, thời gian còn lại kể từ ấn bản này đã ngắn hơn khá nhiều. Ít người trong chúng ta có thể biết chắc khi nào mình sẽ phải đối diện với cái chết. Xin bạn hãy tiếp nhận sự bình an với Chúa ngay hôm nay.")]),n._v(" "),i("p",[n._v("Để có bình an với Chúa, xin bạn thưa với Chúa lời cầu nguyện đơn thành sau đây:")]),n._v(" "),i("p",[i("em",[n._v("Lạy Đức Chúa Trời, là Đấng Tạo Hóa, con là một tội nhân trước mặt Ngài. Đã bao năm qua con không biết Ngài cũng không tôn thờ Ngài là Đấng đã tạo dựng nên con, ban cho con sự sống, hơi thở và cho con hiện hữu trong thế giới cũng do chính Ngài tạo dựng. Con đã lỗi phạm rất nhiều với Chúa khi không tuân giữ các giới răn của Ngài, và trên hết đã không tôn thờ Chúa. Trái lại con đã lầm lạc tôn thờ con người, hình tượng, tà thần, cũng tôn thờ chính mình trong cuộc sống kiêu ngạo và vị kỷ.")])]),n._v(" "),i("p",[i("em",[n._v("Con đau buồn về tất cả những lỗi phạm con đã gây ra suốt bao năm tháng qua. Hôm nay con ăn năn thống hối về cả cuộc đời cũ và biết rằng con không thể nào trở về quá khứ để có thể sửa lại những lầm lỗi cũ, cho nên con xin tiếp nhận Chúa Giê-xu làm Đấng Cứu Chuộc con. Ngài chính là Đức Chúa Trời, vì yêu thương con, đã mang thân xác con người bước vào thế giới, chịu chết trên thập tự giá để chịu án chết thay thế cho con, để trả cho con món nợ tội lỗi con không bao giờ có thể trả nổi. Xin Chúa tiếp nhận con, xin ban Đức Thánh Linh đổi mới tâm hồn, tâm trí, tấm lòng và cả cuộc đời con để con từ thân phận tội nhân trở thành con cái Đức Chúa Trời.")])]),n._v(" "),i("p",[i("em",[n._v("Xin ban cho con niềm vui và sự bình an thâm sâu trong linh hồn để mối tương giao giữa con và Đức Chúa Trời là Cha được nối lại. Cũng xin Cha giúp con liên lạc với các tín hữu trong các hội thánh địa phương để con có cơ hội tôn thờ, học hỏi và phục vụ Ngài cùng với các anh chị em con trong đức tin. Con cầu nguyện tin nhận Chúa, nhân danh Chúa Cứu Thế Giê-xu. A-men.")])]),n._v(" "),i("p",[n._v("Với lời cầu nguyện chân thành trên bạn đã quay trở lại với Đức Chúa Trời, bạn đã đã được giải hòa với Ngài để có sự bình an với Chúa và trở thành con cái Đức Chúa Trời như lời Chúa hứa, "),i("strong",[n._v('"hễ ai nhận Ngài thì Ngài ban cho quyền trở nên con cái Đức Chúa Trời, là ban cho những kẻ tin danh Ngài" (Giăng 1:12)')]),n._v(".")]),n._v(" "),i("p",[n._v("Xin bạn liên lạc với Hội Thánh Tin Lành gần nơi bạn ở để được khích lệ và hướng dẫn ngay từ bước đầu theo Chúa.")])],1)},[],!1,null,null,null);h.default=c.exports}}]);